#!/usr/bin/env python
	# This is a shim to allow GitHub to detect the package, build is done with poetry
	# Taken from https://github.com/Textualize/rich
	import setuptools
	if __name__ == "__main__":
	    setuptools.setup(name="boost-loss")

import warnings
	from unittest import SkipTest, TestCase
	import numpy as np
	from parameterized import parameterized_class
	from boost_loss.base import LossBase
	from boost_loss.regression.regression import L2Loss
	try:
	    from boost_loss.torch import TorchLossBase, _LNLossTorch, _LNLossTorch_
	except ValueError as e:
	    raise SkipTest(f"Failed to import TorchLossBase from PyTorch. Skip the test. {e}")
else:
	    PARAMETERS = [
	        (_LNLossTorch_(n=2, divide_n_loss=False), L2Loss(divide_n_grad=False)),
	        (_LNLossTorch(n=2, divide_n_grad=True), L2Loss(divide_n_grad=True)),
	    ]
	from .test_base import assert_array_almost_equal
	try:
	    from torch.nn.modules.loss import MSELoss
	    PARAMETERS.append(
	        (TorchLossBase.from_callable_torch(MSELoss())(), L2Loss(divide_n_grad=False))
    )
	except ValueError as e:
	    warnings.warn(f"Failed to import MSELoss from PyTorch. Skip the test. {e}")
	@parameterized_class(("loss_torch", "loss_base"), PARAMETERS)
	class TestLossTorch(TestCase):
	    loss_torch: TorchLossBase
	    loss_base: LossBase
	    def setUp(self) -> None:
	        self.y_pred = np.random.randn(10)
	        self.y_true = np.random.randn(10)
    def test_consistent(self) -> None:
	        loss_torch = self.loss_torch
	        loss_base = self.loss_base
	        assert_array_almost_equal(
	            np.mean(loss_torch.loss(self.y_true, self.y_pred)),
	            np.mean(loss_base.loss(self.y_true, self.y_pred)),
	        )
	        assert_array_almost_equal(
	            loss_torch.grad_hess(self.y_true, self.y_pred)[0],
	            loss_base.grad_hess(self.y_true, self.y_pred)[0],
        )
	        assert_array_almost_equal(
	            loss_torch.grad_hess(self.y_true, self.y_pred)[1],
	            loss_base.grad_hess(self.y_true, self.y_pred)[1],
	        )

from __future__ import annotations
	import warnings
	from abc import ABCMeta
	from logging import getLogger
	from numbers import Real
	from typing import Any, Callable, Sequence, final
	import attrs
	import humps
	import lightgbm as lgb
	import numpy as np
import xgboost as xgb
	from numpy.typing import NDArray
	from typing_extensions import Self
	LOG = getLogger(__name__)
	def _dataset_to_ndarray(
	    y: NDArray | lgb.Dataset | xgb.DMatrix,
	) -> tuple[NDArray, NDArray]:
	    if isinstance(y, lgb.Dataset):
	        y_ = y.get_label()
	        if y_ is None:
            raise ValueError("y is None")
	        weight = y.get_weight()
	        if weight is None:
	            weight = np.ones_like(y_)
	        return y_, weight
	    if isinstance(y, xgb.DMatrix):
	        y_ = y.get_label()
	        return y_, np.ones_like(y_)
	    return y, np.ones_like(y)
	def _get_name_from_callable(obj: Callable[..., Any]) -> str:
    if hasattr(obj, "__name__"):
	        return getattr(obj, "__name__")
	    if hasattr(obj, "__class__") and hasattr(getattr(obj, "__class__"), "__name__"):
	        return getattr(getattr(obj, "__class__"), "__name__")
	    raise ValueError(f"Could not get name from callable {obj}")
	class LossBase(metaclass=ABCMeta):
	    """Base class for loss functions.
	    Inherit this class to implement custom loss function.
	    See Also
	    --------
    Catboost:
	    https://catboost.ai/en/docs/concepts/python-usages-examples#user-defined-loss-function
	    LightGBM:
	    https://lightgbm.readthedocs.io/en/latest/Advanced-Topics.html#custom-objective-function
	    XGBoost:
	    https://xgboost.readthedocs.io/en/latest/tutorials/custom_metric_obj.html
	    Example
	    -------
	    >>> from boost_loss.base import LossBase
	    >>> import numpy as np
    >>> from numpy.typing import NDArray
	    >>>
	    >>> class L2Loss(LossBase):
	    >>>     def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	    >>>         return (y_true - y_pred) ** 2
	    >>>     def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray: # dL/dy_pred
	    >>>         return -2 * (y_true - y_pred) # or (y_pred - y_true)
	    >>>     def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray: # d2L/dy_pred2
	    >>>         return 2 * np.ones_like(y_true) # or np.ones_like(y_true)
	    >>>
    >>> from boost.sklearn import apply_custom_loss
	    >>> import lightgbm as lgb
	    >>> apply_custom_loss(lgb.LGBMRegressor(), L2Loss()).fit(X, y)
	    """
	    is_higher_better: bool = False
	    """Whether the result of loss function is better when it is higher."""
	    @classmethod
	    @final
	    def from_callable(
	        cls,
        loss: Callable[[NDArray, NDArray], NDArray | float],
	        grad: Callable[[NDArray, NDArray], NDArray],
	        hess: Callable[[NDArray, NDArray], NDArray],
	        name: str | None = None,
	        is_higher_better: bool = False,
	    ) -> type[Self]:
	        """Create this class from loss, grad, and hess callables.
	        Parameters
	        ----------
	        loss : Callable[[NDArray, NDArray], NDArray | float]
            The loss function. If 1-D array is returned,
	            the mean of array is calculated.
	            Return 1-D array if possible in order to utilize weights in the dataset
	            if available.
	            (y_true, y_pred) -> loss
	        grad : Callable[[NDArray, NDArray], NDArray]
	            The 1st order derivative (gradient) of loss w.r.t. y_pred.
	            (y_true, y_pred) -> grad
	        hess : Callable[[NDArray, NDArray], NDArray]
	            The 2nd order derivative (Hessian) of loss w.r.t. y_pred.
            (y_true, y_pred) -> hess
	        name : str | None, optional
	            The name of loss function.
	            If None, it tries to infer from loss function, by default None
	        is_higher_better : bool, optional
	            Whether the result of loss function is better when it is higher,
	            by default False
	        Returns
	        -------
	        type[Self]
            The subclass of this class.
	        Raises
	        ------
	        ValueError
	            If name is None and it can't infer from loss function.
	        """
	        if name is None:
	            try:
	                name = _get_name_from_callable(loss)
	            except ValueError as e:
                raise ValueError(
	                    "Could not infer name from loss function. Please specify name."
	                ) from e
	        return type(
	            name,
	            (cls,),
	            dict(
	                loss=staticmethod(loss),
	                grad=staticmethod(grad),
	                hess=staticmethod(hess),
                is_higher_better=is_higher_better,
	            ),
	        )
	    @property
	    def _grad_hess_sign(self) -> int:
	        return -1 if self.is_higher_better else 1
	    def __init_subclass__(cls, **kwargs: Any) -> None:
	        grad_inherited = cls.grad is not LossBase.grad
	        hess_inherited = cls.hess is not LossBase.hess
	        grad_hess_inherited = cls.grad_hess is not LossBase.grad_hess
        if grad_inherited and hess_inherited:
	            pass
	        elif grad_hess_inherited:
	            pass
	        else:
	            raise TypeError(
	                f"Can't instantiate abstract class {cls.__name__} "
	                "with grad_hess or both grad and hess not implemented"
	            )
	        super().__init_subclass__(**kwargs)
    @property
	    def name(self) -> str:
	        """Name of loss function.
	        Returns
	        -------
	        str
	            Snake case of class name. e.g. `LogCoshLoss` -> `log_cosh_loss`.
	        """
	        return humps.decamelize(self.__class__.__name__.replace("Loss", ""))
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
        """The 1st order derivative (gradient) of loss w.r.t. y_pred.
	        Parameters
	        ----------
	        y_true : NDArray
	            The true target values.
	        y_pred : NDArray
	            The predicted target values.
	        Returns
	        -------
	        NDArray
            The gradient of loss function. 1-D array with shape (n_samples,).
	        Raises
	        ------
	        NotImplementedError
	            If not implemented.
	        """
	        raise NotImplementedError()
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        """The 2nd order derivative (hessian) of loss w.r.t. y_pred.
	        Parameters
        ----------
	        y_true : NDArray
	            The true target values.
	        y_pred : NDArray
	            The predicted target values.
	        Returns
	        -------
	        NDArray
	            The hessian of loss function. 1-D array with shape (n_samples,).
	        Raises
        ------
	        NotImplementedError
	            If not implemented.
	        """
	        raise NotImplementedError()
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray | float:
	        """Loss function. If 1-D array is returned, the mean of array is calculated.
	        Return 1-D array if possible in order to utilize weights in the dataset
	        if available.
	        Parameters
        ----------
	        y_true : NDArray
	            The true target values.
	        y_pred : NDArray
	            The predicted target values.
	        Returns
	        -------
	        NDArray | float
	            The loss function. 1-D array with shape (n_samples,) or float.
	        Raises
        ------
	        NotImplementedError
	            If not implemented.
	        """
	        raise NotImplementedError()
	    def grad_hess(self, y_true: NDArray, y_pred: NDArray) -> tuple[NDArray, NDArray]:
	        """Gradient and hessian of loss function. Override this method if you want to
	        calculate both gradient and hessian at the same time.
	        Parameters
	        ----------
        y_true : NDArray
	            The true target values.
	        y_pred : NDArray
	            The predicted target values.
	        Returns
	        -------
	        tuple[NDArray, NDArray]
	            The gradient and hessian of loss function.
	            1-D array with shape (n_samples,).
	        """
        return self.grad(y_true=y_true, y_pred=y_pred), self.hess(
	            y_true=y_true, y_pred=y_pred
	        )
	    def _grad_hess_weighted(
	        self, y_true: NDArray, y_pred: NDArray, weight: NDArray
	    ) -> tuple[NDArray, NDArray]:
	        grad, hess = self.grad_hess(y_true=y_true, y_pred=y_pred)
	        if np.any(hess < 0):
	            negative_rate = np.mean(hess < 0)
	            warnings.warn(
                f"Found negative hessian in {negative_rate:.2%} samples."
	                "This may cause convergence issue and cause CatBoostError in CatBoost."
	                "If LightGBM or XGBoost is used, the estimator will return "
	                "nonsense values (like all 0s if 100%).",
	                RuntimeWarning,
	            )
	        grad, hess = grad * weight, hess * weight
	        grad, hess = grad * self._grad_hess_sign, hess * self._grad_hess_sign
	        return grad, hess
	    @final
    def __call__(
	        self,
	        y_true: NDArray | lgb.Dataset | xgb.DMatrix,
	        y_pred: NDArray | lgb.Dataset | xgb.DMatrix,
	    ) -> tuple[NDArray, NDArray]:
	        """Sklearn-compatible interface (Sklearn, LightGBM, XGBoost)"""
	        if isinstance(y_pred, lgb.Dataset) or isinstance(y_pred, xgb.DMatrix):
	            # NOTE: swap (it is so fucking that the order is inconsistent)
	            y_true, y_pred = y_pred, y_true
	        y_true, weight = _dataset_to_ndarray(y=y_true)
        y_pred, _ = _dataset_to_ndarray(y=y_pred)
	        return self._grad_hess_weighted(y_true=y_true, y_pred=y_pred, weight=weight)
	    @final
	    def calc_ders_range(
	        self,
	        preds: Sequence[float],
	        targets: Sequence[float],
	        weights: Sequence[float] | None = None,
	    ) -> list[tuple[float, float]]:
	        """Catboost-compatible interface"""
        y_pred = np.array(preds)
	        y_true = np.array(targets)
	        weight = np.array(weights) if weights is not None else np.ones_like(y_pred)
	        grad, hess = self._grad_hess_weighted(
	            y_true=y_true, y_pred=y_pred, weight=weight
	        )
	        # NOTE: in catboost, the definition of loss is the inverse
	        grad, hess = -grad, -hess
	        return list(zip(grad, hess))
	    @final
    def is_max_optimal(self) -> bool:
	        """Catboost-compatible interface"""
	        return self.is_higher_better
	    @final
	    def evaluate(
	        self,
	        approxes: Sequence[float],
	        target: Sequence[float],
	        weight: Sequence[float] | None = None,
	    ) -> tuple[float, float]:
        """Catboost-compatible interface"""
	        approxes_ = np.array(approxes[0])
	        targets_ = np.array(target)
	        weights_ = np.array(weight) if weight is not None else np.ones_like(approxes_)
	        loss = self.loss(y_true=targets_, y_pred=approxes_)
	        if isinstance(loss, float) and not np.allclose(weights_, 1.0):
	            warnings.warn("loss() should return ndarray when weight is not all 1.0")
	            return loss, np.nan
	        return float(np.sum(loss * weights_)), float(np.sum(weights_))
	    @final
    def get_final_error(self, error: float, weight: float | None = None) -> float:
	        """Catboost-compatible interface"""
	        return error / (weight + 1e-38) if weight is not None else error
	    @final
	    def eval_metric_lgb(
	        self,
	        y_true: NDArray | lgb.Dataset | xgb.DMatrix,
	        y_pred: NDArray | lgb.Dataset | xgb.DMatrix,
	    ) -> tuple[str, float, bool]:
	        """LightGBM-compatible interface"""
        if isinstance(y_pred, lgb.Dataset) or isinstance(y_pred, xgb.DMatrix):
	            # NOTE: swap (it is so fucking that the order is inconsistent)
	            y_true, y_pred = y_pred, y_true
	        y_true, weight = _dataset_to_ndarray(y=y_true)
	        y_pred, _ = _dataset_to_ndarray(y=y_pred)
	        loss = self.loss(y_true=y_true, y_pred=y_pred)
	        if isinstance(loss, float) and not np.allclose(weight, 1.0):
	            warnings.warn("loss() should return ndarray when weight is not all 1.0")
	            return self.name, loss, self.is_higher_better
	        return (
            self.name,
	            float(np.sum(loss * weight) / (np.sum(weight) + 1e-38)),
	            self.is_higher_better,
	        )
	    @final
	    def eval_metric_xgb_native(
	        self,
	        y_true: NDArray | lgb.Dataset | xgb.DMatrix,
	        y_pred: NDArray | lgb.Dataset | xgb.DMatrix,
	    ) -> tuple[str, float]:
        """XGBoost-native-api-compatible interface"""
	        result = self.eval_metric_lgb(y_true=y_true, y_pred=y_pred)
	        return result[0], result[1]
	    @final
	    def eval_metric_xgb_sklearn(
	        self,
	        y_true: NDArray | lgb.Dataset | xgb.DMatrix,
	        y_pred: NDArray | lgb.Dataset | xgb.DMatrix,
	    ) -> float:
	        """XGBoost-sklearn-api-compatible interface"""
        result = self.eval_metric_lgb(y_true=y_true, y_pred=y_pred)
	        return result[1]
	    def __add__(self, other: LossBase) -> LossBase:
	        if not isinstance(other, LossBase):
	            return NotImplemented  # type: ignore
	        return _LossSum(self, other)
	    def __sub__(self, other: LossBase) -> LossBase:
	        return self.__add__(-other)
	    def __mul__(self, other: float | int | Real) -> LossBase:
	        if not isinstance(other, Real):
            return NotImplemented
	        return _LossMul(self, other)
	    def __div__(self, other: float | int | Real) -> LossBase:
	        return self.__mul__(1.0 / other)
	    def __radd__(self, other: LossBase) -> LossBase:
	        return self.__add__(other)
	    def __rsub__(self, other: LossBase) -> LossBase:
	        return self.__sub__(other)
	    def __rmul__(self, other: float | int | Real) -> LossBase:
	        return self.__mul__(other)
    def __rdiv__(self, other: float | int | Real) -> LossBase:
	        return self.__div__(other)
	    def __neg__(self) -> LossBase:
	        return self.__mul__(-1.0)
	    def __pos__(self) -> Self:
	        return self
	@attrs.define()
	class _LossSum(LossBase):
	    loss1: LossBase
	    loss2: LossBase
    @property
	    def name(self) -> str:
	        return self.loss1.name + "+" + self.loss2.name
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray | float:
	        return self.loss1.loss(y_true=y_true, y_pred=y_pred) + self.loss2.loss(
	            y_true=y_true, y_pred=y_pred
	        )
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return self.loss1.grad(y_true=y_true, y_pred=y_pred) + self.loss2.grad(
	            y_true=y_true, y_pred=y_pred
        )
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return self.loss1.hess(y_true=y_true, y_pred=y_pred) + self.loss2.hess(
	            y_true=y_true, y_pred=y_pred
	        )
	    def grad_hess(self, y_true: NDArray, y_pred: NDArray) -> tuple[NDArray, NDArray]:
	        grad1, hess1 = self.loss1.grad_hess(y_true=y_true, y_pred=y_pred)
	        grad2, hess2 = self.loss2.grad_hess(y_true=y_true, y_pred=y_pred)
	        return grad1 + grad2, hess1 + hess2
	@attrs.define()
class _LossMul(LossBase):
	    loss_: LossBase
	    factor: float | int | Real
	    @property
	    def name(self) -> str:
	        return f"{self.factor}*{self.loss_.name}"
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray | float:
	        return self.factor * self.loss_.loss(y_true=y_true, y_pred=y_pred)
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return self.factor * self.loss_.grad(y_true=y_true, y_pred=y_pred)
    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return self.factor * self.loss_.hess(y_true=y_true, y_pred=y_pred)
	    def grad_hess(self, y_true: NDArray, y_pred: NDArray) -> tuple[NDArray, NDArray]:
	        grad, hess = self.loss_.grad_hess(y_true=y_true, y_pred=y_pred)
	        return self.factor * grad, self.factor * hess

from __future__ import annotations
	from typing import Sequence
	import numpy as np
	from numpy.typing import NDArray
	from .base import LossBase
	class ResumingLoss(LossBase):
	    def __init__(
	        self,
	        losses: Sequence[LossBase],
	        *,
        weights: Sequence[float] | None = None,
	        interval: int = 1,
	        random_state: int | None = None,
	    ) -> None:
	        self.losses = losses
	        if weights is None:
	            self.weights = np.ones_like(losses)
	        else:
	            self.weights = np.array(weights)
	        self.interval = interval
        self.random_state = random_state
	        if self.random_state is None:
	            if weights is not None:
	                raise ValueError("weights must be None when random_state is None")
	        else:
	            self.random = np.random.RandomState(self.random_state)
	        self._count = 0
	        self._idx = 0
	    def grad_hess(self, y_true: NDArray, y_pred: NDArray) -> tuple[NDArray, NDArray]:
	        if self._count % self.interval == 0:
            if self.random_state is None:
	                self._idx = self.random.choice(len(self.losses), p=self.weights)
	            else:
	                self._idx = (self._count // self.interval) % len(self.losses)
	        self._count += 1
	        return self.losses[self._idx].grad_hess(y_true=y_true, y_pred=y_pred)
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray | float:
	        return self.losses[self._idx].loss(y_true=y_true, y_pred=y_pred)

from __future__ import annotations
	from logging import getLogger
	import attrs
	import numpy as np
	from numpy.typing import NDArray
	from ..base import LossBase
	LOG = getLogger(__name__)
	# cannot freeze due to FrozenInstanceError in catboost
	@attrs.define()
	class LNLoss(LossBase):
    """LNLoss = |y_true - y_pred|^n
	    - x < 1 is not recommended because the loss is not convex.
	    - x >> 2 is not recommended because the gradient is too steep."""
	    n: float
	    """The exponent of the loss."""
	    divide_n_loss: bool = False
	    """Whether to divide the loss by n. Generally False is used."""
	    divide_n_grad: bool = True
	    """Whether to divide the gradient by n. Generally True is used."""
	    @property
    def name(self) -> str:
	        return f"l{self.n:.2f}"
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.abs(y_pred - y_true) ** self.n / (self.n if self.divide_n_loss else 1)
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        y_diff = y_pred - y_true
	        return (
	            np.abs(y_diff) ** (self.n - 1)
	            * np.sign(y_diff)
	            * self.n
            / (self.n if self.divide_n_grad else 1)
	        )
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        y_diff = y_pred - y_true
	        return (
	            (self.n - 1)
	            * np.abs(y_diff) ** (self.n - 2)
	            * self.n
	            / (self.n if self.divide_n_grad else 1)
	        )
class L1Loss(LNLoss):
	    """L1 loss = |y_true - y_pred|."""
	    def __init__(
	        self, *, divide_n_loss: bool = False, divide_n_grad: bool = True
	    ) -> None:
	        """L1 loss.
	        Parameters
	        ----------
	        divide_n_loss : bool, optional
	            Whether to divide the loss by n, by default False
        divide_n_grad : bool, optional
	            Whether to divide the gradient by n, by default True
	        """
	        super().__init__(n=1, divide_n_loss=divide_n_loss, divide_n_grad=divide_n_grad)
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.ones_like(y_pred - y_true)  # zero hess is not allowed
	class L2Loss(LNLoss):
	    """L2 loss = |y_true - y_pred|^2"""
	    def __init__(
	        self, *, divide_n_loss: bool = False, divide_n_grad: bool = True
    ) -> None:
	        """L2 loss.
	        Parameters
	        ----------
	        divide_n_loss : bool, optional
	            Whether to divide the loss by n, by default False
	        divide_n_grad : bool, optional
	            Whether to divide the gradient by n, by default True
	        """
	        super().__init__(n=2, divide_n_loss=divide_n_loss, divide_n_grad=divide_n_grad)
class LogCoshLoss(LossBase):
	    """LogCosh loss = log(cosh(y_true - y_pred))"""
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.log(np.cosh(y_pred - y_true))
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.tanh(y_pred - y_true)
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.cosh(y_pred - y_true) ** -2
	class HuberLoss(LossBase):
	    """Huber loss = 0.5 (y_true - y_pred)^2 if |y_true - y_pred| <= delta
    else delta * (|y_true - y_pred| - 0.5 * delta)"""
	    def __init__(self, delta: float = 1.0) -> None:
	        """Huber loss.
	        Parameters
	        ----------
	        delta : float, optional
	            The parameter delta, by default 1.0
	        """
	        self.delta = delta
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
        y_diff = y_pred - y_true
	        return np.where(
	            np.abs(y_diff) <= self.delta,
	            0.5 * y_diff**2,
	            self.delta * (np.abs(y_diff) - 0.5 * self.delta),
	        )
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        y_diff = y_pred - y_true
	        return np.where(
	            np.abs(y_diff) <= self.delta,
            y_diff,
	            np.sign(y_diff) * self.delta,
	        )
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.ones_like(y_pred - y_true)  # zero hess is not allowed
	        # return np.where(
	        #     np.abs(y_pred - y_true) <= self.delta,
	        #     np.ones_like(y_true),
	        #     np.zeros_like(y_true),
	        # )
class FairLoss(LossBase):
	    """Fair loss = c^2/2 * (abs(y_true - y_pred) -
	    c * log(1 + abs(y_true - y_pred)/c))"""
	    def __init__(self, c: float = 1.0) -> None:
	        """Fair loss.
	        Parameters
	        ----------
	        c : float, optional
	            The parameter c, by default 1.0
	        """
        self.c = c
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return (
	            self.c**2
	            / 2
	            * (
	                np.abs(y_true - y_pred)
	                - self.c * np.log(1 + np.abs(y_true - y_pred) / self.c)
	            )
	        )
    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return (
	            self.c**2
	            / 2
	            * (np.sign(y_pred - y_true) - self.c / (self.c + np.abs(y_pred - y_true)))
	        )
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return (
	            self.c**2
	            / 2
            * (np.zeros_like(y_true) + self.c / (self.c + np.abs(y_pred - y_true)) ** 2)
	        )
	class PoissonLoss(LossBase):
	    """Poisson loss = y_pred - y_true * log(y_pred)"""
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return y_pred - y_true * np.log(y_pred)
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return 1 - y_true / y_pred
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return y_true / y_pred**2
class LogLoss(LossBase):
	    """Log loss = log(1 + exp(-y_true * y_pred))"""
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.log(1 + np.exp(-y_true * y_pred))
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return -y_true / (1 + np.exp(y_true * y_pred))
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return (
	            y_true**2 * np.exp(y_true * y_pred) / (1 + np.exp(y_true * y_pred)) ** 2
	        )
class MSLELoss(LossBase):
	    """Mean squared logarithmic error loss = (log(1 + y_true) - log(1 + y_pred))^2"""
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.square(np.log1p(y_true) - np.log1p(y_pred))
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return -2 * (np.log1p(y_true) - np.log1p(y_pred)) / (1 + y_pred)
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return 2 * (np.log1p(y_true) - np.log1p(y_pred)) / (1 + y_pred) ** 2
	class MAPELoss(LossBase):
	    """Mean absolute percentage error loss = abs(y_true - y_pred) / y_true"""
    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.abs(y_true - y_pred) / y_true
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.sign(y_pred - y_true) / y_true
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.zeros_like(y_true)
	class SMAPELoss(LossBase):
	    """Symmetric mean absolute percentage error loss =
	    abs(y_true - y_pred) / ((abs(y_true) + abs(y_pred))/2)"""
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
        return np.abs(y_true - y_pred) / ((np.abs(y_true) + np.abs(y_pred)) / 2)
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.sign(y_pred - y_true) / ((np.abs(y_true) + np.abs(y_pred)) / 2)
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return np.zeros_like(y_true)
	class TweedieLoss(LossBase):
	    """Tweedie loss = -y_true * y_pred^(2-p) / (2-p) + y_pred^(1-p) / (1-p)"""
	    def __init__(self, p: float = 1.5) -> None:
	        """Tweedie loss.
	        Parameters
        ----------
	        p : float, optional
	            The parameter p, by default 1.5
	        """
	        self.p = p
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return -y_true * y_pred ** (2 - self.p) / (2 - self.p) + y_pred ** (
	            1 - self.p
	        ) / (1 - self.p)
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
        return -y_true * (2 - self.p) * y_pred ** (1 - self.p) + y_pred ** (-self.p)
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return y_true * (2 - self.p) * (1 - self.p) * y_pred ** (
	            -self.p - 1
	        ) - self.p * y_pred ** (-self.p - 1)
	class GammaLoss(LossBase):
	    """Gamma loss = y_true / y_pred - log(y_true / y_pred) - 1"""
	    def loss(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return y_true / y_pred - np.log(y_true / y_pred) - 1
	    def grad(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
        return -y_true / y_pred**2 + 1 / y_pred
	    def hess(self, y_true: NDArray, y_pred: NDArray) -> NDArray:
	        return 2 * y_true / y_pred**3 - 1 / y_pred**2
