from contextlib import contextmanager
	from functools import partial
	from typing import Callable
	from torch import Tensor, nn
	from .leace import LeaceEraser
	from .utils import assert_type, is_norm_layer, mangle_module_path
	class ConceptScrubber:
	    """Wrapper for a dictionary mapping module paths to `LeaceEraser` objects."""
	    def __init__(self, pre_hook: bool = False):
	        super().__init__()
        self.erasers: dict[str, LeaceEraser] = {}
	        self.pre_hook = pre_hook
	    @contextmanager
	    def scrub(self, model):
	        """Add hooks to the model which apply the erasers during a forward pass."""
	        def scrub_hook(key: str, x: Tensor):
	            eraser = assert_type(LeaceEraser, self.erasers[key])
	            return eraser(x).type_as(x)
	        with self.apply_hook(model, scrub_hook):
	            yield self
    @contextmanager
	    def apply_hook(
	        self,
	        model: nn.Module,
	        hook_fn: Callable[[str, Tensor], Tensor | None],
	    ):
	        """Apply a `hook_fn` to each submodule in `model` that we're scrubbing."""
	        def post_wrapper(_, __, output, name: str) -> Tensor | None:
	            key = mangle_module_path(name)
	            return hook_fn(key, output)
        def pre_wrapper(_, inputs, name: str) -> tuple[Tensor | None, ...]:
	            x, *extras = inputs
	            key = mangle_module_path(name)
	            return hook_fn(key, x), *extras
	        # Unwrap the base model if necessary. This is needed to ensure we don't try to
	        # scrub right before the unembedding layer
	        from transformers import PreTrainedModel
	        if isinstance(model, PreTrainedModel):
	            model = assert_type(PreTrainedModel, model.base_model)
	        handles = [
            (
	                mod.register_forward_pre_hook(partial(pre_wrapper, name=name))
	                if self.pre_hook
	                else mod.register_forward_hook(partial(post_wrapper, name=name))
	            )
	            for name, mod in model.named_modules()
	            if is_norm_layer(mod) and mangle_module_path(name) in self.erasers
	        ]
	        assert len(handles) == len(self.erasers), "Not all erasers can be applied"
	        try:
            yield self
	        finally:
	            # Make sure to remove the hooks even if an exception is raised
	            for handle in handles:
	                handle.remove()

from typing import Any, Type, TypeVar, cast
	from torch import nn
	T = TypeVar("T")
	def assert_type(typ: Type[T], obj: Any) -> T:
	    """Assert that an object is of a given type at runtime and return it."""
	    if not isinstance(obj, typ):
	        raise TypeError(f"Expected {typ.__name__}, got {type(obj).__name__}")
	    return cast(typ, obj)
	def is_norm_layer(module: nn.Module) -> bool:
	    """Return `True` if the module is a normalization layer."""
    cls_name = type(module).__name__
	    return cls_name.endswith("LayerNorm") or cls_name.endswith("RMSNorm")
	def mangle_module_path(name: str) -> str:
	    """Mangle a module path to make it a valid key in a `nn.ModuleDict`."""
	    # This is a weird edge case we probably don't need to support
	    assert "-" not in name, "Module path cannot contain `-`"
	    return name.replace(".", "-")

from functools import wraps
	from typing import Callable
	def cached_property(func: Callable) -> property:
	    """Decorator that converts a method into a lazily-evaluated cached property"""
	    # Create a secret attribute name for the cached property
	    attr_name = "_cached_" + func.__name__
	    @property
	    @wraps(func)
	    def _cached_property(self):
	        # If the secret attribute doesn't exist, compute the property and set it
        if not hasattr(self, attr_name):
	            setattr(self, attr_name, func(self))
	        # Otherwise, return the cached property
	        return getattr(self, attr_name)
	    return _cached_property
	def invalidates_cache(dependent_prop_name: str) -> Callable:
	    """Invalidates a cached property when the decorated function is called"""
	    attr_name = "_cached_" + dependent_prop_name
	    # The actual decorator
	    def _invalidates_cache(func: Callable) -> Callable:
        # The wrapper function
	        @wraps(func)
	        def wrapper(self, *args, **kwargs):
	            # Check if the secret attribute exists; if so delete it so that
	            # the cached property is recomputed
	            if hasattr(self, attr_name):
	                delattr(self, attr_name)
	            return func(self, *args, **kwargs)
	        return wrapper
	    return _invalidates_cache

from warnings import warn
	import torch.nn.functional as F
	from datasets import Dataset
	from transformers import GPTNeoXForCausalLM, LlamaForCausalLM, PreTrainedModel
	from ..concept_scrubber import ConceptScrubber
	from ..leace import ErasureMethod
	from .llama import patch_attention_llama_, scrub_llama
	from .neox import patch_attention_neox_, scrub_neox
	def patch_attention_(model: PreTrainedModel):
	    """Patch the attention layers of a model to use fast attention kernels."""
    ty = model.config.model_type
	    if not hasattr(F, "scaled_dot_product_attention"):
	        warn(
	            "Fast, memory-efficient attention requires PyTorch >= 2.0.0. "
	            "For best performance, please upgrade your version of PyTorch."
	        )
	        return
	    if ty == "gpt_neox":
	        patch_attention_neox_(model)
	    elif ty == "llama":
        patch_attention_llama_(model)
	    else:
	        raise NotImplementedError(f"Unsupported model type: {type(model)}")
	def scrub(
	    model: PreTrainedModel,
	    train: Dataset,
	    z_column: str | None,
	    affine: bool = True,
	    batch_size: int = 1,
	    method: ErasureMethod = "leace",
    sublayers: bool = True,
	) -> tuple[ConceptScrubber | None, float]:
	    """Apply concept scrubbing to `model` on dataset `train`, returning the scrubber."""
	    if isinstance(model, GPTNeoXForCausalLM):
	        return scrub_neox(model, train, z_column, batch_size, method, affine)
	    elif isinstance(model, LlamaForCausalLM):
	        return scrub_llama(
	            model, train, z_column, batch_size, method, sublayers, affine
	        )
	    else:
        raise NotImplementedError(f"Unsupported model type: {type(model).__name__}")

from contextlib import contextmanager
	from functools import partial
	from typing import TYPE_CHECKING
	import torch
	from torch import Tensor, nn
	if (
	    TYPE_CHECKING
	):  # Don't import this unless we're type checking, since it's slow to import
	    from transformers import PreTrainedModel
	from ..utils import is_norm_layer
@contextmanager
	def random_scrub(model: "PreTrainedModel", subspace_dim: int):
	    """Add hooks to the model which erase a random subspace during `forward`."""
	    d = model.config.hidden_size
	    u = torch.empty(d, subspace_dim, device=model.device, dtype=model.dtype)
	    nn.init.orthogonal_(u)
	    def random_erase(_, __, x: Tensor, u: Tensor) -> Tensor:
	        return x - u @ (u.T @ x)
	    handles = [
	        mod.register_forward_hook(partial(random_erase, u=u))
        for mod in model.modules()
	        if is_norm_layer(mod)
	    ]
	    try:
	        yield
	    finally:
	        # Make sure to remove the hooks even if an exception is raised
	        for handle in handles:
	            handle.remove()
