	def get_full_path(folder_name, filename):
	    global folder_names_and_paths
	    if folder_name not in folder_names_and_paths:
	        return None
	    folders = folder_names_and_paths[folder_name]
    filename = os.path.relpath(os.path.join("/", filename), "/")
	    for x in folders[0]:
	        full_path = os.path.join(x, filename)
	        if os.path.isfile(full_path):
	            return full_path
	    return None
	def recursive_search(directory):
	    if not os.path.isdir(directory):
	        return [], {}
	    result = []
    dirs = {directory: os.path.getmtime(directory)}
	    for root, subdir, file in os.walk(directory, followlinks=True):
	        for filepath in file:
	            #we os.path,join directory with a blank string to generate a path separator at the end.
	            result.append(os.path.join(root, filepath).replace(os.path.join(directory,''),''))
	        for d in subdir:
	            path = os.path.join(root, d)
	            dirs[path] = os.path.getmtime(path)
	    return result, dirs
	def filter_files_extensions(files, extensions):
    return sorted(list(filter(lambda a: os.path.splitext(a)[-1].lower() in extensions, files)))
	def filter_files_contains(files, contains):
	    for x in contains:
	        files = list(filter(lambda a: x in a, files))
	    return sorted(files)
	def get_filename_list_(folder_name):
	    global folder_names_and_paths
	    output_list = set()
	    folders = folder_names_and_paths[folder_name]
	    output_folders = {}
    for x in folders[0]:
	        files, folders_all = recursive_search(x)
	        output_list.update(filter_files_extensions(files, folders[1]))
	        output_folders = {**output_folders, **folders_all}
	    return (sorted(list(output_list)), output_folders, time.perf_counter())
	def cached_filename_list_(folder_name):
	    global filename_list_cache
	    global folder_names_and_paths
	    if folder_name not in filename_list_cache:
	        return None
    out = filename_list_cache[folder_name]
	    if time.perf_counter() < (out[2] + 0.5):
	        return out
	    for x in out[1]:
	        time_modified = out[1][x]
	        folder = x
	        if os.path.getmtime(folder) != time_modified:
	            return None
	    folders = folder_names_and_paths[folder_name]
	    for x in folders[0]:
        if os.path.isdir(x):
	            if x not in out[1]:
	                return None
	    return out
	def get_filename_list(folder_name):
	    global filename_list_cache
	    out = cached_filename_list_(folder_name)
	    if out is None:
	        out = get_filename_list_(folder_name)
	        filename_list_cache[folder_name] = out
    return list(out[0])
	def common_ksampler(model, seed, steps, cfg, sampler_name, scheduler, positive, negative, latent, denoise=1.0, disable_noise=False, start_step=None, last_step=None, force_full_denoise=False):
	    use_aitemplate = 'aitemplate_keep_loaded' in model.model_options
	    if use_aitemplate:
	        keep_loaded = model.model_options['aitemplate_keep_loaded']
	    device = comfy.model_management.get_torch_device()
	    latent_image = latent["samples"]
	    if disable_noise:
	        noise = torch.zeros(latent_image.size(), dtype=latent_image.dtype, layout=latent_image.layout, device="cpu")
	    else:
        batch_inds = latent["batch_index"] if "batch_index" in latent else None
	        noise = comfy.sample.prepare_noise(latent_image, seed, batch_inds)
	    noise_mask = None
	    if "noise_mask" in latent:
	        noise_mask = latent["noise_mask"]
	    preview_format = "JPEG"
	    if preview_format not in ["JPEG", "PNG"]:
	        preview_format = "JPEG"
	    previewer = latent_preview.get_previewer(device, model.model.latent_format)
	    pbar = comfy.utils.ProgressBar(steps)
    def callback(step, x0, x, total_steps):
	        preview_bytes = None
	        if previewer:
	            x0 = x0.to(comfy.model_management.get_torch_device())
	            preview_bytes = previewer.decode_latent_to_preview_image(preview_format, x0)
	        pbar.update_absolute(step + 1, total_steps, preview_bytes)
	    samples = comfy.sample.sample(model, noise, steps, cfg, sampler_name, scheduler, positive, negative, latent_image,
	                                  denoise=denoise, disable_noise=disable_noise, start_step=start_step, last_step=last_step,
	                                  force_full_denoise=force_full_denoise, noise_mask=noise_mask, callback=callback, seed=seed)
	    out = latent.copy()
    out["samples"] = samples
	    return (out, )
