import torch.nn as nn
	_LOSSES = {
	          'cross_entropy':    nn.CrossEntropyLoss,
	          'bce':              nn.BCELoss,
	          'bce_logit':        nn.BCEWithLogitsLoss,
	          'mse':              nn.MSELoss
	          }
	def get_loss_func(loss_name):
	    """
	    Get the loss function corresponding to "loss_name"
    """
	    if loss_name not in _LOSSES:
	        raise ValueError(f'Loss {loss_name} is not implemented in models/losses.py')
	    return _LOSSES[loss_name]()

import os
	import glob
	import torch
	from torch_geometric.data import Data, Dataset
	class GraphDataset(Dataset):
	    """
	    General class for graph dataset
	    """
	    def __init__(self, path_graphs):
	        super(GraphDataset, self).__init__()
        self.all_graphs = sorted(glob.glob(os.path.join(path_graphs, '*.pt')))
	    def len(self):
	        return len(self.all_graphs)
	    def get(self, idx):
	        data = torch.load(self.all_graphs[idx])
	        return data
