            {
	                "batch_size": 1,
	            }
	        )
	        validate_config(cfg)
	        cfg = DictDefault(
	            {
	                "gradient_accumulation_steps": 1,
	            }
	        )
        validate_config(cfg)
	    def test_falcon_fsdp(self):
	        regex_exp = r".*FSDP is not supported for falcon models.*"
	        # Check for lower-case
	        cfg = DictDefault(
	            {
	                "base_model": "tiiuae/falcon-7b",
	                "fsdp": ["full_shard", "auto_wrap"],
	            }
