def node_dis(node1,node2):
	    if node2 is None :
	        node2 = {"role":"","triples": [],"logical_rel": ""}
	    dis=0
	    if node1["role"] != node2["role"]:
	        dis += 1
	    #print(dis)
	    if node1["logical_rel"] != node2["logical_rel"]:
	        dis += 1
	    dis += len(list((set(node1["triples"])|set(node2["triples"]))-(set(node1["triples"])&set(node2["triples"]))))
    return(dis)
	def is_path_equal(path1,path2):
	    if (len(path1)!=len(path2)):
	        return False
	    for i in range(len(path1)):
	        if isinstance(path1[i],dict) and isinstance(path2[i],dict):
	            if path1[i]['role'] == path2[i]['role'] and path1[i]['logical_rel'] == path2[i]['logical_rel'] and set(path1[i]['triples']) == set(path2[i]['triples']):
	                continue
	            else:
	                return False
        elif path1[i] != path2[i]:
	            return False
	    return True
	def is_tree_equal(predict_tree,gold_tree):
	    if len(predict_tree) != len(gold_tree):
	        return 0
	    else:
	        for i in range(len(predict_tree)):
	            if predict_tree[i]['role'] == gold_tree[i]['role'] and predict_tree[i]['logical_rel'] == gold_tree[i]['logical_rel'] and set(
	                predict_tree[i]['triples']) == set(gold_tree[i]['triples']):
                continue
	            else:
	                return 0
	    return 1
	# ground turth
	def edit_distance(predict_tree, gold_tree, predict_matrix, gold_matrix):
	    dis = 0
	    if len(predict_matrix) == 0:
	        for node in gold_tree:
	            dis += node_dis(node, None)
        return dis
	    stack1 = [0]
	    stack2 = [0]
	    while stack1:
	        s1=stack1.pop()
	        s2=stack2.pop()
	        if ('L' not in predict_matrix[s1] and 'R' not in predict_matrix[s1]) and ('L' in gold_matrix[s2] or 'R' in gold_matrix[s2]):
	            dis += node_dis(predict_tree[s1], gold_tree[s2])
	            stack_tmp=[]
	            stack_tmp.append(gold_matrix[s2].index('R'))
            stack_tmp.append(gold_matrix[s2].index('L'))
	            while stack_tmp:
	                s_tmp=stack_tmp.pop()
	                dis += node_dis(gold_tree[s_tmp],None)
	                if ('L' in gold_matrix[s_tmp] and 'R' in gold_matrix[s_tmp]):
	                    stack_tmp.append(gold_matrix[s_tmp].index('R'))
	                    stack_tmp.append(gold_matrix[s_tmp].index('L'))
	        elif  ('L' in predict_matrix[s1] and 'R' in predict_matrix[s1]) and ('L' not in gold_matrix[s2] or 'R' not in gold_matrix[s2]):
	            dis += node_dis(predict_tree[s1], gold_tree[s2])
	            stack_tmp=[]
            stack_tmp.append(predict_matrix[s1].index('R'))
	            stack_tmp.append(predict_matrix[s1].index('L'))
	            while stack_tmp:
	                s_tmp=stack_tmp.pop()
	                dis += node_dis(predict_tree[s_tmp], None)
	                if ('L' in predict_matrix[s_tmp] and 'R' in predict_matrix[s_tmp]):
	                    stack_tmp.append(predict_matrix[s_tmp].index('R'))
	                    stack_tmp.append(predict_matrix[s_tmp].index('L'))
	        elif ('L' not in predict_matrix[s1] and 'R' not in predict_matrix[s1]) and ('L' not in gold_matrix[s2] and 'R' not in gold_matrix[s2]):
	            dis += node_dis(predict_tree[s1], gold_tree[s2])
        else:
	            stack1.append(predict_matrix[s1].index('R'))
	            stack1.append(predict_matrix[s1].index('L'))
	            stack2.append(gold_matrix[s2].index('R'))
	            stack2.append(gold_matrix[s2].index('L'))
	            dis += node_dis(predict_tree[s1], gold_tree[s2])
	    return dis
	# TP,TP+FP,TP+FN
	def decision_path(predict_tree, gold_tree, predict_matrix, gold_matrix):
	    leaf1, leaf2, paths1, paths2 = [], [], [], []
    for i in range(len(predict_matrix)):
	        if ('L' not in predict_matrix[i] and 'R' not in predict_matrix[i]):
	            leaf1.append(i)
	    for node in leaf1:
	        path=[predict_tree[node]]
	        while node !=0:
	            # print(predict_matrix)
	            # print(node)
	            # print(predict_matrix[node])
	            # print()
            path.append(predict_matrix[predict_matrix[node].index('F')][node])
	            path.append(predict_tree[predict_matrix[node].index('F')])
	            node =predict_matrix[node].index('F')
	        paths1.append(path)
	    for i in range(len(gold_matrix)):
	        if ('L' not in gold_matrix[i] and 'R' not in gold_matrix[i]):
	            leaf2.append(i)
	    for node in leaf2:
	        path=[gold_tree[node]]
	        while node !=0:
            path.append(gold_matrix[gold_matrix[node].index('F')][node])
	            path.append(gold_tree[gold_matrix[node].index('F')])
	            node =gold_matrix[node].index('F')
	        paths2.append(path)
	    res = 0
	    for path1 in paths1:
	        for path2 in paths2:
	            if is_path_equal(path1, path2):
	                res += 1
	                break
    return res,len(paths1),len(paths2)
	# TP,TP+FP,TP+FN
	def triplet_extraction(predict_tree, gold_tree):
	    predict_triplet, gold_triplet = [], []
	    for i in range(len(predict_tree)):
	        for triplet in predict_tree[i]["triples"]:
	            predict_triplet.append(triplet)
	    for i in range(len(gold_tree)):
	        for triplet in gold_tree[i]["triples"]:
	            gold_triplet.append(triplet)
    predict_triplet_num = len(list(set(predict_triplet)))
	    gold_triplet_num = len(list(set(gold_triplet)))
	    correct_triplet_num =len(list(set(gold_triplet)&set(predict_triplet)))
	    return [correct_triplet_num, predict_triplet_num, gold_triplet_num]
	def node_extraction(predict_tree, gold_tree):
	    predict_node, gold_node = [], []
	    for i in range(len(predict_tree)):
	        if len(predict_tree[i]['triples'])>0:
	            predict_node.append(predict_tree[i])
	    for i in range(len(gold_tree)):
        if len(gold_tree[i]['triples']) > 0:
	            gold_node.append(gold_tree[i])
	    predict_triplet_num = len(predict_node)
	    gold_triplet_num = len(gold_node)
	    correct_triplet_num = 0
	    for node1 in predict_node:
	        for node2 in gold_node:
	            if len(node1['triples'])>0 and node1['role'] == node2['role'] and node1['logical_rel'] == node2['logical_rel'] and set(node1['triples']) == set(node2['triples']):
	                correct_triplet_num +=1
	    return [correct_triplet_num, predict_triplet_num, gold_triplet_num]
#: AccF1F1; 
	def eval(predict_tree, gold_tree):
	    # matrix[i][j]='F'/'L'/'R'ji//
	    for node in predict_tree:
	        for i in range(len(node['triples'])):
	            node['triples'][i]=(node['triples'][i][0].lower(), node['triples'][i][1].lower(), node['triples'][i][2].lower())
	    for node in gold_tree:
	        for i in range(len(node['triples'])):
	            node['triples'][i]=(node['triples'][i][0].lower(), node['triples'][i][1].lower(), node['triples'][i][2].lower())
	    predict_matrix = nodematrix(predict_tree)
    gold_matrix = nodematrix(gold_tree)
	    # Acc
	    tree_num = (0 if predict_tree == [] else 1)
	    correct_tree_num = is_tree_equal(predict_tree,gold_tree)
	    # tripletF1
	    correct_triplet_num, predict_triplet_num, gold_triplet_num = triplet_extraction(predict_tree, gold_tree)
	    # F1
	    correct_path_num, predict_path_num, gold_path_num = decision_path(predict_tree, gold_tree, predict_matrix, gold_matrix)
	    # 
	    edit_dis = edit_distance(predict_tree, gold_tree, predict_matrix, gold_matrix)
    correct_node_num, predict_node_num, gold_node_num = node_extraction(predict_tree, gold_tree)
	    return tree_num,correct_tree_num, correct_triplet_num, predict_triplet_num, gold_triplet_num, correct_path_num, predict_path_num, gold_path_num, edit_dis,correct_node_num, predict_node_num, gold_node_num

import os, json
	rel_label_mapping = {
	    "": "",
	    "": "",
	    "": "",
	    "": "",
	    "": "",
	    "": "",
	}
	def preorder_traverse(tree, start, list_triple2relID, relations, side='left', TreeS=False):
    if start >= len(tree):
	        return None, ''
	    root = tree[start]
	    triple2relID = list_triple2relID[start]
	    role = root['role']
	    tgt_seq = ''
	    if role == 'C':
	        if side == 'left':
	            tgt_seq += ''
	        else:
            tgt_seq += ''
	        tgt_seq += read_node(root, triple2relID, relations, TreeS)
	        tgt_seq += ''
	        left_end, left_tgt_seq = preorder_traverse(tree, start+1, list_triple2relID, relations, TreeS=TreeS)
	        tgt_seq += left_tgt_seq
	        right_end, right_tgt_seq = preorder_traverse(tree, left_end+1, list_triple2relID, relations, side='right', TreeS=TreeS)
	        tgt_seq += right_tgt_seq
	        end = right_end
	    elif role == 'D':
	        if len(root['triples']) > 0:
            if side == 'left':
	                tgt_seq += ''
	            else:
	                tgt_seq += ''
	            tgt_seq += read_node(root, triple2relID, relations, TreeS)
	            tgt_seq += '' if any([len(x['triples']) > 0 for x in tree[start+1:]]) else ''
	        end = start
	    return end, tgt_seq
	def read_node(node, triple2relID, relations, TreeS=False):
	    role = node['role']
    triples = [tuple(t) for t in node['triples']]
	    logical_rel = node['logical_rel']
	    if logical_rel == 'or':
	        conjunction = ''
	    elif role == 'C':
	        conjunction = ''
	    else:
	        conjunction = ''
	    clauses = []
	    visited = set()
    for i, t1 in enumerate(triples):
	        if t1 in visited:
	            continue
	        id1 = triple2relID[t1]
	        clause = [id1]
	        visited.add(t1)
	        for t2 in triples[i+1:]:
	            if t2 in visited:
	                continue
	            if t1[-1] == t2[0] or t1[0] == t2[-1]:
                id2 = triple2relID[t2]
	                clause.append(id2)
	                visited.add(t2)
	        # if len(clause) > 2:
	        #     print([relations[id] for id in clause])
	        try:
	            clause = sorted(clause, key=lambda x: location(x, relations))
	        except:
	            pass
	        clauses.append(clause)
    try:
	        clauses = sorted(clauses, key=lambda x: clause_location(x, relations))
	    except:
	        pass
	    if TreeS:
	        clauses = conjunction.join(['...' for clause in clauses])
	    else:
	        clauses = conjunction.join([''.join([linearize_triple(relations[x]) for x in clause]) for clause in clauses])
	    return clauses
	def location(relID, relations):
    rel = relations[relID]
	    return sum(rel[0][:2] + rel[2][:2])
	    # return (sum(rel[2][:2]), sum(rel[0][:2]))
	def clause_location(clause, relations):
	    return min([location(relID, relations) for relID in clause])
	# def linearize_clause(clause, relations):
	#     for tripleID in clause:
	#         triple = relations[tripleID]
	#     return
	def linearize_triple(triple):
    return '('+ triple[0][-1] + ', ' + rel_label_mapping[triple[1]] + ', ' + triple[2][-1] + ')'
	def convert_sample(input_doc, RE=False, TreeS=False):
	    samples = []
	    with open(input_doc) as f:
	        lines = f.readlines()
	        lines = [eval(ele) for ele in lines]
	    for idx, line in enumerate(lines):
	        # print(line)
	        text = line["text"]
	        tree = line["tree"]
        triples = line["relations"]
	        list_triple2relID = []
	        visited = set()
	        for node in tree:
	            triple2relID = dict()
	            for triple in node['triples']:
	                triple = tuple(triple)
	                candidates = []
	                for relID, rel in enumerate(line["relations"]):
	                    if (rel[0][-1], rel[1], rel[2][-1]) == triple:
                        candidates.append(relID)
	                        if relID not in visited:
	                            triple2relID[triple] = relID
	                            visited.add(relID)
	                            break
	                if triple not in triple2relID:
	                    triple2relID[triple] = candidates[-1]
	            list_triple2relID.append(triple2relID)
	        _, tgt_seq = preorder_traverse(tree, 0, list_triple2relID, triples)
	        sample = {'input': text, 'target': tgt_seq, 'RE_target': '', 'TreeS_target': ''}
        # print(text)
	        # print(tgt_seq)
	        if RE:
	            triples_seq = '[' + ', '.join([linearize_triple(x) for x in triples]) + ']'
	            sample['RE_target'] = triples_seq
	        if TreeS:
	            _, tgt_seq = preorder_traverse(tree, 0, list_triple2relID, triples, TreeS=True)
	            sample['TreeS_target'] = tgt_seq
	        samples.append(sample)
	    return samples
if __name__ == '__main__':
