	        self.pos = pos
        self.size = size
	        self.keepout = keepout
	        self.bound = np.stack([self.pos - (self.size + self.keepout) / 2,
	                               self.pos + (self.size + self.keepout) / 2]).T.flatten()
	        self.spec = None
	        self.neg_spec = None
