	    for i in parsed_gpt_suggested_input.keys():
	        if i not in tool["dynamic_params"].keys():
	            raise Exception("Bad Generated Input")
	    tool_args = replace_variables_for_values(tool["args"], parsed_gpt_suggested_input)
	    url = tool_args["url"]
