def triple_urls(triple: str) -> list[str]:
	    mirrors = APPSIGNAL_AGENT_CONFIG["mirrors"]
	    version = APPSIGNAL_AGENT_CONFIG["version"]
	    filename = triple_filename(triple)
	    return [f"{mirror}/{version}/{filename}" for mirror in mirrors]
