""" Tests for DigitalWiper constructor. """
	import unittest
	try:
	    from src.BDPotentiometer import Potentiometer, DigitalWiper
	except ModuleNotFoundError:
	    from BDPotentiometer import Potentiometer, DigitalWiper
	class TestDigitalWiperConstructor(unittest.TestCase):
	    """
	    Testing DigitalWiper constructor.
	    """
    def test_constructor(self) -> None:
	        """
	        Test DigitalWiper constructor
	        """
	        pot = Potentiometer(r_ab=10e3, r_w=75, rheostat=False, parameters_locked=False)
	        # Normal constructor parameters
	        for locked in [True, False]:
	            for max_value in [1, 10, 128, 1024]:
	                digital_wiper = DigitalWiper(
	                    potentiometer=pot, max_value=max_value, parameters_locked=locked
                )
	                self.assertEqual(digital_wiper.potentiometer, pot)
	                self.assertEqual(digital_wiper.parameters_locked, locked)
	                self.assertEqual(digital_wiper.max_value, max_value)
	        # Wrong potentiometer type
	        for locked in [True, False]:
	            for pot_ in [None, "pot"]:
	                with self.assertRaises(TypeError):
	                    _ = DigitalWiper(
	                        potentiometer=pot_, max_value=128, parameters_locked=locked
                    )
	        # Wrong max_value type
	        for locked in [True, False]:
	            for max_value in [None, "128"]:
	                with self.assertRaises(TypeError):
	                    _ = DigitalWiper(
	                        potentiometer=pot, max_value=max_value, parameters_locked=locked
	                    )
	        # Wrong max_value value
	        for locked in [True, False]:
            for max_value in [0, -128, 12.6]:
	                with self.assertRaises(ValueError):
	                    _ = DigitalWiper(
	                        potentiometer=pot, max_value=max_value, parameters_locked=locked
	                    )
	if __name__ == "__main__":
	    unittest.main()

""" Tests for DigitalWiper fixed resistors. """
	import unittest
	import numpy as np
	try:
	    from src.BDPotentiometer import Potentiometer, DigitalWiper
	except ModuleNotFoundError:
	    from BDPotentiometer import Potentiometer, DigitalWiper
	class TestDigitalWiperFixedResistors(unittest.TestCase):
	    """
	    Testing DigitalWiper fixed resistors.
    """
	    def test_r_lim(self):
	        """
	        Tests current limiting resistor value altering.
	        """
	        pot = Potentiometer(
	            r_ab=200e3, r_w=375, rheostat=False, parameters_locked=False
	        )
	        for locked in [True, False]:
	            for max_value in [1, 14, 43]:
                digital_wiper = DigitalWiper(
	                    potentiometer=pot, max_value=max_value, parameters_locked=locked
	                )
	                # Correct values (positive float numbers)
	                for resistance in np.linspace(0, 104, num=101, endpoint=True):
	                    digital_wiper.r_lim = resistance
	                    self.assertEqual(digital_wiper.r_lim, resistance)
	                    self.assertEqual(
	                        digital_wiper.r_lim, digital_wiper.potentiometer.r_lim
	                    )
                # Wrong values
	                for resistance in [-200.4, -1]:
	                    with self.assertRaises(ValueError):
	                        digital_wiper.r_lim = resistance
	                # Wrong type
	                for resistance in [None, "100.2"]:
	                    with self.assertRaises(TypeError):
	                        digital_wiper.r_lim = resistance
	    def test_r_load(self):
	        """
        Tests load resistor value altering.
	        """
	        pot = Potentiometer(
	            r_ab=170e3, r_w=145, rheostat=False, parameters_locked=False
	        )
	        for locked in [True, False]:
	            for max_value in [1, 26, 51]:
	                digital_wiper = DigitalWiper(
	                    potentiometer=pot, max_value=max_value, parameters_locked=locked
	                )
                # Correct values (positive float numbers)
	                for resistance in np.linspace(0, 10e4, num=101, endpoint=True):
	                    digital_wiper.r_load = resistance
	                    self.assertEqual(digital_wiper.r_load, resistance)
	                    self.assertEqual(
	                        digital_wiper.r_load, digital_wiper.potentiometer.r_load
	                    )
	                # Wrong values
	                for resistance in [-200.4, -1]:
	                    with self.assertRaises(ValueError):
                        digital_wiper.r_load = resistance
	                # Wrong type
	                for resistance in [None, "100.2"]:
	                    with self.assertRaises(TypeError):
	                        digital_wiper.r_load = resistance
	if __name__ == "__main__":
	    unittest.main()

""" Tests for DigitalWiper channel numbering. """
	import unittest
	try:
	    from src.BDPotentiometer import Potentiometer, DigitalWiper
	except ModuleNotFoundError:
	    from BDPotentiometer import Potentiometer, DigitalWiper
	class TestDigitalWiperChannelNumbering(unittest.TestCase):
	    """
	    Testing DigitalWiper channel numbering.
	    """
    def test_channel_numbering(self):
	        """
	        Testing channel numbering.
	        DigitalWiper Channel is a channel of the actual digital potentiometer device,
	        numbered in int numbers starting from 0.
	        """
	        pot = Potentiometer(r_ab=20e3, r_w=50, rheostat=False, parameters_locked=False)
	        for locked in [True, False]:
	            for max_value in [1, 64, 128, 256]:
	                digital_wiper = DigitalWiper(
                    potentiometer=pot, max_value=max_value, parameters_locked=locked
	                )
	                self.assertEqual(
	                    digital_wiper.channel, 0
	                )  # default channel number is 0
	                # Correct channel numbers
	                for channel_number in [3, 4.0, 1234, 0, 17]:
	                    digital_wiper.channel = channel_number
	                    self.assertEqual(digital_wiper.channel, channel_number)
	                # Wrong channel number value
                for channel_number_ in [-1, 1.12]:
	                    with self.assertRaises(ValueError):
	                        digital_wiper.channel = channel_number_
	                # Wrong channel number type
	                for channel_number_ in ["1", None]:
	                    with self.assertRaises(TypeError):
	                        digital_wiper.channel = channel_number_
	                self.assertEqual(
	                    digital_wiper.channel, channel_number
	                )  # Check that channel number did not_change
if __name__ == "__main__":
	    unittest.main()

""" Tests for DigitalWiper output voltage property. """
	import unittest
	import numpy as np
	try:
	    from src.BDPotentiometer import Potentiometer, DigitalWiper
	except ModuleNotFoundError:
	    from BDPotentiometer import Potentiometer, DigitalWiper
	class TestDigitalWiperVoltageOut(unittest.TestCase):
	    """
	    Testing DigitalWiper `voltage_out`.
    """
	    def test_voltage_out(self):
	        """
	        Test output voltage property and methods.
	        """
	        pot = Potentiometer(
	            r_ab=101e3, r_w=185, rheostat=False, parameters_locked=False
	        )
	        for locked in [True, False]:
	            for max_value in [1, 15, 33]:
                digital_wiper = DigitalWiper(
	                    potentiometer=pot, max_value=max_value, parameters_locked=locked
	                )
	                digital_wiper.r_lim = 123.5
	                digital_wiper.r_load = 1e6
	                # Correct values (positive float numbers)
	                digital_wiper.voltage_in = 5
	                for voltage in np.linspace(0, 5, num=13, endpoint=True):
	                    digital_wiper.voltage_out = voltage
	                    np.testing.assert_allclose(
                        digital_wiper.voltage_out,
	                        digital_wiper.potentiometer.voltage_out(
	                            digital_wiper.value_relative
	                        ),
	                    )
	                for voltage in np.linspace(-5, 0, num=13, endpoint=True):
	                    digital_wiper.voltage_out = voltage
	                    self.assertEqual(digital_wiper.voltage_out, 0)
	                digital_wiper.voltage_in = -5
	                for voltage in np.linspace(-5, 0, num=13, endpoint=True):
                    digital_wiper.voltage_out = voltage
	                    np.testing.assert_allclose(
	                        digital_wiper.voltage_out,
	                        digital_wiper.potentiometer.voltage_out(
	                            digital_wiper.value_relative
	                        ),
	                    )
	                for voltage in np.linspace(0, 5, num=13, endpoint=True):
	                    digital_wiper.voltage_out = voltage
	                    self.assertEqual(digital_wiper.voltage_out, 0)
                digital_wiper.voltage_in = 0
	                for voltage in np.linspace(-5, 5, num=13, endpoint=True):
	                    digital_wiper.voltage_out = voltage
	                    self.assertEqual(digital_wiper.voltage_out, 0)
	                digital_wiper.r_load = 0
	                digital_wiper.voltage_in = 5
	                for voltage in np.linspace(-5, 5, num=13, endpoint=True):
	                    digital_wiper.voltage_out = voltage
	                    self.assertEqual(digital_wiper.voltage_out, 0)
	                # Wrong type
                for voltage in [None, "100.2"]:
	                    with self.assertRaises(TypeError):
	                        digital_wiper.voltage_out = voltage
	if __name__ == "__main__":
	    unittest.main()

""" Tests for DigitalWiper min max value properties. """
	import unittest
	try:
	    from src.BDPotentiometer import Potentiometer, DigitalWiper
	except ModuleNotFoundError:
	    from BDPotentiometer import Potentiometer, DigitalWiper
	class TestDigitalWiperMinMaxValue(unittest.TestCase):
	    """
	    Testing DigitalWiper `min_value` and `max_value` properties.
	    """
    def test_min_value(self):
	        """
	        Testing `min_value` property.
	        Should always be zero and can not be changed&
	        """
	        pot = Potentiometer(r_ab=50e3, r_w=200, rheostat=False, parameters_locked=False)
	        for locked in [True, False]:
	            for max_value in [1, 32, 64, 256]:
	                digital_wiper = DigitalWiper(
	                    potentiometer=pot, max_value=max_value, parameters_locked=locked
                )
	                self.assertEqual(digital_wiper.min_value, 0)
	                for min_value_ in [None, -1, 1, "1", "0", 0]:
	                    with self.assertRaises(AttributeError):
	                        digital_wiper.min_value = min_value_
	    def test_max_value(self):
	        """
	        Testing `max_value` property.
	        In contrast with `min_value`, `max_value` can be altered unless wiper is locked.
	        `max_value` must be positive int number.
        """
	        pot = Potentiometer(
	            r_ab=100e3, r_w=175, rheostat=False, parameters_locked=False
	        )
	        for locked in [True, False]:
	            for max_value in [1, 16, 32, 2048]:
	                digital_wiper = DigitalWiper(
	                    potentiometer=pot, max_value=max_value, parameters_locked=locked
	                )
	                self.assertEqual(digital_wiper.max_value, max_value)
                # Correct values
	                for max_value_new in [1, 10, 128, 1024, 128.0]:
	                    digital_wiper.max_value = max_value_new
	                    # Check that parameters_locked locks the max_value
	                    if not digital_wiper.parameters_locked:
	                        self.assertEqual(digital_wiper.max_value, max_value_new)
	                    else:
	                        self.assertEqual(digital_wiper.max_value, max_value)
	                # Wrong value
	                for max_value_ in [0, -1, -10, 128.5]:
                    with self.assertRaises(ValueError):
	                        digital_wiper.max_value = max_value_
	                # Wrong type
	                for max_value_ in [None, "1"]:
	                    with self.assertRaises(TypeError):
	                        digital_wiper.max_value = max_value_
	                # Check that value did not change
	                if not digital_wiper.parameters_locked:
	                    self.assertEqual(digital_wiper.max_value, max_value_new)
	                else:
                    self.assertEqual(digital_wiper.max_value, max_value)
	if __name__ == "__main__":
	    unittest.main()

