"""This module contains internal helper functions and not supposed to be used outside."""
	from numbers import Real, Integral
	from math import isnan
	from typing import Union, Callable
	def check_number(num: Union[float, int, Real]) -> Union[float, int]:
	    """
	    Checks if argument is a real number.
	    check_number does not allow argument to be `+inf` or `-inf`, or `NaN`.
	    Special float numbers `+0.0` and `-0.0` will be converted to just 0.0
	    :param num: variable to check.
    :return: Unchanged `num` as float or int or raises ValueError if num is not a real number.
	    """
	    if not isinstance(num, Real):
	        raise TypeError(f"Expected integer or float number, got {type(num)}")
	    if isinstance(num, Integral):
	        return int(num)
	    if num in [float("inf"), float("-inf")]:
	        raise ValueError(f"Expected integer or float number, got {num}")
	    if isnan(num):
	        raise ValueError("Expected integer or float number, not NaN")
    if num == 0:
	        return 0.0
	    return float(num)
	def check_integer(num: Union[float, int, Real]) -> int:
	    """
	    Checks if `num` is an integer and converts it to int, otherwise raises ValueError.
	    :param num: Number (either int or float) to be checked.
	    :return: int representation of `num` or rises ValueError.
	    """
	    num = check_number(num)
    if isinstance(num, float):
	        if not num.is_integer():
	            raise ValueError("Argument should be integer number")
	    return int(num)
	def check_not_negative(num: Union[float, int]) -> Union[float, int]:
	    """
	    Checks if `num` is a not negative number, otherwise rises ValueError.
	    :param num: Either float or int number.
	    :return: `num` if it is not negative or rises ValueError.
	    """
    num = check_number(num)
	    if num < 0:
	        raise ValueError("Argument should be not negative number")
	    return num
	def check_positive(num: Union[float, int]) -> Union[float, int]:
	    """
	    Checks if `num` is a positive number, otherwise rises ValueError.
	    :param num: Either float or int number.
	    :return: `num` if it is positive or rises ValueError.
	    """
    num = check_number(num)
	    if num <= 0:
	        raise ValueError("Argument should be a positive number")
	    return num
	def clamp(
	    value: Union[float, int], min_value: Union[float, int], max_value: Union[float, int]
	) -> Union[float, int]:
	    """
	    Clamp `value` to given range between `min_value` and `max_value`.
	    :param value: Either float or int number to be coerced.
    :param min_value: Lower coerce range boundary.
	    :param max_value: Upper coerce range boundary.
	    :return: Value coerced to given range.
	    """
	    value = check_number(value)
	    min_value = check_number(min_value)
	    max_value = check_number(max_value)
	    if min_value > max_value:
	        min_value, max_value = max_value, min_value
	    if value < min_value:
        if isinstance(value, float):
	            return float(min_value)
	        return min_value
	    if value > max_value:
	        if isinstance(value, float):
	            return float(max_value)
	        return max_value
	    return value
	def build_tuple(
	    value: Union[float, int, tuple[float, ...], list[float, ...]],
    num: Union[int, float],
	    func: Union[Callable, None] = None,
	) -> tuple[float, ...]:
	    """
	    Builds tuple of floats of given length by applying a callable `func` to value.
	    If `value` is an iterable (either list or tuple) its length must be equal to `num`.
	    The `func` will be applied to each element of the iterable.
	    If `value` is a number the resulting tuple of length `num` will be built of same `func(value)`
	    elements converted to float.
	    If `func` is None the `value` will enter resulting tuple unchanged.
    :param value: Iterable (either list or tuple) or a number.
	    :param num: A number of elements in a tuple.
	    :param func: Callable to be applied to `value` or None for identity function.
	    :return: A tuple of floats of length `num` produced by application of `func` to `value`.
	    """
	    def identity(val: float) -> float:
	        return val
	    if not isinstance(value, (float, int, tuple, list)):
	        raise TypeError("Argument should be a number or list or tuple of numbers")
	    num = check_integer(check_positive(num))
    if func is not None:
	        if not callable(func):
	            raise TypeError("function must be callable or None")
	    else:
	        func = identity
	    if isinstance(value, (list, tuple)):
	        if len(value) != num:
	            raise ValueError(f"A tuple or list of length {num} expected")
	        return tuple(float(func(check_number(value_i))) for value_i in value)
	    value = check_number(value)
    return tuple(float(func(value)) for _ in range(num))
	def adjust_tuple(
	    value: Union[tuple[float, ...], list[float, ...]], num: int, default_value: float
	) -> tuple[float, ...]:
	    """
	    Adjusts tuple or list of floats to a given length `num` and returns a tuple.
	    if `num` is less than initial length of the tuple it will be truncated to first `num` elements,
	    otherwise it will be appended using `default_value` parameter.
	    :param value: Initial iterable (either tuple or list) of float values.
	    :param num: Final length of the tuple.
    :param default_value: Default value to populate the tuple if it needs to be grown.
	    :return: A tuple of floats of length `num`.
	    """
	    if not isinstance(value, (tuple, list)):
	        raise TypeError("Argument should be a list or a tuple of numbers")
	    num = check_integer(check_positive(num))
	    default_value = float(check_number(default_value))
	    if len(value) < num:
	        return tuple(
	            [float(check_number(value_i)) for value_i in value]
            + [default_value] * (num - len(value))
	        )
	    if len(value) > num:
	        return tuple(float(check_number(value[i])) for i in range(num))
	    return tuple(float(check_number(value_i)) for value_i in value)

""" Module contains basic digital wiper class implementation """
	from copy import deepcopy
	from typing import Union
	from gpiozero import SPI
	from .potentiometer import Potentiometer
	from .__helpers import check_integer, check_positive, check_not_negative, clamp
	class DigitalWiper:
	    """
	    Generic digital wiper class.
	    Digital wiper change position by discrete movement between 0 and `max_value`.
    Property `parameters_locked` is used to disable change of `max_value`
	    parameter after object is created, only wiper position change is allowed.
	    """
	    # pylint: disable=too-many-instance-attributes
	    def __init__(
	        self,
	        potentiometer: Potentiometer,
	        max_value: int = 128,
	        parameters_locked: bool = False,
	    ) -> None:
        self.__parameters_locked: bool = bool(parameters_locked)
	        if isinstance(potentiometer, Potentiometer):
	            self.__potentiometer: Potentiometer = potentiometer
	        else:
	            raise TypeError(
	                f"Expected an instance of Potentiometer class, got {type(potentiometer)}"
	            )
	        self.__channel: int = 0
	        self.__max_value: int = check_integer(check_positive(max_value))
	        self.__value: int = 0
        self.read()
	    @property
	    def parameters_locked(self) -> bool:
	        """
	        Check if parameters of the wiper are locked.
	        :return: True if locked and False otherwise
	        """
	        return self.__parameters_locked
	    @property
	    def potentiometer(self) -> Potentiometer:
        """
	        Access Potentiometer instance.
	        :return: Potentiometer
	        """
	        return self.__potentiometer
	    @property
	    def channel(self) -> int:
	        """Wiper channel number"""
	        return self.__channel
	    @channel.setter
    def channel(self, channel: int) -> None:
	        self.__channel = check_integer(check_not_negative(channel))
	    @property
	    def min_value(self) -> int:
	        """
	        Returns minimal wiper position, which is always zero.
	        :return: Always return 0.
	        """
	        return 0
	    @property
    def max_value(self) -> int:
	        """
	        Returns device max wiper position value.
	        :return: Max wiper position value as int.
	        """
	        return self.__max_value
	    @max_value.setter
	    def max_value(self, max_value: int) -> None:
	        new_value = check_integer(check_positive(max_value))
	        if not self.parameters_locked:
            self.__max_value = new_value
	            self.value = self.__value
	    def _set_value(self, value: int) -> int:
	        """
	        Set given wiper position to `value`.
	        :param value: Requested value as int.
	        :return: Value actually set as int.
	        """
	        value = int(round(clamp(value, 0, self.max_value)))
	        return value
    def _read_value(self) -> int:
	        """
	        Read value of wiper position.
	        :return: Wiper position value (int).
	        """
	        return self.__value
	    def read(self) -> None:
	        """
	        Read wiper position into value property.
	        """
        self.__value = self._read_value()
	    @property
	    def value(self) -> int:
	        """
	        Current wiper position.
	        :return: Wiper position value (int).
	        """
	        self.read()
	        return self.__value
	    @value.setter
    def value(self, value: int) -> None:
	        value = clamp(check_integer(value), 0, self.max_value)
	        data = self._set_value(value)
	        self.__value = data
	    @property
	    def value_relative(self) -> float:
	        """
	        Relative wiper position value in the range 0..1.
	        :return: Relative wiper position (float)
	        """
        return self.value / self.max_value
	    @value_relative.setter
	    def value_relative(self, value: float) -> None:
	        value = clamp(value, 0, 1)
	        value_int = int(round(value * self.max_value))
	        self.value = value_int
	    @property
	    def r_wb(self) -> float:
	        """
	        Calculates resistance between terminals B and W.
        :return: Resistance between terminals B and W (float).
	        """
	        return self.potentiometer.r_wb(self.value_relative)
	    @r_wb.setter
	    def r_wb(self, resistance: float) -> None:
	        self.value = int(
	            round(self.potentiometer.r_wb_to_position(resistance) * self.max_value)
	        )
	    @property
	    def r_wa(self) -> float:
        """
	        Calculates resistance between terminals A and W.
	        :return: Resistance between terminals A and W (float).
	        """
	        return self.potentiometer.r_wa(self.value_relative)
	    @r_wa.setter
	    def r_wa(self, resistance: float) -> None:
	        self.value = int(
	            round(self.potentiometer.r_wa_to_position(resistance) * self.max_value)
	        )
    @property
	    def r_lim(self) -> float:
	        """
	        Potentiometer current limiting resistor.
	        """
	        return self.potentiometer.r_lim
	    @r_lim.setter
	    def r_lim(self, r_lim: float) -> None:
	        self.potentiometer.r_lim = r_lim
	    @property
    def r_load(self) -> float:
	        """
	        Potentiometer load resistor.
	        """
	        return self.potentiometer.r_load
	    @r_load.setter
	    def r_load(self, r_load: float) -> None:
	        self.potentiometer.r_load = r_load
	    @property
	    def voltage_in(self) -> float:
        """
	        Device input Voltage.
	        :return: Input voltage (float).
	        """
	        return self.potentiometer.voltage_in
	    @voltage_in.setter
	    def voltage_in(self, voltage: float) -> None:
	        self.potentiometer.voltage_in = voltage
	    @property
	    def voltage_out(self) -> float:
        """
	        Calculates output voltage for given wiper position.
