# -*- coding: utf-8 -*-
	"""
	Retrieves persona data from a variety of formats.
	"""
	import json
	import re
	import typing
	import ruamel.yaml as ryaml
	from oobabot import fancy_logger
	class Persona:
    """
	    Handles retrieving persona data from a variety of formats
	    """
	    # list of keys that, depending on the json/yaml schema, might
	    # contain the AI's name.  Take the first one found, in order.
	    NAME_KEYS = ["char_name", "name"]
	    # list of keys that, depending on the json/yaml schema, might
	    # contain the AI's persona.  Take the first one found, in order.
	    PERSONA_KEYS = ["char_persona", "description", "context", "personality"]
	    def __init__(self, persona_settings: dict) -> None:
        self.ai_name: str = persona_settings["ai_name"]
	        self.persona: str = persona_settings["persona"]
	        self.wakewords: typing.List[str] = persona_settings["wakewords"].copy()
	        # if a json file is specified, load it and have
	        # that overwrite everything else
	        if "persona_file" in persona_settings:
	            filename = persona_settings["persona_file"]
	            try:
	                self.load_from_file(filename)
	            except FileNotFoundError:
                fancy_logger.get().warning(
	                    "Could not find persona file: %s",
	                    filename,
	                )
	                return
	        # match messages that include any `wakeword`, but not as part of
	        # another word
	        self.wakeword_patterns = [
	            re.compile(rf"\b{wakeword}\b", re.IGNORECASE) for wakeword in self.wakewords
	        ]
    def contains_wakeword(self, message: str) -> bool:
	        for wakeword_pattern in self.wakeword_patterns:
	            if wakeword_pattern.search(message):
	                return True
	        return False
	    def substitute(self, text: str) -> str:
	        return text.replace("{{char}}", self.ai_name)
	    def load_from_file(self, filename: str):
	        if not filename:
	            return
        if filename.endswith(".json"):
	            self.load_from_json_file(filename)
	            return
	        if filename.endswith(".yaml"):
	            self.load_from_yaml_file(filename)
	            return
	        if filename.endswith(".txt"):
	            self.load_from_text_file(filename)
	            return
	        fancy_logger.get().warning(
            "Unknown persona file extension (expected .json, or .txt): %s",
	            filename,
	        )
	    def load_from_text_file(self, filename: str):
	        with open(filename, "r", encoding="utf-8") as file:
	            persona = file.read()
	        self.persona = persona
	    def load_from_json_file(self, filename: str):
	        try:
	            with open(filename, "r", encoding="utf-8") as file:
                json_data = json.load(file)
	        except json.JSONDecodeError as err:
	            fancy_logger.get().warning(
	                "Could not parse persona file: %s.  Cause: %s",
	                filename,
	                err,
	            )
	            return
	        self.load_from_dict(json_data)
	    def load_from_yaml_file(self, filename):
        with open(filename, "r", encoding="utf-8") as file:
	            yaml = ryaml.YAML(typ="safe")
	            try:
	                yaml_settings = yaml.load(file)
	            except ryaml.YAMLError as err:
	                fancy_logger.get().warning(
	                    "Could not parse persona file: %s.  Cause: %s",
	                    filename,
	                    err,
	                )
                return
	        self.load_from_dict(yaml_settings)
	    def load_from_dict(self, json_data: dict):
	        for name_key in Persona.NAME_KEYS:
	            if name_key in json_data and json_data[name_key]:
	                self.ai_name = json_data[name_key]
	                break
	        for persona_key in Persona.PERSONA_KEYS:
	            if persona_key in json_data and json_data[persona_key]:
	                self.persona = self.substitute(json_data[persona_key])
                break
	        if self.ai_name not in self.wakewords and self.ai_name:
	            self.wakewords.append(self.ai_name)

# -*- coding: utf-8 -*-
	"""
	Data classes for all Discrivener messages, plus code
	to deserialize them from JSON.
	"""
	import collections
	import datetime
	import typing
	from oobabot import types
	def object_pairs_hook(
    pairs: typing.List[typing.Tuple[str, typing.Any]]
	) -> typing.Union["types.DiscrivenerMessage", dict]:
	    cls = MESSAGE_TYPE_TO_CLASS.get(pairs[0][0])
	    if cls is not None and len(pairs) == 1:
	        return cls(pairs[0][1])
	    return collections.OrderedDict(pairs)
	class ChannelSilentData(types.DiscrivenerMessage):
	    """
	    Represents whether any user is speaking in the channel.
	    """
    def __init__(self, data: dict):
	        self.type = types.DiscrivenerMessageType.CHANNEL_SILENT
	        self.silent = bool(data)
	    def __repr__(self):
	        return f"ChannelSilent(silent={self.silent})"
	class ConnectData(types.DiscrivenerMessage):
	    """
	    Represents us connecting or reconnecting to the voice channel.
	    """
	    def __init__(self, data: dict):
        self.type = types.DiscrivenerMessageType.CONNECT
	        self.channel_id = data.get("channel_id")
	        self.guild_id = data.get("guild_id")
	        self.session_id = data.get("session_id")
	        self.server = data.get("server")
	        self.ssrc = data.get("ssrc")
	    def __repr__(self):
	        return (
	            f"ConnectData(channel_id={self.channel_id}, "
	            + f"guild_id={self.guild_id}, "
            + f"session_id={self.session_id}, "
	            + f"server={self.server}, "
	            + f"ssrc={self.ssrc})"
	        )
	class DisconnectData(types.DiscrivenerMessage):
	    """
	    Represents a disconnect from the voice channel.
	    """
	    def __init__(self, data: dict):
	        self.type = types.DiscrivenerMessageType.DISCONNECT
        self.kind: str = data.get("kind", "unknown")
	        self.reason: str = data.get("reason", "unknown")
	        self.channel_id: int = data.get("channel_id", 0)
	        self.guild_id: int = data.get("guild_id", 0)
	        self.session_id: int = data.get("session_id", 0)
	    def __repr__(self):
	        return (
	            f"DisconnectData(kind={self.kind}, "
	            + f"reason={self.reason}, "
	            + f"channel_id={self.channel_id}, "
            + f"guild_id={self.guild_id}, "
	            + f"session_id={self.session_id})"
	        )
	class UserJoinData(types.DiscrivenerMessage):
	    """
	    Represents a user joining a voice channel.
	    """
	    def __init__(self, data: int):
	        print(f"UserJoinData data is {data}")
	        self.type = types.DiscrivenerMessageType.USER_JOIN
        self.user_id: int = data
	    def __str__(self):
	        return f"User #{self.user_id} joined voice channel"
	class UserLeaveData(types.DiscrivenerMessage):
	    """
	    Represents a user leaving a voice channel.
	    """
	    def __init__(self, data: int):
	        print(f"UserLeaveData data is {data}")
	        self.type = types.DiscrivenerMessageType.USER_LEAVE
        self.user_id: int = data
	    def __str__(self):
	        return f"User #{self.user_id} left voice channel"
	class TokenWithProbability:
	    """
	    Represents a token with a probability.
	    """
	    def __init__(self, data: dict):
	        self.probability: int = data.get("p", 0)
	        self.token_id: int = data.get("token_id", 0)
        self.token_text: str = str(data.get("token_text"))
	    def __repr__(self):
	        return (
	            "TokenWithProbability("
	            + f"probability={self.probability}, "
	            + f"token_id={self.token_id}, "
	            + f"token_text={self.token_text})"
	        )
	def to_datetime(message: dict) -> datetime.datetime:
	    """
    Converts a message into a datetime object.
	    """
	    seconds: int = message.get("secs_since_epoch", 0)
	    nanos: int = message.get("nanos_since_epoch", 0)
	    return datetime.datetime.fromtimestamp(seconds + nanos / 1e9)
	def to_duration(message: dict) -> datetime.timedelta:
	    """
	    Converts a message into a timedelta object.
	    """
	    seconds: int = message.get("secs", 0)
    nanos: int = message.get("nanos", 0)
	    return datetime.timedelta(seconds=seconds, microseconds=nanos / 1e3)
	class TextSegment:
	    """
	    Represents a single text segment of a transcribed message.
	    """
	    def __init__(self, message: dict):
	        self.tokens_with_probability = [
	            TokenWithProbability(data)
	            for data in message.get("tokens_with_probability", [])
        ]
	        self.start_offset_ms: int = message.get("start_offset_ms", 0)
	        self.end_offset_ms: int = message.get("end_offset_ms", self.start_offset_ms + 1)
	    def __repr__(self):
	        return (
	            f"TextSegment(tokens_with_probability={self.tokens_with_probability}, "
	            + f"start_offset_ms={self.start_offset_ms}, "
	            + f"end_offset_ms={self.end_offset_ms})"
	        )
	    def __str__(self) -> str:
        return "".join([t.token_text for t in self.tokens_with_probability])
	class UserVoiceMessage(types.VoiceMessageWithTokens):
	    """
	    Represents a transcribed message.
	    """
	    def __init__(self, message: dict):
	        self.type = types.DiscrivenerMessageType.TRANSCRIPTION
	        self._processing_time: datetime.timedelta = to_duration(
	            message.get("processing_time", datetime.timedelta(milliseconds=1.0))
	        )
        self._segments: typing.List[TextSegment] = [
	            TextSegment(s) for s in message.get("segments", [])
	        ]
	        super().__init__(
	            message.get("user_id", 0),
	            to_datetime(message.get("start_timestamp", datetime.datetime.now)),
	            to_duration(
	                message.get("audio_duration", datetime.timedelta(milliseconds=1.0))
	            ),
	        )
        self._latency: datetime.timedelta = (
	            datetime.datetime.now() - self._start_time - self._audio_duration
	        )
	    @property
	    def processing_time(self):
	        """
	        Returns the processing time of the transcription.
	        """
	        return self._processing_time
	    @property
    def latency(self):
	        """
	        Returns the latency of the transcription.
	        """
	        return self._latency
	    @property
	    def text(self) -> str:
	        """
	        Returns the text of the transcription.
	        """
        return "".join([str(s) for s in self._segments])
	    @property
	    def is_bot(self) -> bool:
	        """
	        Returns whether the user is a bot.
	        """
	        return False
	    @property
	    def tokens_with_confidence(self) -> typing.List[typing.Tuple[str, int]]:
	        """
        Returns the tokens with their confidence.
	        """
	        return [
	            (t.token_text, t.probability)
	            for s in self._segments
	            for t in s.tokens_with_probability
	        ]
	    def __repr__(self) -> str:
	        return (
	            "UserVoiceMessage("
            + f"start_time={self._start_time}, "
	            + f"user_id={self._user_id}, "
	            + f"audio_duration={self._audio_duration}, "
	            + f"processing_time={self._processing_time}, "
	            + f"segments={self._segments}, "
	            + f"latency={self._latency})"
	        )
	MESSAGE_TYPE_TO_CLASS: typing.Dict[str, typing.Type[types.DiscrivenerMessage]] = {
	    types.DiscrivenerMessageType.CHANNEL_SILENT: ChannelSilentData,
	    types.DiscrivenerMessageType.CONNECT: ConnectData,
    types.DiscrivenerMessageType.DISCONNECT: DisconnectData,
	    types.DiscrivenerMessageType.RECONNECT: ConnectData,
	    types.DiscrivenerMessageType.TRANSCRIPTION: UserVoiceMessage,
	    types.DiscrivenerMessageType.USER_JOIN: UserJoinData,
	    types.DiscrivenerMessageType.USER_LEAVE: UserLeaveData,
	}

# -*- coding: utf-8 -*-
	"""
	Settings library which supports both CLI and YAML settings files.
	Allows for settings to be exposed through one or both of these interfaces.
	load - sets defaults, then loads from YAML, then loads from CLI.
	load_from_cli - sets defaults, then loads from CLI only.
	load_from_dict - sets defaults, then loads from a dictionary only.
	load_from_yaml - sets defaults, then loads from YAML only.
	write_to_file - writes the given config as YAML to the given file.
	write_to_stream - writes the given config as YAML to the given stream.
"""
	import argparse
	import pathlib
	import textwrap
	import typing
	import ruamel.yaml as ryaml
	import ruamel.yaml.error as ryaml_error
	import oobabot
	YAML_WIDTH = 88
	DIVIDER = "# " * (YAML_WIDTH >> 1)
INDENT_UNIT = 2
	class ConfigFileMissingError(Exception):
	    """
	    Raised when the config file is missing.
	    """
	SettingDictType = typing.Dict[
	    str, typing.Union[bool, int, float, str, typing.List[str]]
	]
	SettingValueType = typing.Union[
	    bool, int, float, str, typing.List[str], SettingDictType
]
	def format_yaml_comment(comment_lines: typing.List[str]) -> str:
	    out = []
	    for line in comment_lines:
	        out.append("\n".join(textwrap.wrap(line, width=YAML_WIDTH)))
	    return "\n" + "\n".join(out)
	def add_to_group(
	    group: ryaml.CommentedMap,
	    key: str,
	    value: typing.Any,
    comment_lines: typing.List[str],
	    indent: int,
	) -> None:
	    group[key] = value
	    group.yaml_set_comment_before_after_key(
	        key,
	        before=format_yaml_comment(comment_lines),
	        indent=indent,
	    )
	T = typing.TypeVar("T", bound="SettingValueType")
class ConfigSetting(typing.Generic[T]):
	    """
	    An individual setting that can be exposed through CLI and/or YAML
	    """
	    def __init__(
	        self,
	        name: str,
	        default: T,
	        description_lines: typing.List[str],
	        cli_args: typing.Optional[typing.List[str]] = None,
        place_default_in_yaml: bool = False,
	        include_in_argparse: bool = True,
	        include_in_yaml: bool = True,
	        show_default_in_yaml: bool = True,
	        fn_on_set: typing.Callable[[T], None] = lambda x: None,
	    ):
	        self.name = name
	        self.default = default
	        self.description_lines = [x.strip() for x in description_lines]
	        if cli_args is None:
            cli_args = ["--" + name.replace("_", "-")]
	        self.cli_args = cli_args
	        self.value = default
	        self.include_in_argparse = include_in_argparse
	        self.include_in_yaml = include_in_yaml
	        self.place_default_in_yaml = place_default_in_yaml
	        self.show_default_in_yaml = show_default_in_yaml
	        self.fn_on_set = fn_on_set
	    def add_to_argparse(self, parser: argparse._ArgumentGroup):
	        if not self.include_in_argparse:
            return
	        kwargs = {
	            "default": self.value,
	            "help": " ".join(self.description_lines),
	        }
	        # note: the other way to do this is with
	        #   typing.get_args(self.__orig_class__)[0]
	        # but that isn't officially supported, so
	        # let's do it the jankier way
	        if isinstance(self.default, str):
            kwargs["type"] = str
	        elif isinstance(self.default, bool):
	            kwargs["action"] = "store_true"
	            if self.default:
	                kwargs["action"] = "store_false"
	        elif isinstance(self.default, int):
	            kwargs["type"] = int
	        elif isinstance(self.default, float):
	            kwargs["type"] = float
	        elif isinstance(self.default, list):
            kwargs["type"] = str
	            kwargs["nargs"] = "*"
	        parser.add_argument(*self.cli_args, **kwargs)
	    def set_value_from_argparse(self, args: argparse.Namespace) -> None:
	        if not self.include_in_argparse:
	            return
	        if not hasattr(args, self.name):
	            raise ValueError(f"Namespace does not have attribute {self.name}")
	        self.set_value(getattr(args, self.name))
	    def add_to_yaml_group(self, group: ryaml.CommentedMap):
        if not self.include_in_yaml:
	            return
	        value = None
	        if self.place_default_in_yaml or (self.value != self.default):
	            value = self.value
	        add_to_group(
	            group,
	            key=self.name,
	            value=value,
	            comment_lines=self.make_yaml_comment(),
            indent=INDENT_UNIT,
	        )
	    def make_yaml_comment(self) -> typing.List[str]:
	        comment_lines = self.description_lines.copy()
	        if self.show_default_in_yaml:
	            if self.default is not None:
	                comment_lines.append(f"  default: {self.default}")
	            else:
	                comment_lines.append("  default: None")
	        return comment_lines
    def set_value_from_yaml(self, yaml: ryaml.CommentedMap) -> None:
	        if not self.include_in_yaml:
	            return
	        if self.name not in yaml:
	            return
	        if (not self.place_default_in_yaml) and (yaml[self.name] is None):
	            # if we didn't place the default in the yaml, and the setting
	            # is now blank, that's no surprise.  Keep the default
	            # rather than overwriting it with None
	            return
        value = yaml[self.name]
	        # if value is a dict, it's possible that it only contains
	        # some, but not all of the keys that we need.  So we want to
	        # merge it with the default dict, overwriting the default
	        # values with the values from the yaml
	        if isinstance(value, dict):
	            if isinstance(self.default, dict):
	                value = {**self.default, **value}
	            else:
	                raise ValueError(
                    f"Setting {self.name} is a dict, but the default value is not"
	                )
	        # note: keeping the type checker happy across python versions
	        # here is actually kinda hard.  It's worried about what the
	        # values in the combined dict might be.  But really it's fine,
	        # so just tell it to chill.
	        self.set_value(value)  # type: ignore
	    def set_value(self, value: T) -> None:
	        if isinstance(self.default, bool) and isinstance(value, str):
	            if value.lower() in ["true", "yes", "1"]:
                value = True  # type: ignore
	            elif value.lower() in ["false", "no", "0"]:
	                value = False  # type: ignore
	            else:
	                value = bool(value)  # type: ignore
	        self.value = value
	        self.fn_on_set(value)
	    def get(self) -> T:
	        if isinstance(self.value, dict):
	            return self.value.copy()
        return self.value
	class ConfigSettingGroup:
	    """
	    A group of related settings that can be exposed through CLI and/or YAML
	    """
	    def __init__(
	        self,
	        name: str,
	        description: str = "",
	        include_in_argparse: bool = True,
        include_in_yaml: bool = True,
	    ):
	        self.name = name
	        self.description = description
	        self.settings = {}
	        self.include_in_argparse = include_in_argparse
	        self.include_in_yaml = include_in_yaml
	    def add_setting(self, setting: "ConfigSetting") -> None:
	        self.settings[setting.name] = setting
	    def add_to_argparse(self, parser: argparse.ArgumentParser):
        if not self.include_in_argparse:
	            return
	        arg_group = parser.add_argument_group(self.name, self.description)
	        for setting in self.settings.values():
	            setting.add_to_argparse(arg_group)
	    def set_values_from_argparse(self, args: argparse.Namespace) -> None:
	        if not self.include_in_argparse:
	            return
	        for setting in self.settings.values():
	            setting.set_value_from_argparse(args)
    def add_to_yaml(self, yaml: ryaml.CommentedMap):
	        if not self.include_in_yaml:
	            return
	        group_key = self.name.lower().replace(" ", "_")
	        group = ryaml.CommentedMap()
	        for setting in self.settings.values():
	            setting.add_to_yaml_group(group)
	        add_to_group(
	            group=yaml,
	            key=group_key,
            value=group,
	            comment_lines=[DIVIDER, group_key, "."],
	            indent=0,
	        )
	    def set_values_from_yaml(self, yaml: dict):
	        if not self.include_in_yaml:
	            return
	        if yaml is None:
	            return
	        group_key = self.name.lower().replace(" ", "_")
        if group_key not in yaml:
	            return
	        group = yaml[group_key]
	        for setting in self.settings.values():
	            setting.set_value_from_yaml(group)
	    def set_values_from_dict(self, main_dict: dict):
	        group_dict = main_dict.get(self.name.lower().replace(" ", "_"), {})
	        for setting in self.settings.values():
	            if setting.name in group_dict:
	                val = group_dict[setting.name]
                setting.set_value(val)
	    def set(self, name: str, value: SettingValueType) -> None:
	        self.settings[name].set_value(value)
	    def get_setting(self, name: str) -> ConfigSetting:
	        return self.settings[name]
	    def get(self, name: str) -> SettingValueType:
	        return self.settings[name].value
	    def get_str(self, name: str) -> str:
	        return self.settings[name].value
	    def get_list(self, name: str) -> typing.List[SettingValueType]:
        return self.settings[name].value
	    def get_all(self) -> typing.Dict[str, SettingValueType]:
	        return {name: setting.get() for (name, setting) in self.settings.items()}.copy()
	def load_from_yaml(
	    filename: str,
	    setting_groups: typing.List["ConfigSettingGroup"],
	) -> typing.Optional[str]:
	    """
	    Load settings from a YAML file only
	    Returns None if successful, or an error message if not
    """
	    try:
	        with open(filename, "r", encoding="utf-8") as file:
	            return load_from_yaml_stream(file, setting_groups)
	    except (FileNotFoundError, IsADirectoryError):
	        return "File not found"
	def load_from_yaml_stream(
	    stream: typing.Union[pathlib.Path, ryaml.StreamTextType],
	    setting_groups: typing.List["ConfigSettingGroup"],
	) -> typing.Optional[str]:
    """
	    Load settings from a YAML string only
	    Returns None if successful, or an error message if not
	    """
	    try:
	        yaml = ryaml.YAML(typ="safe")
	        yaml_settings = yaml.load(stream)
	    except ryaml_error.MarkedYAMLError as err:
	        return str(err)
	    for group in setting_groups:
        group.set_values_from_yaml(yaml_settings)
	    return None
	def load_from_cli(
	    args,
	    setting_groups: typing.List["ConfigSettingGroup"],
	) -> argparse.ArgumentParser:
	    """
	    Load settings from the command line only
	    """
	    cli_parser = argparse.ArgumentParser(
        description=f"oobabot v{oobabot.__version__}: Discord bot for "
	        + "oobabooga's text-generation-webui",
	        formatter_class=argparse.ArgumentDefaultsHelpFormatter,
	        add_help=False,
	    )
	    # we need to initialize argparse AFTER we read from
	    # the yaml file, so that the argparse defaults are
	    # set to the yaml-read values.
	    for group in setting_groups:
	        group.add_to_argparse(cli_parser)
    cli_settings = cli_parser.parse_args(args=args)
	    for group in setting_groups:
	        group.set_values_from_argparse(cli_settings)
	    return cli_parser
	def load_from_dict(
	    setting_groups: typing.List["ConfigSettingGroup"],
	    settings_dict: dict,
	) -> None:
	    """
	    Load settings from a dictionary
    """
	    for group in setting_groups:
	        group.set_values_from_dict(settings_dict)
	def load(
	    cli_args: typing.List[str],
	    setting_groups: typing.List["ConfigSettingGroup"],
	    config_file: str,
	    raise_if_file_missing: bool,
	) -> argparse.ArgumentParser:
	    """
    Load settings from defaults, config.yml, and command line arguments
	    in that order.  Later sources will overwrite earlier ones.
	    Returns the argparse parser, which can be used to print out the help
	    message.
	    """
	    load_error_message = load_from_yaml(config_file, setting_groups)
	    if load_error_message and raise_if_file_missing:
	        raise ConfigFileMissingError(load_error_message)
	    namespace = load_from_cli(cli_args, setting_groups)
	    # returning this since it can print out the help message
    return namespace
	START_COMMENT = textwrap.dedent(
	    """
	    # Welcome to Oobabot!
	    #
	    # This is the configuration file for Oobabot.  It is a YAML file, and
	    # comments are allowed.  Oobabot attempts to load a file named
	    # "config.yml" from the current directory when it is run.
	    #
	    """
)
	def write_to_stream(
	    setting_groups: typing.List["ConfigSettingGroup"],
	    out_stream: typing.TextIO,
	) -> None:
	    yaml_map = ryaml.CommentedMap()
	    yaml_map.yaml_set_start_comment(START_COMMENT)
	    add_to_group(
	        group=yaml_map,
	        key="version",
        value=oobabot.__version__,
	        comment_lines=[],
	        indent=0,
	    )
	    for group in setting_groups:
	        group.add_to_yaml(yaml_map)
	    yaml = ryaml.YAML()
	    yaml.default_flow_style = False
	    yaml.allow_unicode = True
	    yaml.indent(mapping=INDENT_UNIT, sequence=2 * INDENT_UNIT, offset=INDENT_UNIT)
    yaml.dump(yaml_map, out_stream)
	def write_to_file(
	    setting_groups: typing.List["ConfigSettingGroup"], filename: str
	) -> None:
	    """
	    Write the current values in the setting groups to a YAML file
	    """
	    with open(filename, "w", encoding="utf-8") as file:
	        write_to_stream(setting_groups, file)
