	import torch
	def merge_ckpt(para_block=8):
	    state = None
	    for rank in range(para_block):
	        local_path = "/opt/data/private/ckpt/nar_chat/megatron8_ft/checkpoint9-model_part-{}.pt".format(rank)
	        with open(local_path, "rb") as f:
	            ckpt_state = torch.load(f, map_location=torch.device("cpu"))
	        if state is None:
	            state = ckpt_state
            continue
	        else:
	            ckpt_state = ckpt_state['model']    
