	from acrocord.misc import execution
import pytest
	def test_deprecated():
	    @execution.deprecated
	    def foo():
	        return "hello"
	    with pytest.deprecated_call():
	        foo()
	def test_print():
	    @execution.execution_time
	    def foo():
        return "hello"
	    assert foo() == "hello"

