	    off_diagonal = jnp.zeros(size - 1)
	    return operator.diagonal, off_diagonal, off_diagonal
	@tridiagonal.register(TridiagonalLinearOperator)
	def _(operator):
	    return operator.diagonal, operator.lower_diagonal, operator.upper_diagonal
