""" Util functions for loading and saving checkpoints
	"""
	import os
	import torch
	def load_pretrain_checkpoint(model, pretrain_checkpoint_path):
	    # load pretrained model for point cloud encoding
	    model_dict = model.state_dict()
	    if pretrain_checkpoint_path is not None:
	        print('Load encoder module from pretrained checkpoint...')
	        pretrained_dict = torch.load(os.path.join(pretrain_checkpoint_path, 'checkpoint.tar'))['params']
        pretrained_dict = {'encoder.' + k: v for k, v in pretrained_dict.items()}
	        pretrained_dict = {k: v for k, v in pretrained_dict.items() if k in model_dict}
	        model_dict.update(pretrained_dict)
	        model.load_state_dict(model_dict)
	    else:
	        raise ValueError('Pretrained checkpoint must be given.')
	    return model
	def load_model_checkpoint(model, model_checkpoint_path, optimizer=None, mode='test'):
	    try:
	        checkpoint = torch.load(os.path.join(model_checkpoint_path, 'checkpoint.tar'))
        start_iter = checkpoint['iteration']
	        start_iou = checkpoint['IoU']
	    except:
	        raise ValueError('Model checkpoint file must be correctly given (%s).' %model_checkpoint_path)
	    model.load_state_dict(checkpoint['model_state_dict'], strict=False)
	    if mode == 'test':
	        print('Load model checkpoint at Iteration %d (IoU %f)...' % (start_iter, start_iou))
	        return model
	    else:
	        try:
            optimizer.load_state_dict(checkpoint['optimizer_state_dict'])
	        except:
	            print('Checkpoint does not include optimizer state dict...')
	        print('Resume from checkpoint at Iteration %d (IoU %f)...' % (start_iter, start_iou))
	        return model, optimizer
	def save_pretrain_checkpoint(model, output_path):
	    torch.save(dict(params=model.encoder.state_dict()), os.path.join(output_path, 'checkpoint.tar'))

""" Util functions for writing logs
	"""
	import os
	class IOStream():
	    def __init__(self, path):
	        self.f = open(path, 'a')
	    def cprint(self, text):
	        print(text)
	        self.f.write(text+'\n')
	        self.f.flush()
    def close(self):
	        self.f.close()
	def mkdir(path):
	    if not os.path.exists(path):
	        os.makedirs(path)
	def print_args(logger, args):
	    opt = vars(args)
	    logger.cprint('------------ Options -------------')
	    for k, v in sorted(opt.items()):
	        logger.cprint('%s: %s' % (str(k), str(v)))
    logger.cprint('-------------- End ----------------\n')
	def init_logger(log_dir, args):
	    mkdir(log_dir)
	    log_file = os.path.join(log_dir, 'log_%s.txt' %args.phase)
	    logger = IOStream(log_file)
	    # logger.cprint(str(args))
	    ## print arguments in format
	    print_args(logger, args)
	    return logger

""" Cuda util function
	"""
	def cast_cuda(input):
	    if type(input) == type([]):
	        for i in range(len(input)):
	            input[i] = cast_cuda(input[i])
	    else:
	        return input.cuda()
	    return input

import torch
	from torch import nn
	class GMMNnetwork(nn.Module):
	    def __init__(
	        self,
	        noise_dim,
	        embed_dim,
	        hidden_size,
	        feature_dim,
	        drop_out_gmm,
        semantic_reconstruction=False,
	    ):
	        super().__init__()
	        def block(in_feat, out_feat):
	            layers = [nn.Linear(in_feat, out_feat)]
	            layers.append(nn.LeakyReLU(0.2, inplace=True))
	            layers.append(nn.Dropout(p=drop_out_gmm))
	            return layers
	        def init_weights(m):
	            if type(m) == nn.Linear:
                torch.nn.init.xavier_uniform_(m.weight)
	                m.bias.data.fill_(0.01)
	        if hidden_size:
	            self.model = nn.Sequential(
	                *block(noise_dim + embed_dim, hidden_size),
	                nn.Linear(hidden_size, feature_dim),
	            )
	        else:
	            self.model = nn.Linear(noise_dim + embed_dim, feature_dim)
	        self.model.apply(init_weights)
        self.semantic_reconstruction = semantic_reconstruction
	        if self.semantic_reconstruction:
	            self.semantic_reconstruction_layer = nn.Linear(
	                feature_dim, noise_dim + embed_dim
	            )
	    def forward(self, embd, noise):
	        features = self.model(torch.cat((embd, noise), 1))
	        if self.semantic_reconstruction:
	            semantic = self.semantic_reconstruction_layer(features)
	            return features, semantic
        else:
	            return features
	class GMMNLoss:
	    def __init__(self, sigma=[2, 5, 10, 20, 40, 80], cuda=False):
	        self.sigma = sigma
	        self.cuda = cuda
	    def build_loss(self):
	        return self.moment_loss
	    def get_scale_matrix(self, M, N):
	        s1 = torch.ones((N, 1)) * 1.0 / N
        s2 = torch.ones((M, 1)) * -1.0 / M
	        if self.cuda:
	            s1, s2 = s1.cuda(), s2.cuda()
	        return torch.cat((s1, s2), 0)
	    def moment_loss(self, gen_samples, x):
	        X = torch.cat((gen_samples, x), 0)
	        XX = torch.matmul(X, X.t())
	        X2 = torch.sum(X * X, 1, keepdim=True)
	        exp = XX - 0.5 * X2 - 0.5 * X2.t()
	        M = gen_samples.size()[0]
        N = x.size()[0]
	        s = self.get_scale_matrix(M, N)
	        S = torch.matmul(s, s.t())
	        loss = 0
	        for v in self.sigma:
	            kernel_val = torch.exp(exp / v)
	            loss += torch.sum(S * kernel_val)
	        loss = torch.sqrt(loss)
	        return loss
