	        with open(path, "r") as f:
	            return json.load(f, **self.load_kwargs)

import os
	from pathlib import Path
	import click
	from scrat import Config
	from scrat.db import DBConnector
	def _make_config(path) -> Config:
	    return Config.create_config_file(base_path=path)
	def _make_db(path):
	    DBConnector.create_db(path=path)
	@click.command()
def init():
	    """Initialize Scrat's stash"""
	    cwd = Path(os.getcwd())
	    folder = cwd / ".scrat"
	    if os.path.exists(folder):
	        click.secho("already initialized", fg="red")
	        exit(-1)
	    os.mkdir(folder)
	    config = _make_config(folder)
	    _make_db(config.db_path)
    os.mkdir(config.stash_path)
	    if os.path.exists(cwd / ".git"):
	        with open(folder / ".gitignore", "w") as f:
	            f.write(f"{config.stash_dir}\n{config.db_file}\n{config.config_file}\n")
	@click.command()
	def deinit():
	    """Remove Scrat's stash"""
	    import shutil
	    cwd = Path(os.getcwd())
	    folder = cwd / ".scrat"
    if not os.path.exists(folder):
	        click.secho("not initialized", fg="red")
	        exit(-1)
	    click.confirm(
	        "This will remove everything from the stash are you sure?", abort=True
	    )
	    shutil.rmtree(folder)
	    click.secho("Scrat was deinitialized correctly")
	if __name__ == "__main__":
	    init()

import os
	from datetime import timedelta
	import click
	from sqlalchemy.sql import exists, select
	from scrat.config import Config
	from scrat.db import DBConnector, Nut
	from scrat.utils import humanize_size
	@click.group()
	def stash():
	    pass
COLUMNS = [
	    "hash",
	    "name",
	    "path",
	    "created_at",
	    "used_at",
	    "size_mb",
	    "use_count",
	    "time_s",
	]
def format_datetime(datetime):
	    if datetime is not None:
	        return datetime.isoformat(timespec="minutes", sep=" ")
	    return ""
	@stash.command()
	@click.option(
	    "-s",
	    "--sort-by",
	    type=click.Choice(COLUMNS),
	    default="created_at",
    help="Column to sort by",
	)
	@click.option(
	    "-d", "--desc", default=False, is_flag=True, help="Use to sort descending"
	)
	def list(sort_by, desc):
	    """List content of stash"""
	    config = Config.load()
	    db_connector = DBConnector(config.db_path)
	    with db_connector.session() as session:
        sorting = getattr(Nut, sort_by)
	        if desc:
	            sorting = sorting.desc()
	        click.secho(f"{'name':<15} {'hash':<32} {'created_at':<16} {'size':5}")
	        for nut in session.query(Nut).order_by(sorting).all():
	            click.secho(
	                (
	                    f"{nut.name if len(nut.name) <= 15 else nut.name[:13] + '..' :<15} "
	                    f"{nut.hash} "
	                    f"{format_datetime(nut.created_at)} "
                    f"{humanize_size(nut.size)}"
	                )
	            )
	@stash.command()
	@click.argument("hash_key")
	def delete(hash_key):
	    """Removes one nut from the stash"""
	    config = Config.load()
	    db_connector = DBConnector(config.db_path)
	    with db_connector.session() as session:
        nut = session.scalar(select(Nut).where(Nut.hash == hash_key))
	        if nut is None:
	            click.secho("Nut does not exists", fg="red")
	            exit(-1)
	        try:
	            os.remove(nut.path)
	        except FileNotFoundError:
	            pass
	        session.query(Nut).filter_by(hash=hash_key).delete()
	        session.commit()
@stash.command()
	def clear():
	    """Empty the stash"""
	    config = Config.load()
	    click.confirm(
	        "This will remove everything from the stash are you sure?", abort=True
	    )
	    try:
	        os.remove(config.db_path)
	    except FileNotFoundError:
        click.secho("DB not found")
	    for file in os.listdir(config.cache_path):
	        os.remove(config.cache_path / file)
	@stash.command()
	def stats():
	    """Print stash stats"""
	    config = Config.load()
	    db_connector = DBConnector(config.db_path)
	    seconds_saved = 0
	    size = 0
    entries = 0
	    with db_connector.session() as session:
	        for nut in session.query(Nut).all():
	            seconds_saved += nut.use_count * nut.time_s
	            size += nut.size
	            entries += 1
	    click.secho(f"Total entries: {entries}")
	    if entries == 0:
	        return
	    click.secho(f"Total size: {humanize_size(size)}")
    if seconds_saved > 0:
	        click.secho(f"time saved: {timedelta(seconds=seconds_saved)}")
	@stash.command()
	def check():
	    """Check the integrity of the stash"""
	    config = Config.load()
	    db_connector = DBConnector(config.db_path)
	    with db_connector.session() as session:
	        for nut in session.query(Nut).all():
	            if not os.path.exists(nut.path):
                click.secho(f"Missing file '{nut.hash}'")
	        for file in os.listdir(config.cache_path):
	            if not session.query(exists().where(Nut.hash == file)).scalar():
	                click.secho(f"File not indexed: '{file}'")
	if __name__ == "__main__":
	    stash()

import time
	class Timer:
	    "Simple timer."
	    def __init__(self) -> None:
	        self._start_time = None
	    def start(self):
	        self._start_time = time.time()
	    def end(self) -> float:
	        try:
	            delta = time.time() - self._start_time
        except TypeError:
	            raise RuntimeError("Trying to stop a timer that has not been started")
	        self._start_time = None
	        return delta

import typing as T
	from .base import Hasher
	class PandasHasher(Hasher):
	    """
	    Hasher for Pandas Series and DataFrames
	    Parameters
	    ----------
	    use_values
	        If False, only the index of the dataframe is included in the hash
	        This can help with the speed of the hasher on big dataframes where
        you only care what rows are included but you know the values
	        don't change, by default True
	    """
	    def __init__(self, use_values: bool = True) -> None:
	        super().__init__()
	        self.use_values = use_values
	    def hash(self, value: T.Any) -> str:
	        if self.use_values:
	            return self.md5_hash(value.index.values, value.values)
	        return self.md5_hash(value.index.values)

