import os
	import json
	import random
	class VCTK:
	    """
	    Generate the json file obtained by dumping the dictionary
	    {'train': {fid: wav_path, ...}, 'validation': {fid: wav_path, ...}, 'test': {fid: wav_path, ...}}
	    """
	    def __init__(self, data_dir, out_dir, val_spks=None, test_spks=None):
	        self.data_dir = data_dir
        self.out_dir = out_dir
	        self.summary_file = os.path.join(out_dir, 'vctk-summary.json')
	        self.wav_ext = 'mic2.flac'
	        self.dataset_summary = {}
	        assert ((val_spks is None and test_spks is None) or
	                (val_spks is not None and test_spks is not None),
	                "Please specify both val and test speakers!")
	        self.val_spks = val_spks
	        self.test_spks = test_spks
	        self.validate_dir()
    def validate_dir(self):
	        if not os.path.isdir(self.data_dir):
	            raise NotADirectoryError('{} is not a valid directory!'.format(self.data_dir))
	        if not os.path.isdir(self.out_dir):
	            os.makedirs(self.out_dir)
	        return
	    @staticmethod
	    def extract_spk_fid(filename):
	        """
	        :param filename:
        :return: spk_name, fid
	        """
	        basename = filename.split('/')[-1].split('.')[0]
	        spk = basename.split('_')[0]
	        uid = basename.split('_')[1]
	        fid = '{}_{}'.format(spk, uid)
	        return spk, fid
	    def write_dataset_info(self):
	        with open(self.summary_file, 'w') as f:
	            json.dump(self.dataset_summary, f, sort_keys=True, indent=4)
        return
	    def write_summary(self):
	        dataset_summary = {}
	        for root, dirs, files in os.walk(self.data_dir, followlinks=True):
	            for basename in files:
	                if basename.endswith(self.wav_ext):
	                    filename = os.path.join(root, basename)
	                    spk, fid = self.extract_spk_fid(filename)
	                    wav_path = os.path.join(root, basename)
	                    if spk not in dataset_summary.keys():
                        dataset_summary[spk] = {}
	                        dataset_summary[spk][fid] = wav_path
	                    else:
	                        dataset_summary[spk][fid] = wav_path
	        if self.val_spks is None and self.test_spks is None:
	            all_spks = list(dataset_summary.keys())
	            random.shuffle(all_spks)
	            self.test_spks = all_spks[-10:]
	            self.val_spks = all_spks[-20: -10]
	        train_summary = {}
        val_summary = {}
	        test_summary = {}
	        for spk in dataset_summary.keys():
	            if spk in self.val_spks:
	                val_summary.update(dataset_summary[spk])
	            elif spk in self.test_spks:
	                test_summary.update(dataset_summary[spk])
	            else:
	                train_summary.update(dataset_summary[spk])
	        self.dataset_summary = {'train': train_summary,
                                'validation': val_summary,
	                                'test': test_summary}
	        self.write_dataset_info()
	        return

import os
	import json
	import random
	class AiShell3:
	    """
	    Generate the json file obtained by dumping the dictionary
	    {'train': {fid: wav_path, ...}, 'validation': {fid: wav_path, ...}, 'test': {fid: wav_path, ...}}
	    """
	    def __init__(self, data_dir, out_dir, train_spk_file=None, val_test_spk_file=None):
	        self.data_dir = data_dir
        self.out_dir = out_dir
	        self.train_spks = self.read_train_spks(train_spk_file)
	        val_test_spks = self.read_val_test_spks(val_test_spk_file)
	        random.shuffle(val_test_spks)
	        self.val_spks = val_test_spks[:len(val_test_spks) // 2]
	        self.test_spks = val_test_spks[len(val_test_spks) // 2:]
	        self.summary_file = os.path.join(out_dir, 'aishell3-summary.json')
	        self.wav_ext = '.wav'
	        self.dataset_summary = {}
	        self.validate_dir()
    def read_train_spks(self, f):
	        train_spks = []
	        if f is not None:
	            with open(f, 'r', encoding='utf-8') as f:
	                for line in f:
	                    train_spks.append(line.strip())
	        else:
	            train_spks = os.listdir(os.path.join(self.data_dir, 'train/wav'))
	        assert len(train_spks) > 0
	        return train_spks
    def read_val_test_spks(self, f):
	        vt_spks = []
	        if f is not None:
	            with open(f, 'r', encoding='utf-8') as f:
	                for line in f:
	                    vt_spks.append(line.strip())
	        else:
	            vt_spks = [spk for spk in os.listdir(os.path.join(self.data_dir, 'test/wav'))
	                       if spk not in self.train_spks]
	        assert len(vt_spks) > 0
        return vt_spks
	    def validate_dir(self):
	        if not os.path.isdir(self.data_dir):
	            raise NotADirectoryError('{} is not a valid directory!'.format(self.data_dir))
	        if not os.path.isdir(self.out_dir):
	            os.makedirs(self.out_dir)
	        return
	    @staticmethod
	    def extract_spk_fid(filename):
	        """
        :param filename:
	        :return: spk_name, fid
	        """
	        fid = filename.split('/')[-1].split('.')[0]
	        spk = filename.split('/')[-2]
	        return spk, fid
	    def write_dataset_info(self):
	        with open(self.summary_file, 'w') as f:
	            json.dump(self.dataset_summary, f, sort_keys=True, indent=4)
	        return
    def write_summary(self):
	        train_summary = {}
	        val_summary = {}
	        test_summary = {}
	        for root, dirs, files in os.walk(self.data_dir, followlinks=True):
	            for basename in files:
	                if basename.endswith(self.wav_ext):
	                    wav_path = os.path.join(root, basename)
	                    spk, fid = self.extract_spk_fid(wav_path)
	                    if spk in self.train_spks:
                        train_summary[fid] = wav_path
	                    elif spk in self.val_spks:
	                        val_summary[fid] = wav_path
	                    elif spk in self.test_spks:
	                        test_summary[fid] = wav_path
	                    else:
	                        continue
	        self.dataset_summary = {'train': train_summary,
	                                'validation': val_summary,
	                                'test': test_summary}
        self.write_dataset_info()
	        return

import os
	import numpy as np
	import tensorflow as tf
	from tqdm import tqdm
	class TFRecordWriter:
	    def __init__(self, train_split=None, data_dir=None, save_dir=None, chunk_size=None):
	        self.train_split = train_split
	        self.data_dir = data_dir
	        self.save_dir = save_dir
	        self.chunk_size = chunk_size
        self.train_ids_file = os.path.join(self.data_dir, 'train.txt') if data_dir is not None else None
	        self.val_ids_file = os.path.join(self.data_dir, 'val.txt') if data_dir is not None else None
	        self.test_ids_file = os.path.join(self.data_dir, 'test.txt') if data_dir is not None else None
	    @staticmethod
	    def _bytes_feature(value):
	        """Returns a bytes_list from a string / byte."""
	        if isinstance(value, type(tf.constant(0))):
	            value = value.numpy()  # BytesList won't unpack a string from an EagerTensor.
	        return tf.train.Feature(bytes_list=tf.train.BytesList(value=[value]))
	    @staticmethod
    def _float_feature(value):
	        """Returns a float_list from a float / double."""
	        return tf.train.Feature(float_list=tf.train.FloatList(value=[value]))
	    @staticmethod
	    def _int64_feature(value):
	        """Returns an int64_list from a bool / enum / int / uint."""
	        return tf.train.Feature(int64_list=tf.train.Int64List(value=[value]))
	    @staticmethod
	    def serialize_example(fid, mel, mel_len):
	        """
        :param fid: string
	        :param mel: np array, [mel_len, num_mels]
	        :param mel_len: int32
	        :return: byte string
	        """
	        feature = {
	            'fid': TFRecordWriter._bytes_feature(fid.encode('utf-8')),
	            'mel': TFRecordWriter._bytes_feature(tf.io.serialize_tensor(mel)),
	            'mel_len': TFRecordWriter._int64_feature(mel_len)}
	        # Create a Features message using tf.train.Example.
        example_proto = tf.train.Example(features=tf.train.Features(feature=feature))
	        return example_proto.SerializeToString()
	    def _parse_fids(self, mode='train'):
	        fids_f = {'train': self.train_ids_file,
	                  'val': self.val_ids_file,
	                  'test': self.test_ids_file}[mode]
	        fids = []
	        with open(fids_f, 'r', encoding='utf-8') as f:
	            for line in f:
	                fids.append(line.strip())
        return fids
	    def _get_features(self, fid):
	        mel = np.load(os.path.join(self.data_dir, 'mels', '{}.npy'.format(fid))).astype(np.float64)
	        mel_len = mel.shape[0]
	        return mel, mel_len
	    def mel_exist(self, fid):
	        mel_npy = os.path.join(self.data_dir, 'mels', '{}.npy'.format(fid))
	        return os.path.isfile(mel_npy)
	    def write(self, mode='train'):
	        fids = self._parse_fids(mode)
        if mode == 'train':
	            splited_fids = [fids[i::self.train_split] for i in range(self.train_split)]
	        else:
	            splited_fids = [fids]
	        for i, ids in enumerate(splited_fids):
	            tfrecord_path = os.path.join(self.save_dir, '{}-{}.tfrecords'.format(mode, i))
	            with tf.io.TFRecordWriter(tfrecord_path) as writer:
	                for fid in tqdm(ids):
	                    if not self.mel_exist(fid):
	                        continue
                    mel, mel_len = self._get_features(fid)
	                    serialized_example = self.serialize_example(fid, mel, mel_len)
	                    writer.write(serialized_example)
	        return
	    def write_all(self):
	        self.write('train')
	        self.write('val')
	        self.write('test')
	        return
	    def pad2chunk(self, inputs):
        inp_exp = tf.tile(inputs, tf.constant([14, 1]))
	        inp_exp = inp_exp[:self.chunk_size, :]
	        return inp_exp
	    def parse_example(self, serialized_example):
	        feature_description = {
	            'fid': tf.io.FixedLenFeature((), tf.string),
	            'mel': tf.io.FixedLenFeature((), tf.string),
	            'mel_len': tf.io.FixedLenFeature((), tf.int64)}
	        example = tf.io.parse_single_example(serialized_example, feature_description)
	        fid = example['fid']
        mel = tf.io.parse_tensor(example['mel'], out_type=tf.float64)
	        mel_ext = self.pad2chunk(mel)
	        mel_len = example['mel_len']
	        return (fid,
	                tf.cast(mel, tf.float32),
	                tf.cast(mel_len, tf.int32),
	                tf.cast(mel_ext, tf.float32))
	    def create_dataset(self, buffer_size, num_parallel_reads,
	                       batch_size, num_mels, shuffle_buffer, shuffle,
	                       tfrecord_files, seed=1, drop_remainder=False):
        tfrecord_dataset = tf.data.TFRecordDataset(
	            tfrecord_files, buffer_size=buffer_size,
	            num_parallel_reads=num_parallel_reads)
	        tfdataset = tfrecord_dataset.map(
	            self.parse_example,
	            num_parallel_calls=num_parallel_reads)
	        tfdataset = tfdataset.padded_batch(
	            batch_size=batch_size,
	            drop_remainder=drop_remainder,
	            padded_shapes=([], [None, num_mels], [], [None, num_mels]))
        tfdataset = (tfdataset.shuffle(buffer_size=shuffle_buffer, seed=seed)
	                     if shuffle else tfdataset)
	        tfdataset = tfdataset.prefetch(tf.data.experimental.AUTOTUNE)
	        return tfdataset
	    def get_tfrecords_list(self, mode='train'):
	        assert self.save_dir is not None
	        assert mode in ['train', 'val', 'test']
	        return [os.path.join(self.save_dir, f)
	                for f in os.listdir(self.save_dir) if f.startswith(mode)]
