#!/usr/bin/env python
	# -*- encoding: utf-8 -*-
	"""
	@File    :   prepare_dataset.py
	@Time    :   2023/05/26 20:07:10
	@Author  :   Wenbo Li
	@Desc    :   code for loading and transform txt2img dataset
	"""
	import os
	import random
from typing import Optional
	import numpy as np
	import torch
	from torchvision import transforms
	from huggingface_hub import snapshot_download
	from datasets import load_dataset
	from transformers import CLIPTokenizer
	from dataclasses import dataclass, field
	from stable_diffusion.dataclass import BaseDataclass
	from PIL import Image
@dataclass
	class DatasetConfig(BaseDataclass):
	    dataset: str = field(
	        default="poloclub/diffusiondb",
	        metadata={"help": "name of the dataset to use."},
	    )
	    subset: Optional[str] = field(
	        default=None,  # "2m_first_10k",
	        metadata={"help": "subset of the dataset to use."},
	    )
    # dataset: str = field(
	    #     default="lambdalabs/pokemon-blip-captions",
	    # )
	    data_dir: str = field(
	        default="data/dataset",
	        metadata={"help": "Cache directory to store loaded dataset."},
	    )
	    dataloader_num_workers: int = field(
	        default=4, metadata={"help": "number of workers for the dataloaders."}
	    )
    resolution: int = field(default=64, metadata={"help": "resolution of the images."})
	    center_crop: bool = field(
	        default=True, metadata={"help": "whether to apply center cropping."}
	    )
	    random_flip: bool = field(
	        default=False, metadata={"help": "whether to apply random flipping."}
	    )
	    max_train_samples: Optional[int] = field(
	        default=9000, metadata={"help": "max number of training samples to load."}
	    )
    max_val_samples: Optional[int] = field(
	        default=500, metadata={"help": "max number of validation samples to load."}
	    )
	    max_test_samples: Optional[int] = field(
	        default=500, metadata={"help": "max number of test samples to load."}
	    )
	def add_dataset_args(parser):
	    dataset_group = parser.add_argument_group("dataset")
	    dataset_group.add_argument(
	        "--dataset",
        type=str,
	        default="poloclub/diffusiondb",
	    )
	    dataset_group.add_argument(
	        "--subset",
	        type=str,
	        default="2m_first_100k",
	    )
	    dataset_group.add_argument(
	        "--data_dir",
        type=str,
	        default="data",
	    )
	    dataset_group.add_argument(
	        "--resolution",
	        type=int,
	        default=64,
	    )
	    dataset_group.add_argument(
	        "--center_crop",
        type=bool,
	        default=True,
	    )
	    dataset_group.add_argument(
	        "--random_flip",
	        type=bool,
	        default=True,
	    )
	def collate_fn(examples):
	    pixel_values = torch.stack([example["pixel_values"] for example in examples])
    pixel_values = pixel_values.to(memory_format=torch.contiguous_format).float()
	    input_ids = torch.stack([example["input_ids"] for example in examples])
	    return {"pixel_values": pixel_values, "input_ids": input_ids}
	def tokenize_captions(examples, tokenizer, is_train=True):
	    captions = []
	    for caption in examples:
	        if isinstance(caption, str):
	            captions.append(caption)
	        elif isinstance(caption, (list, np.ndarray)):
	            # take a random caption if there are multiple
            captions.append(random.choice(caption) if is_train else caption[0])
	        else:
	            raise ValueError(
	                "Caption column should contain either strings or lists of strings."
	            )
	    inputs = tokenizer(
	        captions,
	        max_length=tokenizer.model_max_length,
	        padding="max_length",
	        truncation=True,
        return_tensors="pt",
	    )
	    return inputs.input_ids
	def get_transform(resolution, random_flip, center_crop):
	    return transforms.Compose(
	        [
	            transforms.Resize(
	                resolution, interpolation=transforms.InterpolationMode.BILINEAR
	            ),
	            transforms.CenterCrop(resolution)
            if center_crop
	            else transforms.RandomCrop(resolution),
	            transforms.RandomHorizontalFlip()
	            if random_flip
	            else transforms.Lambda(lambda x: x),
	            transforms.ToTensor(),
	            transforms.Normalize([0.5], [0.5]),
	        ]
	    )
	def detransform(latent: torch.Tensor):
    latent = latent.squeeze().cpu().detach().numpy()
	    latent = np.transpose(latent, (1, 2, 0))  # [c,h,w] -> [h,w,c]
	    latent = (latent + 1) / 2
	    latent = np.clip(latent, 0, 1)
	    return (latent * 255).astype(np.uint8)
	def to_img(digit_img, output_path: str = "", name="sample"):
	    img = Image.fromarray(digit_img.astype(np.uint8))
	    img.save(os.path.join(output_path, f"{name}.png"))
	    return img
	def get_dataset(
    args,
	    split: str = "train",
	    tokenizer: CLIPTokenizer = None,
	    logger=None,
	):
	    # check params
	    assert tokenizer is not None, "you need to specify a tokenizer"
	    assert split in {
	        "train",
	        "validation",
        "test",
	    }, "split should be one of train, validation, test"
	    # most of the txt2img datasets are not splited into train, validation and test, manually split it
	    dataset = load_dataset(
	        args.dataset,
	        args.subset,
	        cache_dir=os.path.join(args.data_dir, args.dataset),
	    )["train"]
	    if args.max_train_samples is not None and split == "train":
	        if args.max_train_samples < len(dataset):
            dataset = dataset.select(range(args.max_train_samples))
	        elif logger is not None:
	            logger.info(
	                f"max_train_samples({args.max_train_samples}) is larger than the number of train samples({len(dataset)})"
	            )
	    if args.max_val_samples is not None and split == "validation":
	        if args.max_train_samples + args.max_val_samples < len(dataset):
	            dataset = dataset.select(
	                range(
	                    args.max_train_samples,
                    args.max_train_samples + args.max_val_samples,
	                )
	            )
	        elif logger is not None:
	            logger.info(
	                f"max_val_samples({args.max_val_samples}) is larger than the number of val samples({len(dataset)})"
	            )
	    if args.max_test_samples is not None and split == "test":
	        if args.max_train_samples + args.max_val_samples + args.max_test_samples < len(
	            dataset
        ):
	            dataset = dataset.select(
	                range(
	                    args.max_train_samples + args.max_val_samples,
	                    args.max_train_samples
	                    + args.max_val_samples
	                    + args.max_test_samples,
	                )
	            )
	        elif logger is not None:
            logger.info(
	                f"max_test_samples({args.max_test_samples}) is larger than the number of test samples({len(dataset)})"
	            )
	    image_column = [col for col in ["image", "img"] if col in dataset.column_names][0]
	    caption_colum = [
	        col for col in ["text", "caption", "prompt"] if col in dataset.column_names
	    ][0]
	    transform = get_transform(args.resolution, args.random_flip, args.center_crop)
	    def preprocess_train(examples):
	        """tokenize captions and convert images to pixel values"""
        images = [image.convert("RGB") for image in examples[image_column]]
	        examples["pixel_values"] = [transform(image) for image in images]
	        examples["input_ids"] = tokenize_captions(examples[caption_colum], tokenizer)
	        return examples
	    if logger is not None:
	        logger.info(
	            f"Loaded {len(dataset)} {split} samples from dataset:{args.dataset}"
	        )
	    return dataset.with_transform(preprocess_train)
	def sample_test_image(args, split, tokenizer, logger, num: int = 10):
    test_data = get_dataset(args, split=split, tokenizer=tokenizer, logger=logger)
	    images = []
	    for _ in range(num):
	        idx = np.random.randint(0, len(test_data))
	        images.append(test_data[idx]["pixel_values"])
	    return images

#!/usr/bin/env python
	# -*- encoding: utf-8 -*-
	"""
	@File    :   parse_args.py
	@Time    :   2023/05/25 10:46:53
	@Author  :   Wenbo Li
	@Desc    :   convert dataclass into argparse and group, copied a lot from fairseq
	"""
	import argparse
	from dataclasses import MISSING, fields
from enum import Enum
	from typing import Any, Dict, List, Optional, Tuple
	from omegaconf import DictConfig, OmegaConf
	from stable_diffusion.dataclass import BaseDataclass
	from stable_diffusion.models.autoencoder import AutoencoderConfig
	from stable_diffusion.models.clip_model import ClipConfig
	from stable_diffusion.models.scheduler import DDPMConfig
	from stable_diffusion.models.unet import UnetConfig
	from trainer_args import (
	    LogConfig,
    TrainConfig,
	    OptimConfig,
	)
	from utils.checkpointing_args import CheckpointConfig
	from utils.prepare_dataset import DatasetConfig
	import re, ast, inspect
	# TODO: fix bug: type
	def convert_dataclass_to_argparse(
	    data_classes: List[BaseDataclass], parser: argparse.ArgumentParser
	):
    """Create argument groups and arguments for each data class"""
	    for data_class in data_classes:
	        group = parser.add_argument_group(
	            data_class.__name__.lower().replace("config", "")
	        )
	        # iterate over each arg
	        for field_info in fields(data_class):
	            # get args name
	            arg_name = f"--{field_info.name.replace('_', '-')}"
	            # attributes of this argument
            kwargs = {"type": type(field_info.type)}
	            default_value = field_info.default
	            if default_value is MISSING:
	                kwargs["required"] = True
	            # if arg_name == "--use-8bit-adam":
	            #     kwargs["action"] = "store_true"
	            if field_info.type == bool:
	                kwargs["action"] = (
	                    "store_false" if field_info.default is True else "store_true"
	                )
            print(
	                f"parse dataclass={data_class.__name__.lower()}, arg_name={arg_name}, "
	            )
	            group.add_argument(arg_name, **kwargs)
	def get_parser_from_dataclass(
	    dataclasses: List[BaseDataclass],
	    parser: argparse.ArgumentParser,
	):
	    """
	    implementation using code from [fairseq](https://github.com/facebookresearch/fairseq/blob/25c20e6a5e781e4ef05e23642f21c091ba64872e/fairseq/dataclass/utils.py#L53)
    """
	    # comments are from https://zhuanlan.zhihu.com/p/558760615?utm_id=0
	    def eval_str_list(x, x_type=float):
	        if x is None:
	            return None
	        if isinstance(x, str):
	            if len(x) == 0:
	                return []
	            x = ast.literal_eval(x)
	        try:
            return list(map(x_type, x))
	        except TypeError:
	            return [x_type(x)]
	    def interpret_dc_type(field_type):
	        if isinstance(field_type, str):
	            raise RuntimeError("field should be a type")
	        if field_type == Any:
	            return str
	        typestring = str(field_type)
	        if re.match(
            r"(typing.|^)Union\[(.*), NoneType\]$", typestring
	        ) or typestring.startswith("typing.Optional"):
	            return field_type.__args__[0]
	        return field_type
	    def gen_parser_from_dataclass(
	        parser: argparse.ArgumentParser,
	        dataclass_instance: BaseDataclass,  # FairseqDataclass,
	        delete_default: bool = False,
	        with_prefix: Optional[str] = None,
	    ) -> None:
        """
	        convert a dataclass instance to tailing parser arguments.
	        If `with_prefix` is provided, prefix all the keys in the resulting parser with it. It means that we are
	        building a flat namespace from a structured dataclass (see transformer_config.py for example).
	        """
	        def argparse_name(name: str):
	            """
	            Corner casedata_namedataclass
	            --linux A_B  A_ 
	             {A_}B {}B
            """
	            if name == "data" and (with_prefix is None or with_prefix == ""):
	                # normally data is positional args, so we don't add the -- nor the prefix
	                return name
	            if name == "_name":
	                # private member, skip
	                return None
	            full_name = "--" + name.replace("_", "-")
	            if with_prefix is not None and with_prefix != "":
	                # if a prefix is specified, construct the prefixed arg name
                full_name = with_prefix + "-" + full_name[2:]  # strip -- when composing
	            return full_name
	        def get_kwargs_from_dc(
	            dataclass_instance: BaseDataclass, k: str  # : FairseqDataclass,
	        ) -> Dict[str, Any]:
	            """k: dataclass attributes"""
	            kwargs = {}
	            field_type = dataclass_instance._get_type(k)
	            # filed_typeanyoptional
	            # optionalunionNONE
            inter_type = interpret_dc_type(field_type)
	            field_default = dataclass_instance._get_default(k)
	            if isinstance(inter_type, type) and issubclass(inter_type, Enum):
	                field_choices = [t.value for t in list(inter_type)]
	            else:
	                field_choices = None
	            field_help = dataclass_instance._get_help(k)
	            # metadatadefault
	            field_const = dataclass_instance._get_argparse_const(k)
	            if isinstance(field_default, str) and field_default.startswith("${"):
                kwargs["default"] = field_default
	            else:
	                if field_default is MISSING:
	                    kwargs["required"] = True
	                if field_choices is not None:
	                    kwargs["choices"] = field_choices
	                # kwargstypelambda
	                if (
	                    isinstance(inter_type, type)
	                    and (issubclass(inter_type, List) or issubclass(inter_type, Tuple))
                ) or ("List" in str(inter_type) or "Tuple" in str(inter_type)):
	                    if "int" in str(inter_type):
	                        kwargs["type"] = lambda x: eval_str_list(x, int)
	                    elif "float" in str(inter_type):
	                        kwargs["type"] = lambda x: eval_str_list(x, float)
	                    elif "str" in str(inter_type):
	                        kwargs["type"] = lambda x: eval_str_list(x, str)
	                    else:
	                        raise NotImplementedError(
	                            "parsing of type " + str(inter_type) + " is not implemented"
                        )
	                    if field_default is not MISSING:
	                        kwargs["default"] = (
	                            ",".join(map(str, field_default))
	                            if field_default is not None
	                            else None
	                        )
	                elif (
	                    isinstance(inter_type, type) and issubclass(inter_type, Enum)
	                ) or "Enum" in str(inter_type):
                    kwargs["type"] = str
	                    if field_default is not MISSING:
	                        if isinstance(field_default, Enum):
	                            kwargs["default"] = field_default.value
	                        else:
	                            kwargs["default"] = field_default
	                elif inter_type is bool:
	                    kwargs["action"] = (
	                        "store_false" if field_default is True else "store_true"
	                    )
                    kwargs["default"] = field_default
	                else:
	                    kwargs["type"] = inter_type
	                    if field_default is not MISSING:
	                        kwargs["default"] = field_default
	            # build the help with the hierarchical prefix
	            if with_prefix is not None and with_prefix != "" and field_help is not None:
	                field_help = with_prefix[2:] + ": " + field_help
	            kwargs["help"] = field_help
	            if field_const is not None:
                kwargs["const"] = field_const
	                kwargs["nargs"] = "?"
	            return kwargs
	        # _get_all_attributes->[]
	        for k in dataclass_instance._get_all_attributes():
	            # _get_name__dataclass_fields__Fieldsname
	            # argparse_name--
	            field_name = argparse_name(dataclass_instance._get_name(k))
	            # 
	            field_type = dataclass_instance._get_type(k)
            if field_name is None:
	                continue
	            elif inspect.isclass(field_type) and issubclass(field_type, BaseDataclass):
	                # FieldsFairseqDtaclassfields
	                # for fields that are of type FairseqDataclass, we can recursively
	                # add their fields to the namespace (so we add the args from model, task, etc. to the root namespace)
	                prefix = None
	                if with_prefix is not None:
	                    # if a prefix is specified, then we don't want to copy the subfields directly to the root namespace
	                    # but we prefix them with the name of the current field.
                    prefix = field_name
	                gen_parser_from_dataclass(parser, field_type(), delete_default, prefix)
	                continue
	            kwargs = get_kwargs_from_dc(dataclass_instance, k)
	            field_args = [field_name]
	            # batch_size--max-sentence
	            alias = dataclass_instance._get_argparse_alias(k)
	            if alias is not None:
	                field_args.append(alias)
	            if "default" in kwargs:
                if isinstance(kwargs["default"], str) and kwargs["default"].startswith(
	                    "${"
	                ):
	                    if kwargs["help"] is None:
	                        # this is a field with a name that will be added elsewhere
	                        continue
	                    else:
	                        del kwargs["default"]
	                if delete_default and "default" in kwargs:
	                    del kwargs["default"]
            try:
	                # 
	                # 
	                # 
	                # https://www.cnblogs.com/sddai/p/14303453.html
	                parser.add_argument(*field_args, **kwargs)
	            except argparse.ArgumentError:
	                pass
	    for dataclass in dataclasses:
	        gen_parser_from_dataclass(parser, dataclass())
def load_config(gen_args_fn=get_parser_from_dataclass):
	    # train data classes
	    train_data_classes = [
	        LogConfig,
	        TrainConfig,
	        OptimConfig,
	        DatasetConfig,
	        CheckpointConfig,
	    ]
	    # model data classes
    model_data_classes = [
	        UnetConfig,
	        AutoencoderConfig,
	        ClipConfig,
	        DDPMConfig,
	    ]
	    # Create an argument parser
	    parser = argparse.ArgumentParser()
	    # add train data classes into argparse
	    gen_args_fn(train_data_classes, parser)
    gen_args_fn(model_data_classes, parser)
	    # Parse the command-line arguments
	    args = parser.parse_args()
	    cfg = DictConfig({})
	    def convert_dataclass_to_dictconfig(dataclasses, cfg: DictConfig):
	        """Convert data classes to nested DictConfigs"""
	        for data_class in dataclasses:
	            data_instance = data_class()
	            for field_info in fields(data_class):
	                field_name = field_info.name
                field_value = getattr(args, field_name)
	                setattr(data_instance, field_name, field_value)
	            group_name: str = data_class.__name__.lower().replace("config", "")
	            group_config: DictConfig = OmegaConf.structured(data_instance)
	            cfg[group_name] = group_config
	    # add train data classes into DictConfig
	    convert_dataclass_to_dictconfig(train_data_classes, cfg=cfg)
	    # nest model data classes into cfg.model
	    cfg["model"] = DictConfig({})
	    convert_dataclass_to_dictconfig(model_data_classes, cfg=cfg["model"])
    return args, cfg

#!/usr/bin/env python
	# -*- encoding: utf-8 -*-
	"""
	@File    :   dataclass.py
	@Time    :   2023/05/26 20:09:51
	@Author  :   Wenbo Li
	@Desc    :   Base dataclass copied from fairseq, Configs inherit from this class so that fairseq script can better convert dataclass into argparse
	"""
	from dataclasses import _MISSING_TYPE, dataclass
	from typing import List, Any, Optional
@dataclass
	class BaseDataclass:
	    """Copied wonderful code from [fairseq/fairseq/dataclass/configs.py](https://github.com/facebookresearch/fairseq/blob/25c20e6a5e781e4ef05e23642f21c091ba64872e/fairseq/dataclass/configs.py#L28)"""
	    # _name: Optional[str] = None
	    # @staticmethod
	    # def name():
	    #     return None
	    def _get_all_attributes(self) -> List[str]:
	        return list(self.__dataclass_fields__.keys())
	    def _get_meta(
        self, attribute_name: str, meta: str, default: Optional[Any] = None
	    ) -> Any:
	        return self.__dataclass_fields__[attribute_name].metadata.get(meta, default)
	    def _get_name(self, attribute_name: str) -> str:
	        return self.__dataclass_fields__[attribute_name].name
	    def _get_default(self, attribute_name: str) -> Any:
	        if hasattr(self, attribute_name):
	            if str(getattr(self, attribute_name)).startswith("${"):
	                return str(getattr(self, attribute_name))
	            elif str(self.__dataclass_fields__[attribute_name].default).startswith(
                "${"
	            ):
	                return str(self.__dataclass_fields__[attribute_name].default)
	            elif (
	                getattr(self, attribute_name)
	                != self.__dataclass_fields__[attribute_name].default
	            ):
	                return getattr(self, attribute_name)
	        f = self.__dataclass_fields__[attribute_name]
	        if not isinstance(f.default_factory, _MISSING_TYPE):
            return f.default_factory()
	        return f.default
	    def _get_type(self, attribute_name: str) -> Any:
	        return self.__dataclass_fields__[attribute_name].type
	    def _get_help(self, attribute_name: str) -> Any:
	        return self._get_meta(attribute_name, "help")
	    def _get_argparse_const(self, attribute_name: str) -> Any:
	        return self._get_meta(attribute_name, "argparse_const")
	    def _get_argparse_alias(self, attribute_name: str) -> Any:
	        return self._get_meta(attribute_name, "argparse_alias")
    def _get_choices(self, attribute_name: str) -> Any:
	        return self._get_meta(attribute_name, "choices")

import torch
	from torch import nn
	class GaussianDistribution(nn.Module):
	    def __init__(self, moments: torch.Tensor):
	        super(GaussianDistribution, self).__init__
	        self.mean, self.log_var = torch.chunk(moments, 2, dim=1)
	    def sample(self):
	        std = torch.exp(0.5 * self.log_var)
	        eps = torch.randn_like(std)
	        return self.mean + eps * std
    def kl(self):
	        self.var = torch.exp(self.log_var)
	        return 0.5 * torch.sum(
	            torch.pow(self.mean, 2) + self.var - 1.0 - self.log_var, dim=[1, 2, 3]
	        )
