from abc import ABC, abstractmethod
	from typing import Any, Dict, Generic, Iterable, List, Type, TypeVar, Union, cast
	from pydantic import BaseModel  # pylint: disable=no-name-in-module
	Vector = List[float]
	MetaData = Dict[str, str]
	T = TypeVar("T")
	class LazyProxy(Generic[T], ABC):
	    def __init__(self) -> None:
	        self.__proxied: Union[T, None] = None
	    def __getattr__(self, attr: str) -> object:
        return getattr(self.__get_proxied__(), attr)
	    def __repr__(self) -> str:
	        return repr(self.__get_proxied__())
	    def __dir__(self) -> Iterable[str]:
	        return self.__get_proxied__().__dir__()
	    def __get_proxied__(self) -> T:
	        proxied = self.__proxied
	        if proxied is not None:
	            return proxied
	        self.__proxied = proxied = self.__load__()
        return proxied
	    def __set_proxied__(self, value: T) -> None:
	        self.__proxied = value
	    def __as_proxied__(self) -> T:
	        """Helper method that returns the current proxy, typed as the loaded object"""
	        return cast(T, self)
	    @abstractmethod
	    def __load__(self) -> T:
	        ...
	class FunctionType(BaseModel, ABC):
    _subclasses: List[Type["FunctionType"]] = []
	    @classmethod
	    def __init_subclass__(cls, **kwargs):
	        super().__init_subclass__(**kwargs)
	        _schema = cls.schema()
	        if cls.__doc__ is None:
	            raise ValueError(
	                f"FunctionType subclass {cls.__name__} must have a docstring"
	            )
	        cls.openaischema = {
            "name": cls.__name__,
	            "description": cls.__doc__,
	            "parameters": {
	                "type": "object",
	                "properties": _schema["properties"],
	                "required": _schema["required"],
	            },
	        }
	        cls._subclasses.append(cls)
	    @abstractmethod
    async def run(self) -> Any:
	        ...
	F = TypeVar("F", bound=FunctionType)

import os
	import subprocess
	import click
	from .utils import setup_logging
	logger = setup_logging(__name__)
	@click.group()
	def main():
	    pass
	def print_tree(dirname, pref=""):
	    for item in os.listdir(dirname):
        if item.startswith("."):
	            continue
	        print(pref + item)
	        if os.path.isdir(os.path.join(dirname, item)):
	            logger.info(
	                os.path.join(dirname, item), " " * len(pref) + "|" + ("_" * 5) + " "
	            )
	@main.command()
	def tree():
	    print_tree(os.getcwd())
@main.command()
