import base64
	from base64 import urlsafe_b64decode, urlsafe_b64encode
	from datetime import date, datetime
	from enum import Enum
	from json import JSONEncoder, dumps, loads
	from time import time
	from typing import Any, Dict, List, Literal, TypeVar
	from uuid import UUID
	from iso8601 import parse_date
	from pydantic import BaseModel  # pylint: disable=no-name-in-module
from typing_extensions import override
	from .faunadb.objects import FaunaTime, Native, Query, Ref, SetRef
	from .faunadb.query import Expr
	T = TypeVar("T")
	FaunaKey = Literal[
	    "@ref", "@obj", "@set", "@query", "@ts", "@date", "@bytes", "@index", "@class"
	]
	def _parse_json_hook(dct: Dict[FaunaKey, Any]):
	    if "@ref" in dct:
	        ref = dct["@ref"]
        if not "collection" in ref and not "database" in ref:
	            return Native.from_name(ref["id"])
	        return Ref(ref["id"], ref.get("collection"), ref.get("database"))
	    if "@obj" in dct:
	        return dct["@obj"]
	    if "@set" in dct:
	        return SetRef(dct["@set"])
	    if "@query" in dct:
	        return Query(dct["@query"])
	    if "@ts" in dct:
        return FaunaTime(dct["@ts"])
	    if "@date" in dct:
	        return parse_date(dct["@date"]).date()
	    if "@bytes" in dct:
	        return bytearray(urlsafe_b64decode(dct["@bytes"].encode()))
	    return dct
	def parse_json(json_string):
	    try:
	        return loads(json_string, object_hook=_parse_json_hook)
	    except ValueError:
        pass
	def to_json(dct, pretty=True, sort_keys=True):
	    if pretty:
	        return dumps(
	            dct,
	            cls=FaunaJSONEncoder,
	            sort_keys=True,
	            indent=4,
	            separators=(", ", ": "),
	            allow_nan=False,
            ensure_ascii=True,
	        )
	    return dumps(
	        dct,
	        cls=FaunaJSONEncoder,
	        sort_keys=sort_keys,
	        separators=(",", ":"),
	        allow_nan=False,
	        ensure_ascii=True,
	        exclude_none=True,
    )
	class FaunaJSONEncoder(JSONEncoder):
	    @override
	    def default(self, obj):
	        if isinstance(obj, (Ref, SetRef, FaunaTime, Query)):
	            return obj.to_fauna_json()
	        if isinstance(obj, Expr):
	            return obj.to_fauna_json()
	        elif isinstance(obj, datetime):
	            return obj.astimezone().isoformat()
        elif isinstance(obj, date):
	            return {"@date": obj.isoformat()}
	        elif isinstance(obj, (bytes, bytearray)):
	            _val = None
	            try:
	                _val = obj.decode()
	            except:
	                _val = urlsafe_b64encode(obj).decode()  # pylint: disable=all
	            return {"@bytes": _val}
	        elif isinstance(obj, BaseModel):
            return obj.dict()
	        elif isinstance(obj, Enum):
	            return obj.value
	        elif isinstance(obj, UUID):
	            return {"@uuid": str(obj)}
	        else:
	            return super().default(obj)
	class JSONModel(BaseModel):
	    def to_dict(self, **kwargs):
	        return parse_json(self.to_json(**kwargs))
    def to_json(self, **kwargs) -> str:
	        return to_json(super().dict(exclude_none=True, **kwargs))
	    @override
	    def dict(self, **kwargs):
	        return self.to_dict(**kwargs)
	    @override
	    def json(self, **kwargs) -> str:
	        return self.to_json(exclude_none=True, **kwargs)
	def jsonable_encoder(
	    obj: Any,
    *,
	    include: List[str] = [],
	    exclude: List[str] = [],
	    by_alias: bool = False,
	    skip_defaults: bool = False,
	    custom_encoder: Any = None,
	) -> Any:
	    """
	    Convert any object to a JSON-serializable object.
	    This function is used by Aiofauna to convert objects to JSON-serializable objects.
    It supports all the types supported by the standard json library, plus:
	    * datetime.datetime
	    * datetime.date
	    * datetime.time
	    * uuid.UUID
	    * enum.Enum
	    * pydantic.BaseModel
	    """
	    if custom_encoder is None:
	        custom_encoder = FaunaJSONEncoder
    if obj is str:
	        return "string"
	    if obj is int or obj is float:
	        return "integer"
	    if obj is bool:
	        return "boolean"
	    if obj is None:
	        return "null"
	    if obj is list:
	        return "array"
    if obj is dict:
	        return "object"
	    if obj is bytes:
	        return "binary"
	    if obj is datetime:
	        return "date-time"
	    if obj is date:
	        return "date"
	    if obj is time:
	        return "time"
    if obj is UUID:
	        return "uuid"
	    if obj is Enum:
	        return "enum"
	    if isinstance(obj, (str, int, float, bool, type(None))):
	        return obj
	    if isinstance(obj, (list, tuple, set, frozenset)):
	        return [
	            jsonable_encoder(
	                v,
                include=include,
	                exclude=exclude,
	                by_alias=by_alias,
	                skip_defaults=skip_defaults,
	                custom_encoder=custom_encoder,
	            )
	            for v in obj
	        ]
	    if isinstance(obj, dict):
	        return {
            jsonable_encoder(
	                k,
	                include=include,
	                exclude=exclude,
	                by_alias=by_alias,
	                skip_defaults=skip_defaults,
	                custom_encoder=custom_encoder,
	            ): jsonable_encoder(
	                v,
	                include=include,
                exclude=exclude,
	                by_alias=by_alias,
	                skip_defaults=skip_defaults,
	                custom_encoder=custom_encoder,
	            )
	            for k, v in obj.items()
	        }
	    if isinstance(obj, bytes):
	        return base64.b64encode(obj).decode()
	    if isinstance(obj, (set, frozenset)):
        return [
	            jsonable_encoder(
	                v,
	                include=include,
	                exclude=exclude,
	                by_alias=by_alias,
	                skip_defaults=skip_defaults,
	                custom_encoder=custom_encoder,
	            )
	            for v in obj
        ]
	    if isinstance(obj, datetime):
	        return obj.isoformat()
	    if isinstance(obj, Enum):
	        return obj.value
	    if isinstance(obj, UUID):
	        return str(obj)
	    if isinstance(obj, type):
	        return jsonable_encoder(
	            obj.__name__,
            include=include,
	            exclude=exclude,
	            by_alias=by_alias,
	            skip_defaults=skip_defaults,
	            custom_encoder=custom_encoder,
	        )
	    return custom_encoder().default(obj)

from pydantic.fields import Field as F  # pylint: disable=no-name-in-module
	def Field(*args, index: bool = False, unique: bool = False, **kwargs):
	    """Field Factory"""
	    return F(*args, index=index, unique=unique, **kwargs)

from __future__ import annotations
	import asyncio
	from typing import Any, List, Optional, Type, TypeVar
	from dotenv import load_dotenv
	from pydantic import Field
	from pydantic.main import ModelMetaclass
	from .client import FaunaClient
	from .faunadb import query as q
	from .faunadb.errors import FaunaException
	from .json import JSONModel
from .utils import gen_emptystr, setup_logging
	load_dotenv()
	T = TypeVar("T", bound="FaunaModel")
	class FaunaModelMetaclass(ModelMetaclass):
	    def __new__(cls, name, bases, attrs):
	        new_cls = super().__new__(cls, name, bases, attrs)
	        cls.Metadata.__subclasses__.append(new_cls)
	        return new_cls
	    class Metadata:
	        __subclasses__: List[Type[FaunaModel]] = []
class FaunaModel(JSONModel, metaclass=FaunaModelMetaclass):
	    ref: str = Field(default_factory=gen_emptystr)
	    ts: str = Field(default_factory=gen_emptystr)
	    @classmethod
	    @property
	    def logger(cls):
	        return setup_logging(cls.__name__)
	    @classmethod
	    async def create_all(cls):
	        await asyncio.gather(
            *[model.provision() for model in cls.Metadata.__subclasses__]
	        )
	    @classmethod
	    def client(cls):
	        return FaunaClient()
	    @classmethod
	    def q(cls):
	        return cls.client().query
	    @classmethod
	    async def provision(cls) -> bool:
        _q = cls.q()
	        try:
	            if not await _q(q.exists(q.collection(cls.__name__.lower()))):
	                await _q(q.create_collection({"name": cls.__name__.lower()}))
	                cls.logger.info("Created collection %s", cls.__name__.lower())
	            if not await _q(q.exists(q.index(cls.__name__.lower()))):
	                await _q(
	                    q.create_index(
	                        {
	                            "name": cls.__name__.lower(),
                            "source": q.collection(cls.__name__.lower()),
	                        }
	                    )
	                )
	                cls.logger.info("Created index %s", cls.__name__.lower())
	            for field in cls.__fields__.values():
	                if field.field_info.extra.get("unique"):
	                    if not await _q(
	                        q.exists(q.index(f"{cls.__name__.lower()}_{field.name}_unique"))
	                    ):
                        await _q(
	                            q.create_index(
	                                {
	                                    "name": f"{cls.__name__.lower()}_{field.name}_unique",
	                                    "source": q.collection(cls.__name__.lower()),
	                                    "terms": [{"field": ["data", field.name]}],
	                                    "unique": True,
	                                }
	                            )
	                        )
                        cls.logger.info(
	                            "Created unique index %s_%s",
	                            cls.__name__.lower(),
	                            field.name,
	                        )
	                    continue
	                if field.field_info.extra.get("index"):
	                    if not await _q(
	                        q.exists(q.index(f"{cls.__name__.lower()}_{field.name}"))
	                    ):
                        await _q(
	                            q.create_index(
	                                {
	                                    "name": f"{cls.__name__.lower()}_{field.name}",
	                                    "source": q.collection(cls.__name__.lower()),
	                                    "terms": [{"field": ["data", field.name]}],
	                                }
	                            )
	                        )
	                        cls.logger.info(
                            "Created index %s_%s", cls.__name__.lower(), field.name
	                        )
	                        continue
	            return True
	        except (FaunaException, KeyError, TypeError, ValueError, Exception) as exc:
	            cls.logger.error(exc.__class__.__name__)
	            cls.logger.error(exc)
	            return False
	    @classmethod
	    async def find_unique(cls: Type[T], **kwargs: Any) -> T:
        try:
	            field, value = list(kwargs.items())[0]
	            data = await cls.q()(
	                q.get(q.match(q.index(f"{cls.__name__.lower()}_{field}_unique"), value))
	            )
	            return cls(
	                **{
	                    **data["data"],  # type: ignore
	                    "ref": data["ref"]["@ref"]["id"],  # type: ignore
	                    "ts": data["ts"] / 1000,  # type: ignore
                }
	            )
	        except (FaunaException, KeyError, TypeError, ValueError, Exception) as exc:
	            cls.logger.error(exc.__class__.__name__)
	            cls.logger.error(exc)
	            return None  # type: ignore
	    @classmethod
	    async def find_many(cls: Type[T], limit: int = 50, **kwargs: Any) -> List[T]:
	        try:
	            _q = cls.q()
            field, value = list(kwargs.items())[0]
	            refs = await _q(
	                q.paginate(q.match(q.index(f"{cls.__name__.lower()}_{field}"), value))
	            )
	            refs = refs["data"][:limit]
	            return await asyncio.gather(*[cls.get(item["@ref"]["id"]) for item in refs])
	        except (FaunaException, KeyError, TypeError, ValueError, Exception) as exc:
	            cls.logger.error(exc.__class__.__name__)
	            cls.logger.error(exc)
	            return []
    @classmethod
	    async def get(cls: Type[T], ref: str) -> T:
	        try:
	            data = await cls.q()(q.get(q.ref(q.collection(cls.__name__.lower()), ref)))
	            return cls(
	                **{
	                    **data["data"],  # type: ignore
	                    "ref": data["ref"]["@ref"]["id"],  # type: ignore
	                    "ts": data["ts"] / 1000,  # type: ignore
	                }
            )
	        except (FaunaException, KeyError, TypeError, ValueError, Exception) as exc:
	            cls.logger.error(exc.__class__.__name__)
	            cls.logger.error(exc)
	            raise exc
	    @classmethod
	    async def all(cls: Type[T], limit: int = 100, offset: int = 0) -> List[T]:
	        try:
	            _q = cls.q()
	            query = q.paginate(q.match(q.index(f"{cls.__name__.lower()}")))
            refs = (await _q(query))["data"][offset:limit]
	            return await asyncio.gather(*[cls.get(item["@ref"]["id"]) for item in refs])
	        except (FaunaException, KeyError, TypeError, ValueError, Exception) as exc:
	            cls.logger.error(exc.__class__.__name__)
	            cls.logger.error(exc)
	            raise exc
	    @classmethod
	    async def delete_one(cls: Type[T], **kwargs: Any) -> bool:
	        try:
	            field, value = list(kwargs.items())[0]
            _q = cls.q()
	            ref = await _q(
	                q.get(q.match(q.index(f"{cls.__name__.lower()}_{field}_unique"), value))
	            )
	            await _q(q.delete(ref))
	            return True
	        except (FaunaException, KeyError, TypeError, ValueError, Exception) as exc:
	            cls.logger.error(exc.__class__.__name__)
	            cls.logger.error(exc)
	            return False
    @classmethod
	    async def delete(cls, ref: str) -> bool:
	        try:
	            await cls.q()(q.delete(q.ref(q.collection(cls.__name__.lower()), ref)))
	            return True
	        except (FaunaException, KeyError, TypeError, ValueError, Exception) as exc:
	            cls.logger.error(exc.__class__.__name__)
	            cls.logger.error(exc)
	            return False
	    async def create(self: T) -> T:
        try:
	            for field in self.__fields__.values():
	                if field.field_info.extra.get("unique"):
	                    instance = await self.find_unique(
	                        **{field.name: self.__dict__[field.name]}
	                    )
	                    if instance is None:
	                        continue
	                    if issubclass(instance.__class__, FaunaModel):
	                        return instance
            data = await self.__class__.q()(
	                q.create(
	                    q.collection(self.__class__.__name__.lower()), {"data": self.dict()}
	                )
	            )
	            self.ref = data["ref"]["@ref"]["id"]  # type: ignore
	            self.ts = data["ts"] / 1000  # type: ignore
	            return self
	        except (FaunaException, KeyError, TypeError, ValueError, Exception) as exc:
	            self.logger.error(exc.__class__.__name__)
            self.logger.error(exc)
	            raise exc
	    @classmethod
	    async def update(cls: Type[T], ref: str, **kwargs: Any) -> T:
	        try:
	            instance = await cls.q()(
	                q.update(
	                    q.ref(q.collection(cls.__name__.lower()), ref),
	                    {"data": kwargs.get("kwargs", kwargs)},
	                )
            )
	            return cls(
	                **{
	                    **instance["data"],  # type: ignore
	                    "ref": instance["ref"]["@ref"]["id"],  # type: ignore
	                    "ts": instance["ts"] / 1000,  # type: ignore
	                }
	            )
	        except (FaunaException, KeyError, TypeError) as exc:
	            cls.logger.error(exc.__class__.__name__)
            cls.logger.error(exc)
	            raise ValueError(f"Field {ref} not found")  # pylint: disable=all
	    async def save(self: T) -> Optional[T]:
	        if isinstance(self.ref, str) and len(self.ref) == 18:
	            return await self.update(self.ref, kwargs=self.dict())
	        return await self.create()
	    @classmethod
	    async def cleanup(cls):
	        await cls.client().cleanup()

"""REST API Module with automatic OpenAPI generation."""
	import asyncio
	import os
	from functools import wraps
	from inspect import signature
	from typing import Awaitable, Callable
	from aiohttp.typedefs import Handler
	from aiohttp.web import Application, FileResponse, Request, Response, StreamResponse
	from aiohttp.web_middlewares import middleware
	from aiohttp.web_ws import WebSocketResponse
from aiohttp_sse import EventSourceResponse, sse_response
	from .client import APIClient
	from .docs import extract, html, load, transform
	from .helpers import do_response
	from .json import jsonable_encoder
	from .odm import FaunaModel
	from .utils import setup_logging
	Middleware = Callable[[Request, Handler], Awaitable[StreamResponse]]
	class APIServer(Application):
	    """Aiohttp Application with automatic OpenAPI generation."""
    def __init__(self, *args, **kwargs):
	        super().__init__(*args, logger=setup_logging(self.__class__.__name__), **kwargs)
	        self.openapi = {
	            "openapi": "3.0.0",
	            "info": {"title": "AioFauna", "version": "1.0.0"},
	            "paths": {},
	            "components": {"schemas": {}},
	            "description": "AioFauna API",
	        }
	        self._route_open_api_params = {}
        @self.get("/openapi.json")
	        async def openapi():
	            response = jsonable_encoder(self.openapi)
	            return response
	        @self.get("/docs")
	        async def docs():
	            return Response(text=html, content_type="text/html")
	        @self.on_event("startup")
	        async def startup(_):
	            await FaunaModel.create_all()
        @self.on_event("shutdown")
	        async def shutdown(_):
	            await APIClient.cleanup()
	            await FaunaModel.cleanup()
	    def document(self, path: str, method: str):
	        """
	        Decorator to document a function.
	        """
	        def decorator(func):
	            sig = signature(func)
            params = sig.parameters
	            open_api_params = extract(params.copy(), path)
	            self._route_open_api_params[(path, method)] = open_api_params
	            transform(self.openapi, path, method, func, open_api_params)
	            async def wrapper(*args, **kwargs):
	                request: Request = args[0]
	                args = args[1:]
	                args_to_apply = await load(request, params.copy())
	                definitive_args = {}
	                for name, param in params.items():
                    if name in args_to_apply:
	                        definitive_args[name] = args_to_apply[name]
	                    elif param.default is not param.empty:
	                        definitive_args[name] = param.default
	                    else:
	                        raise ValueError(
	                            f"Missing parameter {name} for {func.__name__}"
	                        )
	                if asyncio.iscoroutinefunction(func):
	                    response = await func(*args, **kwargs, **definitive_args)
                else:
	                    response = func(*args, **kwargs, **definitive_args)
	                return do_response(response)
	            func.injectable = True
	            wrapper._handler = func
	            return wrapper
	        return decorator
	    def get(self, path: str, **kwargs):
	        """GET decorator"""
	        def decorator(func):
            self.router.add_get(path, self.document(path, "GET")(func), **kwargs)
	            return func
	        return decorator
	    def post(self, path: str, **kwargs):
	        """POST decorator"""
	        def decorator(func):
	            self.router.add_post(path, self.document(path, "POST")(func), **kwargs)
	            return func
	        return decorator
	    def put(self, path: str, **kwargs):
        """PUT decorator"""
	        def decorator(func):
	            self.router.add_put(path, self.document(path, "PUT")(func), **kwargs)
	            return func
	        return decorator
	    def delete(self, path: str, **kwargs):
	        """DELETE decorator"""
	        def decorator(func):
	            self.router.add_delete(path, self.document(path, "DELETE")(func), **kwargs)
	            return func
        return decorator
	    def patch(self, path: str, **kwargs):
	        """PATCH decorator"""
	        def decorator(func):
	            self.router.add_patch(path, self.document(path, "PATCH")(func), **kwargs)
	            return func
	        return decorator
	    def head(self, path: str, **kwargs):
	        """HEAD decorator"""
	        def decorator(func):
            self.router.add_head(path, self.document(path, "HEAD")(func), **kwargs)
	            return func
	        return decorator
	    def options(self, path: str, **kwargs):
	        """OPTIONS decorator"""
	        def decorator(func):
	            self.router.add_options(
	                path, self.document(path, "OPTIONS")(func), **kwargs
	            )
	            return func
        return decorator
	    def on_event(self, event: str):
	        """On event handler"""
	        def decorator(func):
	            if event not in ("startup", "shutdown"):
	                raise ValueError("Event must be startup or shutdown")
	            elif event == "startup":
	                self.on_startup.append(func)
	            else:
	                self.on_shutdown.append(func)
            return func
	        return decorator
	    def sse(self, path: str) -> Callable:  # pylint: disable=invalid-name
	        """Server-Sent Events decorator"""
	        def decorator(func: Callable) -> Callable:
	            @wraps(func)
	            async def wrapper(request: Request) -> EventSourceResponse:
	                async with sse_response(request) as resp:
	                    args_to_apply = await load(
	                        request, signature(func).parameters.copy()
                    )
	                    definitive_args = {}
	                    for name, param in signature(func).parameters.items():
	                        if param.annotation == EventSourceResponse:
	                            definitive_args[name] = resp
	                        elif name in args_to_apply:
	                            definitive_args[name] = args_to_apply[name]
	                            args_to_apply.pop(name)
	                        elif param.default is not param.empty:
	                            definitive_args[name] = param.default
                        else:
	                            raise ValueError(
	                                f"Missing parameter {name} for {func.__name__}"
	                            )
	                    await func(**definitive_args)
	                    return resp
	            self.router.add_get(path, wrapper)
	            return wrapper
	        return decorator
	    def websocket(self, path: str) -> Callable:  # pylint: disable=invalid-name
        """Websocket decorator"""
	        def decorator(func: Callable) -> Callable:
	            @wraps(func)
	            async def wrapper(request: Request):
	                args_to_apply = await load(request, signature(func).parameters.copy())
	                ws = WebSocketResponse()
	                await ws.prepare(request)
	                definitive_args = {}
	                for name, param in signature(func).parameters.items():
	                    if param.annotation == WebSocketResponse:
                        definitive_args[name] = ws
	                    elif name in args_to_apply:
	                        definitive_args[name] = args_to_apply[name]
	                        args_to_apply.pop(name)
	                    elif param.default is not param.empty:
	                        definitive_args[name] = param.default
	                    else:
	                        raise ValueError(
	                            f"Missing parameter {name} for {func.__name__}"
	                        )
                await func(**definitive_args)
	                return ws
	            self.router.add_get(path, wrapper)
	            return wrapper
	        return decorator
	    def static(self):
	        """Static folder creation and serving"""
	        try:
	            os.makedirs("static", exist_ok=True)
	        except OSError:
            pass
	        self.router.add_static("/", "static")
	        @self.get("/")
	        def index():
	            return FileResponse("static/index.html")
	        return self
	    def middleware(self, func: Middleware) -> Middleware:
	        @wraps(func)
	        @middleware
	        async def wrapper(request: Request, handler: Handler) -> Response:
            response = await func(request, handler)
	            if isinstance(response, Response):
	                return response
	            return do_response(response)
	        self.middlewares.append(wrapper)
	        return wrapper

"""
	Flaskaesque helper functions for aiohttp.
	"""
	import asyncio
	import functools
	import json
	import types
	import typing
	from concurrent.futures import ThreadPoolExecutor
	from functools import singledispatch
from typing import Any, List, Union
	from aiohttp.web import HTTPException, Request, Response, json_response
	from pydantic import BaseModel
	from aiofauna.json import FaunaJSONEncoder
	from .json import parse_json, to_json
	from .odm import FaunaModel
	T = typing.TypeVar("T")
	def asyncify(
	    func: typing.Callable[..., typing.Any],
	) -> typing.Callable[..., typing.Awaitable[typing.Any]]:
    @functools.wraps(func)
	    def wrapper(self, *args, **kwargs):
	        bound = functools.partial(func, self, *args, **kwargs)
	        if asyncio.get_event_loop().is_running():
	            loop = asyncio.get_event_loop()
	            return loop.run_in_executor(self.executor, bound)
	        loop = asyncio.new_event_loop()
	        asyncio.set_event_loop(loop)
	        return loop.run_in_executor(self.executor, bound)
	    return typing.cast(typing.Callable[..., typing.Awaitable[typing.Any]], wrapper)
def aio(max_workers: int = 5) -> typing.Callable[[typing.Type], typing.Type]:
	    """Decorator that converts all the methods of a class into async methods."""
	    def decorator(cls: typing.Type) -> typing.Type:
	        attrs: typing.Dict[str, typing.Any] = {}
	        attrs["executor"] = ThreadPoolExecutor(max_workers=max_workers)
	        for attr_name, attr_value in cls.__dict__.items():
	            if (
	                isinstance(attr_value, types.FunctionType)
	                and attr_name.startswith("__") is False
	            ):
                attrs[attr_name] = asyncify(attr_value)
	            else:
	                attrs[attr_name] = attr_value
	        return type(cls.__name__, cls.__bases__, attrs)
	    return decorator
	@singledispatch
	def do_response(response: Any) -> Response:
	    """
	    Flask-esque function to make a response from a function.
	    """
    return response
	@do_response.register(BaseModel)
	def _(response: BaseModel) -> Response:
	    return json_response(response.dict(exclude_none=True), dumps=to_json)
	@do_response.register(FaunaModel)
	def _(response: FaunaModel) -> Response:
	    return json_response(response.dict(), dumps=to_json)
	@do_response.register(dict)
	def _(response: dict) -> Response:
	    return json_response(response, dumps=to_json)
@do_response.register(str)
	def _(response: str) -> Response:
	    if response.startswith("<") and response.endswith(">"):
	        return Response(status=200, text=response, content_type="text/html")
	    return Response(status=200, text=response, content_type="text/plain")
	@do_response.register(bytes)
	def _(response: bytes) -> Response:
	    return Response(status=200, body=response, content_type="application/octet-stream")
	@do_response.register(int)
	def _(response: int) -> Response:
    return Response(status=200, text=str(response), content_type="text/plain")
	@do_response.register(float)
	def _(response: float) -> Response:
	    return Response(status=200, text=str(response), content_type="text/plain")
	@do_response.register(bool)
	def _(response: bool) -> Response:
	    return Response(status=200, text=str(response), content_type="text/plain")
	@do_response.register(list)
	def _(response: List[Union[FaunaModel, BaseModel, dict, str, int, float]]) -> Response:
	    return json_response([x for x in response], dumps=to_json)

from . import query
	class Page:
	    @staticmethod
	    def from_raw(raw):
	        return Page(raw["data"], raw.get("before"), raw.get("after"))
	    def __init__(self, data, before=None, after=None):
	        self.data = data
	        self.before = before
	        self.after = after
	    def map_data(self, func):
        return Page([func(x) for x in self.data], self.before, self.after)
	    def __repr__(self):
	        return "Page(data=%s, before=%s, after=%s)" % (
	            self.data,
	            self.before,
	            self.after,
	        )
	    def __eq__(self, other):
	        return (
	            isinstance(other, Page)
            and self.data == other.data
	            and self.before == other.before
	            and self.after == other.after
	        )
	    @staticmethod
	    def set_iterator(client, set_query, map_lambda=None, mapper=None, page_size=None):
	        def get_page(**kwargs):
	            queried = query.paginate(set_query, **kwargs)
	            if map_lambda is not None:
	                queried = query.map_(map_lambda, queried)
            return Page.from_raw(client.query(queried))
	        page = get_page(size=page_size)
	        for val in page.data:
	            yield val if mapper is None else mapper(val)
	        next_cursor = "after" if page.after is not None else "before"
	        while getattr(page, next_cursor) is not None:
	            page = get_page(
	                **{"size": page_size, next_cursor: getattr(page, next_cursor)}
	            )
	            for val in page.data:
                yield val if mapper is None else mapper(val)

"""
	Types used in queries and responses.
