	        if id == "*":
	            raise ValueError('id cannot be "*"')
	        self.__id: str = id
	        self.__receive_own_broadcasts = receive_own_broadcasts
	        self._space = None  # set by Space when added
	        self._message_log = []  # stores all messages
	    def id(self) -> str:
	        """
	        Returns the id of this agent
        """
	        return self.__id
