	        pass
	    @abstractmethod
	    def delete(self, entity: VectorSchemaBase):
	        pass
	    @abstractmethod
	    def search(
	        self,
        schema: Type[VectorSchemaBase],
	        output_fields: list[str],
	        filters: list[AttributeCompare] | None,
	        search_embedding: np.ndarray | None,
	        search_embedding_key: str | None,
