	class BadAck(Exception):
	    pass
	class Range:
	    def __init__(self, min=None, max=None):
	        self.min = min
        self.max = max
	    def __str__(self):
	        return f'[{self.min},{self.max}]'
	class Acknowledgments:
	    def __init__(self, ranges=list()):
	        # [Range(min,max), Range(min,max), ...]
	        self.ranges = ranges
	    def __str__(self):
	        return f'<Acknowledgments {"".join([str(r) for r in self.ranges])}>'
	    def add(self, n):
        #self.acks.append(number)
	        for r in self.ranges:
	            if r.min <= n <= r.max: # already present
	                return
	            elif n == r.min - 1: # add n to the start
	                r.min -= 1
	                # check if we can merge
	                for r2 in self.ranges:
	                    if r.min - 1 == r2.max:
	                        r.min = r2.min
                        self.ranges.remove(r2)
	                        return
	                return
	            elif n == r.max + 1: # add n to the end
	                r.max += 1
	                # check if we can merge
	                for r2 in self.ranges:
	                    if r.max + 1 == r2.min:
	                        r.max = r2.max
	                        self.ranges.remove(r2)
                        return
	                return
	        # if we get we could not add 'n'; lets make a new range
	        self.ranges.append(Range(n, n))
	    def remove(self, ranges):
	        for r in self.ranges[:]:
	            for r2 in ranges:
	                if not (r2.max < r.min or r2.min > r.max): # check if it will remove
	                    if r2.min <= r.min and r2.max >= r.max: # remove
	                        self.ranges.remove(r)
                    elif r2.min > r.min and r2.max < r.max: # split
	                        self.ranges.append(Range(r2.max + 1, r.max))
	                        r.max = r2.min - 1
	                    elif r2.min <= r.min: # trim left
	                        r.min = r2.max + 1
	                    elif r2.max >= r.max: # trim right
	                        r.max = r2.min - 1
	    def encode(self, bs):
	        if len(self.ranges) > 0:
	            # write ack data
            bs.write_bit(1) # has acks
	            bs.write_compressed_u16(len(self.ranges))
	            for r in self.ranges:
	                bs.write_bit(int(r.min == r.max))
	                bs.write_u16(r.min)
	                if r.min != r.max: # only write max if min!=max otherwise it'd redundant
	                    bs.write_u16(r.max)
	    @staticmethod
	    def decode(bs):
	        ranges = []
        has_acks = bs.read_bit()
	        if has_acks: # parse them
	            range_count = bs.read_compressed_u16() # read the amount of ranges
	            for i in range(range_count): # iterate and decode each range
	                min_equals_max = bs.read_bit()
	                min = bs.read_u16()
	                if min_equals_max == 0:
	                    max = bs.read_u16()
	                    if min > max:
	                        raise BadAck # Exception(f'bad acks: min({min}) > max({max})')
                    ranges.append(Range(min, max))
	        return Acknowledgments(ranges)
	class Peer:
	    def __init__(self, addr, is_server_peer=True, sendto=None):
	        self.addr = addr
	        if sendto is not None:
	            self.sendto = sendto
	        if is_server_peer:
	            self.encode_message = Message.encode_from_client
	            self.decode_message = Message.decode_from_server
        else:
	            self.encode_message = Message.encode_from_server
	            self.decode_message = Message.decode_from_client
	        self.first_packet_time = None
	        self.last_packet_time = None
	        self.first_message_time = None
	        self.out_bs = Bitstream(capacity=1500)
	        '''
	        For every reliable message received from the peer, we add its sequence
	        number to 'send_acks'. For every packet we send, we send as much
        acknowledgments as we can from 'send_acks' so the peer knows we received
	        the reliable messages sent in the past.
	        '''
	        self.send_acks = Acknowledgments()
	        self.expected_acks = Acknowledgments()
	        #self.send_acks_lock = threading.Lock()
	        self.split_packets = []
	        self.ordered_packet_write_index = [0] * ORDERED_STREAMS_COUNT
	        self.ordered_packet_read_index = [0] * ORDERED_STREAMS_COUNT
	        self.sequenced_packet_write_index = [0] * ORDERED_STREAMS_COUNT
        self.sequenced_packet_read_index = [0] * ORDERED_STREAMS_COUNT
	        self.ordering_list = [[]] * ORDERED_STREAMS_COUNT
	        self.send_internal_packet_queues = [queue.Queue()] * NUMBER_OF_PRIORITIES
	        #self.local_static_data = bytearray()
	        # incremented for every messaged pushed
	        self.sequence_number = 0
	        # incremented after each split message pushed
	        self.split_message_id = 0
	        self.connected_message_callbacks = [] # callback(message, internal_packet, peer)
	        self.unconnected_message_callbacks = [] # callback(message, peer)
        self.connected = False
	    def sendto(self, buffer):
	        raise NotImplementedError('You must implement Peer.sendto()')
	    def push_message(self, message, reliability=RELIABILITY.RELIABLE, priority=PRIORITY.HIGH, ordering_channel=None):
	        self.push_encoded_message(self.encode_message(message), reliability, priority, ordering_channel)
	    def push_encoded_message(self, message_data, reliability=RELIABILITY.RELIABLE, priority=PRIORITY.HIGH, ordering_channel=None):
	        if reliability.sequenced():
	            ordering_index = self.sequenced_packet_write_index[ordering_channel]
	            self.sequenced_packet_write_index[ordering_channel] = (self.sequenced_packet_write_index[ordering_channel] + 1) % (2**16)
	        elif reliability.ordered():
            ordering_index = self.ordered_packet_write_index[ordering_channel]
	            self.ordered_packet_write_index[ordering_channel] = (self.ordered_packet_write_index[ordering_channel] + 1) % (2**16)
	        else:
	            ordering_index = None
	        # check if message has to be split
	        split_message_count = DIV_INT_CEIL(len(message_data), MAX_MESSAGE_SIZE)
	        if split_message_count > 1:
	            remaining_size = len(message_data)
	            for i in range(split_message_count):
	                split_size = min(MAX_MESSAGE_SIZE, remaining_size)
                remaining_size -= MAX_MESSAGE_SIZE
	                split_data_offset = i * MAX_MESSAGE_SIZE
	                split_data = message_data[split_data_offset : split_data_offset + split_size]
	                packet = InternalPacket(self.sequence_number, reliability, ordering_channel, ordering_index, self.split_message_id, i, split_message_count, split_data)
	                self.sequence_number = (self.sequence_number + 1) % (2**16)
	                self.send_internal_packet_queues[priority].put(packet)
	            self.split_message_id = (self.split_message_id + 1) % (2**32)
	        else:
	            packet = InternalPacket(self.sequence_number, reliability, ordering_channel, ordering_index, payload=message_data)
	            self.sequence_number = (self.sequence_number + 1) % (2**16)
            self.send_internal_packet_queues[priority].put(packet)
	    def generate_packet(self, bs):
	        #with self.send_acks_lock:
	        self.send_acks.encode(bs)
	        for internal_packet_queue in self.send_internal_packet_queues:
	            try:
	                internal_packet = internal_packet_queue.get_nowait()
	            except queue.Empty:
	                continue
	            # check if fits
            internal_packet_size = MAX_INTERNAL_PACKET_HEADER_SIZE + len(internal_packet.payload)
	            if bs.write_offset + TO_BITS(internal_packet_size) > TO_BITS(MAX_UDP_PACKET_PAYLOAD_SIZE):
	                internal_packet_queue.put(internal_packet) # put back into queue
	                break
	            if bs.write_offset == 0: # if nothing was written
	                bs.write_bit(0) # no acks
	            internal_packet.encode(bs)
	        self.send_acks.ranges = []
	        return bs.len > 0
	    def update(self):
        # process send queue
	        # send all available data(acks + messages in send queue) to the socket
	        while self.generate_packet(self.out_bs):
	            buffer_size = TO_BYTES(self.out_bs.len)
	            buffer = self.out_bs.data[:buffer_size]
	            self.sendto(buffer)
	            self.out_bs.reset()
	    def handle_unconnected_packet(self, data):
	        # if this peer is not connected, a message is present at the
	        # start of the packet, instead of acks or internal packet headers
        try:
	            message = self.decode_message(data)
	        except Exception as e:
	            e.add_note(f'Peer.handle_packet(data={data.hex(" ")})')
	            raise e
	        self.handle_unconnected_message(message)
	    def handle_connected_packet(self, data):
	        bs = Bitstream(data)
	        try:
	            '''
            Decode acknowlegements for reliable raknet messages we sent in the
	            past. We use these to know the peer receive our reliable message.
	            If we do not receive an acknowlegement we need to send the message
	            again. 'self.expected_acks' tracks these.
	            '''
	            # decode acknowledgments in this packet
	            acks = Acknowledgments.decode(bs)
	            # remove these acknowledgments from 'expected_acks'
	            self.expected_acks.remove(acks.ranges)
	            #todo reliable send list; resend
            # decode raknet messages
	            # do not decode leftover bits(<16; not necessarily a bad packet)
	            while bs.unread_bits_count() > 16:
	                packet = InternalPacket.decode(bs)
	                # if we didn't have GIl I guess I'd be beneficial to push the packet
	                # into a queue and process it on another thread potentially in
	                # parallel; but because we use asyncio I guess I won't matter if we
	                # just handle it here
	                if packet.reliability.reliable():
	                    #with self.send_acks_lock:
                    # add message to acknowlegements queue so we can later tell the peer we received this [reliable] message
	                    self.send_acks.add(packet.sequence_number)
	                if packet.reliability.sequenced():
	                    expected_index = self.sequenced_packet_read_index[packet.ordering_channel]
	                    if packet.ordering_index < expected_index:
	                        # ignore old sequenced packet
	                        continue
	                    else:
	                        # update expected index
	                        self.sequenced_packet_read_index[packet.ordering_channel] = (packet.ordering_index + 1) % (2**16)
                if packet.split_id is not None:
	                    now = time.time()
	                    packet.time = now
	                    self.split_packets.append(packet)
	                    index_sum = 0
	                    count = 0
	                    for p in self.split_packets[:]:
	                        # remove split packets older than 10s
	                        if now - p.time > 10 * 1000:
	                            self.split_packets.remove(p)
                            continue
	                        # find related split packets
	                        if p.split_id == packet.split_id:
	                            index_sum += p.split_index
	                            count += 1
	                    # check if this was the last part of a split packet
	                    if count == packet.split_count and index_sum == sum(range(count)):
	                        parts = [None] * count
	                        # remove split packets from list
	                        for p in self.split_packets[:]:
                            if p.split_id == packet.split_id:
	                                parts[p.split_index] = p
	                                self.split_packets.remove(p)
	                        payload = bytearray()
	                        for part in parts:
	                            payload += part.payload
	                        # make internal packet to represent the assembled split messages
	                        packet.split_id = packet.split_count = packet.split_index = None
	                        packet.payload = payload
	                        # assemble whole packet and handle it
                    else: # could not assemble whole packet from split packets; do not handle it yet
	                        continue
	                else: # internal packet contains a whole message; handle it
	                    pass
	                if packet.reliability.ordered():
	                    expected_index = self.ordered_packet_read_index[packet.ordering_channel]
	                    if packet.ordering_index == expected_index:
	                        self.ordered_packet_read_index[packet.ordering_channel] = (self.ordered_packet_read_index[packet.ordering_channel] + 1) % (2**16)
	                        # handle expected ordered packet
	                        message = self.decode_message(packet.payload)
                        self.handle_connected_message(message, packet)
	                        # handle all adjacent sucessors
	                        while True:
	                            expected_index = self.ordered_packet_read_index[packet.ordering_channel]
	                            try:
	                                p = next(p for p in self.ordering_list[packet.ordering_channel] if p.ordering_index == expected_index)
	                                message = self.decode_message(p.payload)
	                                self.handle_connected_message(message, p)
	                                self.ordering_list[packet.ordering_channel].remove(p)
	                                self.ordered_packet_read_index[packet.ordering_channel] = (self.ordered_packet_read_index[packet.ordering_channel] + 1) % (2**16)
                            except StopIteration: # no more adjacent sucessors
	                                break
	                        continue
	                        #0 1 2   4 5 6  3
	                    else: # packet out of order
	                        if packet.ordering_index < expected_index: # bad packet
	                            continue
	                        # save to handle in the future
	                        self.ordering_list[packet.ordering_channel].append(packet)
	                        continue
                message = self.decode_message(packet.payload)
	                self.handle_connected_message(message, packet)
	        except Exception as e: # bad packet
	            e.add_note(f'Peer.handle_packet(data={data.hex(" ")})')
	            log(traceback.format_exc())
	    def handle_packet(self, data):
	        if self.connected:
	            self.handle_connected_packet(data)
	        else:
	            self.handle_unconnected_packet(data)
    def handle_unconnected_message(self, message):
	        if message.id == MSG.OPEN_CONNECTION_REPLY:
	            self.connected = True
	        for callback in self.unconnected_message_callbacks:
	            if callback(message, self) is True:
	                return
	    def handle_connected_message(self, message, internal_packet):
	        for callback in self.connected_message_callbacks:
	            if callback(message, internal_packet, self) is True:
	                return
        if message.id == MSG.INTERNAL_PING:
	            ping = message
	            self.push_message(ConnectedPong(ping.time, get_time()))
	    def send_unconnected_message(self, message):
	        if message.id == MSG.OPEN_CONNECTION_REPLY:
	            self.connected = True
	        self.sendto(self.encode_message(message))

import struct
	from sa import Vec2, Vec3, Quat
	from .common import SAMP_ENCODING
	def TO_BYTES(bit_count): return (bit_count + 7) // 8
	def TO_BITS(byte_count): return byte_count * 8
	def DIV_INT_CEIL(a, b): return (a - 1) // b + 1
	# copy n bits from i(at offset io) to o(at offset oo)
	# i: input buffer
	# o: output buffer (bytearray)
	# n: number of bits to copy
# io: input offset in bits
	# oo: output offset in bits
	def copy_bits(i, o, n, io, oo):
	    # determine helper variables
	    ibi = io // 8 # index(in bytes) in input of first byte read from
	    obi = oo // 8 # index(in bytes) in output first byte written to
	    # determine how many bytes will be written to in o
	    # n      pattern  write_byte_count
	    # 0      P0       0        =   0
	    # 1      P1       1        =   1 + 0
    # 2-8    P2       1 or 2   =   1 + 0 + (0 or 1)
	    # 9      P1       2        =   1 + 1
	    # 10-16  P2       2 or 3   =   1 + 1 + (0 or 1)
	    # 17     P1       3        =   1 + 2
	    # 18-24  P2       3 or 4   =   1 + 2 + (0 or 1)
	    # 25     P1       4        =   1 + 3
	    #
	    # 3 patterns:
	    # PATTERN 0(P0) n = 0     -> write_byte_count = 0
	    # PATTERN 1(P1) n % 8 = 1 -> write_byte_count = 1 + ((n-1) // 8)
    # PATTERN 2(P2) else      -> write_byte_count = 1 + ((n-2) // 8) + (0 if (((oo%8)+((n-2)%8)+2)<=8) else 1)
	    if n == 0:       write_byte_count = 0
	    elif n % 8 == 1: write_byte_count = 1 + ((n-1) // 8)
	    else:            write_byte_count = 1 + ((n-2) // 8) + (0 if (((oo % 8)+((n-2) % 8)+2)<=8) else 1)
	    k = 0
	    bits_written = 0
	    bits_just_written = 0
	    #print(f'write_byte_count={write_byte_count} ibi={ibi} obi={obi}')
	    # iterate each byte written to in output and modify it
	    for j in range(write_byte_count):
        #calculate some helper variables; determine if input bits are in one or two bytes
	        if j == 0:
	            obo = oo % 8
	            if obo + n < 8:
	                obr = 8 - obo - n
	            else:
	                obr = 0
	            bits_just_written = 8 - obo - obr
	            ibo = io % 8
	        else:
            ibo = (ibo + bits_just_written) % 8
	            obo = 0
	            if j != write_byte_count - 1:
	                obr = 0
	                bits_just_written = 8
	            else:
	                obr = 8 - (n - bits_written)
	                bits_just_written = n - bits_written
	        if ibo + bits_just_written <= 8:
	            # bits come from ONE byte in input
            ibr = 8 - ibo - bits_just_written
	            input_one_byte = True # 1
	        else:
	            # bits come from TWO byte in input
	            ibr = 16 - ibo - bits_just_written
	            input_one_byte = False # 2
	        bits_written += bits_just_written
	        # calculate input bits
	        if input_one_byte:
	            byte = (i[ibi + k] >> ibr) & (0xff >> (ibo+ibr))
        else:
	            bit_count = 16 - ibo - ibr
	            hi_bit_count = 8 - ibo
	            lo_bit_count = bit_count - hi_bit_count
	            hi = (i[ibi + k] & (0xff >> ibo))
	            lo = i[ibi + k + 1] >> ibr
	            byte = (hi << lo_bit_count) | lo
	        if ibo + bits_just_written >= 8:
	            k += 1
	        # clear bits
        clear_mask = ((0xff >> (obo + obr)) << obr) & 0xff
	        o[obi + j] &= ~clear_mask
	        # or bits
	        o[obi + j] |= (byte << obr)
	class Bitstream:
	    def __init__(self, data=None, data_len_in_bits=0, capacity=0):
	        maximum_data_len_in_bits = len(data) * 8 if data else 0
	        if data_len_in_bits == 0:
	            self.len = maximum_data_len_in_bits
	        else:
            if data_len_in_bits > maximum_data_len_in_bits:
	                raise ValueError('bad data_len_in_bits')
	            self.len = data_len_in_bits
	        self.data = data if data else bytearray(max(capacity, TO_BYTES(self.len)))
	        self.read_offset = 0 # read offset in bits
	        self.write_offset = 0 # write offset in bits
	    def to_bytes(self):
	        return self.data[:TO_BYTES(self.len)]
	    def reserve(self, size_in_bytes):
	        if len(self.data) >= size_in_bytes:
            return
	        self.data += bytearray(size_in_bytes - len(self.data))
	    def empty(self):
	        return self.len == 0
	    def capacity(self):
	        return len(self.data)
	    def skip_bits(self, bit_count):
	        self.read_offset += bit_count
	    def skip_bytes(self, byte_count):
	        self.read_offset += byte_count * 8
    def get_read(self):
	        return self.read_offset
	    def set_read(self, offset):
	        self.read_offset = offset
	    def get_write(self):
	        return self.write_offset
	    def set_write(self, offset):
	        self.write_offset = offset
	    def align_read_to_byte_boundary(self):
	        # if not aligned to byte boundary
        if self.read_offset % 8 != 0:
	            # add the number of bits remaining to be aligned
	            self.read_offset += 8 - (self.read_offset % 8)
	    def align_write_to_byte_boundary(self):
	        # if not aligned to byte boundary
	        if self.write_offset % 8 != 0:
	            # add the number of bits remaining to be aligned
	            self.write_offset += 8 - (self.write_offset % 8)
	    def reset(self):
	        self.read_offset = 0
        self.write_offset = 0
	        self.len = 0
	    def unread_bits_count(self):
	        return self.len - self.read_offset
	    def read_bit(self):
	        bit = (self.data[self.read_offset // 8] >> (7 - self.read_offset % 8)) & 1
	        self.read_offset += 1
	        return bit
	    # o: bytearray that receives the data
	    # n: number of bits to read from the Bitstream
    # oo(in bits) output offset in the dst bytearray to write the data to
	    def read_bits(self, o, n, oo=0):
	        copy_bits(self.data, o, n, self.read_offset, oo)
	        self.read_offset += n
	    def read_bits_num(self, n):
	        num = 0
	        for i in range(n):
	            num += self.read_bit() << (n - 1 - i)
	        return num
	    def read_aligned(self, o, byte_count):
        bit_count = byte_count * 8
	        self.align_read_to_byte_boundary()
	        for j in range(byte_count):
	            o[j] = self.data[(self.read_offset // 8) + j]
	        self.read_offset += bit_count
	    def read_u8(self):
	        return struct.unpack('B', self.read_buffer(8))[0]
	    def read_i8(self):
	        return struct.unpack('b', self.read_buffer(8))[0]
	    def read_u16(self):
        return struct.unpack('H', self.read_buffer(16))[0]
	    def read_i16(self):
	        return struct.unpack('h', self.read_buffer(16))[0]
	    def read_u32(self):
	        return struct.unpack('I', self.read_buffer(32))[0]
	    def read_i32(self):
	        return struct.unpack('i', self.read_buffer(32))[0]
	    def read_u64(self):
	        return struct.unpack('Q', self.read_buffer(64))[0]
	    def read_i64(self):
        return struct.unpack('q', self.read_buffer(64))[0]
	    def read_float(self):
	        return struct.unpack('f', self.read_buffer(32))[0]
	    # 00 00 00 20
	    # 1 1 1 + bin(20h)
	    # o: bytearray that receives the data
	    def read_compressed(self, o, byte_count):
	        one_count = 0
	        for i in range(byte_count):
	            if one_count == byte_count - 1:
                break
	            if self.read_bit() == 1:
	                one_count += 1
	            else: break
	        for i in range(one_count): # fill zeros
	            o[byte_count - one_count + i] = 0x00
	        if one_count < byte_count - 1:
	            self.read_bits(o, (byte_count - one_count) * 8)
	        else:
	            bits_to_read = 4 if self.read_bit() else 8
            self.read_bits(o, bits_to_read, bits_to_read % 8)
	    def read_compressed_u16(self):
	        b = bytearray(2)
	        self.read_compressed(b, 2)
	        return struct.unpack('H', b)[0]
	    def read_compressed_u32(self):
	        b = bytearray(4)
	        self.read_compressed(b, 4)
	        return struct.unpack('I', b)[0]
	    # return [-1, +1]
    def read_compressed_float(self):
	        return (self.read_u16() / 32767.5) - 1.0
	    # n: bit count
	    def read_buffer(self, n):
	        buffer = bytearray(TO_BYTES(n))
	        self.read_bits(buffer, n)
	        return buffer
	    def read_dynamic_buf8(self):
	        return self.read_buffer(TO_BITS(self.read_u8()))
	    def read_dynamic_buf16(self):
        return self.read_buffer(TO_BITS(self.read_u16()))
	    def read_dynamic_buf32(self):
	        return self.read_buffer(TO_BITS(self.read_u32()))
	    def read_dynamic_str8(self):
	        return self.read_dynamic_buf8().decode(SAMP_ENCODING)
	    def read_dynamic_str16(self):
	        return self.read_dynamic_buf16().decode(SAMP_ENCODING)
	    def read_dynamic_str32(self):
	        return self.read_dynamic_buf32().decode(SAMP_ENCODING)
	    def read_huffman_buffer(self, root_node):
        output = bytearray()
	        node = root_node
	        bit_count = self.read_compressed_u16()
	        for _ in range(bit_count):
	            node = node.left if (self.read_bit() == 0) else node.right
	            if node.left is None and node.right is None:
	                output += bytearray([node.value])
	                node = root_node
	        return output
	    def read_vec2(self):
        x = self.read_float()
	        y = self.read_float()
	        return Vec2(x, y)
	    def read_vec3(self):
	        x = self.read_float()
	        y = self.read_float()
	        z = self.read_float()
	        return Vec3(x, y, z)
	    def read_compressed_vec3(self):
	        length = self.read_float()
        if length > 0.00001:
	            x = self.read_compressed_float()
	            y = self.read_compressed_float()
	            z = self.read_compressed_float()
	        else:
	            x = y = z = 0.0
	        return Vec3(x, y, z)
	    def read_quat(self):
	        w = self.read_float()
	        x = self.read_float()
        y = self.read_float()
	        z = self.read_float()
	        return Quat(w, x, y, z)
	    def read_norm_quat(self):
	        w_sign = -1 if self.read_bit() else 1
	        x_sign = -1 if self.read_bit() else 1
	        y_sign = -1 if self.read_bit() else 1
	        z_sign = -1 if self.read_bit() else 1
	        x = x_sign * (self.read_u16() / 2**16)
	        y = y_sign * (self.read_u16() / 2**16)
        z = z_sign * (self.read_u16() / 2**16)
	        w = w_sign * min(1 - x*x - y*y - z*z, 0)**0.5
	        return Quat(w, x, y, z)
	    # WRITE METHODS
	    # n: size of bit sequence to be written
	    def check_resize(self, n):
	        if n + self.len > TO_BITS(len(self.data)): # resize buffer
	            self.data = self.data + bytearray(max(TO_BYTES(n*2), len(self.data)))
	    def update_len(self):
	        self.len = self.write_offset if (self.write_offset > self.len) else self.len
    def write_bit(self, bit):
	        self.check_resize(1)
	        if bit == 1: # set bit
	            self.data[self.write_offset // 8] |= 1 << (7 - (self.write_offset % 8))
	        else: # clear bit
	            self.data[self.write_offset // 8] &= ~(1 << (7 - (self.write_offset % 8)))
	        self.write_offset += 1
	        self.update_len()
	    def write_bits(self, i, n, io=0):
	        self.check_resize(n)
        copy_bits(i, self.data, n, io, self.write_offset)
	        self.write_offset += n
	        self.update_len()
	    def write_bits_num(self, num, n):
	        for i in range(n):
	            self.write_bit(int(not not (num & (1 << (n - 1 - i)))))
	    def write_aligned(self, buffer):
	        self.check_resize(len(buffer))
	        self.align_write_to_byte_boundary()
	        for i in range(len(buffer)):
            self.data[self.write_offset // 8 + i] = buffer[i]
	        self.write_offset += len(buffer) * 8
	        self.update_len()
	    def write_u8(self, n):
	        self.write_bits(struct.pack('B', n), 8)
	    def write_i8(self, n):
	        self.write_bits(struct.pack('b', n), 8)
	    def write_u16(self, n):
	        self.write_bits(struct.pack('H', n), 16)
	    def write_i16(self, n):
        self.write_bits(struct.pack('h', n), 16)
	    def write_u32(self, n):
	        self.write_bits(struct.pack('I', n), 32)
	    def write_i32(self, n):
	        self.write_bits(struct.pack('i', n), 32)
	    def write_u64(self, n):
	        self.write_bits(struct.pack('Q', n), 32)
	    def write_i64(self, n):
	        self.write_bits(struct.pack('q', n), 32)
	    def write_float(self, float):
        self.write_bits(struct.pack('f', float), 32)
	    def write_compressed(self, b):
	        i = len(b) - 1
	        while i > 0:
	            if b[i] == 0x00:
	                self.write_bit(1)
	            else:
	                self.write_bit(0)
	                self.write_bits(b, (i + 1) * 8)
	                return
            i -= 1
	        bit = int((b[i] & 0xF0) == 0x00)
	        self.write_bit(bit)
	        sz = 4 if bit else 8
	        self.write_bits(b[i:i+1], sz, sz % 8)
	    def write_compressed_u16(self, value):
	        self.write_compressed(struct.pack('H', value))
	    def write_compressed_u32(self, value):
	        self.write_compressed(struct.pack('I', value))
	    # value = [-1, +1]
    def write_compressed_float(self, value):
	        if value < -1.0: value = -1.0
	        elif value > 1.0: value = 1.0
	        self.write_u16(int((value + 1.0) * 32767.5))
	    def write_buffer(self, buffer, n):
	        self.write_bits(buffer, n)
	    def write_dynamic_buf8(self, buffer):
	        self.write_u8(len(buffer))
	        self.write_buffer(buffer, TO_BITS(len(buffer)))
	    def write_dynamic_buf16(self, buffer):
        self.write_u16(len(buffer))
	        self.write_buffer(buffer, TO_BITS(len(buffer)))
	    def write_dynamic_buf32(self, buffer):
	        self.write_u32(len(buffer))
	        self.write_buffer(buffer, TO_BITS(len(buffer)))
	    def write_dynamic_str8(self, s):
	        self.write_dynamic_buf8(s.encode(SAMP_ENCODING))
	    def write_dynamic_str16(self, s):
	        self.write_dynamic_buf16(s.encode(SAMP_ENCODING))
	    def write_dynamic_str32(self, s):
        self.write_dynamic_buf32(s.encode(SAMP_ENCODING))
	    def write_huffman_buffer(self, buffer, encoding_table):
	        bit_count = sum(encoding_table[byte].len for byte in buffer)
	        self.write_compressed_u16(bit_count)
	        for byte in buffer:
	            self.write_bits(encoding_table[byte].data, encoding_table[byte].len)
	    def write_vec2(self, v):
	        self.write_float(v.x)
	        self.write_float(v.y)
	    def write_vec3(self, v):
        self.write_float(v.x)
	        self.write_float(v.y)
	        self.write_float(v.z)
	    def write_compressed_vec3(self, v):
	        length = (v.x**2 + v.y**2 + v.z**2)**0.5 # theorem of pitagoras
	        self.write_float(length)
	        if length > 0.00001:
	            self.write_compressed_float(v.x / length)
	            self.write_compressed_float(v.y / length)
	            self.write_compressed_float(v.z / length)
    def write_quat(self, q):
	        self.write_float(q.w)
	        self.write_float(q.x)
	        self.write_float(q.y)
	        self.write_float(q.z)
	    def write_norm_quat(self, q):
	        self.write_bit(q.w < 0)
	        self.write_bit(q.x < 0)
	        self.write_bit(q.y < 0)
	        self.write_bit(q.z < 0)
        self.write_u16(int(abs(q.x) * 2**16))
	        self.write_u16(int(abs(q.y) * 2**16))
	        self.write_u16(int(abs(q.z) * 2**16))

import asyncio
	from sa import *
	from sa.samp import *
	import traceback
	import math
	def on_message(message, internal_packet, peer, server):
	    if message.id == MSG.RPC:
	        rpc = message
	        if rpc.rpc_id == RPC.CLICK_SCOREBOARD_PLAYER:
	            peer.push_message(ShowGameText(0, 5000, f'You clicked on id {rpc.player_id}'))
            if rpc.player_id == 0:
	                peer.push_message(GiveWeapon(WEAPON.M4, 250))
	            elif rpc.player_id == 1:
	                peer.push_message(RemoveAllWeapons())
	        elif rpc.rpc_id == RPC.REQUEST_CHAT_COMMAND:
	            # WARNING: REALLY INSECURE, but useful for developing/debugging
	            if rpc.command.startswith('/x'):
	                try:
	                    exec(rpc.command[2:])
	                except:
                    for line in traceback.format_exc().split('\n'):
	                        peer.push_message(ChatMessage(line, color=0xdd0000ff))
	            elif rpc.command.startswith('/rpc'):
	                try:
	                    peer.push_message(eval(rpc.command[4:]))
	                except:
	                    for line in traceback.format_exc().split('\n'):
	                        peer.push_message(ChatMessage(line, color=0xdd0000ff))
	            elif rpc.command.startswith('/money'):
	                try:
                    amount = int(rpc.command.split()[1])
	                    peer.push_message(GiveMoney(amount))
	                except:
	                    peer.push_message(ChatMessage('Usage: /money <amount>', color=0x999900ff))
	            elif rpc.command == '/nomoney':
	                peer.push_message(ResetMoney())
	            elif rpc.command.startswith('/face'):
	                try:
	                    angle = float(rpc.command.split()[1])
	                    peer.push_message(SetFacingAngle(angle))
                except:
	                    peer.push_message(ChatMessage('Usage: /face <angle>', color=0x999900ff))
	            elif rpc.command == '/rhinos':
	                for i in range(10):
	                    peer.push_message(RemoveVehicle(100+i))
	                    peer.push_message(AddVehicle(100+i, VEHICLE.RHINO, Vec3(0 + 20*math.cos(i/10*2*math.pi), 0 + 20*math.sin(i/10*2*math.pi), 3), dir_z=0))
	                    peer.push_message(Show3DTextLabel(100+i, f'Bot Rhino {i}', 0xdddd00dd, Vec3(0, 0, 0), draw_distance=100, test_los=False, attached_vehicle_id=100+i))
	            else:
	                peer.push_message(ChatMessage('Invalid command', color=0xdd0000ff))
	async def main():
    s = Server(('127.0.0.1', 7777))
	    s.message_callbacks.append(on_message)
	    s.fake_player_list =  {'alice': 100, 'zebra': 999}
