from __future__ import annotations
	import torch
	from mfglib.env import Environment
	class TransitionFn:
	    def __init__(self, n: int, p_still: float) -> None:
	        prs = [(1 - p_still) / 2, p_still, (1 - p_still) / 2]
	        self.p = torch.zeros(n, n, 3)
	        for s in range(n):
	            for a in range(3):
	                for epsilon, pr in zip([-1, 0, 1], prs):
                    s_next = min(max(s + a - 1 + epsilon, 0), n - 1)
	                    self.p[s_next, s, a] += pr
	    def __call__(self, env: Environment, t: int, L_t: torch.Tensor) -> torch.Tensor:
	        return self.p
	class RewardFn:
	    def __init__(self, n: int, bar_loc: int, log_eps: float) -> None:
	        self.c1 = torch.abs(
	            torch.arange(0, n).repeat(3, 1).T - bar_loc * torch.ones(n, 3)
	        )
	        self.c2 = -torch.tensor([1, 0, 1]).repeat(n, 1) / n
        self.log_eps = log_eps
	    def __call__(self, env: Environment, t: int, L_t: torch.Tensor) -> torch.Tensor:
	        l_s = len(env.S)
	        mu_t = L_t.flatten(start_dim=l_s).sum(-1)
	        c3 = -torch.log(mu_t.repeat(L_t.shape[1], 1).T + self.log_eps)
	        return self.c1 + self.c2 + c3

from __future__ import annotations
	import torch
	from mfglib.env import Environment
	class TransitionFn:
	    def __init__(self, n: int, c: tuple[float, ...]) -> None:
	        self.n = n
	        self.c = c
	    def __call__(self, env: Environment, t: int, L_t: torch.Tensor) -> torch.Tensor:
	        p_t = torch.zeros(self.n, self.n, self.n)
	        if t == 0:
            for s in range(self.n):
	                for a in range(self.n):
	                    p_t[a, s, a] = 1.0
	            return p_t
	        for s in range(self.n):
	            eye_s = torch.zeros(self.n, self.n)
	            eye_s[s, s] = 1.0
	            diff_s = (L_t - eye_s).pow(2).sum()
	            p_t[:, s, :] = (
	                (self.c[t - 1] * diff_s)
                / (1.0 + self.n * self.c[t - 1] * diff_s)
	                * torch.ones(self.n, self.n)
	            )
	            for a in range(self.n):
	                p_t[a, s, a] += 1.0 / (1.0 + self.n * self.c[t - 1] * diff_s)
	        return p_t
	class RewardFn:
	    def __init__(self, n: int, r: tuple[float, ...]) -> None:
	        self.n = n
	        self.r = r
    def __call__(self, env: Environment, t: int, L_t: torch.Tensor) -> torch.Tensor:
	        r_t = torch.zeros(self.n, self.n)
	        if t == 0:
	            return r_t
	        for s in range(self.n):
	            eye_s = torch.zeros(self.n, self.n)
	            eye_s[s, s] = 1.0
	            diff_s = (L_t - eye_s).pow(2).sum() / 2.0
	            r_t[s, s] = self.r[s] * (1 - diff_s)
	        return r_t

from __future__ import annotations
	from typing import cast
	import torch
	from mfglib.env import Environment
	class TransitionFn:
	    def __init__(self, torus_l: int, torus_w: int, p_still: float) -> None:
	        x = (1 - p_still) / 4
	        eps_prob = [x, x, x, x, p_still]
	        acts = [(1, 0), (-1, 0), (0, 1), (0, -1), (0, 0)]
	        delta = [(1, 0), (-1, 0), (0, 1), (0, -1), (0, 0)]
        self.p = torch.zeros(torus_l, torus_w, torus_l, torus_w, 5)
	        for s_0 in range(torus_l):
	            for s_1 in range(torus_w):
	                for i, (a_0, a_1) in enumerate(acts):
	                    for j, (dx, dy) in enumerate(delta):
	                        s_next_0 = min(max(s_0 + a_0 + dx, 0), torus_l - 1)
	                        s_next_1 = min(max(s_1 + a_1 + dy, 0), torus_w - 1)
	                        self.p[s_next_0, s_next_1, s_0, s_1, i] += eps_prob[j]
	    def __call__(self, env: Environment, t: int, L_t: torch.Tensor) -> torch.Tensor:
	        return self.p
class RewardFn:
	    def __init__(
	        self,
	        T: int,
	        torus_l: int,
	        torus_w: int,
	        loc_change_freq: int,
	        c: float,
	        log_eps: float,
	    ) -> None:
        self.c = c
	        self.log_eps = log_eps
	        bar_loc = (torus_l // 2, torus_w // 2)
	        bar_locs = [bar_loc]
	        for i in range(T + 1):
	            if (i + 1) % loc_change_freq == 0:
	                choices = [-1, 1]
	                rand_rl = choices[torch.randint(0, 2, size=(1,))]
	                rand_ud = choices[torch.randint(0, 2, size=(1,))]
	                new_bar_loc = (bar_loc[0] + rand_rl, bar_loc[1] + rand_ud)
                if 0 <= new_bar_loc[0] < torus_l and 0 <= new_bar_loc[1] < torus_w:
	                    bar_loc = new_bar_loc
	            bar_locs.append(bar_loc)
	        self.c1s = []
	        for t in range(T + 1):
	            bar_loc = bar_locs[t]
	            dist = torch.zeros(torus_l, torus_w)
	            for s_0 in range(torus_l):
	                for s_1 in range(torus_w):
	                    dist[s_0, s_1] = torch.abs(
                        torch.tensor(s_0 - bar_loc[0])
	                    ) + torch.abs(torch.tensor(s_1 - bar_loc[1]))
	            dist /= torus_l + torus_w
	            c1 = 1.0 - dist.repeat(5, 1, 1).permute(1, 2, 0)
	            self.c1s.append(c1)
	    def __call__(self, env: Environment, t: int, L_t: torch.Tensor) -> torch.Tensor:
	        mu_t = L_t.flatten(start_dim=2).sum(dim=-1)
	        return cast(
	            torch.Tensor,
	            self.c * self.c1s[t]
            - torch.log(mu_t.repeat(5, 1, 1).permute(1, 2, 0) + self.log_eps),
	        )

from __future__ import annotations
	from typing import cast
	import torch
	from mfglib.env import Environment
	class TransitionFn:
	    def __init__(self, n: int, m: float) -> None:
	        self.p1 = 2 * m * torch.rand(n, n, n, dtype=torch.float) - m
	        self.p2 = 2 * m * torch.rand(n, n, n, dtype=torch.float) - m
	    def __call__(self, env: Environment, t: int, L_t: torch.Tensor) -> torch.Tensor:
	        soft_max = torch.nn.Softmax(dim=0)
        return cast(torch.Tensor, soft_max(self.p1 @ L_t.float() + self.p2))
	class RewardFn:
	    def __init__(self, n: int, m: float) -> None:
	        self.r1 = 2 * m * torch.rand(n, n, dtype=torch.float) - m
	        self.r2 = 2 * m * torch.rand(n, n, dtype=torch.float) - m
	    def __call__(self, env: Environment, t: int, L_t: torch.Tensor) -> torch.Tensor:
	        return self.r1 @ L_t.float() + self.r2

