import re
	from typing import List, NamedTuple
	from textual import log
	from textual.events import MouseEvent
	WWB = re.compile(r"\W*\w+\b")
	class Cursor(NamedTuple):
	    lno: int
	    pos: int
	    @classmethod
	    def from_mouse_event(cls, event: MouseEvent) -> "Cursor":
        return Cursor(event.y, event.x - 1)
	def handle_arrow(key: str, lines: List[str], cursor: Cursor) -> Cursor:
	    arrow = key.split("+")[-1]
	    if "ctrl" in key:
	        assert arrow not in ("up", "down"), "ctrl+up/down should be handled first"
	        if arrow == "right":
	            return _handle_ctrl_right(lines, cursor)
	        else:  # if arrow == "left":
	            return _handle_ctrl_left(lines, cursor)
	    else:
        if arrow == "right":
	            return _handle_right(lines, cursor)
	        elif arrow == "left":
	            return _handle_left(lines, cursor)
	        elif arrow == "down":
	            return _handle_down(lines, cursor)
	        else:  # arrow == "up":
	            return _handle_up(lines, cursor)
	def _handle_right(lines: List[str], cursor: Cursor) -> Cursor:
	    max_x = len(lines[cursor.lno]) - 1
    max_y = len(lines) - 1
	    if cursor.lno == max_y:
	        return Cursor(lno=max_y, pos=min(max_x, cursor.pos + 1))
	    elif cursor.pos == max_x:
	        return Cursor(lno=cursor.lno + 1, pos=0)
	    else:
	        return Cursor(lno=cursor.lno, pos=cursor.pos + 1)
	def _handle_left(lines: List[str], cursor: Cursor) -> Cursor:
	    if cursor.lno == 0:
	        return Cursor(0, pos=max(0, cursor.pos - 1))
    elif cursor.pos == 0:
	        return Cursor(
	            lno=cursor.lno - 1,
	            pos=len(lines[cursor.lno - 1]) - 1,
	        )
	    else:
	        return Cursor(lno=cursor.lno, pos=cursor.pos - 1)
	def _handle_down(lines: List[str], cursor: Cursor) -> Cursor:
	    max_y = len(lines) - 1
	    if cursor.lno == max_y:
        return Cursor(lno=max_y, pos=len(lines[cursor.lno]) - 1)
	    else:
	        max_x = len(lines[cursor.lno + 1]) - 1
	        return Cursor(lno=cursor.lno + 1, pos=min(max_x, cursor.pos))
	def _handle_up(lines: List[str], cursor: Cursor) -> Cursor:
	    if cursor.lno == 0:
	        return Cursor(0, 0)
	    else:
	        max_x = len(lines[cursor.lno - 1]) - 1
	        return Cursor(lno=cursor.lno - 1, pos=min(max_x, cursor.pos))
def _handle_ctrl_right(lines: List[str], cursor: Cursor) -> Cursor:
	    max_x = len(lines[cursor.lno]) - 1
	    max_y = len(lines) - 1
	    if cursor.pos == max_x and cursor.lno == max_y:
	        return cursor
	    elif cursor.pos == max_x:
	        lno = cursor.lno + 1
	        pos = 0
	    else:
	        lno = cursor.lno
        pos = cursor.pos
	    tail = lines[lno][pos:]
	    if match := WWB.match(tail):
	        return Cursor(lno=lno, pos=pos + match.span()[1])
	    else:  # no more words, move to end of line
	        return Cursor(lno=lno, pos=len(lines[lno]) - 1)
	def _handle_ctrl_left(lines: List[str], cursor: Cursor) -> Cursor:
	    if cursor.pos == 0 and cursor.lno == 0:
	        return cursor
	    elif cursor.pos == 0:
        lno = cursor.lno - 1
	        pos = len(lines[lno]) - 1
	    else:
	        lno = cursor.lno
	        pos = cursor.pos
	    tail = lines[lno][:pos][::-1]
	    if match := WWB.match(tail):
	        log(match)
	        return Cursor(lno=lno, pos=pos - match.span()[1])
	    else:  # no more words, move to start of line
        return Cursor(lno=lno, pos=0)

import stat
	from pathlib import Path
	from typing import Union
	from rich.highlighter import Highlighter
	from textual.message import Message
	from textual.suggester import Suggester
	from textual.validation import ValidationResult, Validator
	from textual.widgets import Input
	class CancelPathInput(Message):
	    pass
class PathSuggester(Suggester):
	    def __init__(self) -> None:
	        super().__init__(use_cache=True, case_sensitive=True)
	    async def get_suggestion(self, value: str) -> Union[str, None]:
	        try:
	            p = Path(value).expanduser()
	            matches = list(p.parent.glob(f"{p.parts[-1]}*"))
	            if len(matches) == 1:
	                return str(matches[0])
	            else:
                return None
	        except Exception:
	            return None
	class PathValidator(Validator):
	    def __init__(
	        self,
	        dir_okay: bool,
	        file_okay: bool,
	        must_exist: bool,
	        failure_description: str = "Not a valid path.",
    ) -> None:
	        self.dir_okay = dir_okay
	        self.file_okay = file_okay
	        self.must_exist = must_exist
	        super().__init__(failure_description)
	    def validate(self, value: str) -> ValidationResult:
	        if self.dir_okay and self.file_okay and not self.must_exist:
	            return self.success()
	        try:
	            p = Path(value).expanduser().resolve()
        except Exception:
	            return self.failure("Not a valid path.")
	        try:
	            st = p.stat()
	        except FileNotFoundError:
	            if self.must_exist:
	                return self.failure("File or directory does not exist.")
	            return self.success()
	        if not self.dir_okay and stat.S_ISDIR(st.st_mode):
	            return self.failure("Path cannot be a directory.")
        elif not self.file_okay and stat.S_ISREG(st.st_mode):
	            return self.failure("Path cannot be a regular file.")
	        return self.success()
	class PathInput(Input):
	    BINDINGS = [
	        ("escape", "cancel", "Cancel"),
	        ("tab", "complete", "Accept Completion"),
	    ]
	    def __init__(
	        self,
        value: Union[str, None] = None,
	        placeholder: str = "",
	        highlighter: Union[Highlighter, None] = None,
	        password: bool = False,
	        *,
	        name: Union[str, None] = None,
	        id: Union[str, None] = None,
	        classes: Union[str, None] = None,
	        disabled: bool = False,
	        dir_okay: bool = True,
        file_okay: bool = True,
	        must_exist: bool = False,
	        tab_advances_focus: bool = False,
	    ) -> None:
	        self.tab_advances_focus = tab_advances_focus
	        super().__init__(
	            value,
	            placeholder,
	            highlighter,
	            password,
            suggester=PathSuggester(),
	            validators=PathValidator(dir_okay, file_okay, must_exist),
	            name=name,
	            id=id,
	            classes=classes,
	            disabled=disabled,
	        )
	    def action_cancel(self) -> None:
	        self.post_message(CancelPathInput())
	    def action_complete(self) -> None:
        if self._suggestion and self._suggestion != self.value:
	            self.action_cursor_right()
	        elif self.tab_advances_focus:
	            self.app.action_focus_next()

from typing import List
	def serialize_lines(lines: List[str]) -> str:
	    return "\n".join([line.rstrip() for line in lines])
	def deserialize_lines(text: str, trim: bool = False) -> List[str]:
	    if text:
	        lines = [f"{line} " for line in text.splitlines()]
	        if text.endswith(("\n", "\r", "\r\n")):
	            lines.append(" ")
	    else:
	        lines = [" "]
    if trim:
	        lines[-1] = lines[-1].rstrip()
	    return lines
