	        self._eid = eid  # ID within the corresponding dataset
	        self._entity_type = entity_type
	        self._tokens = tokens
	        self._phrase = phrase
	    def as_tuple(self):
        return self.span_start, self.span_end, self._entity_type
	    def as_tuple_token(self):
