	class DistributedIterableDataset(IterableTorchDataset):
	    TRAIN_MODE = 'train'
	    EVAL_MODE = 'eval'
	    def __init__(self, label, path, rel_types, entity_types, input_reader, random_mask_word = False, tokenizer = None, processor = None, repeat_gt_entities = None):
	        self._label = label
	        self._path = path
