"""Manage Pydocstyle output on workflow."""
	import sys
	def check_output() -> None:
	    """Check output of Pydocstyle.
	    Raises
	    ------
	    ValueError
	        If Pydocstyle find errors.
	    """
	    args = sys.argv[1:]
    n_errors = -1  # A default value
	    for arg in args:
	        if arg.startswith("--n_errors="):
	            n_errors = int(arg.split("=")[1])
	    if n_errors == -1:
	        raise ValueError(
	            "Please specify the number of errors found by Pydocstyle "
	            "using the flag --n_errors=N."
	        )
	    if n_errors > 0:
        raise ValueError(
	            f"Pydocstyle found {n_errors} errors in python "
	            "docstrings. Please fix them.",
	        )
	if __name__ == "__main__":
	    check_output()

"""Shared pytest fixtures."""
	from typing import Any, Dict
	import pytest
	from cliconfig.base import Config
	from cliconfig.processing.base import Processing
	from cliconfig.tag_routines import clean_all_tags, clean_tag, is_tag_in
	class ProcessAdd1(Processing):
	    """Add 1 to values with tag "@add1"."""
	    def premerge(self, flat_config: Config) -> Config:
	        """Pre-merge processing."""
        items = list(flat_config.dict.items())
	        for flat_key, value in items:
	            if is_tag_in(flat_key, "add1"):
	                flat_config.dict[clean_tag(flat_key, "add1")] = value + 1
	                del flat_config.dict[flat_key]
	        return flat_config
	    def endbuild(self, flat_config: Config) -> Config:
	        """End-build processing."""
	        flat_config.dict["processing name"] = "ProcessAdd1"
	        return flat_config
    def presave(self, flat_config: Config) -> Config:
	        """Pre-save processing."""
	        return self.premerge(flat_config)
	class ProcessKeep(Processing):
	    """Prevent a value from being changed after the merge."""
	    def __init__(self) -> None:
	        super().__init__()
	        self.keep_vals: Dict[str, Any] = {}
	    # pylint: disable=unused-argument
	    def premerge(self, flat_config: Config) -> Config:
        """Pre-merge processing."""
	        items = list(flat_config.dict.items())
	        for flat_key, value in items:
	            if is_tag_in(flat_key, "keep"):
	                new_key = clean_tag(flat_key, "keep")
	                flat_config.dict[new_key] = value
	                del flat_config.dict[flat_key]
	                self.keep_vals[clean_all_tags(flat_key)] = value
	        return flat_config
	    # pylint: disable=unused-argument
    def postmerge(self, flat_config: Config) -> Config:
	        """Post-merge processing."""
	        for key, value in self.keep_vals.items():
	            if key in flat_config.dict:
	                flat_config.dict[key] = value
	        self.keep_vals = {}
	        return flat_config
	    # pylint: disable=unused-argument
	    def postload(self, flat_config: Config) -> Config:
	        """Post-load processing."""
        for key, value in self.keep_vals.items():
	            flat_config.dict[key] = value
	        self.keep_vals = {}
	        return flat_config
	@pytest.fixture()
	def process_add1() -> ProcessAdd1:
	    """Return a processing object that adds 1 on tag "@add1"."""
	    return ProcessAdd1()
	@pytest.fixture()
	def process_keep() -> ProcessKeep:
    """Return a processing object that keep a value unchanged after the merge."""
	    return ProcessKeep()

"""Tests for dict routines."""
	import os
	import shutil
	import pytest
	import pytest_check as check
	from cliconfig.dict_routines import (
	    _del_key,
	    clean_pre_flat,
	    flatten,
	    load_dict,
    merge_flat,
	    merge_flat_paths,
	    save_dict,
	    show_dict,
	    unflatten,
	)
	def test_flatten() -> None:
	    """Test flatten(."""
	    check.equal(
	        flatten({"a.b": 1, "a": {"c": 2}, "d": 3}),
        {"a.b": 1, "a.c": 2, "d": 3},
	    )
	    check.equal(
	        flatten({"a.b": {"c": 1}, "a": {"b.d": 2}, "a.e": {"f.g": 3}}),
	        {"a.b.c": 1, "a.b.d": 2, "a.e.f.g": 3},
	    )
	    check.equal(
	        flatten({"a.b": 1, "a": {"c": {}}, "a.c": 3}),
	        {"a.b": 1, "a.c": 3},
	    )
    with pytest.raises(ValueError, match="duplicated key 'a.b'"):
	        flatten({"a.b": 1, "a": {"b": 1}})
	def test_unflatten() -> None:
	    """Test unflatten."""
	    check.equal(
	        unflatten({"a.b": 1, "a.c": 2, "c": 3}),
	        {"a": {"b": 1, "c": 2}, "c": 3},
	    )
	    with pytest.raises(ValueError, match="The dict must be flatten.*"):
	        unflatten({"a.b": 1, "a": {"c": 2}})
def test_merge_flat() -> None:
	    """Test merge_flat."""
	    dict1 = {"a.b": 1, "a": {"c": 2}}
	    dict2 = {"c": 3}
	    check.equal(
	        merge_flat(dict1, dict2, allow_new_keys=True),
	        {"a.b": 1, "a.c": 2, "c": 3},
	    )
	    with pytest.raises(ValueError, match="New parameter found 'c'.*"):
	        merge_flat(dict1, dict2, allow_new_keys=False)
    dict1 = {"a.b": 1, "a": {"b": 1, "c": 2}}
	    with pytest.raises(
	        ValueError,
	        match="Duplicated key found.*You may consider calling 'clean_pre_flat'.*",
	    ):
	        merge_flat(dict1, dict2, allow_new_keys=True)
	def test_merge_flat_paths() -> None:
	    """Test merge_flat_paths."""
	    dict1 = {
	        "param1": 1,
        "param2": 2,
	        "letters": {
	            "letter1": "a",
	            "letter2": "b",
	        },
	    }
	    dict2 = {
	        "param3": 3,
	        "letters": {
	            "letter3": "c",
            "letter4": "d",
	        },
	    }
	    expected_dict = {
	        "param1": 1,
	        "param2": 2,
	        "param3": 3,
	        "letters.letter1": "a",
	        "letters.letter2": "b",
	        "letters.letter3": "c",
        "letters.letter4": "d",
	    }
	    flat_dict = merge_flat_paths(
	        "tests/configs/default1.yaml",
	        "tests/configs/default2.yaml",
	        allow_new_keys=True,
	    )
	    check.equal(flat_dict, expected_dict)
	    flat_dict = merge_flat_paths(dict1, dict2, allow_new_keys=True)
	    check.equal(flat_dict, expected_dict)
    flat_dict = merge_flat_paths(
	        dict1, "tests/configs/default2.yaml", allow_new_keys=True
	    )
	    check.equal(flat_dict, expected_dict)
	    with pytest.raises(ValueError, match="New parameter found 'param3'.*"):
	        merge_flat_paths(
	            "tests/configs/default1.yaml",
	            "tests/configs/default2.yaml",
	            allow_new_keys=False,
	        )
def test_del_key() -> None:
	    """Test _del_key."""
	    in_dict = {"a": {"b": {"c": 1}, "d": 2}, "a.e": 3, "a.b.c": 4}
	    _del_key(in_dict, "a.b.c")
	    check.equal(in_dict, {"a": {"d": 2}, "a.e": 3})
	    in_dict = {"a": {"b": {"c": 1}, "d": 2}, "a.e": 3, "a.b.c": 4}
	    _del_key(in_dict, "a.b.c", keep_flat=True)
	    check.equal(in_dict, {"a": {"d": 2}, "a.e": 3, "a.b.c": 4})
	    in_dict = {"a": {"b": {"c": 1}, "d": 2}, "a.e": 3, "a.b.c": 4}
	    _del_key(in_dict, "a.b.c", keep_unflat=True)
    check.equal(in_dict, {"a": {"b": {"c": 1}, "d": 2}, "a.e": 3})
	    with pytest.raises(ValueError, match="Key 'a.b.z' not found in dict."):
	        _del_key({"a": {"b": {"c": 1}, "d": 2}, "a.e": 3, "a.b.c": 4}, "a.b.z")
	    with pytest.raises(ValueError, match="Key 'a.z.c' not found in dict."):
	        _del_key({"a": {"b": {"c": 1}, "d": 2}, "a.e": 3, "a.b.c": 4}, "a.z.c")
	def test_clean_pre_flat() -> None:
	    """Test clean_pre_flat."""
	    check.equal(
	        clean_pre_flat({"a.b": 1, "a": {"b": 2}, "c": 3}, priority="flat"),
	        {"a.b": 1, "c": 3},
    )
	    check.equal(
	        clean_pre_flat({"a.b": 1, "a": {"b": 2}, "c": 3}, priority="unflat"),
	        {"a": {"b": 2}, "c": 3},
	    )
	    with pytest.raises(ValueError, match="duplicated key 'a.b'"):
	        clean_pre_flat({"a.b": 1, "a": {"b": 2}, "c": 3}, priority="error")
	    with pytest.raises(
	        ValueError,
	        match=(
            "priority argument must be one of 'flat', 'unflat' or 'error' but "
	            "found 'UNKNOWN'"
	        ),
	    ):
	        clean_pre_flat({"a.b": 1, "a": {"b": 2}, "c": 3}, priority="UNKNOWN")
	def test_save_load_dict() -> None:
	    """Test save_dict and load_dict."""
	    dict1 = {"a": 1, "b": {"c": 2}, "d": [2, 3.0], "e": [{"f": 4}]}
	    save_dict(dict1, "tests/tmp/config.yaml")
	    check.is_true(os.path.isfile("tests/tmp/config.yaml"))
    dict2 = load_dict("tests/tmp/config.yaml")
	    check.equal(dict1, dict2)
	    # Case multiple files and yaml tags
	    out_dict = load_dict("tests/configs/multi_files_with_tags.yaml")
	    expected_dict = {
	        "config@cfg": {
	            "param1@par@other": 1,
	            "param2": {"a": 1.0, "b@par2": "2.1"},
	        },
	        "config2": {
            "param3@par3": 3.2,
	            "param4@par4": [4, 5, {"6": 6}],
	            "param5": [True, 8],
	        },
	        "config3@cfg3": {"param6": {"param7@par7": None}, "param8": "True"},
	        "config4@cfg4": {"config5@cfg5": {"param9": "11"}},
	    }
	    check.equal(out_dict, expected_dict)
	    shutil.rmtree("tests/tmp")
