	# Assumes layer is perfectly divisible into 256 * 256 blocks
	class QuantLinear(nn.Module): 
    def __init__(self, bits, infeatures, outfeatures):
	        super().__init__()
	        if bits not in [2,3,4,8]:
	            raise NotImplementedError("Only 2,3,4,8 bits are supported.")
	        self.bits = bits
	        self.register_buffer('zeros', torch.zeros((outfeatures, 1)))
	        self.register_buffer('scales', torch.zeros((outfeatures, 1)))
	        self.register_buffer('bias', torch.zeros(outfeatures))
	        self.register_buffer(
	            'qweight', torch.zeros((infeatures // 256 * (bits * 8), outfeatures), dtype=torch.int)
        )
	    def pack(self, linear, scales, zeros):
	        self.zeros = zeros * scales
	        self.scales = scales.clone()
	        if linear.bias is not None:
	            self.bias = linear.bias.clone()  
	        intweight = torch.round((linear.weight.data + self.zeros) / self.scales).to(torch.int)
	        intweight = intweight.t().contiguous()
	        intweight = intweight.numpy().astype(np.uint32)
	        qweight = np.zeros(
            (intweight.shape[0] // 256 * (self.bits * 8), intweight.shape[1]), dtype=np.uint32
	        )
	        i = 0
	        row = 0
	        while row < qweight.shape[0]:
	            if self.bits in [2,4,8]:
	                for j in range(i, i + (32//self.bits)):
	                    qweight[row] |= intweight[j] << (self.bits * (j - i))
	                i += 32//self.bits
	                row += 1
            elif self.bits == 3:
	                for j in range(i, i + 10):
	                    qweight[row] |= intweight[j] << (3 * (j - i))
	                i += 10
	                qweight[row] |= intweight[i] << 30
	                row += 1
	                qweight[row] |= (intweight[i] >> 2) & 1
	                i += 1
	                for j in range(i, i + 10):
	                    qweight[row] |= intweight[j] << (3 * (j - i) + 1)
                i += 10
	                qweight[row] |= intweight[i] << 31
	                row += 1
	                qweight[row] |= (intweight[i] >> 1) & 0x3
	                i += 1
	                for j in range(i, i + 10):
	                    qweight[row] |= intweight[j] << (3 * (j - i) + 2)
	                i += 10
	                row += 1
	            else:
                raise NotImplementedError("Only 2,3,4,8 bits are supported.")
	        qweight = qweight.astype(np.int32)
	        self.qweight = torch.from_numpy(qweight) 
	    def forward(self, x):
	        outshape = list(x.shape)
	        x = x.reshape(-1, x.shape[-1])
	        y = self.bias.clone().repeat(x.shape[0],1)
	        outshape[-1] = self.bias.numel()
	        dtype = x.dtype
	        x = x.float()
        if self.bits == 2:
	            quant_cuda.vecquant2matmul(x, self.qweight, y, self.scales, self.zeros)
	        elif self.bits == 3:
	            quant_cuda.vecquant3matmul(x, self.qweight, y, self.scales, self.zeros)
	        elif self.bits == 4:
	            quant_cuda.vecquant4matmul(x, self.qweight, y, self.scales, self.zeros)
	        elif self.bits == 8:
	            quant_cuda.vecquant8matmul(x, self.qweight, y, self.scales, self.zeros)
	        else:
	            raise NotImplementedError("Only 2,3,4,8 bits are supported.")
        y = y.to(dtype)
	        return y.reshape(outshape)
	def make_quant(module, names, bits, name=''):
	    if isinstance(module, QuantLinear):
	        return
	    for attr in dir(module):
	        tmp = getattr(module, attr)
	        name1 = name + '.' + attr if name != '' else attr
	        if name1 in names:
	            setattr(
                module, attr, QuantLinear(bits, tmp.in_features, tmp.out_features)
	            )
	    for name1, child in module.named_children():
	        make_quant(child, names, bits, name + '.' + name1 if name != '' else name1)

from transformers.models.llama.modeling_llama import LlamaDecoderLayer
	from torch.utils.checkpoint import checkpoint
	from torch.autograd import Variable
	import torch
	from torch import nn
	import numpy as np
	class NewForward:
	    def __init__(self, layer):
	        self.layer = layer
	        self.apply_patch()
    def apply_patch(self):
	        self.layer.old_forward_for_cp = self.layer.forward
	        self.layer.forward = self.new_forward
	    def new_forward(self, *args, **kwargs):
	        def func(*args):
	            return self.layer.old_forward_for_cp(*args, **kwargs)
	        output = checkpoint(func, *args)
	        return output
	class VarWrapper:
	    def __init__(self, model):
        self.model = model
	        self.apply_patch()
	        print('Var Wrapper Patch Applied')
	    def apply_patch(self):
	        self.model.old_forward_for_cp = self.model.forward
	        self.model.forward = self.new_forward
	    def new_forward(self, *args, **kwargs):
	        out = self.model.old_forward_for_cp(*args, **kwargs)
	        out = Variable(out.data, requires_grad=True)
	        return out
def apply_gradient_checkpointing(model, checkpoint_ratio=1):
	    new_forwards = []
	    modules = []
	    for n, m in model.named_modules():
	        if isinstance(m, LlamaDecoderLayer):
	            modules.append(m)
	    if checkpoint_ratio < 1 and checkpoint_ratio > 0:
	        checkpoint_locs = np.array((np.linspace(0, 1, int(len(modules) * checkpoint_ratio)) * (len(modules)-1)).round(), dtype=int)
	    else:
	        checkpoint_locs = np.arange(len(modules))
    for i in checkpoint_locs:
	        m = modules[i]
	        new_forwards.append(NewForward(m))
	        print('Forward Patch Applied For Block {}'.format(i))
	    for n, m in model.named_modules():
	        if isinstance(m, torch.nn.Embedding):
	            wrapper = VarWrapper(m)
	            break
	    return new_forwards, wrapper

