	        warp = src.warp
	        if not isinstance(warp, nodes.UnconditionalWarp):
	            continue
	        if warp.type != nodes.UnconditionalWarp.T_FLOW:
	            continue
	        assert blocks.index(src) == i - 1
	        # Move the to-be-deleted block's contents over
	        src.contents += block.contents
	        src.warp = block.warp
