	    # ##
	    def _visit_node(self, handler, node):
	        handler(node)
	    def _leave_node(self, handler, node):
        handler(node)
	    # ##
	    def _visit(self, node):
	        assert node is not None
	        node._accept(self)
	    def _visit_list(self, nodes_list):
	        assert isinstance(nodes_list, list)
	        for node in nodes_list:
	            self._visit(node)
	def traverse(visitor: Visitor, node):
    if isinstance(node, list):
	        visitor._visit_list(node)
	    else:
	        visitor._visit(node)

import ljd.ast.nodes as nodes
	_printers = {}
	_indent_unit = '\t'
	def dump(name, obj, level=0, **kwargs):
	    indent = level * _indent_unit
	    if name is not None:
	        prefix = indent + name + " = "
	    else:
	        prefix = indent
	    if isinstance(obj, (int, float, str)):
        print(prefix + str(obj))
	    elif isinstance(obj, list):
	        if len(obj) == 0:
	            print(prefix + "[]")
	            return
	        print(prefix + "[")
	        for value in obj:
	            dump(None, value, level + 1)
	        print(indent + "]")
	    elif isinstance(obj, dict):
        print(prefix + "{")
	        for key, value in obj.items():
	            dump(key, value, level + 1)
	        print(indent + "}")
	    elif type(obj) in _printers:
	        _printers[type(obj)](obj, prefix, level, **kwargs)
	    else:
	        _print_default(obj, prefix, level)
	def _print_default(obj, prefix, level, exclude_blocks=False, extra_attrs=None):
	    header_keys = _header(prefix, obj, attrs=extra_attrs)
    for key in dir(obj):
	        if key.startswith("__") or key in header_keys:
	            continue
	        val = getattr(obj, key)
	        # Exclude methods, they're of no use
	        if callable(val):
	            continue
	        # Definitely don't show the blocks, otherwise it explodes the output
	        if exclude_blocks and isinstance(val, nodes.Block):
	            print(_indent_unit * (level + 1) + key + " = Block[index=%d]" % val.index)
            continue
	        dump(key, val, level + 1)
	def _header(prefix, obj, attrs=None, suffix="", **values):
	    if attrs is None:
	        attrs = ["_addr", "_line"]
	    for name in attrs:
	        if not hasattr(obj, name):
	            continue
	        pretty_name = name.lstrip("_")
	        values[pretty_name] = getattr(obj, name)
    if len(values) == 0:
	        attr_block = ""
	    else:
	        attr_block = "[%s]" % ", ".join(["%s=%s" % (k, v) for k, v in values.items()])
	    if suffix != "":
	        suffix = ": " + str(suffix)
	    print(prefix + type(obj).__name__ + attr_block + suffix)
	    return attrs
	def _printer(klass):
	    def wrapper(func):
        _printers[klass] = func
	        return func
	    return wrapper
	# TODO add some way to show the _addr elements of all these
	@_printer(nodes.Identifier)
	def _print_str(obj, prefix, level):
	    print(prefix + str(obj))
	@_printer(nodes.Constant)
	def _print_const(obj: nodes.Constant, prefix, level):
	    s = '"' + obj.value + '"' if obj.type == nodes.Constant.T_STRING else obj.value
    _header(prefix, obj, suffix=s)
	@_printer(nodes.Assignment)
	def _print_assn(obj: nodes.Assignment, prefix, level):
	    print(prefix + "Assignment[type=%s]" % ["T_LOCAL_DEFINITION", "T_NORMAL"][obj.type])
	    dump("dest", obj.destinations, level + 1, omit_single=True)
	    dump("expr", obj.expressions, level + 1, omit_single=True)
	@_printer(nodes.VariablesList)
	@_printer(nodes.ExpressionsList)
	@_printer(nodes.StatementsList)
	def _print_list(obj: nodes.VariablesList, prefix: str, level, omit_single=False):
    name = prefix[:-2].strip()  # chop out the = and strip it to recover the name
	    if len(obj.contents) == 0:
	        print(prefix + type(obj).__name__ + "[empty]")
	    elif len(obj.contents) == 1:
	        if omit_single:
	            dump(name, obj.contents[0], level)
	        else:
	            print(prefix + type(obj).__name__ + "[single]: ", end='')
	            dump(None, obj.contents[0], 0)
	    else:
        print(prefix + type(obj).__name__ + "[")
	        for value in obj.contents:
	            dump(None, value, level + 1)
	        print(_indent_unit * level + "]")
	@_printer(nodes.GetItem)
	def _print_table_elem(obj: nodes.GetItem, prefix, level):
	    print(prefix + "GetItem")
	    dump("table", obj.table, level + 1)
	    dump("key", obj.key, level + 1)
	@_printer(nodes.UnconditionalWarp)
@_printer(nodes.ConditionalWarp)
	@_printer(nodes.IteratorWarp)
	@_printer(nodes.NumericLoopWarp)
	@_printer(nodes.EndWarp)
	def _print_warp(obj, prefix, level):
	    _print_default(obj, prefix, level, exclude_blocks=True)
	@_printer(nodes.Block)
	def _print_block(obj, prefix, level):
	    _print_default(obj, prefix, level, extra_attrs=["index", "first_address", "last_address"])

import ljd.ast.nodes as nodes
	import ljd.ast.traverse as traverse
	def insert_table_record(constructor, key, value, replace, allow_duplicates=True):
	    array = constructor.array.contents
	    records = constructor.records.contents
	    if isinstance(key, nodes.MULTRES):
	        assert len(records) == 0 \
	               or isinstance(records[-1], nodes.TableRecord)
	        records.append(value)
	        return True
    while isinstance(key, nodes.Constant) \
	            and key.type == key.T_INTEGER \
	            and key.value >= 0:
	        index = key.value
	        if index == 1 and len(array) == 0:
	            record = nodes.ArrayRecord()
	            record.value = nodes.Primitive()
	            record.value.type = nodes.Primitive.T_NIL
	            array.append(record)
	        if index > len(array):
            break
	        record = nodes.ArrayRecord()
	        record.value = value
	        if len(array) == 0 or index == len(array):
	            array.append(record)
	            return True
	        elif replace:
	            array[index] = record
	            return True
	        else:
            current_value = array[index].value
	            if isinstance(current_value, nodes.Primitive) and current_value.type == nodes.Primitive.T_NIL:
	                array[index] = record
	                return True
	            return False
	    # Check for record duplicates
	    # This isn't nearly as important as duplicate protection with arrays, since both values
	    # end up in the table to the user can make sense of what happened. Nonetheless, we should still
	    # reject stuff like this.
	    if not allow_duplicates:
        for rec in records:
	            if isinstance(rec, nodes.TableRecord):
	                if is_equal(rec.key, key, strict=False):
	                    return False
	    record = nodes.TableRecord()
	    record.key = key
	    record.value = value
	    if len(records) == 0:
	        records.append(record)
	        return True
    last = records[-1]
	    if isinstance(last, (nodes.FunctionCall, nodes.Vararg)):
	        records.insert(-1, record)
	    else:
	        records.append(record)
	    return True
	def has_same_table(node, table):
	    class Checker(traverse.Visitor):
	        def __init__(self, checker_table):
	            super().__init__()
            self.found = False
	            self.table = checker_table
	            self.current_function_depth = 0
	        def visit_table_element(self, checked_node):
	            if is_equal(self.table, checked_node.table):
	                self.found = True
	        def visit_function_definition(self, node):
	            self.current_function_depth += 1
	        def leave_function_definition(self, node):
	            self.current_function_depth -= 1
        def visit_identifier(self, node):
	            if self.current_function_depth > 0 and node.type == node.T_UPVALUE:
	                if getattr(self.table, "name", False) == node.name:  # Use False to avoid matches on None
	                    self.found = True
	        def _visit(self, checked_node):
	            if not self.found:
	                traverse.Visitor._visit(self, checked_node)
	        def _visit_list(self, nodes_list):
	            if not self.found:
	                traverse.Visitor._visit_list(self, nodes_list)
    checker = Checker(table)
	    traverse.traverse(checker, node)
	    return checker.found
	def is_equal(a, b, strict=True):
	    if type(a) != type(b):
	        return False
	    if isinstance(a, nodes.Identifier):
	        return a.type == b.type and a.slot == b.slot
	    elif isinstance(a, nodes.GetItem):
	        return is_equal(a.table, b.table, strict) \
               and is_equal(a.key, b.key, strict)
	    elif isinstance(a, nodes.Constant):
	        return a.type == b.type and a.value == b.value
	    else:
	        assert not strict
	        return False
