import torch
	import torch.nn.functional as F
	from torch import nn, Tensor
	from typing import Iterable, Dict, Tuple
	from ..Framework import Framework
	class LossManager(nn.Module):
	    def __init__(self, 
	                 model: Framework, 
	                 loss_mse_scale: float = 1.0, 
	                 loss_at_teacher_scale: float = 0.0, 
                 loss_at_student_scale: float = 0.0, 
	                 loss_contrastive_scale: float = 0.0,
	                 ):
	        """
	        :param model: Framework based on sentence-transformers
	        :loss_mse_scale: The scale of MSE loss between teacher's and student's sentence embeddings
	        :loss_at_teacher_scale: The scale of cross-entropy loss of decoding on teacher's sentence embeddings (translation)
	        :loss_at_teacher_scale: The scale of cross-entropy loss of decoding on student's sentence embeddings (auto-encoding)
	        :loss_contrastive_scale: The scale of contrastive loss between teacher's and student's sentence embeddings
	        """
        super(LossManager, self).__init__()
	        self.model = model
	        self.loss_fct = nn.MSELoss()
	        self.loss_mse_scale = loss_mse_scale
	        self.loss_at_teacher_scale = loss_at_teacher_scale
	        self.loss_at_student_scale = loss_at_student_scale
	        self.loss_contrastive_scale = loss_contrastive_scale
	        if loss_contrastive_scale > 0:
	            self.temp = nn.Parameter(torch.ones([]) * 0.07) # identical to CLIP
	    def forward(self, sentence_features: Iterable[Dict[str, Tensor]], labels: Tensor) -> Tuple[Tensor, Dict[str, float]]:
        outputs = self.model(sentence_features)
	        loss, loss_msg_dict = 0, {}
	        for name in ['mse', 'at_teacher', 'at_student', 'contrastive']:
	            this_loss, this_dict = getattr(self, f'forward_{name}')(outputs, labels)
	            loss += this_loss
	            loss_msg_dict.update(this_dict)
	        return loss, loss_msg_dict
	    def forward_mse(self, 
	            outputs: Dict[str, Tensor], 
	            labels: Tensor,
            name: str = 'loss_mse'
	            ) -> Tuple[Tensor, Dict[str, float]]:
	        """
	        Computes the MSE loss between the computed sentence embedding and a target sentence embedding. This loss
	        is used when extending sentence embeddings to new languages as described in our publication
	        Making Monolingual Sentence Embeddings Multilingual using Knowledge Distillation: https://arxiv.org/abs/2004.09813
	        For an example, see the documentation on extending language models to new languages.
	        """
	        if self.loss_mse_scale > 0:
	            loss = self.loss_fct(outputs['sentence_embedding'], labels)
            return self.loss_mse_scale * loss, {name: loss.detach().cpu().item()}
	        return 0, {}
	    def forward_at_teacher(self, 
	            outputs: Dict[str, Tensor], 
	            labels: Tensor,
	            name: str = 'loss_at_teacher',
	            ) -> Tuple[Tensor, Dict[str, float]]:
	        if self.loss_at_teacher_scale > 0:
	            loss = outputs['loss_at_teacher']
	            return self.loss_at_teacher_scale * loss, {name: loss.detach().cpu().item()}
        return 0, {}
	    def forward_at_student(self,
	            outputs: Dict[str, Tensor], 
	            labels: Tensor,
	            name: str = 'loss_at_student',
	            ) -> Tuple[Tensor, Dict[str, float]]:
	        if self.loss_at_student_scale > 0:
	            loss = outputs['loss_at_student']
	            return self.loss_at_student_scale * loss, {name: loss.detach().cpu().item()}
	        return 0, {}
    def forward_contrastive(self,
	            outputs: Dict[str, Tensor], 
	            labels: Tensor,
	            name: str = 'loss_cl'
	            ) -> Tuple[Tensor, Dict[str, float]]:
	        if self.loss_contrastive_scale > 0:
	            with torch.no_grad():
	                self.temp.clamp_(0.001, 0.5)
	            feats_student = F.normalize(outputs['sentence_embedding'], dim=-1)
	            feats_teacher = F.normalize(labels, dim=-1)
            logits_s2t = feats_student @ feats_teacher.t() / self.temp
	            logits_t2s = feats_teacher @ feats_student.t() / self.temp
	            cl_labels = torch.arange(logits_s2t.size(0), device=logits_s2t.device)
	            loss_s2t = F.cross_entropy(logits_s2t, cl_labels, reduction='mean')
	            loss_t2s = F.cross_entropy(logits_t2s, cl_labels, reduction='mean')
	            loss = (loss_s2t + loss_t2s) / 2
	            return self.loss_contrastive_scale * loss, {
	                name: loss.detach().cpu().item(),
	                'temp': self.temp.detach().cpu().item(),
	            }
        return 0, {}

import time
	import datetime
	import numpy as np
	import torch
	import torch.distributed as dist
	from collections import defaultdict, deque
	from .distributed import is_dist_avail_and_initialized
	class SmoothedValue(object):
	    """Track a series of values and provide access to smoothed values over a
	    window or the global series average.
    """
	    def __init__(self, window_size=20, fmt=None):
	        if fmt is None:
	            fmt = "{median:.4f} ({global_avg:.4f})"
	        self.deque = deque(maxlen=window_size)
	        self.total = 0.0
	        self.count = 0
	        self.fmt = fmt
	    def update(self, value, n=1):
	        if np.isnan(value):
            # There occurs gradient overflow in apex.amp
	            return
	        self.deque.append(value)
	        self.count += n
	        self.total += value * n
	    def synchronize_between_processes(self):
	        """
	        Warning: does not synchronize the deque!
	        """
	        if not is_dist_avail_and_initialized():
            return
	        t = torch.tensor([self.count, self.total], dtype=torch.float64, device='cuda')
	        dist.barrier()
	        dist.all_reduce(t)
	        t = t.tolist()
	        self.count = int(t[0])
	        self.total = t[1]
	    @property
	    def median(self):
	        if len(self.deque) == 0:
            return 0
	        d = torch.tensor(list(self.deque))
	        return d.median().item()
	    @property
	    def avg(self):
	        if len(self.deque) == 0:
	            return 0
	        d = torch.tensor(list(self.deque), dtype=torch.float32)
	        return d.mean().item()
	    @property
    def global_avg(self):
	        if self.count == 0:
	            return 0
	        return self.total / self.count
	    @property
	    def max(self):
	        if len(self.deque) == 0:
	            return 0
	        return max(self.deque)
	    @property
    def value(self):
	        if len(self.deque) == 0:
	            return 0
	        return self.deque[-1]
	    def __str__(self):
	        return self.fmt.format(
	            median=self.median,
	            avg=self.avg,
	            global_avg=self.global_avg,
	            max=self.max,
            value=self.value)
	class MetricLogger(object):
	    def __init__(self, delimiter="\t", window_size=1, fmt='{value:.4f}'):
	        self.meters = defaultdict(SmoothedValue)
	        self.delimiter = delimiter
	        self.window_size = window_size
	        self.fmt = fmt
	    def update(self, **kwargs):
	        for k, v in kwargs.items():
	            if k not in self.meters:
                self.add_meter(k, SmoothedValue(window_size=self.window_size, fmt=self.fmt))
	            if isinstance(v, torch.Tensor):
	                v = v.item()
	            assert isinstance(v, (float, int))
	            self.meters[k].update(v)
	    def __getattr__(self, attr):
	        if attr in self.meters:
	            return self.meters[attr]
	        if attr in self.__dict__:
	            return self.__dict__[attr]
        raise AttributeError("'{}' object has no attribute '{}'".format(
	            type(self).__name__, attr))
	    def __str__(self):
	        loss_str = []
	        for name, meter in self.meters.items():
	            loss_str.append(
	                "{}: {}".format(name, str(meter))
	            )
	        return self.delimiter.join(loss_str)
	    def global_avg(self):
        loss_str = []
	        for name, meter in self.meters.items():
	            loss_str.append(
	                "{}: {:.4f}".format(name, meter.global_avg)
	            )
	        return self.delimiter.join(loss_str)
	    def synchronize_between_processes(self):
	        for meter in self.meters.values():
	            meter.synchronize_between_processes()
	    def add_meter(self, name, meter):
        self.meters[name] = meter
	    def log_every(self, loader, print_freq, header=None):
	        if not header:
	            header = ''
	        dataset_len = len(loader)
	        start_time = time.time()
	        end = time.time()
	        iter_time = SmoothedValue(fmt='{avg:.4f}')
	        data_time = SmoothedValue(fmt='{avg:.4f}')
	        space_fmt = ':' + str(len(str(dataset_len))) + 'd'
        _msg = [
	            '[{0' + space_fmt + '}/{1}]',
	            'eta: {eta}',
	            '{meters}',
	            'time: {time}',
	            'data: {data}'
	        ]
	        if torch.cuda.is_available():
	            _msg.append('max mem: {memory:.0f}')
	        _msg = self.delimiter.join(_msg)
        MB = 1024.0 * 1024.0
	        iterable = iter(loader)
	        total_train_steps = dataset_len
	        start_step = 0
	        for i in range(start_step, total_train_steps):
	            obj = next(iterable)
	            data_time.update(time.time() - end)
	            yield obj
	            iter_time.update(time.time() - end)
	            log_msg = header + " " + _msg
            if (i % dataset_len) % print_freq == 0 or i == dataset_len - 1:
	                eta_seconds = iter_time.global_avg * (dataset_len - i % dataset_len)
	                eta_string = str(datetime.timedelta(seconds=int(eta_seconds)))
	                if torch.cuda.is_available():
	                    print(log_msg.format(
	                        i % dataset_len, dataset_len, eta=eta_string,
	                        meters=str(self),
	                        time=str(iter_time), data=str(data_time),
	                        memory=torch.cuda.max_memory_allocated() / MB))
	                else:
                    print(log_msg.format(
	                        i % dataset_len, dataset_len, eta=eta_string,
	                        meters=str(self),
	                        time=str(iter_time), data=str(data_time)))
	            end = time.time()
	        total_time = time.time() - start_time
	        total_time_str = str(datetime.timedelta(seconds=int(total_time)))
	        print('{} Total time: {} ({:.4f} s / it)'.format(
	            header, total_time_str, total_time / dataset_len))

import os
	import torch
	import torch.distributed as dist
	def setup_for_distributed(is_master):
	    """
	    This function disables printing when not in master process
	    """
	    import builtins as __builtin__
	    builtin_print = __builtin__.print
	    def print(*args, **kwargs):
        force = kwargs.pop('force', False)
	        if is_master or force:
	            builtin_print(*args, **kwargs)
	    __builtin__.print = print
	def is_dist_avail_and_initialized():
	    if not dist.is_available():
	        return False
	    if not dist.is_initialized():
	        return False
	    return True
def get_world_size():
	    if not is_dist_avail_and_initialized():
	        return 1
	    return dist.get_world_size()
	def get_rank():
	    if not is_dist_avail_and_initialized():
	        return 0
	    return dist.get_rank()
	def is_main_process():
	    return get_rank() == 0
def save_on_master(*args, **kwargs):
	    if is_main_process():
	        torch.save(*args, **kwargs)
	def init_distributed_mode(args):
	    if 'RANK' in os.environ and 'WORLD_SIZE' in os.environ:
	        args.rank = int(os.environ["RANK"])
	        args.world_size = int(os.environ['WORLD_SIZE'])
	        args.gpu = int(os.environ['LOCAL_RANK'])
	    elif 'SLURM_PROCID' in os.environ:
	        args.rank = int(os.environ['SLURM_PROCID'])
        args.gpu = args.rank % torch.cuda.device_count()
	    else:
	        print('Not using distributed mode')
	        args.distributed = False
	        return
	    args.distributed = True
	    torch.cuda.set_device(args.gpu)
	    args.dist_backend = 'nccl'
	    print('| distributed init (rank {}): {}'.format(
	        args.rank, args.dist_url), flush=True)
    torch.distributed.init_process_group(backend=args.dist_backend, init_method=args.dist_url,
	                                         world_size=args.world_size, rank=args.rank)
	    torch.distributed.barrier()
	    setup_for_distributed(args.rank == 0)

import os
	import json
	import torch.distributed as dist
	from .distributed import (
	    is_dist_avail_and_initialized, 
	    is_main_process, 
	    get_rank, 
	    get_world_size,
	)
	def read_json(rpath: str):
    result = []
	    with open(rpath, 'rt') as f:
	        for line in f:
	            result.append(json.loads(line.strip()))
	    return result
	def write_json(result: list, wpath: str):
	    with open(wpath, 'wt') as f:
	        for res in result:
	            f.write(json.dumps(res) + '\n')
	def collect_result(result, filename, local_wdir, save_result=False, remove_duplicate='', do_not_collect=False):
    assert isinstance(result, list)
	    write_json(result, os.path.join(local_wdir,'%s_rank%d.json' % (filename, get_rank())))
	    if is_dist_avail_and_initialized():
	        dist.barrier()
	    if do_not_collect:
	        return None
	    result = []
	    final_result_file = ''
	    if is_main_process():
	        # combine results from all processes
        for rank in range(get_world_size()):
	            result += read_json(os.path.join(local_wdir, '%s_rank%d.json' % (filename, rank)))
	        if remove_duplicate:  # for evaluating captioning tasks
	            result_new = []
	            id_list = set()
	            for res in result:
	                if res[remove_duplicate] not in id_list:
	                    id_list.add(res[remove_duplicate])
	                    result_new.append(res)
	            result = result_new
        if save_result:
	            final_result_file = os.path.join(local_wdir, '%s.json' % filename)
	            json.dump(result, open(final_result_file, 'w'), indent=4)
	            print('result file saved to %s' % final_result_file)
	    if is_dist_avail_and_initialized():
	        dist.barrier()
	    return final_result_file if save_result else result
	def get_cache_folder(cache_folder: str=None):
	    if cache_folder is None:
	        cache_folder = os.getenv('ZERONLG_HOME')
        if cache_folder is None:
	            try:
	                from torch.hub import _get_torch_home
	                torch_cache_home = _get_torch_home()
	            except ImportError:
	                torch_cache_home = os.path.expanduser(os.getenv('TORCH_HOME', os.path.join(os.getenv('XDG_CACHE_HOME', '~/.cache'), 'torch')))
	            cache_folder = os.path.join(torch_cache_home, 'zeronlg')
	    return cache_folder
	def get_formatted_string(kwargs, key, assigned_keys=None, assigned_kwargs=None, format_key=None) -> str:
	    if format_key is None:
        format_key = f'{key}_format'
	    if kwargs.get(key, None) is None:
	        assert format_key in kwargs
	        if assigned_kwargs is None:
	            assert assigned_keys is not None
	            assigned_kwargs = {k: kwargs[k] for k in assigned_keys}
	        return kwargs[format_key].format(**assigned_kwargs)
	    return kwargs[key]

import os
	import json
	import transformers
	import torch
	from torch import nn
	from PIL import Image
	# Adapted from sentence_transformers.models.CLIPModel
	# (https://github.com/UKPLab/sentence-transformers/blob/master/sentence_transformers/models/CLIPModel.py)
	class CLIPModel(nn.Module):
	    def __init__(self,  model_name: str = "openai/clip-vit-base-patch32", processor_name = None, use_clip_tokens: bool = False):
        """
	        Yang B. modification: 
	        1) add truncation=True and max_length=77 in CLIPModel.tokenize to avoid bugs
	        2) add function: get_word_embedding_dimension and get_sentence_embedding_dimension
	        3) add an extra argument `use_clip_tokens` to return token-level embeddings in addition to the global-level vector
	        4) save configs for re-loading
	        """
	        super(CLIPModel, self).__init__()
	        self.config_keys = ['use_clip_tokens']
	        if processor_name is None:
            processor_name = model_name
	        self.model = transformers.CLIPModel.from_pretrained(model_name)
	        self.processor = transformers.CLIPProcessor.from_pretrained(processor_name)
	        self.use_clip_tokens = use_clip_tokens
	    def __repr__(self):
	        return "CLIPModel({})".format(self.get_config_dict())
	    def forward(self, features):
	        image_embeds = []
	        text_embeds = []
	        if 'pixel_values' in features:
            vision_outputs = self.model.vision_model(pixel_values=features['pixel_values'], return_dict=False)
	            last_hidden_state, pooled_output, *_ = vision_outputs
	            image_embeds = self.model.visual_projection(pooled_output)
	            if self.use_clip_tokens:
	                image_token_embeds = self.model.visual_projection(last_hidden_state)
	        if 'input_ids' in features:
	            text_outputs = self.model.text_model(
	                input_ids=features.get('input_ids'),
	                attention_mask=features.get('attention_mask', None),
	                position_ids=features.get('position_ids', None),
                output_attentions=features.get('output_attentions', None),
	                output_hidden_states=features.get('output_hidden_states', None),
	                return_dict=False,
	            )
	            last_hidden_state, pooled_output, *_ = text_outputs
	            text_embeds = self.model.text_projection(pooled_output)
	            if self.use_clip_tokens:
	                text_token_embeds = self.model.text_projection(last_hidden_state)
	        sentence_embedding = []
	        image_features = iter(image_embeds)
        text_features = iter(text_embeds)
	        for idx, input_type in enumerate(features['image_text_info']):
	            if input_type == 0:
	                sentence_embedding.append(next(image_features))
	            else:
	                sentence_embedding.append(next(text_features))
	        features['sentence_embedding'] = torch.stack(sentence_embedding).float()
	        if self.use_clip_tokens:
	            prev_input_type = None
	            for input_type in features['image_text_info']:
                if prev_input_type is None:
	                    prev_input_type = input_type
	                else:
	                    assert prev_input_type == input_type
	            # (batch_size, num_tokens, D)
	            if prev_input_type == 0:
	                features['token_embeddings'] = image_token_embeds
	                features['attention_mask'] = torch.ones(*image_token_embeds.shape[:2])
	            else:
	                features['token_embeddings'] = text_token_embeds
        return features
	    def tokenize(self, texts):
	        images = []
	        texts_values = []
	        image_text_info = []
	        for idx, data in enumerate(texts):
	            if isinstance(data, Image.Image):  # An Image
	                images.append(data)
	                image_text_info.append(0)
	            else:  # A text
                texts_values.append(data)
	                image_text_info.append(1)
	        if len(texts_values) == 0:
	            texts_values = None
	        if len(images) == 0:
	            images = None
	        inputs = self.processor(text=texts_values, images=images, return_tensors="pt", padding=True, truncation=True, max_length=77)
	        inputs['image_text_info'] = image_text_info
	        return inputs
	    def get_word_embedding_dimension(self) -> int:
        return self.model.text_embed_dim
	    def get_sentence_embedding_dimension(self) -> int:
	        return self.model.projection_dim
	    def get_config_dict(self):
	        return {key: self.__dict__[key] for key in self.config_keys}
	    def save(self, output_path: str):
	        self.model.save_pretrained(output_path)
	        self.processor.save_pretrained(output_path)
	        with open(os.path.join(output_path, 'clip_config.json'), 'w') as fOut:
	            json.dump(self.get_config_dict(), fOut, indent=2)
    @staticmethod
	    def load(input_path: str):
	        config = {}
	        config_path = os.path.join(input_path, 'clip_config.json')
	        if os.path.exists(config_path):
	            config = json.load(open(config_path))
	        return CLIPModel(model_name=input_path, **config)

import os
	import logging
	import torch
	import numpy as np
	import json
	import pickle
	import decord
	from sentence_transformers import LoggingHandler
	from torch.utils.data import Dataset
	from PIL import Image
from .. import Framework
	from ..utils import get_uniform_frame_ids
	logging.basicConfig(format='%(asctime)s - %(message)s',
	                    datefmt='%Y-%m-%d %H:%M:%S',
	                    level=logging.INFO,
	                    handlers=[LoggingHandler()])
	global_logger = logging.getLogger(__name__)
	class CaptionDataset(Dataset):
	    def __init__(self, 
	                 vision_root: str, 
                 ann_rpath: str, 
	                 num_frames: int = 8, 
	                 lang: str = None, 
	                 clip_model: Framework = None, 
	                 pickle_path: str = None, 
	                 logger: logging.Logger = None, 
	                 return_images: bool = False, 
	                 mean_pooling: bool = False,
	                 ):
	        if return_images:
            assert pickle_path is None
	        else:
	            assert clip_model is not None
	        self.vision_root = vision_root
	        self.lang = lang
	        self.num_frames = num_frames
	        self.clip_model = clip_model
	        self.logger = logger or global_logger
	        self.return_images = return_images
	        self.mean_pooling = mean_pooling
        self.annotation = json.load(open(ann_rpath, 'r'))
	        assert 'image' in self.annotation[0], f'{self.annotation[0]} does not contain the key `image`'
	        self.pickle_path = pickle_path
	        self.has_been_updated = False
	        if pickle_path is not None and os.path.exists(pickle_path):
	            self.log(f'Load CLIP embs from {pickle_path}')
	            self.rpath2emb = pickle.load(open(pickle_path, 'rb'))
	        else:
	            self.log(f'CLIP embs does not exist: {pickle_path}')
	            self.rpath2emb = {}
        self.rpath2images = {}
	    def get_item(self, image_id):
	        for index, ann in enumerate(self.annotation):
	            if ann['image_id'] == image_id:
	                return self.__getitem__(index)
	    def __len__(self):
	        return len(self.annotation)
	    def __getitem__(self, index):
	        out = {}
	        ann = self.annotation[index]
        out['image_id'] = ann['image_id']
	        if 'caption' in ann:
	            out['text'] = ann['caption']
	        rpath = ann['image']
	        if rpath not in self.rpath2emb:
	            self.has_been_updated = True
	            try:
	                image_path = os.path.join(self.vision_root, rpath)
	                image = Image.open(image_path).convert('RGB')
	                images = [image]
            except:
	                video_path = os.path.join(self.vision_root, rpath)
	                reader = decord.VideoReader(video_path)
	                images = reader.get_batch(get_uniform_frame_ids(len(reader), self.num_frames)).asnumpy()
	                images = [Image.fromarray(image) for image in images]
	            if self.clip_model is not None:
	                output_value = 'token_embeddings' if self.clip_model.get_module_attribute('use_clip_tokens', False) else 'sentence_embedding'
	                emb = self.clip_model.encode(images, output_value=output_value, show_progress_bar=False)
	                if isinstance(emb, list):
	                    emb = torch.stack(emb, dim=0).cpu().numpy()
                out['emb'] = emb
	                self.rpath2emb[rpath] = emb
	            if self.return_images:
	                out['images'] = images
	                if self.clip_model is not None:
	                    self.rpath2images[rpath] = images
	        else:
	            out['emb'] = self.rpath2emb[rpath]
	            if self.return_images:
	                out['images'] = self.rpath2images[rpath]
        out['lang'] = self.lang
	        return out
	    def log(self, msg):
	        self.logger.info(msg)
	    def save_pickle(self):
	        if self.has_been_updated and self.pickle_path is not None:
	            self.log(f'Save CLIP embs to {self.pickle_path}')
	            with open(self.pickle_path, 'wb') as wf:
	                pickle.dump(self.rpath2emb, wf)
	        self.has_been_updated = False
    def collate_fn(self, batch):
	        out = {}
	        for key in batch[0].keys():
	            out[key] = [b[key] for b in batch]
	        image_ids = [b['image_id'] for b in batch]
	        if 'emb' in batch[0]:
	            embs = torch.FloatTensor(np.array([b['emb'] for b in batch]))
	            if self.mean_pooling:
	                embs = embs.mean(1, keepdims=True)
	            return image_ids, embs
        else:
	            images = [b['images'] for b in batch]
	            return image_ids, images
	class CaptionDatasetForRetrieval(CaptionDataset):
	    def __init__(self, 
	                vision_root: str, 
	                ann_rpath: str, 
	                num_frames: int = 8, 
	                lang: str = None, 
	                clip_model: Framework = None, 
                pickle_path: str = None, 
	                logger: logging.Logger = None, 
	                return_images: bool = False, 
	                mean_pooling: bool = False,
	                ):
	        super().__init__(
	            vision_root=vision_root, 
	            ann_rpath=ann_rpath, 
	            num_frames=num_frames, 
	            lang=lang, 
            clip_model=clip_model, 
	            pickle_path=pickle_path, 
	            logger=logger, 
	            return_images=return_images, 
	            mean_pooling=True, # TODO: we now always apply mean pooling
	        )
	    def collate_fn(self, batch):
	        out = {}
	        for key in batch[0].keys():
	            out[key] = [b[key] for b in batch]
        image_ids = [b['image_id'] for b in batch]
	        texts = []
	        for b in batch:
	            if isinstance(b['text'], (list, tuple)):
	                texts.extend(b['text'])
	            else:
	                texts.append(b[texts])
	        if 'emb' in batch[0]:
	            embs = torch.FloatTensor(np.array([b['emb'] for b in batch]))
	            if self.mean_pooling:
                embs = embs.mean(1, keepdims=True)
	            return image_ids, embs, texts
	        else:
	            images = [b['images'] for b in batch]
	            return image_ids, images, texts

