import { error, getInput, info, notice, setFailed } from '@actions/core'
	import {
	  ChatCompletionRequestMessageRoleEnum,
	  Configuration,
	  OpenAIApi,
	} from 'openai'
	import { encode } from 'gpt-3-encoder'
	const API_KEY = getInput('apikey')
	const BASE_PATH = getInput('basePath') || 'https://api.openai.com/v1'
	const PROMPT =
  getInput('prompt') ||
	  'Please translate the given text into naturalistic {targetLanguage}.'
	if (!API_KEY) {
	  setFailed('Error: API_KEY could not be retrieved.')
	}
	const configuration = new Configuration({
	  apiKey: API_KEY,
	  basePath: BASE_PATH,
	})
	const openAIApi = new OpenAIApi(configuration)
export const askGPT = async (text: string, prompt: string): Promise<string> => {
	  const {
	    data: {
	      choices: [{ message: { content: content } = { content: '' } }],
	    },
	  } = await openAIApi
	    .createChatCompletion({
	      model: 'gpt-3.5-turbo-16k',
	      messages: [
	        {
          role: ChatCompletionRequestMessageRoleEnum.System,
	          content: prompt,
	        },
	        { role: ChatCompletionRequestMessageRoleEnum.User, content: text },
	      ],
	      top_p: 0.5,
	    })
	    .catch((err) => {
	      error(err)
	      notice(
        'If the status code is 400, the file exceeds 16,000 tokens without line breaks. \nPlease open one line as appropriate.',
	      )
	      process.exit(1)
	    })
	  if (content === '') {
	    info('Possible Error: Translation result is empty')
	  }
	  return content
	}
	export const gptTranslate = async (
  text: string,
	  targetLanguage: string,
	  targetFileExt: string, // filename extension. Must be within availableFileExtensions.
	  maxToken = 16000,
	  splitter = `\n\n`,
	): Promise<string> => {
	  const prompt = PROMPT.replaceAll(
	    '{targetLanguage}',
	    targetLanguage,
	  ).replaceAll('{targetFileExt}', targetFileExt)
  let translated = ''
	  let chunk = ''
	  info('Start translating...')
	  const contentChunks = text.split(splitter)
	  for (let i = 0; i < contentChunks.length; i++) {
	    if (encode(chunk + contentChunks[i]).length > maxToken) {
	      const translatedContent = await askGPT(chunk, prompt)
	      translated += translatedContent + splitter
	      chunk = ''
	    }
    chunk += contentChunks[i] + (i < contentChunks.length - 1 ? splitter : '')
	  }
	  translated += await askGPT(chunk, prompt)
	  info('Translation completed!')
	  return translated
	}

import fs from 'fs/promises'
	import path from 'path'
	import { gptTranslate } from './gpt'
	import { generatePRBody, isPR } from './utils'
	import {
	  gitCheckout,
	  gitCommitPush,
	  gitCreateBranch,
	  gitCreatePullRequest,
	  gitPostComment,
  gitSetConfig,
	} from './git'
	import { context } from '@actions/github'
	import {
	  createFile,
	  generateOutputFilePaths,
	  getFilePathsWithExtension,
	  isFileExists,
	} from './file'
	import { info } from '@actions/core'
export const translateByCommand = async (
	  inputFilePath: string,
	  outputFilePath: string,
	  targetLang: string,
	) => {
	  await gitSetConfig()
	  const branch = isPR() ? await gitCheckout() : await gitCreateBranch()
	  const isMultipleFileSelected = path.basename(inputFilePath).includes('*')
	  const inputFilePaths = isMultipleFileSelected
	    ? await getFilePathsWithExtension(
        path.dirname(inputFilePath),
	        path.extname(inputFilePath),
	      )
	    : [inputFilePath]
	  const outputFilePaths = isMultipleFileSelected
	    ? generateOutputFilePaths(inputFilePaths, outputFilePath)
	    : [outputFilePath]
	  await createTranslatedFiles(inputFilePaths, outputFilePaths, targetLang)
	  await gitCommitPush(branch, outputFilePaths)
	  if (isPR()) {
    await gitPostComment('ðŸŽ‰Translation completed!')
	    return
	  }
	  const issueNumber = context.issue.number
	  const title = 'ðŸŒ Add LLM Translations'
	  const body = generatePRBody(
	    inputFilePaths,
	    outputFilePaths,
	    targetLang,
	    issueNumber,
  )
	  await gitCreatePullRequest(branch, title, body)
	  await gitPostComment('ðŸŽ‰Translation PR created!')
	}
	export const translateByManual = async (
	  inputFiles: string[],
	  outputFiles: string[],
	  languages: string[],
	) => {
	  if (!inputFiles.length) {
    info('No input files specified. Skip translation.')
	    return
	  }
	  if (!inputFiles.length || !outputFiles.length || !languages.length) {
	    throw new Error(
	      'Error: For push execution, all three parameters (inputFiles, outputFiles and language ) are required',
	    )
	  }
	  if (outputFiles.length !== languages.length) {
	    throw new Error('Error: outputFiles and language must be same length.')
  }
	  const outputFilePaths: string[][] = outputFiles.map((outputFile) => {
	    return generateOutputFilePaths(inputFiles, outputFile)
	  })
	  // TODO: Dealing with request limits (503 error)
	  await Promise.all(
	    languages.map((language, index) =>
	      createTranslatedFiles(inputFiles, outputFilePaths[index], language),
	    ),
	  )
  await gitSetConfig()
	  const branch = await gitCreateBranch()
	  const title = 'ðŸŒ Add LLM Translations'
	  await gitCommitPush(branch, outputFilePaths.flat())
	  const body = generatePRBody(inputFiles, outputFilePaths, languages)
	  await gitCreatePullRequest(branch, title, body)
	}
	/*
	 * Parallel creation of translation files
	 * inputFilePaths and outputFilePaths must be same length and same order
 */
	export const createTranslatedFiles = async (
	  inputFilePaths: string[],
	  outputFilePaths: string[],
	  targetLang: string,
	) => {
	  const processFiles = inputFilePaths.map(async (inputFile, i) => {
	    const content = await fs.readFile(inputFile, 'utf-8')
	    const ext = path.extname(inputFile)
	    const translated = await gptTranslate(content, targetLang, ext)
    // Check if the translation is same as the original
	    if (await isFileExists(outputFilePaths[i])) {
	      const fileContent = await fs.readFile(outputFilePaths[i], 'utf-8')
	      if (fileContent === translated) {
	        info(
	          'â›” The result of translation was same as the existed output file.',
	        )
	        return
	      }
	    }
    info(`Create Translated File ${outputFilePaths[i]}`)
	    await createFile(translated, outputFilePaths[i])
	  })
	  await Promise.all(processFiles)
	}

import { exec } from '@actions/exec'
	import { context, getOctokit } from '@actions/github'
	import { setFailed, info, getInput } from '@actions/core'
	import { randomUUID } from 'crypto'
	const GITHUB_TOKEN = getInput('token')
	if (!GITHUB_TOKEN) {
	  setFailed('Error: GITHUB_TOKEN could not be retrieved.')
	}
	export const gitSetConfig = async () => {
	  info('Setting git config...')
  // TODO: Get command user info from
	  // const { rest } = getOctokit(GITHUB_TOKEN)
	  // const { data: user } = await rest.users.getAuthenticated()
	  const user = {
	    name: 'github-actions[bot]',
	    email: '41898282+github-actions[bot]@users.noreply.github.com',
	  }
	  await exec('git', ['config', 'user.name', user.name])
	  await exec('git', ['config', 'user.email', user.email])
	}
export const gitCheckout = async () => {
	  info('Checking out...')
	  const {
	    rest: { pulls },
	  } = getOctokit(GITHUB_TOKEN)
	  const {
	    data: {
	      head: { ref: branch },
	    },
	  } = await pulls.get({
    ...context.repo,
	    pull_number: context.issue.number,
	  })
	  await exec('git', ['fetch', 'origin', branch])
	  await exec('git', ['checkout', branch])
	  return branch as string
	}
	export const gitCreateBranch = async () => {
	  info('Creating branch...')
	  const branch = `gt-${randomUUID()}`
  await exec('git', ['checkout', '-b', branch])
	  return branch
	}
	export const gitCommitPush = async (
	  branch: string,
	  filePath: string | string[],
	) => {
	  info('Committing and pushing...')
	  if (Array.isArray(filePath)) {
	    await exec('git', ['add', ...filePath])
  } else {
	    await exec('git', ['add', filePath])
	  }
	  await exec('git', ['commit', '-m', `ðŸ’¬Generate LLM translations`])
	  await exec('git', ['push', 'origin', branch])
	}
	export const gitCreatePullRequest = async (
	  head: string,
	  title: string,
	  body: string,
) => {
	  const {
	    rest: { pulls, repos },
	  } = getOctokit(GITHUB_TOKEN)
	  const {
	    data: { default_branch: base },
	  } = await repos.get(context.repo)
	  info(`Creating pull request from ${base} to ${head}...`)
	  await pulls.create({
	    ...context.repo,
    head,
	    base,
	    title,
	    body,
	  })
	}
	export const gitPostComment = async (message: string) => {
	  const {
	    rest: { issues },
	  } = getOctokit(GITHUB_TOKEN)
  await issues.createComment({
	    ...context.repo,
	    issue_number: context.issue.number,
	    body: message,
	  })
	}
	export const authorizeUser = async () => {
	  const {
	    rest: { repos },
	  } = getOctokit(GITHUB_TOKEN)
  const { data: user } = await repos.getCollaboratorPermissionLevel({
	    ...context.repo,
	    username: context.actor,
	  })
	  info(`User permission: ${user.permission}`)
	  return user.permission === 'admin' || user.permission === 'write'
	}

import { availableFileExtensions } from './const'
	import path from 'path'
	import { postError, removeSymbols } from './utils'
	import { COMMAND_USAGE, INVALID_FILE_EXTENSION } from './error'
	export const isValidFileExt = (filename: string): boolean => {
	  // Allow input file extension inheritance by asterisk in addition to normal file formats
	  const fileExts = [...availableFileExtensions, '*']
	  return fileExts.some((type) => filename.endsWith(type))
	}
	export const commandValidator = async (
  userCommand: string | undefined,
	  match: RegExpExecArray | null,
	): Promise<{
	  inputFilePath: string
	  outputFilePath: string
	  targetLang: string
	}> => {
	  if (!match || match.length < 4) {
	    await postError(`Invalid command: \`${userCommand}\`\n${COMMAND_USAGE}`)
	  }
  const [, inputFilePath, outputFilePath, targetLang] = match!
	  if (!isValidFileExt(inputFilePath) || !isValidFileExt(outputFilePath)) {
	    await postError(INVALID_FILE_EXTENSION)
	  }
	  const inputFileName = path.basename(inputFilePath)
	  const outputFileName = path.basename(outputFilePath)
	  // If multiple files are specified, input and output must be specified in the same way.
	  if (
	    (inputFileName.includes('*') && !outputFileName.includes('*')) ||
	    (!inputFileName.includes('*') && outputFileName.includes('*'))
  ) {
	    await postError(
	      `Error: Multiple file specification mismatch.\n${inputFileName} and ${outputFileName}`,
	    )
	  }
	  return {
	    inputFilePath,
	    outputFilePath,
	    targetLang: removeSymbols(targetLang),
	  }
}

import fs from 'fs/promises'
	import path from 'path'
	/**
	 * Create files recursively if no directory.
	 */
	export const createFile = async (
	  data: string,
	  filePath: string,
	): Promise<void> => {
	  try {
    await fs.writeFile(filePath, data)
	  } catch (err) {
	    if ((err as NodeJS.ErrnoException).code === 'ENOENT') {
	      await fs.mkdir(path.dirname(filePath), { recursive: true })
	      await createFile(data, filePath)
	    } else {
	      throw err
	    }
	  }
	}
export const isFileExists = async (inputPath: string) => {
	  try {
	    await fs.stat(inputPath)
	    return true
	  } catch (error) {
	    if ((error as NodeJS.ErrnoException).code === 'ENOENT') {
	      return false
	    }
	    throw error
	  }
}
	/**
	 * Replace the part with '**' with the rest
	 */
	function replaceWildcard(inputArray: string[], outputArray: string[]) {
	  const output = [...outputArray]
	  const indexOfAsteriskAsterisk = outputArray.indexOf('**')
	  if (indexOfAsteriskAsterisk !== -1) {
	    output.splice(
	      indexOfAsteriskAsterisk,
      1,
	      ...inputArray.slice(indexOfAsteriskAsterisk),
	    )
	  }
	  return output
	}
	/**
	 * Extracts the extension from a filename string
	 */
	function extractFileExtension(filename: string): string | null {
  const match = filename.match(/\.\w+(\.\w+)?$/)
	  return match ? match[0] : null
	}
	/*
	 * Get all file paths in a directory with a specific extension.
	 * @param directoryPath The path to the directory to search.
	 * @param extension The extension to filter by.
	 */
	export const getFilePathsWithExtension = async (
	  directoryPath: string,
  extension: string,
	): Promise<string[]> => {
	  const files = await fs.readdir(directoryPath)
	  const filteredFiles: string[] = []
	  for (const file of files) {
	    const filePath = path.join(directoryPath, file)
	    const stat = await fs.stat(filePath)
	    if (stat.isFile() && path.extname(file) === extension) {
	      filteredFiles.push(filePath)
	    }
  }
	  return filteredFiles
	}
	/**
	 * Generates an array of output file paths based on the provided array of input file paths
	 *
	 * @param {string[]} inputFilePaths - The array of input file paths
	 * @param {string} outputFilePath - The format of the output file path. The '*' character in the output file path is replaced by the filename of each input file path
	 * @returns {string[]} An array of output file paths.
	 * @throws {Error} Throws an error if the outputFilePath does not contain extensions(.md) or wildcards(*)
 *
	 */
	export const generateOutputFilePaths = (
	  inputFilePaths: string[],
	  outputFilePath: string,
	): string[] => {
	  const outputSegments = outputFilePath.replace('./', '').split('/')
	  const outputFilePattern = outputSegments.pop()!
	  const outputFileExt = extractFileExtension(outputFilePattern) || ''
	  const outputFilenameWithoutExt = outputFilePattern.replace(outputFileExt, '')
  return inputFilePaths.map((inputFilePath) => {
	    const inputSegments = inputFilePath.split('/')
	    const inputFile = inputSegments.pop()!
	    const inputFileExt = extractFileExtension(inputFile) || ''
	    const inputFilenameWithoutExt = inputFile.replace(inputFileExt, '')
	    const resolvedPathSegments = replaceWildcard(inputSegments, outputSegments) // Resolve path segments
	    // If output file pattern contains '*', replace it with the input filename
	    if (outputFilenameWithoutExt.includes('*')) {
	      const finalExt = outputFileExt || inputFileExt // If output extension isn't specified, use input file extension
	      const finalFilename = `${inputFilenameWithoutExt}${finalExt}` // Concatenate filename and extension
      resolvedPathSegments.push(finalFilename)
	    } else {
	      // Otherwise, use the specified output filename
	      resolvedPathSegments.push(outputFilePattern)
	    }
	    return resolvedPathSegments.join('/')
	  })
	}

import { setFailed } from '@actions/core'
	import { gitPostComment } from './git'
	import { context } from '@actions/github'
	export const postError = async (message: string) => {
	  await gitPostComment(`âŒ${message}`)
	  setFailed(message)
	  process.exit(1)
	}
	export const isPR = () => {
	  const { payload } = context
  return !!payload.issue?.pull_request
	}
	export const removeSymbols = (input: string): string => {
	  return input.replace(/[^\w\s]/gi, '')
	}
	export const generatePRBody = (
	  inputFilePaths: string[],
	  outputFilePaths: string[] | string[][],
	  targetLang: string | string[],
	  issueNumber?: number,
) => {
	  const generateRow = (label: string, filePaths: string[] | string[][]) => {
	    let result: string[] = []
	    if (Array.isArray(filePaths[0])) {
	      // filePaths: string[][]
	      let cnt = -1
	      filePaths.forEach((subArr) => {
	        // subArr: string[]
	        subArr.forEach((filePath: string) => {
	          cnt++
          return result.push(
	            `${cnt > 0 ? '|' : `|**${label}**`}|\`${filePath}\`|`,
	          )
	        })
	      })
	    } else {
	      // filePaths: string[]
	      filePaths.forEach((filePath, i) => {
	        return result.push(`${i > 0 ? '|' : `|**${label}**`}|\`${filePath}\`|`)
	      })
    }
	    return result.join('\n')
	  }
	  return `## âœ… LLM Translation completed
	  |**Name**|**Value**|
	  |---|---|
	  ${generateRow('Source', inputFilePaths)}
	  ${generateRow('Output', outputFilePaths)}
	  |**Language**|${
	    Array.isArray(targetLang) ? targetLang.join(', ') : targetLang
  }|
	  ${issueNumber ? `|**Issue**|#${issueNumber}|` : ''}
	  `
	}

import { postError } from './utils'
	import { translateByCommand, translateByManual } from './translate'
	import { authorizeUser } from './git'
	import { extractInput, getCommandParams } from './extract'
	import { context } from '@actions/github'
	import { setFailed } from '@actions/core'
	async function main() {
	  switch (context.eventName) {
	    case 'issue_comment':
	      const isAuthorized = await authorizeUser()
      if (!isAuthorized) {
	        await postError('You have no permission to use this bot.')
	      }
	      const { inputFilePath, outputFilePath, targetLang } =
	        await getCommandParams()
	      await translateByCommand(inputFilePath, outputFilePath, targetLang)
	      break
	    case 'push':
	      // âš  Experimental Feature
	      // Translate any file from the parameter specification.
      // Multiple output and target languages can be selected.
	      // [IMPORTANT]
	      // outputFiles must be specified using wildcards.
	      const { inputFiles, outputFiles, languages } = extractInput()
	      await translateByManual(inputFiles, outputFiles, languages)
	      break
	    default:
	      await postError('This event is not supported.')
	  }
	}
main().catch((e) => setFailed(e))

import { getInput, setFailed } from '@actions/core'
	import { context } from '@actions/github'
	import { commandValidator, isValidFileExt } from './validate'
	type CommandParams = {
	  inputFilePath: string
	  outputFilePath: string
	  targetLang: string
	}
	type ManualParams = {
	  inputFiles: string[]
  outputFiles: string[]
	  languages: string[]
	}
	export const getCommandParams = async (): Promise<CommandParams> => {
	  const comment = context.payload.comment?.body
	  if (!comment) setFailed('Error: Comment could not be retrieved correctly.')
	  const regex = /\/(?:gpt-translate|gt)\s+(\S+)\s+(\S+)\s+(\S+)/
	  const match = regex.exec(comment)
	  return commandValidator(comment, match)
	}
export const extractInput = (): ManualParams => {
	  const inputFilesRaw = getInput('inputFiles')
	  const outputFilesRaw = getInput('outputFiles')
	  const languagesRaw = getInput('languages')
	  const inputFiles = inputFilesRaw
	    ? inputFilesRaw.split(' ').filter((v) => v)
	    : []
	  const outputFiles = outputFilesRaw
	    ? outputFilesRaw.split(' ').filter((v) => v)
	    : []
  const languages = languagesRaw ? languagesRaw.split(' ').filter((v) => v) : []
	  // validate input
	  const isValidInput =
	    inputFiles.every((v) => isValidFileExt(v)) &&
	    outputFiles.every((v) => isValidFileExt(v))
	  if (!isValidInput) {
	    throw new Error(
	      'Invalid input. Please check the inputFiles and outputFiles parameters',
	    )
	  }
  return {
	    inputFiles,
	    outputFiles,
	    languages,
	  }
	}
