import RulesEngine from "../src/index";
	import { RuleSet } from "../types/types/rules";
	const RULE_DEFINITION: RuleSet = {
	  version: 1,
	  rules: [
	    {
	      condition: {
	        type: "matcher",
	        definition: {
	          key: "text",
          matcher: "ew",
	          values: ["Hello", "Mahalo", "Aloha", "Ohana", "true"],
	        },
	      },
	      consequences: [
	        {
	          id: "123",
	          type: "data",
	          detail: {
	            html: "<h1>Ua ola loko i ke aloha!</h1>",
          },
	        },
	      ],
	    },
	  ],
	};
	describe("matcher type - ends with (ew)", () => {
	  let ruleset;
	  beforeEach(() => {
	    ruleset = RulesEngine(RULE_DEFINITION);
  });
	  it("returns consequences when any of the condition definition values ends with the relevant key's value of given context", () => {
	    const result = ruleset.execute({
	      state: "Hawaii",
	      weather: "Tropical",
	      text: "Ohana is a human circle of complete aloha",
	    });
	    expect(result).toEqual([RULE_DEFINITION.rules[0].consequences]);
	  });
	  it("returns consequences when boolean true is passed", () => {
    const result = ruleset.execute({
	      state: "Hawaii",
	      weather: "Tropical",
	      text: true,
	    });
	    expect(result).toEqual([RULE_DEFINITION.rules[0].consequences]);
	  });
	  it("returns empty consequence when condition definition values does not ends with the relevant key's value of given context", () => {
	    const result = ruleset.execute({
	      state: "Hawaii",
      weather: "Tropical",
	      text: "Ho'omaluhia",
	    });
	    expect(result).toEqual([]);
	  });
	  it("returns empty consequence for context key's value is null", () => {
	    const result = ruleset.execute({
	      state: "Hawaii",
	      weather: "Tropical",
	      text: null,
    });
	    expect(result).toEqual([]);
	  });
	  it("returns  empty consequence for input context key's value is undefined", () => {
	    const result = ruleset.execute({
	      state: "Hawaii",
	      weather: "Tropical",
	      text: undefined,
	    });
	    expect(result).toEqual([]);
  });
	});

