	  return {
	    execute: (context: Context) => {
      if (condition.evaluate(context)) {
	        return consequences;
	      }
	      return [];
	    },
	    toString: () => {
	      return `Rule{condition=${condition}, consequences=${consequences}}`;
	    },
	  };
	}
export function createCondition(
	  type: SupportedCondition,
