  return events.reduce((countTotal, event) => {
	    const eventsOfType = context.events[event.type];
	    if (!eventsOfType) {
	      return countTotal;
	    }
	    const contextEvent = eventsOfType[event.id];
	    if (!contextEvent) {
	      return countTotal;
	    }
	    const { count: eventCount = 1 } = contextEvent;
    if (
	      isUndefined(from) ||
	      isUndefined(to) ||
