	  html: string
}
	export interface CMS {
	  stages: Array<string>
	  lastUpdateTimestamp?: number
	  metadata: {
	    options?: string
	    rootUrl?: string | URL | undefined
	    databaseId: string
	    stats: Partial<Stats>
	  }
  routes: Array<string | Array<string>>
	  tags: Array<string>
	  tagGroups: Record<string, Array<string>>
	  siteData: Record<string, Page>
	}
	export interface PageContent {
	  _ancestors?: PageContent[]
	  _updateNeeded?: boolean
	  name?: string
	  path?: string
  url?: string
	  otherProps?: PageObjectResponse['properties']
	  _notion?: {
	    parent?: PageContent
	    id: string
	    last_edited_time: string
	  }
	  authors?: Array<string>
	  slug?: string
	  tags?: Array<string>
  coverImage?: string
	  content?: Content
	}
	export interface Route {
	  [key: string]: PageContent
	}
	export type ExtendedPageContent = PageContent & {
	  [key: string]: unknown
	}
	export type Page = PageContent | (PageContent & Route) | ExtendedPageContent
// Directly stolen from PageObjectResponse Record Type in notionClient/API-endpoints.ts
	export interface PageObjectTitle { type: 'title'; title: Array<RichTextItemResponse>; id: string }
	export interface PageObjectRelation { type: 'relation'; relation: Array<{ id: string }>; id: string }
	export interface PageObjectUser {
	  type: 'people'
	  people: Array<PersonUserObjectResponse>
	  id: string
	}
	export interface PageMultiSelect {
	  type: 'multi_select'
  multi_select: Array<{
	    id: string
	    name: string
	    color: string
	  }>
	  id: string
	}
	export interface PageSelect {
	  type: 'select'
	  select: {
    id: string
	    name: string
	    color: string
	  } | null
	  id: string
	}
	export interface PageRichText {
	  type: 'rich_text'
	  rich_text: Array<RichTextItemResponse>
	  id: string
}
	export type Cover = { type: 'external'; external: { url: string } } | { type: 'file'; file: { url: string; expiry_time: string } } | null
	export type RouteObject = [string, object]
	export type PluginPassthrough = Blocks | CMS | Page | string
	export interface PluginExecOptions {
	  debug?: boolean
	  localCacheDirectory?: string
	  notion?: Client
	}
	export interface Plugin {
  name: string
	  hook: 'import' | 'pre-tree' | 'pre-parse' | 'post-parse' | 'during-tree' | 'post-tree' | '*'
	  exec: (context: PluginPassthrough, instanceOptions?: PluginExecOptions) => PluginPassthrough
	}
	export interface UnsafePlugin {
	  parser: NotionBlocksParser
	  name: string
	  core: boolean
	  hook: 'parse' | 'import' | 'pre-tree' | 'pre-parse' | 'post-parse' | 'during-tree' | 'post-tree' | '*'
	  exec: (context: PluginPassthrough, instanceOptions?: PluginExecOptions) => PluginPassthrough
}
	export interface FlatListItem {
	  _key: string
	  [pid: string]: string | Partial<FlatListItem> | unknown
	}
	export type FlatList = FlatListItem[]

import fs from 'node:fs'
	import path from 'node:path'
	import { fileURLToPath } from 'node:url'
	import { createHash } from 'node:crypto'
	import { Client, LogLevel, collectPaginatedAPI, isFullBlock, isFullPage } from '@notionhq/client'
	import type {
	  BlockObjectResponse,
	  PageObjectResponse,
	  SelectPropertyItemObjectResponse,
	} from '@notionhq/client/build/src/api-endpoints'
import type { Blocks } from '@notion-stuff/v4-types'
	import _ from 'lodash'
	import type { AsyncCallbackFn, WalkNode } from 'walkjs'
	import { AsyncWalkBuilder, WalkBuilder } from 'walkjs'
	import humanInterval from 'human-interval'
	import { log, spinner } from '@clack/prompts'
	import kleur from 'kleur'
	import type {
	  CMS,
	  Content,
  Cover,
	  ExtendedPageContent,
	  FlatListItem,
	  Options,
	  Page,
	  PageContent,
	  PageMultiSelect,
	  PageObjectRelation,
	  PageObjectTitle,
	  PageObjectUser,
  Plugin,
	  PluginPassthrough,
	  UnsafePlugin,
	} from './types'
	import {
	  JSONParseWithFunctions,
	  JSONStringifyWithFunctions,
	  filterAncestors,
	  routify,
	  slugify,
  writeFile,
	} from './utilities'
	import NotionLogger from './notion-logger'
	import renderer from './plugins/render'
	const __filename = fileURLToPath(import.meta.url)
	const __dirname = path.dirname(__filename)
	const COVER_IMAGE_REGEX = /<figure notion-figure>[\s\S]+<img[^>]*src=['|"](https?:\/\/[^'|"]+)(?:['|"])/
	const STEADY_PROPS = [
	  'name',
	  'Author',
  'Published',
	  'Tags',
	  'publishDate',
	  'parent-page',
	  'sub-page',
	]
	const clackSpinner = spinner()
	export default class NotionCMS {
	  cms: CMS
	  cmsId: string
  notionClient: Client
	  autoUpdate: boolean
	  refreshTimeout: number | string
	  draftMode: boolean
	  defaultCacheFilename: string
	  localCacheDirectory: string
	  localCacheUrl: string
	  debug: boolean | undefined
	  limiter: { schedule: Function }
	  plugins: Array<Plugin | UnsafePlugin> | undefined
  options: Options
	  private timer: number
	  private coreRenderer: UnsafePlugin
	  private logger: NotionLogger
	  pull: () => Promise<CMS>
	  rootAlias: string
	  withinRefreshTimeout: boolean
	  quietMode: boolean
	  constructor({
	    databaseId,
    notionAPIKey,
	    debug = false,
	    draftMode = false,
	    refreshTimeout = 0,
	    autoUpdate = true,
	    localCacheDirectory = './.notion-cms/',
	    rootAlias = '',
	    rootUrl = '',
	    quiet = false,
	    limiter = {
      schedule: (func: Function) => {
	        const result = func() as unknown
	        return Promise.resolve(result)
	      },
	    },
	    plugins = [],
	  }: Options = { databaseId: '', notionAPIKey: '' }) {
	    this.timer = Date.now()
	    this.options = {
	      databaseId,
      notionAPIKey,
	      debug,
	      draftMode,
	      refreshTimeout,
	      autoUpdate,
	      localCacheDirectory,
	      rootUrl,
	      rootAlias,
	      limiter,
	      plugins,
      quiet,
	    }
	    this.cms = {
	      metadata: {
	        options: this._documentOptions(this.options),
	        databaseId,
	        rootUrl: rootUrl || '',
	        stats: {
	          durationSeconds: 0,
	          totalPages: 0,
        },
	      },
	      stages: [],
	      routes: [],
	      tags: [],
	      tagGroups: {},
	      siteData: {},
	    }
	    this.cmsId = this._produceCMSIdentifier(databaseId) // Can't have multiple instances that reference the same db.
	    this.debug = debug
    this.logger = new NotionLogger({ debug: this.debug })
	    this.notionClient = new Client({
	      auth: notionAPIKey,
	      logLevel: LogLevel.DEBUG,
	      logger: this.logger.log.bind(this.logger),
	    })
	    this.autoUpdate = autoUpdate
	    this.refreshTimeout
	      = (refreshTimeout && _.isString(refreshTimeout))
	        ? (humanInterval(refreshTimeout) || refreshTimeout)
        : (refreshTimeout || 0)
	    this.draftMode = draftMode || false
	    this.localCacheDirectory = localCacheDirectory
	    this.defaultCacheFilename = `ncms-cache-${this.cmsId}.json`
	    this.localCacheUrl = path.resolve(__dirname, this.localCacheDirectory + this.defaultCacheFilename)
	    this.limiter = limiter
	    this.limiter.schedule.bind(limiter)
	    this.coreRenderer = renderer({ blockRenderers: {}, debug })
	    this.coreRenderer.name = 'core-renderer'
	    this.plugins = this._dedupePlugins([...plugins, this.coreRenderer])
    this.pull = this.fetch.bind(this)
	    this.rootAlias = rootAlias
	    this.withinRefreshTimeout = false
	    this.quietMode = quiet
	  }
	  get data() {
	    if (_.isEmpty(this.cms.siteData))
	      return
	    return this.cms.siteData
	  }
  get routes(): Array<string> {
	    const routes = [] as Array<string>
	    this.walk((node: PageContent) => {
	      if (node.path)
	        routes.push(node.path)
	    })
	    return routes
	  }
	  _documentOptions(options: Options): string {
	    let hex
    try {
	      const tempOptions = _.cloneDeep(options)
	      delete tempOptions.limiter
	      const hasher = createHash('md5')
	      // Detecting Function string differences needs
	      // to be implemented using the JSONStringifyWithFunctions util.
	      const json = JSON.stringify(tempOptions)
	      hasher.update(json)
	      hex = hasher.digest('hex')
	    }
    catch (e) {
	      // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
	      throw new Error(`Failed to document options object: ${e}`)
	    }
	    return hex
	  }
	  _produceCMSIdentifier(id: string): string {
	    return id.slice(-4)
	  }
	  _dedupePlugins(plugins: Array<Plugin | UnsafePlugin>): Array<Plugin | UnsafePlugin> {
    const numParsePlugins = _.filter(plugins, { hook: 'parse' })
	    if (numParsePlugins.length > 1)
	      return _.initial(plugins)
	    return plugins
	  }
	  _checkDuplicateParsePlugins(pluginsList: Array<Plugin | UnsafePlugin>): boolean {
	    return _(pluginsList).filter(plugin => plugin.hook === 'parse').uniq().value().length > 1
	  }
	  async _runPlugins(
	    context: PluginPassthrough,
    hook: Plugin['hook'] | 'parse'): Promise<PluginPassthrough> {
	    if (!this.plugins?.length)
	      return context
	    if (this._checkDuplicateParsePlugins(this.plugins))
	      throw new Error('Only one parse-capable plugin must be used. Use the default NotionCMS render plugin.')
	    let val = context
	    for (const plugin of this.plugins.flat()) {
	      if (plugin.hook === hook || plugin.hook === '*') {
	        // eslint-disable-next-line @typescript-eslint/await-thenable
	        val = await plugin.exec(val, {
          debug: !!this.debug,
	          localCacheDirectory: this.localCacheDirectory,
	          notion: this.notionClient,
	        })
	      }
	    }
	    return val
	  }
	  _flatListToTree = (
	    flatList: Partial<FlatListItem>[],
    idPath: keyof FlatListItem,
	    parentIdPath: keyof FlatListItem,
	    isRoot: (t: Partial<FlatListItem>) => boolean,
	  ): Record<string, Page> => {
	    const rootParents: Partial<FlatListItem>[] = []
	    const map = {} as { [x: string]: Partial<FlatListItem> }
	    const tree = {}
	    for (const item of flatList)
	      map[item[idPath] as string] = item
	    for (const item of flatList) {
      const parentId = item[parentIdPath]
	      if (isRoot(item)) {
	        if (item._key === this.rootAlias)
	          item._key = '/'
	        rootParents.push(item)
	      }
	      else {
	        const parentItem = map[parentId as string]
	        parentItem[item._key as string] = item
	      }
    }
	    _.forEach(rootParents, (page) => {
	      _.assign(tree, { [page._key as string]: page })
	    })
	    return tree
	  }
	  _notionListToTree(list: Partial<FlatListItem>[]): Record<string, Page> {
	    return this._flatListToTree(list, 'id', 'pid', (node: Partial<FlatListItem>) => !node.pid)
	  }
	  static _isPageContentObject(node: WalkNode): boolean {
    return typeof node.key === 'string' && node?.key?.startsWith('/')
	      && ((typeof node?.parent?.key === 'string' && node?.parent?.key?.startsWith('/'))
	        || !node?.parent?.key || node?.parent?.key === 'siteData')
	  }
	  static _createCMSWalker(cb: (node: ExtendedPageContent) => void): WalkBuilder {
	    return new WalkBuilder()
	      .withCallback({
	        filters: [(node: WalkNode) => NotionCMS._isPageContentObject(node)],
	        nodeTypeFilters: ['object'],
	        positionFilter: 'postWalk',
        callback: (node: WalkNode) => cb(node.val as ExtendedPageContent),
	      })
	  }
	  _getParentPageId(response: PageObjectResponse): string {
	    const parentPage = response?.properties['parent-page'] as PageObjectRelation
	    return parentPage?.relation[0]?.id
	  }
	  _getBlockName(response: PageObjectResponse): string {
	    const nameProp = response?.properties.name as PageObjectTitle
	    return nameProp.title[0]?.plain_text
  }
	  _extractTags(response: PageObjectResponse): Array<string> {
	    const tagProp = response?.properties?.Tags as PageMultiSelect
	    return tagProp.multi_select ? tagProp.multi_select.map(multiselect => multiselect.name) : []
	  }
	  _assignTagGroup(tag: string, path: string, cms: CMS): void {
	    if (!cms.tagGroups[tag])
	      cms.tagGroups[tag] = []
	    cms.tagGroups[tag].push(path)
	  }
  _buildTagGroups(tags: Array<string>, path: string, cms: CMS): void {
	    _.forEach(tags, (tag) => {
	      if (!_.includes(cms.tags, tag))
	        cms.tags.push(tag)
	      this._assignTagGroup(tag, path, cms)
	    })
	  }
	  _getCoverImage(page: PageObjectResponse): string | undefined {
	    const pageCoverProp = (page)?.cover as Cover
	    let coverImage
    if (pageCoverProp && 'external' in pageCoverProp)
	      coverImage = pageCoverProp?.external?.url
	    else if (pageCoverProp?.file)
	      coverImage = pageCoverProp?.file.url
	    return coverImage
	  }
	  async _pullPageContent(id: string): Promise<BlockObjectResponse[] | undefined> {
	    let pageContent
	    try {
	      pageContent = await this.limiter.schedule(
        async () => await collectPaginatedAPI(this.notionClient.blocks.children.list, {
	          block_id: id,
	        }),
	      ) as BlockObjectResponse[]
	    }
	    catch (e) {
	      if (this.debug)
	        // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
	        console.error(`NotionCMS Error: ${e}`)
	    }
    if (!pageContent)
	      return
	    for (const block of pageContent) {
	      if (isFullBlock(block) && block.has_children)
	        // @ts-expect-error children
	        // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
	        block[block.type].children = (await this._pullPageContent(block.id))
	    }
	    return pageContent
	  }
  async _parsePageContent(pageContent: BlockObjectResponse[]): Promise<Content> {
	    const results = await this._runPlugins(pageContent as Blocks, 'pre-parse')
	    const markdown: string = this.coreRenderer.parser.blocksToMarkdown(pageContent as Blocks)
	    const plaintext: string = this.coreRenderer.parser.markdownToPlainText(markdown)
	    const parsedBlocks = await this._runPlugins(results as Blocks, 'parse')
	    const html = await this._runPlugins(parsedBlocks, 'post-parse') as string
	    return {
	      plaintext,
	      markdown,
	      html,
    }
	  }
	  async _getPageContent(state: CMS, cachedState?: CMS): Promise<CMS> {
	    let stateWithContent = _.cloneDeep(state)
	    await new AsyncWalkBuilder()
	      .withCallback({
	        filters: [(node: WalkNode) => NotionCMS._isPageContentObject(node)],
	        nodeTypeFilters: ['object'],
	        positionFilter: 'postWalk',
	        callback: async (node: WalkNode) => {
          const pageContent = node.val as PageContent
	          if (!pageContent || !pageContent?._notion?.id)
	            return
	          // Definitely grab content if there is no cache.
	          if (pageContent._updateNeeded || !cachedState) {
	            if (!this.quietMode && pageContent.path)
	              clackSpinner.start(kleur.blue(`[ncms][updating]: ${pageContent.path}`))
	            const blocks = await this._pullPageContent(pageContent._notion.id)
	            if (!blocks)
	              return
            const content = await this._parsePageContent(blocks)
	            _.assign(pageContent, {
	              content,
	              ...(!pageContent.coverImage && { coverImage: content.html.match(COVER_IMAGE_REGEX)?.[1] }),
	              _ancestors: this._gatherNodeAncestors(node),
	            })
	          }
	          else if (cachedState && pageContent.path) {
	            const cachedPage = this._queryByPath(pageContent.path, cachedState?.siteData)
	            if (cachedPage) {
              _.assign(pageContent, {
	                content: cachedPage.content,
	                ...(!cachedPage.coverImage && { coverImage: cachedPage.content?.html.match(COVER_IMAGE_REGEX)?.[1] }),
	                _ancestors: this._gatherNodeAncestors(node),
	              })
	              if (!this.quietMode && pageContent.path)
	                clackSpinner.start(kleur.yellow(`[ncms][using cache]: ${pageContent.path}`))
	            }
	          }
	          else {
            clackSpinner.stop(kleur.red('[ncms]: aborted due to fetch error.'))
	            throw new Error(`ncms: error when updating page content. No page found for ${node.key || 'undetermined node key'}`)
	          }
	          _.assign(
	            pageContent,
	            await this._runPlugins(pageContent, 'during-tree') as Page)
	          delete pageContent.otherProps
	          // We only want access to ancestors for plugins, otherwise it creates circular ref headaches.
	          delete pageContent._ancestors
	          delete pageContent._updateNeeded
          // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
	          clackSpinner.stop(kleur.blue(`[ncms]: updated@ ${pageContent.path}`))
	        },
	      })
	      .withRootObjectCallbacks(false)
	      .withParallelizeAsyncCallbacks(true)
	      .walk(stateWithContent.siteData)
	    stateWithContent.stages.push('content')
	    stateWithContent = await this._runPlugins(stateWithContent, 'post-tree') as CMS
	    return stateWithContent
  }
	  _extractUnsteadyProps(properties: PageObjectResponse['properties']): PageObjectResponse['properties'] {
	    return _(properties)
	      .entries()
	      .reject(([key]) => _.includes(STEADY_PROPS, key))
	      .fromPairs().value()
	  }
	  _getPageUpdate(entry: PageObjectResponse): Page {
	    const tags = [] as Array<string>
	    if (isFullPage(entry)) {
      const name = this._getBlockName(entry)
	      const authorProp = entry.properties?.Author as PageObjectUser
	      const authors = authorProp.people.map(authorId => authorId.name as string)
	      const coverImage = this._getCoverImage(entry)
	      const extractedTags = this._extractTags(entry)
	      extractedTags.forEach(tag => tags.push(tag))
	      const otherProps = this._extractUnsteadyProps(entry.properties)
	      return {
	        name,
	        otherProps,
        slug: slugify(name),
	        authors,
	        tags,
	        coverImage,
	        _notion: {
	          id: entry.id,
	          last_edited_time: entry.last_edited_time,
	        },
	      }
	    }
    return {}
	  }
	  _publishedFilter = (e: PageObjectResponse) => {
	    const publishProp = e.properties.Published as SelectPropertyItemObjectResponse
	    return this.draftMode ? true : (publishProp.select && publishProp.select.name === 'Published')
	  }
	  _gatherNodeAncestors(node: WalkNode): Array<PageContent> {
	    return _(node.ancestors).map((ancestor) => {
	      if ((ancestor.val as PageContent)._notion)
	        return ancestor.val as PageContent
      return false
	    }).compact().value()
	  }
	  async _getDb(state: CMS, cachedState?: CMS): Promise<CMS | undefined> {
	    let stateWithDb = _.cloneDeep(state)
	    let db
	    try {
	      db = await this.limiter.schedule(
	        async () => (await collectPaginatedAPI(
	          this.notionClient.databases.query, { database_id: state.metadata.databaseId },
        )),
	      ) as PageObjectResponse[]
	    }
	    catch (e) {
	      if (this.debug)
	        // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
	        console.error(`NotionCMS Error: ${e}`)
	    }
	    if (!db)
	      return
    stateWithDb.metadata.stats.totalPages = db.length
	    stateWithDb.siteData = this._notionListToTree(
	      _(db)
	        // @ts-expect-error filter
	        .filter(this._publishedFilter)
	        .map(page => _.assign({}, {
	          _key: routify(this._getBlockName(page)),
	          id: page.id,
	          pid: this._getParentPageId(page),
	          _notion: page, // this property is recycled to eventually house metadata.
        }))
	        .value(),
	    )
	    if (_.isEmpty(stateWithDb.siteData))
	      throw new Error('NotionCMS is empty. Did you mean to set `draftMode: true`?')
	    new WalkBuilder()
	      .withCallback({
	        nodeTypeFilters: ['object'],
	        callback: (node: WalkNode) => {
	          const pageContent = node.val as PageContent
          if (!pageContent?._notion)
	            return
	          pageContent._updateNeeded = !this.withinRefreshTimeout
	          const update = this._getPageUpdate(pageContent._notion as PageObjectResponse)
	          _.assign(pageContent, update)
	          _.assign(pageContent, {
	            // Replace double // so that root aliasing works properly
	            path: node.getPath(node => `${node.key as string}`)
	              .replace('siteData', '')
	              .replace('//', '/'),
            url: (stateWithDb.metadata.rootUrl && pageContent.path)
	              ? (`${stateWithDb.metadata.rootUrl as string}${pageContent.path}`)
	              : '',
	          })
	          if (cachedState && pageContent.path && !this.withinRefreshTimeout) {
	            const cachedPage = this._queryByPath(pageContent.path, cachedState?.siteData)
	            pageContent._updateNeeded = this.autoUpdate
	              && (update._notion?.last_edited_time !== cachedPage?._notion?.last_edited_time)
	          }
	          if (node.key && typeof node.key === 'string' && pageContent.tags && pageContent.path)
            this._buildTagGroups(pageContent.tags, pageContent.path, stateWithDb)
	        },
	      })
	      .withRootObjectCallbacks(false)
	      .walk(stateWithDb)
	    stateWithDb.stages.push('db')
	    stateWithDb = await this._runPlugins(stateWithDb, 'pre-tree') as CMS
	    return stateWithDb
	  }
	  async fetch(): Promise<CMS> {
    let cachedCMS, optionsHaveChanged
	    if (fs.existsSync(this.localCacheUrl)) {
	      try {
	        cachedCMS = JSONParseWithFunctions(fs.readFileSync(this.localCacheUrl, 'utf-8')) as CMS
	        optionsHaveChanged = cachedCMS.metadata.options !== this.cms.metadata.options
	      }
	      catch (e) {
	        if (this.debug)
	          console.error('Parsing cached CMS failed. Using API instead.')
	      }
    }
	    this.withinRefreshTimeout = Boolean(cachedCMS && ((cachedCMS.lastUpdateTimestamp
	      && (Date.now() < cachedCMS.lastUpdateTimestamp + _.toNumber(this.refreshTimeout)))
	        && !optionsHaveChanged))
	    if (!this.quietMode)
	      log.step(kleur.red('[ncms]: pulling your content from Notion...ðŸ›¸'))
	    try {
	      const cmsOutput = await this._getDb(this.cms, cachedCMS)
	      if (!cmsOutput)
	        throw new Error('NotionCMS Error: DB fetch unsuccessful.')
      this.cms = cmsOutput
	      this.cms = await this._getPageContent(this.cms, cachedCMS)
	      this.cms.stages.push('complete')
	      this.export()
	      if (!this.quietMode)
	        log.step(kleur.green('[ncms]: mission complete! ðŸ‘½'))
	    }
	    catch (e) {
	      if (!this.quietMode)
	        clackSpinner.stop(kleur.red('[ncms]: Something went wrong. Mission aborted.'))
      // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
	      console.error(`ncms error: ${e}`)
	    }
	    this.cms.routes = this.routes
	    this.cms.metadata.stats = {
	      ...this.logger.stats,
	      totalPages: this.cms.metadata.stats.totalPages,
	      durationSeconds: (Date.now() - this.timer) / 1000,
	    }
	    if (this.debug)
      writeFile('debug/site-data.json', JSONStringifyWithFunctions(this.cms))
	    return this.cms
	  }
	  async asyncWalk(cb: Function, path?: string) {
	    const startPoint = path ? this.queryByPath(path) : this.cms.siteData
	    await new AsyncWalkBuilder()
	      .withCallback({
	        nodeTypeFilters: ['object'],
	        filters: [(node: WalkNode) => NotionCMS._isPageContentObject(node)],
	        callback: (node: WalkNode) => cb(node.val) as AsyncCallbackFn,
      })
	      .withRootObjectCallbacks(false)
	      .withParallelizeAsyncCallbacks(true)
	      .walk(startPoint)
	  }
	  walk(cb: Function, path?: string) {
	    const startPoint = path ? this.queryByPath(path) : this.cms.siteData
	    new WalkBuilder()
	      .withCallback({
	        nodeTypeFilters: ['object'],
        filters: [(node: WalkNode) => NotionCMS._isPageContentObject(node)],
	        callback: (node: WalkNode) => cb(node.val) as unknown,
	      })
	      .withRootObjectCallbacks(false)
	      .walk(startPoint)
	  }
	  getTaggedCollection(tags: string | Array<string>): Array<Page | undefined> {
	    if (!_.isArray(tags))
	      tags = [tags]
	    const taggedPages = [] as Array<string>
    for (const tag of tags)
	      taggedPages.push(...this.cms.tagGroups[tag])
	    if (typeof this.cms.siteData !== 'string')
	      return _(taggedPages).map(path => this.queryByPath(path)).uniq().value()
	    return []
	  }
	  filterSubPages(pathOrPage: string | Page): Array<Page> {
	    if (typeof pathOrPage === 'string')
	      pathOrPage = this.queryByPath(pathOrPage)
	    return _(pathOrPage)
      .entries()
	      .filter(([key]) => key.startsWith('/'))
	      .map(e => e[1]).value() as Page[]
	  }
	  rejectSubPages(pathOrPage: string | Page): Page {
	    if (typeof pathOrPage === 'string')
	      pathOrPage = this.queryByPath(pathOrPage)
	    return _(pathOrPage)
	      .entries()
	      .reject(([key]) => key.startsWith('/'))
      .fromPairs().value() as Page
	  }
	  _queryByPath(path: string, siteData: Record<string, Page>): Page {
	    const segments = path.split('/').slice(1)
	    if (this.rootAlias && path !== '/')
	      segments.unshift('') // This lets us access the root aliased page.
	    let access: Page = siteData
	    for (const segment of segments) {
	      // @ts-expect-error-next-line
	      access = access[`/${segment}`] as Page
    }
	    return access
	  }
	  queryByPath(path: string): Page {
	    return this._queryByPath(path, this.cms.siteData)
	  }
	  export({ pretty = false, path = this.localCacheUrl }:
	  { pretty?: boolean; path?: string } = {}) {
	    this.cms.lastUpdateTimestamp = Date.now()
	    if (pretty) {
      // This drops Functions too, so only use for inspection
	      writeFile(path, JSON.stringify(this.cms, filterAncestors))
	    }
	    else {
	      writeFile(path, JSONStringifyWithFunctions(this.cms))
	    }
	  }
	  async import(previousState: string, flatted?: boolean): Promise<CMS> {
	    let parsedPreviousState
	    try {
      if (flatted)
	        parsedPreviousState = JSONParseWithFunctions(previousState) as CMS
	      else
	        parsedPreviousState = JSON.parse(previousState) as CMS
	    }
	    catch (e) {
	      throw new Error(`Parsing input CMS failed.
	                      Make sure your input follows the NotionCMS spec, uses a validator plugin or a transformation plugin.`)
	    }
	    const transformedPreviousState = await this._runPlugins(parsedPreviousState, 'import') as CMS
    return this.cms = transformedPreviousState
	  }
	  purgeCache(): boolean {
	    try {
	      fs.rmSync(this.localCacheUrl)
	    }
	    catch (e) {
	      if (this.debug)
	        // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
	        console.log(`ncms: error when attempting to clear cache: ${e}`)
      return false
	    }
	    if (this.debug)
	      console.log('ncms: cache has been successfully cleared.')
	    return true
	  }
	}

import type { Renderer as MarkedRenderer } from 'marked'
	import { marked } from 'marked'
	import hljs from 'highlight.js'
	import type { Blocks } from '@notion-stuff/v4-types'
	import _ from 'lodash'
	// @ts-expect-error module
	import { gfmHeadingId } from 'marked-gfm-heading-id'
	import type NotionBlocksMarkdownParser from './notion-blocks-md-parser'
	export default class NotionBlocksHtmlParser {
	  markdownParser: NotionBlocksMarkdownParser
  renderer: MarkedRenderer
	  markedOptions
	  debug: boolean
	  constructor(parser: NotionBlocksMarkdownParser, debug?: boolean) {
	    this.markdownParser = parser
	    this.debug = debug || false
	    this.renderer = new marked.Renderer()
	    this.renderer.code = this._highlight.bind(this)
	    this.markedOptions = {
	      renderer: this.renderer,
      pedantic: false,
	      mangle: false,
	      gfm: true,
	      headerIds: true,
	      breaks: false,
	      sanitize: false,
	      smartypants: false,
	      xhtml: false,
	      // eslint-disable-next-line @typescript-eslint/no-unsafe-call
	      extensions: [gfmHeadingId({ prefix: '' })],
    }
	    marked.use({ silent: true })
	    // This is a workaround so that hljs doesn't complain about mermaid not being a registered lang.
	    hljs.registerAliases('mermaid', { languageName: 'plaintext' })
	  }
	  marked(md: string): string {
	    return marked(md, this.markedOptions)
	  }
	  parse(blocks: Blocks) {
	    let markdown = this.markdownParser.parse(blocks)
    // if (this.debug)
	    //   fs.writeFileSync('./debug/parsed.md', markdown)
	    // Take another pass to wrap any deeply nested mixed HTML content's inner text in p tags
	    markdown = this._mixedHTML(markdown)
	    // if (this.debug)
	    //   fs.appendFileSync('./debug/parsed.md', `--------ALTERED----------**\n\n\n${markdown}`)
	    return marked(markdown, this.markedOptions)
	  }
	  _highlight(code: string, lang: string | undefined): string {
	    let language
    if (lang === 'mermaid')
	      language = 'mermaid'
	    else
	      language = (lang && hljs.getLanguage(lang)) ? lang : 'plaintext'
	    const higlighted = hljs.highlight(code, { language })
	    const langClass = `language-${
	      (!language || language.includes('plain')) ? 'none' : language}`
	    return `<pre><code class='hljs ${langClass}'>${higlighted.value}</code></pre>`
	  }
	  _mixedHTML(mixedHtml: string) {
    return mixedHtml.replaceAll(/[^<>]+?(?=<\/)/g, (match) => {
	      // Must trim or Marked classifies text preceded by tabs as indented code.
	      const tokens = marked.lexer(_.trim(match), this.markedOptions)
	      return marked.parser(tokens, this.markedOptions)
	    })
	  }
	}

import type {
	  Annotations,
	  AudioBlock,
	  Blocks,
	  BulletedListItemBlock,
	  CalloutBlock,
	  CalloutIconEmoji,
	  CalloutIconExternal,
	  CalloutIconFile,
	  CodeBlock,
  EmbedBlock,
	  ExternalFileWithCaption,
	  FileBlock,
	  FileWithCaption,
	  HeadingBlock,
	  ImageBlock,
	  LinkPreviewBlock,
	  LinkToPageBlock,
	  NumberedListItemBlock,
	  PDFBlock,
  ParagraphBlock,
	  QuoteBlock,
	  RichText,
	  RichTextEquation,
	  RichTextMention,
	  RichTextText,
	  ToDoBlock,
	  ToggleBlock,
	  VideoBlock,
	} from '@notion-stuff/v4-types'
import { uuidToId } from 'notion-utils'
	const EOL_MD = '\n'
	export default class NotionBlocksMarkdownParser {
	  parse(blocks: Blocks, depth = 0): string {
	    return blocks
	      .reduce((markdown, childBlock) => {
	        let childBlockString = ''
	        // @ts-expect-error children
	        // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
	        if (childBlock.has_children && childBlock[childBlock.type].children) {
          childBlockString = ' '
	            .repeat(depth)
	            .concat(
	              childBlockString,
	              // @ts-expect-error children
	              // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access, @typescript-eslint/no-unsafe-argument
	              this.parse(childBlock[childBlock.type].children, depth + 2),
	            )
	        }
	        if (childBlock.type === 'unsupported') {
          markdown += 'NotionAPI Unsupported'.concat(
	            EOL_MD.repeat(2),
	            childBlockString,
	          )
	        }
	        if (childBlock.type === 'paragraph')
	          markdown += this.parseParagraph(childBlock).concat(childBlockString)
	        if (childBlock.type === 'code')
	          markdown += this.parseCodeBlock(childBlock).concat(childBlockString)
	        if (childBlock.type === 'quote')
          markdown += this.parseQuoteBlock(childBlock).concat(childBlockString)
	        if (childBlock.type === 'callout') {
	          markdown
	            += this.parseCalloutBlock(childBlock).concat(childBlockString)
	        }
	        if (childBlock.type.startsWith('heading_')) {
	          const headingLevel = Number(childBlock.type.split('_')[1])
	          markdown += this.parseHeading(
	            childBlock as HeadingBlock,
	            headingLevel,
          ).concat(childBlockString)
	        }
	        if (childBlock.type === 'bulleted_list_item') {
	          markdown
	            += this.parseBulletedListItems(childBlock).concat(childBlockString)
	        }
	        if (childBlock.type === 'numbered_list_item') {
	          markdown
	            += this.parseNumberedListItems(childBlock).concat(childBlockString)
	        }
        if (childBlock.type === 'to_do')
	          markdown += this.parseTodoBlock(childBlock).concat(childBlockString)
	        if (childBlock.type === 'toggle') {
	          markdown += this.parseToggleBlock(childBlock).replace(
	            '{{childBlock}}',
	            childBlockString,
	          )
	        }
	        if (childBlock.type === 'image')
	          markdown += this.parseImageBlock(childBlock).concat(childBlockString)
        if (childBlock.type === 'embed')
	          markdown += this.parseEmbedBlock(childBlock).concat(childBlockString)
	        if (childBlock.type === 'audio')
	          markdown += this.parseAudioBlock(childBlock).concat(childBlockString)
	        if (childBlock.type === 'video')
	          markdown += this.parseVideoBlock(childBlock).concat(childBlockString)
	        if (childBlock.type === 'file')
	          markdown += this.parseFileBlock(childBlock).concat(childBlockString)
	        if (childBlock.type === 'pdf')
	          markdown += this.parsePdfBlock(childBlock).concat(childBlockString)
        if (childBlock.type === 'link_to_page')
	          markdown += this.parseLinkToPageBlock(childBlock).concat(childBlockString)
	        if (childBlock.type === 'divider')
	          markdown += EOL_MD.concat('---', EOL_MD, childBlockString)
	        if (childBlock.type === 'link_preview')
	          markdown += this.parseLinkPreview(childBlock).concat(childBlockString)
	        return markdown
	      }, '')
	      .concat(EOL_MD)
	  }
  parseLinkPreview(linkPreviewBlock: LinkPreviewBlock): string {
	    return `<div notion-link-preview>
	      <a href="${linkPreviewBlock.link_preview.url}">${linkPreviewBlock.link_preview.url}</a>
	    </div>\n\n`
	  }
	  parseParagraph(paragraphBlock: ParagraphBlock): string {
	    const text: string = this.parseRichTexts(paragraphBlock.paragraph.rich_text)
	    return EOL_MD.concat(text, EOL_MD)
	  }
	  parseCodeBlock(codeBlock: CodeBlock): string {
    return `\n\n\`\`\`${codeBlock.code.language.toLowerCase() || ''}
	${(codeBlock.code.rich_text[0] as RichTextText).text.content}
	\`\`\`\n\n`.concat(EOL_MD)
	  }
	  parseQuoteBlock(quoteBlock: QuoteBlock): string {
	    return EOL_MD.concat(
	      `> ${this.parseRichTexts(quoteBlock.quote.rich_text)}`,
	      EOL_MD,
	    )
	  }
  parseCalloutBlock(calloutBlock: CalloutBlock) {
	    const callout = `<div notion-callout>
	  {{icon}}
	  <span notion-callout-text>
	    ${this.parseRichTexts(calloutBlock.callout.rich_text)}
	  </span>
	</div>`
	    function getCalloutIcon(
	      icon: CalloutIconEmoji | CalloutIconExternal | CalloutIconFile,
	    ) {
      switch (icon.type) {
	        case 'emoji':
	          return `<span notion-callout-emoji>${icon.emoji}</span>`
	        case 'external':
	          return `<img notion-callout-external src='${icon.external.url}' alt='notion-callout-external-link'/>`
	        case 'file':
	          // TODO: add support for Callout File
	          return 'notion-callout-file'
	      }
	    }
    return EOL_MD.concat(
	      // @ts-expect-error callout
	      callout.replace('{{icon}}', getCalloutIcon(calloutBlock.callout.icon)),
	      EOL_MD,
	    )
	  }
	  parseHeading(headingBlock: HeadingBlock, headingLevel: number): string {
	    return EOL_MD.concat(
	      '#'.repeat(headingLevel),
	      ' ',
      // @ts-expect-error heading
	      // eslint-disable-next-line @typescript-eslint/no-unsafe-argument, @typescript-eslint/no-unsafe-member-access
	      this.parseRichTexts(headingBlock[headingBlock.type].rich_text),
	      EOL_MD,
	    )
	  }
	  parseBulletedListItems(bulletedListItemBlock: BulletedListItemBlock): string {
	    return '* '.concat(
	      this.parseRichTexts(bulletedListItemBlock.bulleted_list_item.rich_text),
	      EOL_MD,
    )
	  }
	  parseNumberedListItems(numberedListItemBlock: NumberedListItemBlock): string {
	    return '1. '.concat(
	      this.parseRichTexts(numberedListItemBlock.numbered_list_item.rich_text),
	      EOL_MD,
	    )
	  }
	  parseTodoBlock(todoBlock: ToDoBlock): string {
	    return `- [${todoBlock.to_do.checked ? 'x' : ' '}] `.concat(
      this.parseRichTexts(todoBlock.to_do.rich_text),
	      EOL_MD,
	    )
	  }
	  parseToggleBlock(toggleBlock: ToggleBlock): string {
	    return `<details><summary>${this.parseRichTexts(
	      toggleBlock.toggle.rich_text,
	    )}</summary>{{childBlock}}</details>`
	  }
	  parseImageBlock(imageBlock: ImageBlock): string {
    const { url, caption } = this.parseFile(imageBlock.image)
	    return `
	<figure notion-figure>
	  <img src='${url}' alt='${caption}'>
	  <figcaption notion-figcaption>${caption}</figcaption>
	</figure>
	`.concat(EOL_MD)
	  }
	  parseAudioBlock(audioBlock: AudioBlock): string {
	    const { url, caption } = this.parseFile(audioBlock.audio)
    return `![${caption}](${url})`
	  }
	  parseVideoBlock(videoBlock: VideoBlock): string {
	    const { url, caption } = this.parseFile(videoBlock.video)
	    const [processed, iframeOrUrl] = processExternalVideoUrl(url)
	    if (processed)
	      return EOL_MD.concat(iframeOrUrl, EOL_MD)
	    return `To be supported: ${url} with ${caption}`.concat(EOL_MD)
	  }
	  parseFileBlock(fileBlock: FileBlock): string {
    const { url, caption } = this.parseFile(fileBlock.file)
	    return `To be supported: ${url} with ${caption}`.concat(EOL_MD)
	  }
	  parsePdfBlock(pdfBlock: PDFBlock): string {
	    const { url, caption } = this.parseFile(pdfBlock.pdf)
	    return `
	<figure>
	  <object data='${url}' type='application/pdf'></object>
	  <figcaption>${caption}</figcaption>
	</figure>
`.concat(EOL_MD)
	  }
	  parseEmbedBlock(embedBlock: EmbedBlock): string {
	    const embedded = `<iframe src='${embedBlock.embed.url}'></iframe>`
	    if (embedBlock.embed.caption) {
	      return `
	<figure>
	  ${embedded}
	  <figcaption>${this.parseRichTexts(embedBlock.embed.caption)}</figcaption>
	</figure>`.concat(EOL_MD)
    }
	    return embedded.concat(EOL_MD)
	  }
	  parseRichTexts(richTexts: RichText[]): string {
	    return richTexts.reduce((parsedContent, richText) => {
	      switch (richText.type) {
	        case 'text':
	          parsedContent += this.parseText(richText)
	          break
	        case 'mention':
          parsedContent += this.parseMention(richText)
	          break
	        case 'equation':
	          parsedContent += this.parseEquation(richText)
	          break
	      }
	      return parsedContent
	    }, '')
	  }
	  parseText(richText: RichTextText): string {
    const content = this.annotate(richText.annotations, richText.text.content)
	    return richText.text.link
	      ? this.annotateLink(richText.text, content)
	      : content
	  }
	  // TODO: support mention when we know what it actually means
	  parseMention(mention: RichTextMention): string {
	    switch (mention.mention.type) {
	      case 'user':
	        break
      case 'page':
	        break
	      case 'database':
	        break
	      case 'date':
	        break
	    }
	    return this.annotate(mention.annotations, mention.plain_text)
	  }
	  parseEquation(equation: RichTextEquation): string {
    return this.annotate(
	      equation.annotations,
	      `$${equation.equation.expression}$`,
	    )
	  }
	  parseLinkToPageBlock(linkToPage: LinkToPageBlock) {
	    const link = linkToPage.link_to_page as {
	      type: 'page_id'
	      page_id: string
	    }
    const id = uuidToId(link.page_id)
	    // These will get replaced if ncms-plugin-linker is used.
	    return `<a href="/${id}">/${id}</a>`
	  }
	  parseFile(file: ExternalFileWithCaption | FileWithCaption): {
	    caption: string
	    url: string
	  } {
	    const fileContent = {
	      caption: '',
      url: '',
	    }
	    switch (file.type) {
	      case 'external':
	        fileContent.url = file.external.url
	        break
	      case 'file':
	        fileContent.url = file.file.url
	        break
	    }
    fileContent.caption = file.caption
	      ? this.parseRichTexts(file.caption)
	      : fileContent.url
	    return fileContent
	  }
	  private annotate(annotations: Annotations, originalContent: string): string {
	    // @ts-expect-error reduce
	    return Object.entries(annotations).reduce(
	      // @ts-expect-error reduce
	      (
        annotatedContent,
	        [modifier, isOnOrColor]: [
	          keyof Annotations,
	          boolean | Annotations['color'],
	        ],
	      ) =>
	        isOnOrColor
	          ? this.annotateModifier(
	            modifier,
	            annotatedContent,
            isOnOrColor as Annotations['color'],
	          )
	          : annotatedContent,
	      originalContent,
	    )
	  }
	  private annotateLink(
	    text: RichTextText['text'],
	    annotatedContent: string,
	  ): string {
    return `[${annotatedContent}](${
	      // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
	      text?.link?.url ? text?.link.url : text.link
	    })`
	  }
	  private annotateModifier(
	    modifier: keyof Annotations,
	    originalContent: string,
	    color?: Annotations['color'],
	  ): string {
    switch (modifier) {
	      case 'bold':
	        return `**${originalContent}**`
	      case 'italic':
	        return `_${originalContent}_`
	      case 'strikethrough':
	        return `~~${originalContent}~~`
	      case 'underline':
	        return `<u>${originalContent}</u>`
	      case 'code':
        return `\`${originalContent}\``
	      case 'color':
	        if (color !== 'default')
	          // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
	          return `<span notion-color='${color}'>${originalContent}</span>`
	        return originalContent
	    }
	  }
	}
	function processExternalVideoUrl(url: string): [boolean, string] {
  if (url.includes('youtu'))
	    return processYoutubeUrl(url)
	  return [false, url]
	}
	function processYoutubeUrl(youtubeUrl: string): [boolean, string] {
	  const lastPart = youtubeUrl.split('/').pop()
	  const youtubeIframe = '<iframe width=\'560\' height=\'315\' src=\'https://www.youtube-nocookie.com/embed/{{videoId}}\' title=\'YouTube video player\' frameborder=\'0\' allow=\'accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\' allowfullscreen></iframe>'
	  if (!lastPart)
	    return [true, youtubeIframe]
	  if (lastPart.includes('watch')) {
    const [, queryString] = lastPart.split('watch')
	    const queryParams = new URLSearchParams(queryString)
	    if (queryParams.has('v'))
	      return [true, youtubeIframe.replace('{{videoId}}', queryParams.get('v') || '')]
	    return [false, youtubeUrl]
	  }
	  return [true, youtubeIframe.replace('{{videoId}}', lastPart)]
	}

import { dirname } from 'node:path'
	import fs from 'node:fs'
	import serializeJS from 'serialize-javascript'
	import { parse, stringify } from 'flatted'
	import _ from 'lodash'
	export function deserialize(serializedJavascript: string): Function {
	  // eslint-disable-next-line no-eval
	  return eval?.(`(${serializedJavascript})`) as Function
	}
	export function replaceFuncs(key: string, value: unknown) {
  return typeof value === 'function'
	    ? `__func__${serializeJS(value)}`
	    : value
	}
	export function reviveFuncs(key: string, value: unknown) {
	  return (typeof value === 'string' && value.startsWith('__func__'))
	    ? deserialize(value.replace('__func__', ''))
	    : value
	}
	export function filterAncestors(key: string, value: unknown) {
  if (key === '_ancestors')
	    return '[ancestors ref]'
	  return value
	}
	export function JSONStringifyWithFunctions(obj: Object): string {
	  return stringify(obj, replaceFuncs)
	}
	export function JSONParseWithFunctions(string: string): Object {
	  return parse(string, reviveFuncs) as Object
	}
export function writeFile(path: string, contents: string): void {
	  fs.mkdirSync(dirname(path), { recursive: true })
	  fs.writeFileSync(path, contents)
	}
	export function slugify(name: string): string {
	  return _.kebabCase(name)
	}
	export function routify(name: string): string {
	  const slug = slugify(name)
	  return slug.padStart(slug.length + 1, '/')
}

import fs from 'node:fs'
	import { Buffer } from 'node:buffer'
	import { fileTypeFromBuffer } from 'file-type'
	import { nanoid } from 'nanoid'
	import sharp from 'sharp'
	import type { Content, PageContent, PluginExecOptions } from '../types'
	interface ImageCacheEntry {
	  filename?: string
	  location?: string
	  url?: string
}
	interface ImageCache { [key: string]: Array<ImageCacheEntry> }
	const IMAGE_FILE_MATCH_REGEX = /(.*)X-Amz-Algorithm/g
	const IMAGE_CACHE_FILENAME = 'ncms-image-cache.json'
	const GENERIC_MATCH = /\b(https?:\/\/[\w_#&?.\/-]*?\.(?:png|jpe?g|svg|ico))(?=[`'")\]])/ig
	const IMAGE_SOURCE_MATCH = /<img[^>]*src=['|"](https?:\/\/[^'|"]+)(?:['|"])/ig
	function multiStringMatch(stringA: unknown, stringB: unknown): Boolean {
	  if (typeof stringA !== 'string' || typeof stringB !== 'string' || !stringA || !stringB)
	    return false
	  const matchA = stringA.match(IMAGE_FILE_MATCH_REGEX)
  const matchB = stringB.match(IMAGE_FILE_MATCH_REGEX)
	  return Boolean(matchA && matchB && (matchA[0] === matchB[0]))
	}
	export default function ({
	  globalExtension = 'webp',
	  compression = 80,
	  imageCacheDirectory = './public',
	  customMatchers = [],
	}: {
	  globalExtension?: 'webp' | 'png' | 'jpeg'
  compression?: number
	  imageCacheDirectory?: string
	  customMatchers?: RegExp[]
	} = {}) {
	  let imageCache: ImageCache
	  try {
	  // Pull existing imageCache
	    if (fs.existsSync(`${imageCacheDirectory}/remote/${IMAGE_CACHE_FILENAME}`)) {
	      imageCache = JSON.parse(
	        fs.readFileSync(`${imageCacheDirectory}/remote/${IMAGE_CACHE_FILENAME}`, 'utf-8')) as ImageCache
    }
	    else {
	      imageCache = {}
	    }
	  }
	  catch (e) {
	    console.warn(e, 'ncms-plugin-images: error attempting to read image cache.')
	    imageCache = {}
	  }
	  async function writeOutImage(imageUrl: string, existingImageFile: ImageCacheEntry): Promise<string> {
    let filename = ''
	    if (existingImageFile)
	      return existingImageFile.filename as string
	    const response = await fetch(imageUrl)
	    const arrayBuffer = await response.arrayBuffer()
	    const buffer = Buffer.from(arrayBuffer)
	    const fileType = await fileTypeFromBuffer(buffer)
	    if (fileType?.ext) {
	      const id = nanoid(6)
	      filename = `${id}.remote.${globalExtension}`
      const outputFilePath = `${imageCacheDirectory}/remote/${filename}`
	      const imageBuffer = sharp(buffer)
	      const webPBuffer = await imageBuffer[globalExtension]({
	        quality: compression,
	        nearLossless: true,
	        effort: 6,
	      }).toBuffer()
	      const writeStream = fs.createWriteStream(outputFilePath)
	      // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
	      writeStream.on('error', err => console.warn(`ncms-plugin-images: failed to write image file: ${err}`))
      writeStream.write(webPBuffer)
	    }
	    return filename
	  }
	  function detectExisting(path: string, imageUrl: string): ImageCacheEntry {
	    const entries = imageCache[path]
	    return entries.filter((entry) => {
	      return multiStringMatch(entry.url, imageUrl) || multiStringMatch(entry.location, imageUrl)
	    })[0]
	  }
  async function processImage(
	    path: string,
	    imageUrl: string,
	    updator: { update: Content | string },
	    debug?: boolean): Promise<void> {
	    if (imageUrl && path) {
	      let filename = ''
	      try {
	        filename = await writeOutImage(imageUrl, detectExisting(path, imageUrl))
	      }
      catch (e) {
	        if (debug)
	          console.warn('ncms-plugin-images: File type could not be reliably determined! The binary data may be malformed! No file saved!')
	        return
	      }
	      if (filename) {
	        imageCache[path].push({
	          filename,
	          location: `/remote/${filename}`,
	          url: imageUrl,
        })
	        // if we don't do this, the replaceall cant find the proper url below
	        if (typeof updator.update !== 'string') {
	          if (updator.update?.html.includes('amazonaws'))
	            updator.update.html = updator.update.html.replaceAll('&amp;', '&')
	          updator.update.html = updator.update.html.replace(imageUrl, `/remote/${filename}`)
	        }
	        else {
	          // This replaces the coverImage
	          updator.update = updator.update.replace(imageUrl, `/remote/${filename}`)
        }
	        if (debug)
	          console.log('ncms-plugin-images: rewriting', path, 'at', filename)
	      }
	    }
	  }
	  return {
	    name: 'ncms-plugin-images',
	    hook: 'during-tree',
	    core: true,
    exec: async (context: PageContent, options: PluginExecOptions) => {
	      const copyOfContext = structuredClone(context)
	      if (!copyOfContext.path)
	        return
	      const matchables = [
	        GENERIC_MATCH,
	        IMAGE_SOURCE_MATCH,
	        ...customMatchers,
	      ]
	      if (!imageCache[copyOfContext.path])
        imageCache[copyOfContext.path] = [] as ImageCacheEntry[]
	      const contents = {
	        update: copyOfContext.content as Content,
	      }
	      const coverImage = {
	        update: copyOfContext.coverImage as string,
	      }
	      // Must run all async in series so that we don't end up with duplicates
	      for (const match of matchables) {
	        if (!copyOfContext.path)
          return
	        const path = copyOfContext.path
	        const matched = (contents.update && Array.from(contents.update.html.matchAll(match), m => m[1])) || []
	        const matchedCoverImages = (coverImage.update && [coverImage.update]) || []
	        for (const imageUrl of matched)
	          await processImage(path, imageUrl, contents, options.debug)
	        for (const imageUrl of matchedCoverImages)
	          await processImage(path, imageUrl, coverImage, options.debug)
	      }
	      copyOfContext.content = contents.update
      copyOfContext.coverImage = coverImage.update
	      try {
	        if (!fs.existsSync(`${imageCacheDirectory}/remote`))
	          fs.mkdirSync(`${imageCacheDirectory}/remote`)
	        fs.writeFileSync(`${imageCacheDirectory}/remote/${IMAGE_CACHE_FILENAME}`, JSON.stringify(imageCache))
	        if (options.debug)
	          fs.writeFileSync('debug/images.json', JSON.stringify(imageCache))
	      }
	      catch (e) {
	        if (options.debug)
          console.warn(e, 'ncms-plugin-images: error writing to image cache.')
	      }
	      return copyOfContext
	    },
	  }
	}

