import { Action, ActionFactory as ActionFactoryInterface, Message } from '@binsoul/node-red-bundle-processing';
	import type { Node, NodeAPI } from '@node-red/registry';
	import { NodeMessageInFlow, NodeStatus } from 'node-red';
	import { clearTimeout, setTimeout } from 'timers';
	import { DailyResetAction } from './Action/DailyResetAction';
	import { OutputAction } from './Action/OutputAction';
	import { UnavailableAction } from './Action/UnavailableAction';
	import { UpdateAction } from './Action/UpdateAction';
	import type { Configuration } from './Configuration';
	import { Storage } from './Storage';
interface MessageData extends NodeMessageInFlow {
	    command?: string;
	    timestamp?: number;
	}
	function formatTime(timestamp: number) {
	    const date = new Date(timestamp);
	    return date.getHours().toString().padStart(2, '0') + ':' + date.getMinutes().toString().padStart(2, '0');
	}
	/**
	 * Generates actions.
 */
	export class ActionFactory implements ActionFactoryInterface {
	    private readonly configuration: Configuration;
	    private readonly RED: NodeAPI;
	    private readonly node: Node;
	    private readonly storage: Storage;
	    private firstMessage = true;
	    private updateTimer: NodeJS.Timeout | null = null;
	    private dailyResetTimer: NodeJS.Timeout | null = null;
	    private unavailableTimer: NodeJS.Timeout | null = null;
    constructor(RED: NodeAPI, node: Node, configuration: Configuration) {
	        this.RED = RED;
	        this.node = node;
	        this.configuration = configuration;
	        this.storage = new Storage(configuration);
	    }
	    build(message: Message): Action | Array<Action> | null {
	        const data: MessageData = message.data;
	        const command = data.command;
	        if (this.firstMessage) {
            this.firstMessage = false;
	            this.scheduleUnavailableCheck();
	        }
	        if (typeof command !== 'undefined' && ('' + command).trim() !== '') {
	            switch (command.toLowerCase()) {
	                case 'update':
	                    this.storage.setUpdating(true);
	                    return new UpdateAction(
	                        this.configuration,
	                        this.storage,
                        () => this.outputCallback(),
	                        (status: NodeStatus) => this.nodeStatusCallback(status),
	                    );
	                case 'output':
	                    this.storage.setUpdating(false);
	                    this.scheduleUnavailableCheck();
	                    return new OutputAction(this.configuration, this.storage);
	                case 'dailyreset':
	                    return new DailyResetAction(this.configuration, this.storage);
	                case 'unavailable':
                    return new UnavailableAction(this.configuration, this.storage);
	            }
	        }
	        if (!this.storage.isUpdating()) {
	            if (this.updateTimer !== null) {
	                clearTimeout(this.updateTimer);
	                this.updateTimer = null;
	            }
	            this.scheduleUpdate();
	            this.storage.setUpdating(true);
            return new UpdateAction(
	                this.configuration,
	                this.storage,
	                () => this.outputCallback(),
	                (status: NodeStatus) => this.nodeStatusCallback(status),
	            );
	        }
	        return null;
	    }
	    setup(): void {
        if (this.configuration.updateMode === 'never') {
	            this.node.status({
	                fill: 'yellow',
	                shape: 'dot',
	                text: 'waiting for message',
	            });
	            return;
	        }
	        const now = Date.now();
	        const firstUpdateAt = Math.ceil(now / (this.configuration.updateFrequency * 60000)) * this.configuration.updateFrequency * 60000;
        this.updateTimer = setTimeout(() => this.executeUpdate(), firstUpdateAt - now + 1000);
	        const tomorrow = new Date();
	        tomorrow.setDate(tomorrow.getDate() + 1);
	        tomorrow.setHours(0, 0, 0);
	        this.dailyResetTimer = setTimeout(() => this.executeDailyReset(), tomorrow.getTime() - now);
	        this.node.status({
	            fill: 'yellow',
	            shape: 'dot',
	            text: `waiting until ${formatTime(firstUpdateAt)}`,
	        });
    }
	    teardown(): void {
	        if (this.updateTimer !== null) {
	            clearTimeout(this.updateTimer);
	            this.updateTimer = null;
	        }
	        if (this.dailyResetTimer !== null) {
	            clearTimeout(this.dailyResetTimer);
	            this.dailyResetTimer = null;
	        }
        if (this.unavailableTimer !== null) {
	            clearTimeout(this.unavailableTimer);
	            this.unavailableTimer = null;
	        }
	    }
	    /**
	     * Starts a timer if automatic updates are enabled and no timer exists.
	     */
	    private scheduleUpdate(): void {
	        if (this.updateTimer !== null || this.configuration.updateMode === 'never') {
            return;
	        }
	        const now = Date.now();
	        this.updateTimer = setTimeout(() => this.executeUpdate(), this.getStartOfSlot(now) - now + this.configuration.updateFrequency * 60000 + 1000);
	    }
	    /**
	     * Handles automatic updates.
	     */
	    executeUpdate(): void {
	        const now = new Date().getTime();
        const nextUpdateAt = this.getStartOfSlot(now) + this.configuration.updateFrequency * 60000 + 1000;
	        this.updateTimer = setTimeout(() => this.executeUpdate(), nextUpdateAt - now);
	        // trigger node.on('input', () => {})
	        this.node.receive(<MessageData>{
	            command: 'update',
	            timestamp: now,
	        });
	    }
	    /**
	     * Handles daily resets of counters.
     */
	    executeDailyReset(): void {
	        const now = Date.now();
	        const tomorrow = new Date();
	        tomorrow.setDate(tomorrow.getDate() + 1);
	        tomorrow.setHours(0, 0, 0);
	        this.dailyResetTimer = setTimeout(() => this.executeDailyReset(), tomorrow.getTime() - now);
	        // trigger node.on('input', () => {})
	        this.node.receive(<MessageData>{
	            command: 'dailyReset',
            timestamp: now,
	        });
	    }
	    /**
	     * Starts a timer which changes the device status to unavailable.
	     */
	    private scheduleUnavailableCheck(): void {
	        if (this.unavailableTimer !== null) {
	            clearTimeout(this.unavailableTimer);
	            this.unavailableTimer = null;
        }
	        const now = Date.now();
	        this.unavailableTimer = setTimeout(() => this.executeUnavailableCheck(), this.getStartOfSlot(now) - now + this.configuration.deviceTimeout * 60000);
	    }
	    private executeUnavailableCheck(): void {
	        this.unavailableTimer = null;
	        this.node.receive(<MessageData>{
	            command: 'unavailable',
	        });
	    }
    outputCallback(): void {
	        this.node.receive(<MessageData>{
	            command: 'output',
	        });
	    }
	    nodeStatusCallback(status: NodeStatus): void {
	        this.node.status(status);
	    }
	    private getStartOfSlot(timestamp: number) {
	        return Math.floor(timestamp / 60000) * 60000;
    }
	}

import { Configuration } from './Configuration';
	import type { UserConfiguration } from './UserConfiguration';
	const getString = function (value: unknown, defaultValue: string): string {
	    const result = value || defaultValue;
	    const stringValue = '' + result;
	    if (stringValue.trim() === '') {
	        return defaultValue;
	    }
	    return stringValue;
	};
/**
	 * Creates a sanitized configuration from user input.
	 */
	export function buildConfiguration(config: UserConfiguration): Configuration {
	    const outputTarget = getString(config.outputTarget, 'msg');
	    const outputProperty = getString(config.outputProperty, 'payload');
	    const deviceIp = getString(config.deviceIp, '');
	    const deviceSerialNumber = getString(config.deviceSerialNumber, '');
	    const deviceTimeout = Number(config.deviceTimeout || 30);
	    const updateMode = getString(config.updateMode, 'never');
    const updateFrequency = Number(config.updateFrequency || 5);
	    return new Configuration(outputTarget, outputProperty, deviceIp, deviceSerialNumber, deviceTimeout, updateMode, updateFrequency);
	}

import { MessageHandler } from '@binsoul/node-red-bundle-processing';
	import type { Node, NodeInitializer } from 'node-red';
	import { ActionFactory } from './ActionFactory';
	import { buildConfiguration } from './ConfigurationBuilder';
	import type { UserConfiguration } from './UserConfiguration';
	const nodeInitializer: NodeInitializer = (RED): void => {
	    function NodeConstructor(this: Node, userConfiguration: UserConfiguration): void {
	        RED.nodes.createNode(this, userConfiguration);
	        const configuration = buildConfiguration(userConfiguration);
	        const actionFactory = new ActionFactory(RED, this, configuration);
        const messageHandler = new MessageHandler(RED, this, actionFactory);
	        actionFactory.setup();
	        this.on('input', (msg, send, done) => messageHandler.handle(msg, send, done));
	        this.on('close', () => actionFactory.teardown());
	    }
	    RED.nodes.registerType('binsoul-deye-sun-g3', NodeConstructor);
	};
	export = nodeInitializer;

import type { NodeDef } from 'node-red';
	/**
	 * Configuration options available to users.
	 */
	export interface UserConfigurationOptions {
	    outputTarget?: string;
	    outputProperty?: string;
	    deviceIp?: string;
	    deviceSerialNumber?: string;
	    deviceTimeout?: number;
    updateMode?: string;
	    updateFrequency?: number;
	}
	/**
	 * Configuration generated by the user in the editor.
	 */
	export interface UserConfiguration extends NodeDef, UserConfigurationOptions {}

/**
	 * Sanitized configuration generated from user input.
	 */
	export class Configuration {
	    outputProperty: string;
	    outputTarget: string;
	    deviceIp: string;
	    deviceSerialNumber: string;
	    deviceTimeout: number;
	    updateMode: string;
    updateFrequency: number;
	    constructor(outputTarget = 'msg', outputProperty = 'payload', deviceIp: string, deviceSerialNumber: string, deviceTimeout = 30, updateMode = 'never', updateFrequency = 5) {
	        this.outputTarget = outputTarget;
	        this.outputProperty = outputProperty;
	        this.deviceIp = deviceIp;
	        this.deviceSerialNumber = deviceSerialNumber;
	        this.deviceTimeout = deviceTimeout;
	        this.updateMode = updateMode;
	        this.updateFrequency = updateFrequency;
	    }
}

import { Configuration } from './Configuration';
	import { RegisterValues } from './DeyeRegisters';
	import { NodeOutput } from './NodeOutput';
	/**
	 * Stores events and a history of past events.
	 */
	export class Storage {
	    private configuration: Configuration;
	    private updating = false;
	    private connected = false;
    private lastConnectionAt: number | null = null;
	    private lastDataAt: number | null = null;
	    private available = true;
	    private data: RegisterValues | null = null;
	    constructor(configuration: Configuration) {
	        this.configuration = configuration;
	    }
	    public isUpdating(): boolean {
	        return this.updating;
	    }
    public setUpdating(value: boolean) {
	        this.updating = value;
	        if (value) {
	            this.connected = false;
	        }
	    }
	    public setConnected() {
	        this.connected = true;
	        this.lastConnectionAt = Date.now();
	        this.available = true;
    }
	    public getData(): NodeOutput | null {
	        if (this.data === null || !this.available) {
	            return null;
	        }
	        const pv1 = {
	            voltage: this.data.pv1Voltage,
	            current: this.data.pv1Current,
	            power: this.round(this.data.pv1Voltage * this.data.pv1Current, '4'),
	        };
        const pv2 = {
	            voltage: this.data.pv2Voltage,
	            current: this.data.pv2Current,
	            power: this.round(this.data.pv2Voltage * this.data.pv2Current, '4'),
	        };
	        const pv3 = {
	            voltage: this.data.pv3Voltage,
	            current: this.data.pv3Current,
	            power: this.round(this.data.pv3Voltage * this.data.pv3Current, '4'),
	        };
        const pv4 = {
	            voltage: this.data.pv4Voltage,
	            current: this.data.pv4Current,
	            power: this.round(this.data.pv4Voltage * this.data.pv4Current, '4'),
	        };
	        const output = {
	            power: this.data.acPower,
	            voltage: this.data.acVoltage,
	            current: this.data.acCurrent,
	            frequency: this.data.acFrequency,
        };
	        const counters = {
	            totalEnergy: this.data.totalEnergy,
	            pv1TotalEnergy: this.data.pv1TotalEnergy,
	            pv2TotalEnergy: this.data.pv2TotalEnergy,
	            pv3TotalEnergy: this.data.pv3TotalEnergy,
	            pv4TotalEnergy: this.data.pv4TotalEnergy,
	            totalEnergyToday: this.data.totalEnergyToday,
	            pv1TotalEnergyToday: this.data.pv1TotalEnergyToday,
	            pv2TotalEnergyToday: this.data.pv2TotalEnergyToday,
            pv3TotalEnergyToday: this.data.pv3TotalEnergyToday,
	            pv4TotalEnergyToday: this.data.pv4TotalEnergyToday,
	        };
	        return {
	            pv1: pv1,
	            pv2: pv2,
	            pv3: pv3,
	            pv4: pv4,
	            output: output,
	            counters: counters,
            temperature: this.data.temperature,
	            isAvailable: this.available,
	        };
	    }
	    public setData(data: RegisterValues) {
	        if (this.data !== null && this.data.totalEnergy > data.totalEnergy) {
	            // totalEnergy should always increase while the node is running
	            return;
	        }
	        this.data = data;
        this.lastDataAt = Date.now();
	        this.available = true;
	    }
	    public resetCounters(): void {
	        if (this.data !== null) {
	            this.data.pv1TotalEnergyToday = 0;
	            this.data.pv2TotalEnergyToday = 0;
	            this.data.pv3TotalEnergyToday = 0;
	            this.data.pv4TotalEnergyToday = 0;
	            this.data.totalEnergyToday = 0;
        }
	    }
	    public resetRuntime(): void {
	        if (this.data !== null) {
	            this.data.pv1Voltage = 0;
	            this.data.pv1Current = 0;
	            this.data.pv2Voltage = 0;
	            this.data.pv2Current = 0;
	            this.data.pv3Voltage = 0;
	            this.data.pv3Current = 0;
            this.data.pv4Voltage = 0;
	            this.data.pv4Current = 0;
	            this.data.acFrequency = 0;
	            this.data.acPower = 0;
	            this.data.acVoltage = 0;
	            this.data.acCurrent = 0;
	            this.data.uptime = 0;
	            this.data.operatingPower = 0;
	            this.data.temperature = null;
	        }
    }
	    public isAvailable(): boolean {
	        return this.available;
	    }
	    public setAvailable(value: boolean): void {
	        this.available = value;
	    }
	    private round(value: number, decimals: string): number {
	        return Number(Math.round(<number>(<unknown>(value + 'e' + decimals))) + 'e-' + decimals);
	    }
}

interface Definition {
	    name: string;
	    type: 'unsigned' | 'signed' | 'string';
	    scale?: number;
	    offset?: number;
	    registers: Array<number>;
	    unit: string;
	}
	export interface RegisterValues {
	    [key: string]: number | string | null;
    id: string;
	    statusCode: number;
	    totalEnergy: number;
	    totalEnergyToday: number;
	    acPower: number;
	    acVoltage: number;
	    acCurrent: number;
	    acFrequency: number;
	    pv1Voltage: number;
	    pv1Current: number;
    pv1TotalEnergy: number;
	    pv1TotalEnergyToday: number;
	    pv2Voltage: number;
	    pv2Current: number;
	    pv2TotalEnergy: number;
	    pv2TotalEnergyToday: number;
	    pv3Voltage: number;
	    pv3Current: number;
	    pv3TotalEnergy: number;
	    pv3TotalEnergyToday: number;
    pv4Voltage: number;
	    pv4Current: number;
	    pv4TotalEnergy: number;
	    pv4TotalEnergyToday: number;
	    temperature: number | null;
	    uptime: number;
	    operatingPower: number;
	}
	export class DeyeRegisters {
	    private dataDefinition: Array<Definition> = [
        { name: 'id', type: 'string', registers: [0, 1, 2, 3, 4], unit: '' },
	        { name: 'statusCode', scale: 1, type: 'unsigned', registers: [56], unit: '' },
	        { name: 'totalEnergy', scale: 0.1, type: 'unsigned', registers: [60, 61], unit: 'kWh' },
	        { name: 'totalEnergyToday', scale: 0.1, type: 'unsigned', registers: [57], unit: 'kWh' },
	        { name: 'acPower', scale: 0.1, type: 'unsigned', registers: [83, 84], unit: 'W' },
	        { name: 'acVoltage', scale: 0.1, type: 'unsigned', registers: [70], unit: 'V' },
	        { name: 'acCurrent', scale: 0.1, type: 'signed', registers: [73], unit: 'A' },
	        { name: 'acFrequency', scale: 0.01, type: 'unsigned', registers: [76], unit: 'Hz' },
	        { name: 'pv1Voltage', scale: 0.1, type: 'unsigned', registers: [106], unit: 'V' },
	        { name: 'pv1Current', scale: 0.1, type: 'unsigned', registers: [107], unit: 'A' },
        { name: 'pv1TotalEnergy', scale: 0.1, type: 'unsigned', registers: [66, 67], unit: 'kWh' },
	        { name: 'pv1TotalEnergyToday', scale: 0.1, type: 'unsigned', registers: [62], unit: 'kWh' },
	        { name: 'pv2Voltage', scale: 0.1, type: 'unsigned', registers: [108], unit: 'V' },
	        { name: 'pv2Current', scale: 0.1, type: 'unsigned', registers: [109], unit: 'A' },
	        { name: 'pv2TotalEnergy', scale: 0.1, type: 'unsigned', registers: [68, 69], unit: 'kWh' },
	        { name: 'pv2TotalEnergyToday', scale: 0.1, type: 'unsigned', registers: [63], unit: 'kWh' },
	        { name: 'pv3Voltage', scale: 0.1, type: 'unsigned', registers: [110], unit: 'V' },
	        { name: 'pv3Current', scale: 0.1, type: 'unsigned', registers: [111], unit: 'A' },
	        { name: 'pv3TotalEnergy', scale: 0.1, type: 'unsigned', registers: [71, 72], unit: 'kWh' },
	        { name: 'pv3TotalEnergyToday', scale: 0.1, type: 'unsigned', registers: [64], unit: 'kWh' },
        { name: 'pv4Voltage', scale: 0.1, type: 'unsigned', registers: [112], unit: 'V' },
	        { name: 'pv4Current', scale: 0.1, type: 'unsigned', registers: [113], unit: 'A' },
	        { name: 'pv4TotalEnergy', scale: 0.1, type: 'unsigned', registers: [74, 75], unit: 'kWh' },
	        { name: 'pv4TotalEnergyToday', scale: 0.1, type: 'unsigned', registers: [65], unit: 'kWh' },
	        { name: 'temperature', scale: 0.01, offset: 1000, type: 'unsigned', registers: [87], unit: 'Â°C' },
	        { name: 'uptime', scale: 1, type: 'unsigned', registers: [59], unit: 'm' },
	        { name: 'operatingPower', scale: 0.1, type: 'unsigned', registers: [77], unit: 'W' },
	    ];
	    public parse(modbusRegisters: Array<number>): RegisterValues {
	        const result: RegisterValues = {
            id: '',
	            statusCode: 0,
	            totalEnergy: 0,
	            totalEnergyToday: 0,
	            acPower: 0,
	            acVoltage: 0,
	            acCurrent: 0,
	            acFrequency: 0,
	            pv1Voltage: 0,
	            pv1Current: 0,
            pv1TotalEnergy: 0,
	            pv1TotalEnergyToday: 0,
	            pv2Voltage: 0,
	            pv2Current: 0,
	            pv2TotalEnergy: 0,
	            pv2TotalEnergyToday: 0,
	            pv3Voltage: 0,
	            pv3Current: 0,
	            pv3TotalEnergy: 0,
	            pv3TotalEnergyToday: 0,
            pv4Voltage: 0,
	            pv4Current: 0,
	            pv4TotalEnergy: 0,
	            pv4TotalEnergyToday: 0,
	            temperature: 0,
	            uptime: 0,
	            operatingPower: 0,
	        };
	        for (const definition of this.dataDefinition) {
	            let value;
            const type = definition.type;
	            if (type === 'unsigned') {
	                value = this.parseUnsigned(modbusRegisters, definition.registers, definition.scale || 1, definition.offset || 0);
	            } else if (type === 'signed') {
	                value = this.parseSigned(modbusRegisters, definition.registers, definition.scale || 1, definition.offset || 0);
	            } else if (type === 'string') {
	                value = this.parseString(modbusRegisters, definition.registers);
	            } else {
	                value = 'unknown';
	            }
            result[definition.name] = value;
	        }
	        return result;
	    }
	    private parseUnsigned(modbusRegisters: Array<number>, registers: Array<number>, scale: number, offset: number): number {
	        let value = 0;
	        let shift = 0;
	        for (const index of registers) {
	            const temp = modbusRegisters[index];
	            value += (temp & 0xffff) << shift;
            shift += 16;
	        }
	        value = value - offset;
	        value = value * scale;
	        return this.round(value, '4');
	    }
	    private parseSigned(modbusRegisters: Array<number>, registers: Array<number>, scale: number, offset: number): number {
	        let value = 0;
	        let shift = 0;
	        let maxint = 0;
        for (const index of registers) {
	            maxint <<= 16;
	            maxint |= 0xffff;
	            const temp = modbusRegisters[index];
	            value += (temp & 0xffff) << shift;
	            shift += 16;
	        }
	        value = value - offset;
	        if (value > maxint / 2) {
	            value = (value - maxint) * scale;
        } else {
	            value = value * scale;
	        }
	        return this.round(value, '4');
	    }
	    private parseString(modbusRegisters: Array<number>, registers: Array<number>): string {
	        let value = '';
	        for (const index of registers) {
	            const temp = modbusRegisters[index];
	            value = value + String.fromCharCode(temp >> 8) + String.fromCharCode(temp & 0xff);
        }
	        return value;
	    }
	    private round(value: number, decimals: string): number {
	        return Number(Math.round(<number>(<unknown>(value + 'e' + decimals))) + 'e-' + decimals);
	    }
	}

import { Action, InputDefinition, Output, OutputDefinition } from '@binsoul/node-red-bundle-processing';
	import type { Configuration } from '../Configuration';
	import { Storage } from '../Storage';
	export class DailyResetAction implements Action {
	    private readonly configuration: Configuration;
	    private storage: Storage;
	    constructor(configuration: Configuration, storage: Storage) {
	        this.configuration = configuration;
	        this.storage = storage;
	    }
    defineInput(): InputDefinition {
	        return new InputDefinition();
	    }
	    defineOutput(): OutputDefinition {
	        const result = new OutputDefinition();
	        result.set('output', {
	            target: this.configuration.outputTarget,
	            property: this.configuration.outputProperty,
	            type: 'object',
	            channel: 0,
        });
	        return result;
	    }
	    execute(): Output {
	        const result = new Output();
	        const isAvailable = this.storage.isAvailable();
	        this.storage.setAvailable(true);
	        this.storage.resetCounters();
	        const data = this.storage.getData();
	        this.storage.setAvailable(isAvailable);
        if (data !== null) {
	            data.isAvailable = isAvailable;
	            result.setValue('output', data);
	            result.setNodeStatus({
	                fill: 'green',
	                shape: 'dot',
	                text: `daily reset`,
	            });
	        }
	        return result;
    }
	}

import { Action, InputDefinition, Output, OutputDefinition } from '@binsoul/node-red-bundle-processing';
	import type { Configuration } from '../Configuration';
	import { Storage } from '../Storage';
	export class UnavailableAction implements Action {
	    private readonly configuration: Configuration;
	    private storage: Storage;
	    constructor(configuration: Configuration, storage: Storage) {
	        this.configuration = configuration;
	        this.storage = storage;
	    }
    defineInput(): InputDefinition {
	        return new InputDefinition();
	    }
	    defineOutput(): OutputDefinition {
	        const result = new OutputDefinition();
	        result.set('output', {
	            target: this.configuration.outputTarget,
	            property: this.configuration.outputProperty,
	            type: 'object',
	            channel: 0,
        });
	        return result;
	    }
	    execute(): Output {
	        const result = new Output();
	        this.storage.setAvailable(true);
	        this.storage.resetRuntime();
	        const data = this.storage.getData();
	        this.storage.setAvailable(false);
	        if (data !== null) {
            data.isAvailable = false;
	            result.setValue('output', data);
	        }
	        result.setNodeStatus({
	            fill: 'yellow',
	            shape: 'dot',
	            text: 'unavailable',
	        });
	        return result;
	    }
}

import { Action, InputDefinition, Output, OutputDefinition } from '@binsoul/node-red-bundle-processing';
	import type { Configuration } from '../Configuration';
	import { Storage } from '../Storage';
	export class OutputAction implements Action {
	    private readonly configuration: Configuration;
	    private storage: Storage;
	    constructor(configuration: Configuration, storage: Storage) {
	        this.configuration = configuration;
	        this.storage = storage;
	    }
    defineInput(): InputDefinition {
	        return new InputDefinition();
	    }
	    defineOutput(): OutputDefinition {
	        const result = new OutputDefinition();
	        result.set('output', {
	            target: this.configuration.outputTarget,
	            property: this.configuration.outputProperty,
	            type: 'object',
	            channel: 0,
        });
	        return result;
	    }
	    execute(): Output {
	        const result = new Output();
	        const data = this.storage.getData();
	        if (data !== null) {
	            result.setValue('output', data);
	            result.setNodeStatus({
	                fill: 'green',
                shape: 'dot',
	                text: `${data.output.power} W`,
	            });
	        }
	        return result;
	    }
	}

import { Action, InputDefinition, Output, OutputDefinition } from '@binsoul/node-red-bundle-processing';
	import { ModbusRtu } from '@binsoul/nodejs-modbus';
	import { SolarmanV5 } from '@binsoul/nodejs-solarman';
	import * as net from 'net';
	import { NodeStatus } from 'node-red';
	import type { Configuration } from '../Configuration';
	import { DeyeRegisters } from '../DeyeRegisters';
	import { Storage } from '../Storage';
	export class UpdateAction implements Action {
	    private readonly configuration: Configuration;
    private readonly storage: Storage;
	    private readonly outputCallback: () => void;
	    private readonly nodeStatusCallback: (status: NodeStatus) => void;
	    constructor(configuration: Configuration, storage: Storage, outputCallback: () => void, nodeStatusCallback: (status: NodeStatus) => void) {
	        this.configuration = configuration;
	        this.storage = storage;
	        this.outputCallback = outputCallback;
	        this.nodeStatusCallback = nodeStatusCallback;
	    }
	    defineInput(): InputDefinition {
        return new InputDefinition();
	    }
	    defineOutput(): OutputDefinition {
	        return new OutputDefinition();
	    }
	    execute(): Output {
	        const client = new net.Socket();
	        client.setTimeout(5000);
	        let hasConnected = false;
	        let errorMessage = '';
        let retryCount = 0;
	        const unitAddress = 1;
	        const firstRegister = 0x0003;
	        const lastRegister = 0x0080;
	        const modbus = new ModbusRtu(unitAddress);
	        const modbusFrame = modbus.requestHoldingRegisters(firstRegister, lastRegister);
	        const solarman = new SolarmanV5(this.configuration.deviceSerialNumber, true);
	        const request = solarman.wrapModbusFrame(modbusFrame);
	        // Functions to handle socket events
	        const makeConnection = () => {
            client.connect({ host: this.configuration.deviceIp, port: 8899 });
	        };
	        const connectEventHandler = () => {
	            hasConnected = true;
	            this.storage.setConnected();
	            client.write(request);
	        };
	        const dataEventHandler = (data: Buffer) => {
	            try {
	                const modbusFrame = solarman.unwrapModbusFrame(data);
                const values = modbus.fetchHoldingRegisters(modbusFrame);
	                const parser = new DeyeRegisters();
	                this.storage.setData(parser.parse(values));
	                this.outputCallback();
	            } catch (error) {
	                if (error instanceof Error) {
	                    this.nodeStatusCallback({
	                        fill: 'red',
	                        shape: 'dot',
	                        text: error.message,
                    });
	                }
	            }
	            client.end();
	        };
	        const timeoutEventHandler = () => {
	            errorMessage = 'Connection timed out';
	            client.end();
	        };
	        const errorEventHandler = (error: Error) => {
            errorMessage = error.message;
	            client.end();
	        };
	        const closeEventHandler = () => {
	            if (hasConnected) {
	                return;
	            }
	            if (!this.storage.isAvailable()) {
	                this.nodeStatusCallback({
	                    fill: 'yellow',
                    shape: 'dot',
	                    text: 'unavailable',
	                });
	            } else if (retryCount < 5) {
	                retryCount++;
	                this.nodeStatusCallback({
	                    fill: 'yellow',
	                    shape: 'dot',
	                    text: 'retry ' + retryCount,
	                });
                setTimeout(makeConnection, 5000);
	            } else {
	                this.nodeStatusCallback({
	                    fill: 'red',
	                    shape: 'dot',
	                    text: errorMessage,
	                });
	            }
	        };
	        client.on('connect', connectEventHandler);
        client.on('data', dataEventHandler);
	        client.on('timeout', timeoutEventHandler);
	        client.on('error', errorEventHandler);
	        client.on('close', closeEventHandler);
	        this.nodeStatusCallback({
	            fill: 'yellow',
	            shape: 'dot',
	            text: 'updating',
	        });
	        makeConnection();
        return new Output();
	    }
	}

