#!/usr/bin/env -S ts-node --esm
	import { writeXmltv } from "../src/main.js";
	import { ArgumentParser } from "argparse";
	import type { Xmltv } from "../src/types.js";
	/**
	 * Generate a xmltv file with the given number of channels and programmes
	 *
	 * Usage:
	 *   ./generateXml.ts -c 100 -p 250000 > tests/fixtures/c100-p250_000.xml
	 *   ./generateXml.ts --channels 100 --programmes 250000 > tests/fixtures/c100-p250_000.xml
 */
	const parser = new ArgumentParser({
	  description: "Generate xmltv xml file",
	});
	parser.add_argument("-c", "--channels", {
	  help: "Number of channels to generate",
	  required: true,
	});
	parser.add_argument("-p", "--programmes", {
	  help: "Number of programmes to generate",
  required: true,
	});
	const { channels: numberOfChannels, programmes: numberOfProgrammes } =
	  parser.parse_args();
	const channelFactory = (count: number) => {
	  const channels: any = [];
	  for (let i = 0; i < count; i++) {
	    channels.push({
	      id: `${i + 1}`,
	      displayName: [{ _value: `Channel ${i + 1}` }],
    });
	  }
	  return channels;
	};
	const programmeFactory = (count: number) => {
	  const programmes: any = [];
	  let currentStartTime = new Date(Date.now() - 4 * 60 * 60 * 1000);
	  for (let i = 0; i < count; i++) {
	    const duration = Math.floor(Math.random() * 180) + 30;
	    const end = new Date(currentStartTime.getTime() + duration * 60 * 1000);
    programmes.push({
	      title: `Programme ${i + 1}`,
	      desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed euismod, nisl nec ultricies lacinia, nisl nisl aliquam nisl",
	      start: currentStartTime,
	      stop: end,
	      channel: `${Math.floor(Math.random() * numberOfChannels) + 1}`,
	    });
	    currentStartTime = end;
	  }
	  return programmes;
};
	const channels = channelFactory(numberOfChannels);
	const programmes = programmeFactory(numberOfProgrammes);
	const xmltv: Xmltv = {
	  channels,
	  programmes: programmes,
	};
	const xml = writeXmltv(xmltv);
	process.stdout.write(xml + "\n");

import fs from "node:fs";
	import { describe, bench } from "vitest";
	import { parser, writeXmltv, parseXmltv } from "../src/main.js";
	import { XMLBuilder, XMLParser } from "fast-xml-parser";
	import epgParser from "epg-parser";
	import {
	  xmltvTagTranslations,
	  xmltvAttributeTranslations,
	} from "../src/xmltvTranslations.js";
	import type { XmltvTags, XmltvAttributes } from "../src/xmltvTagsAttributes.js";
import xmltv from "xmltv";
	import path from "node:path";
	const files = ["example.xml"];
	const file = files[0];
	const xmltvString = fs.readFileSync(`./tests/fixtures/${file}`, {
	  encoding: "utf-8",
	});
	const preParsed = parser(xmltvString);
	// these options bring it to feature parity with @iptv/xmltv
	const fxpParser = new XMLParser({
  ignoreAttributes: false,
	  alwaysCreateTextNode: true,
	  parseTagValue: true,
	  preserveOrder: true,
	  trimValues: true,
	  transformTagName: (tag) => xmltvTagTranslations.get(tag as XmltvTags) || tag,
	  transformAttributeName: (tag) =>
	    xmltvAttributeTranslations.get(tag as XmltvAttributes) || tag,
	});
	// unable to transform tags and attributes with XMLBuilder
const fxpBuilder = new XMLBuilder({
	  ignoreAttributes: false,
	  preserveOrder: true,
	});
	const fxpParsed = fxpParser.parse(xmltvString);
	describe("Writing XML Files", () => {
	  bench("@iptv/xmltv", () => {
	    writeXmltv(preParsed, { fromDom: true });
	  });
	  bench("fast-xml-parser", () => {
    fxpBuilder.build(fxpParsed);
	  });
	});
	describe("Parsing XML Files", () => {
	  bench("@iptv/xmltv parseXmltv", () => {
	    parseXmltv(xmltvString);
	  });
	  bench("@iptv/xmltv parser", () => {
	    parser(xmltvString);
	  });
  bench("fast-xml-parser", () => {
	    fxpParser.parse(xmltvString);
