import { describe, expect, test } from "vitest";
	import { objectToDom, parseXmltv, writeXmltv } from "../src/main";
	import fs from "node:fs";
	const fileAsString = fs.readFileSync(`./tests/fixtures/example.xml`, {
	  encoding: "utf-8",
	});
	const fileWithOnlyChannelData = fs.readFileSync(
	  `./tests/fixtures/channel-only.xml`,
	  {
	    encoding: "utf-8",
  }
	);
	describe("Parses a XMLTV file", () => {
	  test("Fully parses to a JS object", async () => {
	    const parsed = parseXmltv(fileAsString);
	    expect(parsed).toMatchSnapshot();
	  });
	  test("it can convert back to xml", () => {
	    const parsed = parseXmltv(fileAsString);
	    const xml = writeXmltv(parsed);
    expect(xml).toMatchSnapshot();
	  });
	  test("it can convert an object into the dom structure", () => {
	    const obj = parseXmltv(fileAsString);
	    const dom = objectToDom(obj);
	    expect(dom).toMatchSnapshot();
	  });
	  test("convertToDom produces the same DOM as an xml file parse", () => {
	    const [, , exampleOutput] = parseXmltv(fileAsString, { asDom: true });
	    const xml = parseXmltv(fileAsString);
    const [dom] = objectToDom(xml);
	    expect(exampleOutput).toEqual(dom);
	  });
	  test("Handles empty input string gracefully", () => {
	    const parsed = parseXmltv("");
	    expect(parsed).toEqual({});
	  });
	  test("Throws error on malformed XML input string", () => {
	    const malformedXml = "<tv><channel></tv>";
	    expect(() => parseXmltv(malformedXml)).toThrowError(`Unexpected close tag
Line: 0
	Column: 18
	Char: >`);
	  });
	  test("Parses a XMLTV file with only channel data", () => {
	    const parsed = parseXmltv(fileWithOnlyChannelData);
	    expect(parsed).toEqual({
	      channels: [{ displayName: [{ _value: "Channel 1" }], id: "1" }],
	    });
	  });
});

import { describe, expect, test } from 'vitest';
	import { dateToXmltvUtcTimestamp, xmltvTimestampToUtcDate } from '../src/utils';
	describe('dateToXmltvUtcTimestamp', () => {
	  test('should return a valid xmltv UTC timestamp', () => {
	    const date = new Date('2022-01-01T00:00:00Z');
	    const expected = '20220101000000 +0000';
	    const result = dateToXmltvUtcTimestamp(date);
	    expect(result).toEqual(expected);
	  });
	});
describe('xmltvTimestampToUtcDate', () => {
	  test('should return a valid UTC date', () => {
	    const timestamp = '20220101000000 +0000';
	    const expected = new Date('2022-01-01T00:00:00Z');
	    const result = xmltvTimestampToUtcDate(timestamp);
	    expect(result).toEqual(expected);
	  });
	  test('should handle positive timezones', () => {
	    const timestamp = '20220101000000 +0100';
	    const expected = new Date('2021-12-31T23:00:00Z');
    const result = xmltvTimestampToUtcDate(timestamp);
	    expect(result).toEqual(expected);
	  });
	  test('should handle negative timezones ', () => {
	    const timestamp = '20220101000000 -0400';
	    const expected = new Date('2022-01-01T04:00:00Z');
	    const result = xmltvTimestampToUtcDate(timestamp);
	    expect(result).toEqual(expected);
	  });
	  test('should handle misformatted timestamps ', () => {
    const timestamp = '2022 01 01 000000    -0400';
	    const expected = new Date('2022-01-01T04:00:00Z');
	    const result = xmltvTimestampToUtcDate(timestamp);
	    expect(result).toEqual(expected);
	  });
	  test('should handle iso formatted timestamps without spaces ', () => {
	    const timestamp = '2022-01-01T00:00:00.000-0400';
	    const expected = new Date('2022-01-01T04:00:00Z');
	    const result = xmltvTimestampToUtcDate(timestamp);
	    expect(result).toEqual(expected);
  });
	  test('should handle iso formatted timestamps with Z timezone', () => {
	    const timestamp = '2022-01-01T00:00:00.000Z';
	    const expected = new Date('2022-01-01T00:00:00Z');
	    const result = xmltvTimestampToUtcDate(timestamp);
	    expect(result).toEqual(expected);
	  });
	});

