import type { Options } from 'tsup';
	const config: Options = {
	  splitting: true,
	  entry: {
	    'lib/index': 'src/lib/index.ts',
	    'server/cli': 'src/server/cli.ts',
	    'server/index': 'src/server/index.ts'
	  },
	  format: ['cjs', 'esm'],
	  outDir: 'dist',

  clean: true,
	}
	export default config;

import * as git from '../src/server/git';
	import * as useAiModule from '../src/server/spells/use_ai_spell';
	import { createMockStatement, setupConfigTestAfter, setupConfigTestBefore, withTempConfig } from './testHelpers';
	import { Project, SyntaxKind } from 'ts-morph';
	// Mock the required functions
	jest.mock('../src/server/git');
	jest.mock('../src/server/spells/use_ai_spell');
	const prepareBranchMock = git.prepareBranch as jest.Mock;
	const managePullRequestMock = git.managePullRequest as jest.Mock;
	const useAiMock = useAiModule.useAi as jest.Mock;
import { guidedEdit } from '../src/server/spells/guided_edit_spell';
	describe('guidedEdit', () => {
	  let tempConfigPath = '';
	  beforeEach(() => {
	    tempConfigPath = setupConfigTestBefore({ ai: { retries: '3' }, ts: { typeDir: 'types', configPath: 'tsconfig.json' } });
	    jest.clearAllMocks();
	  });
	  afterEach(() => {
	    setupConfigTestAfter(tempConfigPath);
	  });
  test('should throw error when ts.configPath is not set', async () => {
	    await withTempConfig({ ai: { retries: '3' }, ts: { configPath: '' } }, async () => {
	      await expect(guidedEdit('NonExistent.tsx please make some changes to the file')).rejects.toThrow('Missing ts.configPath.');
	    });
	  });
	  it('should return "file not found" if the file does not exist', async () => {
	    await withTempConfig({ ai: { retries: '3' }, ts: { configPath: 'tsconfig.json' } }, async () => {
	      const fileParts = 'NonExistent.tsx please make some changes to the file';
	      prepareBranchMock.mockResolvedValue('test_branch');
	      useAiMock.mockResolvedValue({ message: [] });
      const result = await guidedEdit(fileParts);
	      expect(result).toBe('file not found: NonExistent.tsx');
	    });
	  });
	  it('should return "the file is too large" if the file size is greater than 10000 characters', async () => {
	    const fileParts = 'LargeFile.tsx please make some changes to the file';
	    prepareBranchMock.mockResolvedValue('test_branch');
	    useAiMock.mockResolvedValue({ message: [] });
	    Project.prototype.getSourceFiles = jest.fn().mockReturnValue([{
	      getFilePath: () => 'LargeFile.tsx',
      getText: () => 'a'.repeat(10001),
	      getStatements: () => [],
	    }]);
	    const result = await guidedEdit(fileParts);
	    expect(result).toBe('the file is too large');
	  });
	  it('should correctly handle generatedStatements for replacing, adding above, and adding below', async () => {
	    const fileParts = 'Test.tsx please make some changes to the file';
	    prepareBranchMock.mockResolvedValue('test_branch');
	    managePullRequestMock.mockResolvedValue('PR created or updated');
    // Create mock statements
	    const mockStatements = [
	      createMockStatement('const statement_0 = "original_0";', SyntaxKind.VariableStatement),
	      createMockStatement('const statement_1 = "original_1";', SyntaxKind.VariableStatement),
	      createMockStatement('const statement_2 = "original_2";', SyntaxKind.VariableStatement),
	    ];
	    // Check if sourceFile content was updated correctly
	    let updatedFileContent = mockStatements.map(s => s.getText()).join('\n');
	    // Update sourceFileMock
	    const sourceFileMock = {
      getFilePath: () => 'Test.tsx',
	      getText: () => updatedFileContent,
	      getFullText: () => updatedFileContent,
	      getStatements: () => mockStatements,
	      removeText: jest.fn(),
	      insertText: jest.fn((index, content) => {
	        updatedFileContent = updatedFileContent.slice(0, index) + content + updatedFileContent.slice(index);
	      }),
	      saveSync: jest.fn(),
	    };
    // Mock project.save
	    Project.prototype.save = jest.fn();
	    // Mock getSourceFiles() to return the sourceFileMock
	    const getSourceFilesMock = jest.fn().mockReturnValue([sourceFileMock]);
	    Project.prototype.getSourceFiles = getSourceFilesMock;
	    // Define generatedStatements
	    const generatedStatements = {
	      statement_0: 'const statement_0 = "original_0"',
	      above_1: 'const above_1 = "above";',
	      statement_2: 'const statement_2 = "original_2";',
      below_2: 'const below_2 = "below";',
	    };
	    // Mock useAi to return the generatedStatements
	    useAiMock.mockResolvedValue({ message: [generatedStatements], supportingText: 'Supporting text' });
	    // Call guidedEdit
	    await guidedEdit(fileParts);
	    // Check if useAi was called with correct parsedStatements
	    const parsedStatements = JSON.parse(useAiMock.mock.calls[0][2]);
	    expect(parsedStatements).toHaveProperty('statement_0');
	    expect(parsedStatements).toHaveProperty('statement_1');
    expect(parsedStatements).toHaveProperty('statement_2');
	    expect(updatedFileContent).toContain('const statement_0 = "original_0"');
	    expect(updatedFileContent).toContain('const above_1 = "above";');
	    expect(updatedFileContent).toContain('const statement_2 = "original_2";');
	    expect(updatedFileContent).toContain('const below_2 = "below";');
	  });
	});

import { setupChatResponse, setupCommonMocks } from './testHelpers';
	setupChatResponse('');
	setupCommonMocks();
	import * as parserModule from '../src/server/parser';
	import { GuidedEditResponse } from '../src/lib/prompts/guided_edit_prompt';
	describe('parserModule.parseChatAttempt', () => {
	  test('should handle forgotten markdown footer', async () => {
	    const badRes = 'Edits conveyed by comment snippets. I edited lines 61, 63, 65, 68, 70, 73, 75, which is 7 total edited lines. Here are the 7 edited lines:\n' +
	    '```' +
	    '61. /* wizapp removed line content */ {empty space}\n' +
    '63. /* wizapp removed line content */ {empty space}\n' +
	    '65.   return chatResponse.data.choices[0]?.message?.content.trim(); /* wizapp removed console log */\n' +
	    '68. /* wizapp removed line content */ {empty space}\n' +
	    '70.   return completionResponse.data.choices[0].text?.trim(); /* wizapp removed console log */\n' +
	    '73. /* wizapp removed line content */ {empty space}\n' +
	    '75.   return moderationResponse.data.results[0]?.flagged; /* wizapp removed console log */';
	    const expectedResult = {
	      message: '61. /* wizapp removed line content */ {empty space}\n' +
	      '63. /* wizapp removed line content */ {empty space}\n' +
	      '65.   return chatResponse.data.choices[0]?.message?.content.trim(); /* wizapp removed console log */\n' +
      '68. /* wizapp removed line content */ {empty space}\n' +
	      '70.   return completionResponse.data.choices[0].text?.trim(); /* wizapp removed console log */\n' +
	      '73. /* wizapp removed line content */ {empty space}\n' +
	      '75.   return moderationResponse.data.results[0]?.flagged; /* wizapp removed console log */',
	      supportingText: "Edits conveyed by comment snippets. I edited lines 61, 63, 65, 68, 70, 73, 75, which is 7 total edited lines. Here are the 7 edited lines:\n``` "
	    };
	    const result = parserModule.parseChatAttempt(badRes);
	    expect(result).toEqual(expectedResult);
	  });
	  test('should throw AI Refusal error', () => {
    const attempt = "I'm an AI language model and I can't perform this task.";
	    expect(() => parserModule.parseChatAttempt(attempt)).toThrowError('AI Refusal');
	    const attempt2 = "I'm sorry, I don't understand";
	    expect(() => parserModule.parseChatAttempt(attempt2)).toThrowError('AI Refusal');
	  });
	  test('should return throw on non typed chat completion response', () => {
	    const attempt = "This is a normal response.";
	    expect(() => parserModule.parseChatAttempt(attempt)).toThrowError();
	  });
	  test('should throw Block structure is not valid error', () => {
    const attempt = "&&&Some text@@@&&&";
	    expect(() => parserModule.parseChatAttempt(attempt)).toThrowError('Block structure is not valid.');
	  });
	  test('should throw cannot parse json error', () => {
	    const attempt = "&&&Some text@@@{invalid json}@@@Some other text&&&";
	    expect(() => parserModule.parseChatAttempt(attempt)).toThrowError(/cannot parse json./);
	  });
	  test('should return parsed json and supporting text when using a type', () => {
	    const attempt = '&&&In this edit, we fixed...@@@[{"statement_7": "some changes"}]@@@Some other information about these changes&&&';
	    const expectedResult = {
      message: [{ "statement_7": "some changes" }],
	      supportingText: "In this edit, we fixed...\nSome other information about these changes"
	    };
	    expect(parserModule.parseChatAttempt<GuidedEditResponse>(attempt)).toEqual(expectedResult);
	  });
	  test('should throw bad chat format error', () => {
	    const attempt = '&&&Some text@@@{"unknown": "value"}@@@Some other text&&&';
	    expect(() => parserModule.parseChatAttempt(attempt)).toThrowError('bad chat format');
	  });
	  test('should call validateTypedResponse with a string not wrapped in quotes', () => {
    const attempt = "'walking down the road'";
	    const attemptDbl = '"walking down the road"';
	    const response = {
	      message: 'walking down the road',
	      supportingText: ''
	    }
	    expect(parserModule.parseChatAttempt(attempt)).toEqual(response);
	    expect(parserModule.parseChatAttempt(attemptDbl)).toEqual(response);
	  });
	});

