import fs from 'fs';
	import { setConfig, defaultConfig, configFilePath } from '../src/server/config';
	import type { Config } from '../src/lib/types';
	import { SyntaxKind } from 'ts-morph';
	import { generateTempFilePath, getDirPathOf } from '../src/server/util';
	let chatResponseContent: string;
	let completionResponseText: string;
	let moderationResponseFlagged: boolean;
	export let openai: Record<string, ReturnType<ReturnType<typeof jest.fn>['mockResolvedValue']>>;
	export function setupOpenAiMocks({ chat, completion, moderation }: { chat?: string, completion?: string, moderation?: string }) {
  openai = {
	    createChatCompletion: jest.fn().mockResolvedValue({
	      data: { choices: [{ message: { content: chat || chatResponseContent } }] },
	    }),
	    createCompletion: jest.fn().mockReturnValue({
	      data: { choices: [{ text: completion || completionResponseText }] },
	    }),
	    createModeration: jest.fn().mockReturnValue({
	      data: { results: [{ flagged: moderation || moderationResponseFlagged }] },
	    }),
  };
	}
	export function setupCommonMocks() {
	  setupOpenAiMocks({});
	  jest.mock('openai', () => ({
	    OpenAIApi: jest.fn().mockImplementation(() => openai),
	  }));
	  jest.mock('ts-morph', () => ({
	    // Mock the ts-morph library's methods here.
	    Project: jest.fn().mockImplementation(() => ({
      addSourceFileAtPath: jest.fn().mockReturnValue({}),
	      getSourceFile: jest.fn().mockReturnValue({}),
	      save: jest.fn(),
	      // ...mock other methods if needed
	    })),
	    SyntaxKind: jest.fn().mockReturnValue({}),
	    FunctionDeclaration: jest.fn(),
	    Node: jest.fn()
	  }));
	}
export function setupChatResponse(content: string) {
	  chatResponseContent = content;
	}
	export function setupCompletionResponse(text: string) {
	  completionResponseText = text;
	}
	export function setupModerationResponse(flagged: boolean) {
	  moderationResponseFlagged = flagged;
	}
	export function generateTempConfigPath() {
  const configDirPath = getDirPathOf(configFilePath);
	  return generateTempFilePath(configDirPath, `test-config`);
	}
	export function setupConfigTestBefore(testConfig?: Config) {
	  const updatedConfig = Object.assign(defaultConfig, (testConfig || {}));
	  setConfig(updatedConfig);
	  // Reset the config file to default values before each test using a custom test file
	  const tempConfigPath = generateTempConfigPath();
	  fs.writeFileSync(tempConfigPath, JSON.stringify(updatedConfig, null, 2));
	  return tempConfigPath;
}
	export function setupConfigTestAfter(tempConfigPath: string) {
	  // Clean up the config file after each test
	  if (fs.existsSync(tempConfigPath)) {
	    fs.unlinkSync(tempConfigPath);
	  }
	}
	export function mockGetConfig(tempConfigPath: string) {
	  jest.resetModules();
	  jest.mock('../src/config', () => {
    const original = jest.requireActual('../src/config');
	    return {
	      ...original,
	      getConfig: () => original.getConfigFromPath(tempConfigPath),
	    };
	  });
	}
	export async function withTempConfig(tempConfig: Config, testFunc: () => Promise<void>) {
	  const tempConfigPath = setupConfigTestBefore(tempConfig);
	  mockGetConfig(tempConfigPath);
  try {
	    await testFunc();
	  } finally {
	    setupConfigTestAfter(tempConfigPath);
	  }
	}
	export function withOriginalGetConfig(testFunc: () => Promise<void>) {
	  jest.resetModules(); // Clear the Jest module cache
	  try {
	    return testFunc();
  } finally {
	    jest.resetModules(); // Clear the Jest module cache again after the test execution
	  }
	}
	export function createMockStatement(text: string, kind: SyntaxKind) {
	  return {
	    getText: () => text,
	    getFullText: () => text,
	    getKind: () => kind,
	    getParent: () => null,
  };
	}

import fs from 'fs';
	import { createComponent } from '../src/server/spells/create_component_spell';
	import * as useAiModule from '../src/server/spells/use_ai_spell';
	import { setupConfigTestAfter, setupConfigTestBefore, withOriginalGetConfig, withTempConfig } from './testHelpers';
	import { getPathOf, sanitizeName } from '../src/server/util';
	import { getConfig } from '../src/server/config';
	jest.mock('fs', () => ({
	  ...jest.requireActual('fs'),
	  writeFileSync: jest.fn(),
	  existsSync: jest.fn(),
  mkdirSync: jest.fn()
	}));
	jest.mock('../src/server/spells/use_ai_spell', () => ({
	  useAi: jest.fn(),
	}));
	const useAiMock = useAiModule.useAi as jest.Mock;
	describe('createComponent', () => {
	  let tempConfigPath = '';
	  beforeEach(() => {
	    tempConfigPath = setupConfigTestBefore({ ts: { compDir: 'components', configPath: 'tsconfig.json' } });
    useAiMock.mockClear();
	  });
	  afterEach(() => {
	    setupConfigTestAfter(tempConfigPath);
	  });
	  test('should throw error when ts.compDir is not set', async () => {
	    await withTempConfig({ ts: { compDir: '', configPath: 'tsconfig.json' } }, async () => {
	      await expect(createComponent('Create a test component', 'user123')).rejects.toThrow('Missing ts.compDir.');
	    });
	  });
  test('should create a new component', async () => {
	    await withOriginalGetConfig(async () => {
	      const config = getConfig();
	      const componentName = 'TestComponent';
	      const componentContent = `
	          import React from 'react';
	          function ${componentName}() {
	            return <div>${componentName}</div>;
	          }
	          export default ${componentName};
        `;
	      useAiMock.mockResolvedValue({ message: componentContent });
	      fs.writeFileSync = jest.fn()
	      await createComponent('Create a test component', 'user123');
	      const coreCompsPath = sanitizeName(config.ts.compDir);
	      const compFileDir = getPathOf(`${coreCompsPath}/${componentName}.tsx`);
	      const comment = `/* Created by user123, Create a test component */\n`;
	      // Check if the directory was created
	      expect(fs.existsSync).toHaveBeenCalledWith(coreCompsPath);
	      expect(fs.mkdirSync).toHaveBeenCalledWith(coreCompsPath, { recursive: true });
      expect(fs.writeFileSync).toHaveBeenCalledWith(compFileDir, `${comment}${componentContent}`)
	    });
	  });
	  test('should return an error message if the component cannot be created', async () => {
	    const invalidComponentContent = `import React from 'react';`;
	    useAiMock.mockResolvedValue({ message: invalidComponentContent });
	    const result = await createComponent('Create an invalid component', 'user123');
	    expect(result).toBe('unable to create a component ' + invalidComponentContent);
	  });
	});

import fs from 'fs';
	import { ChatResponse } from '../src/lib/types';
	import { IPrompts } from '../src/lib/prompts';
	import { logAiResult } from '../src/server/stats';
	import { GuidedEditResponse } from '../src/lib/prompts/guided_edit_prompt';
	describe('logAiResult', () => {
	  const logFilePath = './testLogFile.log';
	  const configSpy = jest.spyOn(require('../src/server/config'), 'getConfig');
	  configSpy.mockReturnValue({ ai: { logFile: logFilePath } });
	  afterEach(() => {
    if (fs.existsSync(logFilePath)) {
	      fs.unlinkSync(logFilePath);
	    }
	  });
	  test('should append a line to the log file with the correct metrics', () => {
	    const chatResponse: ChatResponse<GuidedEditResponse> = {
	      successful: true,
	      timestamp: new Date(),
	      failures: [''],
	      rawResponses: [''],
      prompts: ['test'],
	      model: 'testModel',
	      message: [{ "above_9": "yep" }],
	      supportingText: 'testSupportingText',
	      promptTemplate: 'testTemplate',
	      promptType: IPrompts.GUIDED_EDIT,
	    };
	    const expectedLogLine = {
	      pass: chatResponse.successful,
	      timestamp: chatResponse.timestamp.toISOString(),
      duration: expect.any(Number),
	      prompts: chatResponse.prompts,
	      model: chatResponse.model,
	      data: { message: chatResponse.message, supportingText: chatResponse.supportingText },
	      failures: chatResponse.failures,
	      rawResponses: chatResponse.rawResponses,
	      template: chatResponse.promptTemplate,
	      type: chatResponse.promptType,
	    };
	    logAiResult<GuidedEditResponse>(chatResponse);
    const logFileContent = fs.readFileSync(logFilePath, { encoding: 'utf-8' });
	    const loggedMetrics = JSON.parse(logFileContent);
	    console.log({ expectedLogLine, loggedMetrics })
	    expect(loggedMetrics).toEqual(expectedLogLine);
	  });
	});

