import axios from "axios";
	import { translate } from "@vitalets/google-translate-api";
	export async function googleTranslate({
	  text,
	  target_language,
	  source_language,
	}: {
	  text: string;
	  target_language: string;
	  source_language: string;
}) {
	  try {
	    const { text: textTranslated } = await translate(text, {
	      to: target_language,
	    });
	    return textTranslated;
	  } catch (error) {
	    try {
	      //fallback rate limit:
	      console.log("-- RATE LIMIT AND CALL RAPID API HOST FALLBACK --");
      const encodedParams = new URLSearchParams();
	      encodedParams.append("source_language", source_language);
	      encodedParams.append("target_language", target_language);
	      encodedParams.append("text", text);
	      const options = {
	        method: "POST",
	        url: `${process.env.TRANSLATE_URL}`,
	        headers: {
	          "content-type": "application/x-www-form-urlencoded",
	          "X-RapidAPI-Key": `${process.env.RAPID_API_KEY}`,
          "X-RapidAPI-Host": `${process.env.RAPID_API_HOST}`,
	        },
	        data: encodedParams,
	      };
	      const { data } = await axios.request(options);
	      return await data.data.translatedText;
	    } catch (error) {
	      console.error("GOOGLE TRANSLATE ERROR: ", error);
	    }
	  }
}

import { OXFORD_DICTIONARY_URL } from "../configs";
	import type { Language } from "../types";
	import axios from "axios";
	import { parse } from "node-html-parser";
	export async function getAudioOxford({ word }: { word: string }) {
	  let phonetics;
	  try {
	    const raw_phonetics = await (
	      await axios.get(`${OXFORD_DICTIONARY_URL}/definition/english/${word}`)
	    ).data;
    const document = parse(raw_phonetics);
	    phonetics = document
	      .querySelectorAll(".phonetics > div")
	      ?.map((div, idx) => {
	        return {
	          phrase: div.querySelector(".phon")?.textContent,
	          author: `OxfordDictionary_${idx === 0 ? "English" : "American"}`,
	          url: div.querySelector(".sound")?.getAttribute("data-src-mp3"),
	        };
	      });
    return phonetics ? phonetics.slice(0, 2) : phonetics;
	  } catch (error) {
	    console.error("get audio oxford error: ", error);
	  }
	  return phonetics;
	}
	export async function translateOxford({ word }: { word: string }) {
	  try {
	    const rawData = await (
	      await axios.get(`${OXFORD_DICTIONARY_URL}/definition/english/${word}`)
    ).data;
	    const document = parse(rawData);
	    const wordContent = document.querySelector(`#${word}_h_1`)?.textContent;
	    if (!wordContent) throw new Error();
	    const typesOfWord = document.querySelector(`span.pos`)?.textContent;
	    const senses = document.querySelectorAll("span.def").map((span, index) => {
	      const examples = document
	        .querySelectorAll(`#${word}_sng_${index + 1} > ul > li`)
	        .map((li) => li?.textContent);
	      return { sense: span?.textContent, typeOfWord: typesOfWord, examples };
    });
	    return {
	      wordContent,
	      typesOfWord: [typesOfWord],
	      senses,
	    };
	  } catch (error) {
	    console.log("translateOxford error: ", error);
	    return null;
	  }
}

import axios from "axios";
	import { GLOSBE_URL, GLOSBE_TRANSLATOR_API } from "../configs";
	import type { Language } from "../types";
	import { parse } from "node-html-parser";
	import { normalizeString } from "../utils/string";
	import { translate } from "@vitalets/google-translate-api";
	import { googleTranslate } from "./google.dictionary";
	export async function getAudioGlosbe({
	  format,
	  word,
}: {
	  format: Language;
	  word: string;
	}) {
	  let raw_phonetics;
	  let phonetics;
	  try {
	    raw_phonetics = await (
	      await axios.get(`${GLOSBE_URL}/api/audios/${format}/${word}`)
	    ).data;
    phonetics = raw_phonetics?.phraseAudioCarrier?.audioEntries?.map(
	      (entry: any) => {
	        return {
	          phrase: entry.phrase,
	          author: entry.author.name,
	          url: `${GLOSBE_URL}/fb_aud/mp3/${entry.url.mp3}`,
	        };
	      }
	    );
	  } catch (error) {
    console.error("get audio glosbe error: ", error);
	  }
	  return phonetics;
	}
	export async function translateWordGlosbe({
	  language_1,
	  language_2,
	  word,
	}: {
	  language_1: Language;
  language_2: Language;
	  word: string;
	}) {
	  try {
	    const raw = await (
	      await axios.get(`${GLOSBE_URL}/${language_1}/${language_2}/${word}`)
	    ).data;
	    //@ts-ignore
	    const document = parse(raw);
	    const wordContent = document.querySelector("span.break-words")?.textContent;
    const typesOfWord = document
	      .querySelectorAll(
	        "#phraseDetails_activator-0 > div.text-xl.text-gray-900.px-1.pb-1 > span.text-xxs.text-gray-500 > span"
	      )
	      .map((e) => e?.textContent);
	    const senses = document
	      .querySelectorAll(
	        "#dictionary-content > article > div > div > section.bg-white.px-1 > div.pl-1 > div > ul.pr-1 > li"
	      )
	      .map((li) => {
        const sense = li.querySelector("h3")?.textContent;
	        const typeOfWord = li.querySelector(
	          "span.text-xxs.text-gray-500 > span"
	        )?.textContent;
	        const topic = li.querySelector("div.py-1 > p > span");
	        // check if word has example or not:
	        const example = li.querySelector("div > p.dir-aware-pr-1")
	          ? {
	              [language_1]: normalizeString(
	                String(li.querySelector("div > p.dir-aware-pr-1")?.textContent)
              ),
	              [language_2]: normalizeString(
	                String(li.querySelector("div > p.px-1.ml-2")?.textContent)
	              ),
	            }
	          : undefined;
	        return {
	          sense,
	          typeOfWord,
	          category: topic ? normalizeString(String(topic).trim()) : undefined,
          example,
	        };
	      });
	    const less_frequent_senses = document
	      .querySelectorAll(
	        "#less-frequent-translations-container-0 > li > div:nth-child(2) > ul > li"
	      )
	      .map((li) => li?.textContent);
	    const similar_phrases = document
	      .querySelectorAll("#simmilar-phrases > ul > li")
      .map((li) => {
	        return {
	          [language_1]: li.querySelector("a")?.textContent,
	          [language_2]: normalizeString(
	            String(li.querySelector(".dir-aware-pl-2")?.textContent)
	          ),
	        };
	      });
	    const examples = document
	      .querySelectorAll(
        "#tmem_first_examples > div.px-1.text-sm.text-gray-900.break-words > div > div.py-2.flex"
	      )
	      .map((div) => {
	        return {
	          [language_1]: normalizeString(
	            String(div.querySelector("div:nth-child(1)")?.textContent)
	          ),
	          [`keyword_${language_1}`]: div.querySelector(
	            "div:nth-child(1) .keyword"
	          )
            ? normalizeString(
	                String(
	                  div.querySelector("div:nth-child(1) .keyword")?.textContent
	                )
	              )
	            : undefined,
	          [language_2]: normalizeString(
	            String(div.querySelector("div:nth-child(2)")?.textContent)
	          ),
	          [`keyword_${language_2}`]: div.querySelector(
            "div:nth-child(2) .keyword"
	          )
	            ? normalizeString(
	                String(
	                  div.querySelector("div:nth-child(2) .keyword")?.textContent
	                )
	              )
	            : undefined,
	        };
	      });
    if (typesOfWord && wordContent && senses) {
	      return {
	        wordContent,
	        typesOfWord,
	        senses,
	        less_frequent_senses,
	        similar_phrases,
	        examples,
	      };
	    } else {
      return null;
	    }
	  } catch (error) {
	    console.log("glosbe translate error: ", error);
	    return null;
	  }
	}
