export const normalizeString = (str: string) => {
	  const htmlTagsRegex = /(&nbsp;|<([^>]+)>)/g;
	  return str
	    .trim()
	    .replace(/(\r\n|\n|\r|\")/gm, "")
	    .replace(htmlTagsRegex, "");
	};

//@ts-nocheck
	import { prisma } from "./prismaClient";
	// prisma can not nested create in createMany, LMAO:
	// https://github.com/prisma/prisma/issues/5455
	export async function createManySense(senses, wordId) {
	  await prisma.$transaction(
	    senses.map((e) => {
	      return prisma.sense.create({
	        data: {
	          sense: e.sense,
          category: e?.category ? e?.category : undefined,
	          example: e?.example
	            ? { create: { en: e.example.en, vi: e.example.vi } }
	            : undefined,
	          typeOfWord: e?.typeOfWord
	            ? {
	                connectOrCreate: {
	                  create: { type: e.typeOfWord },
	                  where: { type: e.typeOfWord },
	                },
              }
	            : undefined,
	          Word: {
	            connect: { id: wordId },
	          },
	        },
	      });
	    })
	  );
	}
export async function connectTypeOfWord(typesOfWord, word) {
	  await prisma.$transaction(
	    typesOfWord.map((type) => {
	      return prisma.word.update({
	        where: { wordContent: word },
	        data: {
	          typesOfWord: { connect: { type } },
	        },
	      });
	    })
  );
	}

import { getAudioGlosbe, getAudioOxford, getAudioCambridge } from "../libs";
	import type { Source } from "../types";
	export async function getAudioInfo({
	  word,
	  format,
	  source,
	}: {
	  word: string;
	  format: "en" | "vi";
	  source: Source;
}) {
	  switch (source) {
	    case "glosbe":
	      return await getAudioGlosbe({ format, word });
	    case "oxford":
	      return await getAudioOxford({ word });
	    case "cambridge":
	      return await getAudioCambridge({ word });
	  }
	}

import { NextFunction, Request, Response } from "express";
	import { AnyZodObject } from "zod";
	export const validate =
	  (schema: AnyZodObject) =>
	  async (req: Request, res: Response, next: NextFunction) => {
	    try {
	      await schema.parseAsync({
	        body: req.body,
	        query: req.query,
	        params: req.params,
      });
	      return next();
	    } catch (error) {
	      return res.status(400).json(error);
	    }
	  };

