	              less_frequent_senses: { select: { sense: true } },
	              format: true,
	              wordContent: true,
	              senses: true,
	            },
	          },
	        },
	        take: Number(limit),
        skip: (Number(page) - 1) * limit,
	        orderBy: {
	          createdAt: "desc",
	        },
	      }),
	      prisma.practice.count({
	        where: {
	          //@ts-ignore
	          userId: user.id,
	          tag: { in: _tags_ },
          //@ts-ignore
	          status: { in: _status_ },
	        },
