import { html } from './test-data/offline-sites/gmod-wiki/recent-changes';
	import { main } from '../src/cli-change-checker';
	import fetchMock from "jest-fetch-mock";
	import { jest } from '@jest/globals';
	const gitTags = [
	  '2023-04-30_14-44-14',
	  '2023-04-30_14-37-57',
	  '2023-04-30_02-13-46',
	  '2023-04-10_21-17-13',
	  '2023-04-10_08-43-08',
  '2023-04-08_18-02-22',
	]
	describe('cli-change-checker', () => {
	  let mockExit: jest.SpiedFunction<(code?: number | undefined) => never>;
	  let mockConsoleError: jest.SpiedFunction<typeof console.error>;
	  beforeEach(() => {
	    mockExit = jest.spyOn(process, 'exit')
	      .mockImplementation((code) => { 
	        throw new Error(`Process.exit(${code})`); // Throw so normal execution doesn't continue
	      });
    mockConsoleError = jest.spyOn(console, 'error')
	      .mockImplementation(jest.fn());
	    const baseUrl = 'https://wiki.facepunch.com/gmod/~recentchanges';
	    fetchMock.mockResponseOnce(html, { url: baseUrl });
	  });
	  afterEach(() => {
	    mockExit.mockRestore();
	    mockConsoleError.mockRestore();
	  });
	  it('should exit with code 1 if no URL is provided', async () => {
    await expect(main({ url: '', gitTags: [] })).rejects.toThrow();
	    expect(mockConsoleError).toHaveBeenCalledWith(expect.any(String));
	    expect(mockExit).toHaveBeenCalledWith(1);
	  });
	  it('should exit with code 1 if no git tags are provided', async () => {
	    await expect(main({ url: 'https://wiki.facepunch.com/gmod/~recentchanges', gitTags: [] })).rejects.toThrow();
	    expect(mockConsoleError).toHaveBeenCalledWith(expect.any(String));
	    expect(mockExit).toHaveBeenCalledWith(1);
	  });
	  it('should exit with code 1 if the tag does not exist and there are changes', async () => {
    await expect(main({ url: 'https://wiki.facepunch.com/gmod/~recentchanges', gitTags: ['invalid-tag-name'] })).rejects.toThrow();
	    expect(mockExit).toHaveBeenCalledWith(1);
	  });
	  it('should exit with code 0 if the tag exists, meaning there is no changes', async () => {
	    await expect(main({ url: 'https://wiki.facepunch.com/gmod/~recentchanges', gitTags })).resolves.not.toThrow();
	  });
	});

import { removeNewlines, toLowerCamelCase, putCommentBeforeEachLine, safeFileName } from '../../src/utils/string';
	describe('toLowerCamelCase', () => {
	  it('should convert a string to lowerCamelCase', () => {
	    expect(toLowerCamelCase('hello world')).toBe('helloWorld');
	    expect(toLowerCamelCase('Hello World')).toBe('helloWorld');
	    expect(toLowerCamelCase('HelloWorld')).toBe('helloWorld');
	    expect(toLowerCamelCase('helloWorld')).toBe('helloWorld');
	    expect(toLowerCamelCase('hello_world')).toBe('helloWorld');
	  });
	  it('should not convert a string that is already lowerCamelCase', () => {
    expect(toLowerCamelCase('helloWorld')).toBe('helloWorld');
	  });
	});
	describe('removeNewlines', () => {
	  it.each([
	    ['hello\nworld', 'hello world'],
	    ['hello\r\nworld', 'hello world'],
	    ['hello\n\nworld', 'hello  world'],
	    ['hello\r\n\r\nworld', 'hello  world'],
	  ])('should replace newlines with spaces', (input, expected) => {
    expect(removeNewlines(input)).toBe(expected);
	  });
	});
	describe('putCommentBeforeEachLine', () => {
	  it.each([
	    ['hello\nworld', 'hello\n--- world', true],
	    ['hello\nworld', '--- hello\n--- world'],
	    ['hello\r\nworld', '--- hello\n--- world'],
	    ['hello\n\nworld', '--- hello\n--- \n--- world'],
	    ['hello\r\n\r\nworld', '--- hello\n--- \n--- world'],
    ['hello\n\n\n\nworld', '--- hello\n--- \n--- \n--- \n--- world'],
	    ['hello\r\n\r\n\r\n\r\nworld', '--- hello\n--- \n--- \n--- \n--- world'],
	  ])('should put a comment before each line', (input, expected, skipFirstLine = false) => {
	    expect(putCommentBeforeEachLine(input, skipFirstLine)).toBe(expected);
	  });
	});
	describe('safeFileName', () => {
	  it.each([
	    ['hello world','hello world'],
	    ['hello:World', 'hello_World'],
    ['hello:World', 'hello-World', '-'],
	    ['hello:World', 'helloWorld', ''],
	    ['hello:World', 'hello World', ' '],
	  ])('should make a string "%s" safe ("%s") for use as a file name', (input: string, expected: string, replacement: string|undefined = undefined) => {
	    if (replacement !== undefined)
	      expect(safeFileName(input, replacement)).toBe(expected);
	    else
	      expect(safeFileName(input)).toBe(expected);
	  });
	});

import { writeMetadata, readMetadata, metadataFilename } from '../../src/utils/metadata.js';
	import { html } from '../test-data/offline-sites/gmod-wiki/recent-changes.js';
	import fetchMock from "jest-fetch-mock";
	import path from 'path';
	import fs from 'fs';
	import os from 'os';
	describe('Metadata', () => {
	  let outputDirectory = '';
	  beforeAll(() => {
	    outputDirectory = path.join(os.tmpdir(), 'glua-wiki-metadata');
    fs.mkdirSync(outputDirectory, { recursive: true });
	  });
	  afterAll(() => {
	    fs.rmSync(outputDirectory, { recursive: true });
	  });
	  it('should be able to write metadata', async () => {
	    fetchMock.mockResponseOnce(html, { url: 'https://wiki.facepunch.com/gmod/~recentchanges' });
	    await writeMetadata('https://wiki.facepunch.com/gmod', outputDirectory);
	    expect(fs.existsSync(path.join(outputDirectory, metadataFilename))).toBeTruthy();
	  });
  it('should be able to write metadata to a non-existing directory', async () => {
	    fetchMock.mockResponseOnce(html, { url: 'https://wiki.facepunch.com/gmod/~recentchanges' });
	    const nonExistingDirectory = path.join(outputDirectory, 'non-existing');
	    await writeMetadata('https://wiki.facepunch.com/gmod', nonExistingDirectory);
	    expect(fs.existsSync(path.join(nonExistingDirectory, metadataFilename))).toBeTruthy();
	  });
	  it('should be able to read metadata', async () => {
	    fetchMock.mockResponseOnce(html, { url: 'https://wiki.facepunch.com/gmod/~recentchanges' });
	    await writeMetadata('https://wiki.facepunch.com/gmod', outputDirectory);
	    const metadata = await readMetadata(outputDirectory);
    expect(metadata).not.toBeUndefined();
	    expect(metadata!.lastUpdate).toBeTruthy();
	  });
	  describe('invalid metadata', () => {
	    let nonExistingDirectory = '';
	    beforeEach(() => {
	      nonExistingDirectory = path.join(outputDirectory, 'glua-wiki-metadata', 'non-existing');
	      fs.mkdirSync(nonExistingDirectory, { recursive: true });
	    });
	    afterEach(() => {
      fs.rmSync(nonExistingDirectory, { recursive: true });
	    });
	    it('should return undefined when reading non-existing metadata', async () => {
	      const metadata = await readMetadata(nonExistingDirectory);
	      expect(metadata).toBeUndefined();
	    });
	  });
	});

import { TypedEventEmitter } from '../../src/utils/typed-event-emitter';
	import { jest } from '@jest/globals';
	describe('TypedEventEmitter', () => {
	  it('should emit and receive events', () => {
	    const emitter = new TypedEventEmitter<{ test: [string] }>();
	    const handler = jest.fn();
	    emitter.on('test', handler);
	    emitter.emit('test', 'hello');
	    expect(handler).toHaveBeenCalledWith('hello');
	  });

  it('should not receive events after being removed', () => {
	    const emitter = new TypedEventEmitter<{ test: [string] }>();
	    const handler = jest.fn();
	    emitter.on('test', handler);
	    emitter.off('test', handler);
	    emitter.emit('test', 'hello');
	    expect(handler).not.toHaveBeenCalled();
	  });
	});

import { deserializeXml } from "../../src/utils/xml";
	const xml = `
	<test test="xxxx">
	  <complex inner="inside">
	    <with>
	      <nested>1</nested>
	      <nested>2</nested>
	      <nested>3</nested>
	    </with>
	  </complex>
</test>
	`;
	type TestType = {
	  test?: string;
	  complex: {
	    inner?: string;
	    with: {
	      nested: number[];
	    }
	  };
}
	describe('deserializeXml', () => {
	  it('should deserialize XML to described type', () => {
	    const result = deserializeXml<TestType>(xml, ($) => {
	      const root = $('test');
	      const complex = root.find('complex');
	      const nested = complex.find('nested');
	      return {
	        test: root.attr('test'),
	        complex: {
          inner: complex.attr('inner'),
	          with: {
	            nested: nested.map(function () {
	              return Number.parseInt($(this).text());
	            }).get()
	          }
	        }
	      }
	    });
	    expect(result.test).toBe('xxxx');
    expect(result.complex.inner).toBe('inside');
	    expect(result.complex.with.nested).toEqual([1, 2, 3]);
	  });
	});

