	export const metadataFilename = '__metadata.json';
	export const metadataEncoding = 'utf8';
	export type Metadata = {
	  lastUpdate: Date;
	}
export async function writeMetadata(url: string, outputDirectory: string): Promise<Metadata> {
	  const results = await scrapeAndCollect(new WikiHistoryPageScraper(path.join(url, '~recentchanges')));
	  const exists = promisify(fs.exists);
	  const lastUpdate = results[0].history[0].dateTime;
	  if (!(await exists(outputDirectory)))
	    fs.mkdirSync(outputDirectory, { recursive: true });
	  const write = await promisify(fs.writeFileSync);
	  const metadataPath = path.join(outputDirectory, metadataFilename);
	  const metadata: Metadata = { lastUpdate };
	  write(metadataPath, JSON.stringify(metadata, null, 2), metadataEncoding);
  return metadata;
	}
	export async function readMetadata(outputDirectory: string) : Promise<Metadata | undefined> {
	  const metadataPath = path.join(outputDirectory, metadataFilename);
	  const exists = promisify(fs.exists);
	  if (!(await exists(metadataPath)))
	    return undefined;
	  const read = promisify(fs.readFile);
	  const metadata = JSON.parse(await read(metadataPath, metadataEncoding));
	  return {
    lastUpdate: new Date(metadata.lastUpdate)
	  };
	}

/**
	 * Converts a string to lowerCamelCase 
	 * 
	 * @param str The string to convert
	 * @returns The converted string
	 */
	export function toLowerCamelCase(str: string) {
	  str = str.replace(/[_-]/g, ' ');
	  return str.replace(/(?:^\w|\b\w)/g, (letter, index) => {
	    return index === 0 ? letter.toLowerCase() : letter.toUpperCase();
  }).replace(/\s+/g, '');
	}
	/**
	 * Replaces all newlines in a string with spaces
	 */
	export function removeNewlines(text: string) {
	  return text.replace(/\r?\n/g, ' ');
	}
	/**
	 * Puts a comment before each line in a string
 */
	export function putCommentBeforeEachLine(text: string, skipLineOne: boolean = true) {
	  return text.split(/\r?\n/g).map((line, index) => {
	    if (index === 0 && skipLineOne)
	      return line;
	    return `--- ${line}`;
	  }).join('\n');
	}
	/**
	 * Makes a string safe for use as a file name
 * 
	 * @param str The string to make safe
	 * @param replacement The string to replace unsafe characters with
	 * @returns The safe string
	 */
	export function safeFileName(str: string, replacement: string = '_') {
	  return str.replace(/[^a-z0-9_\-\. ]/gi, replacement);
	}

import EventEmitter from "events"
	/**
	 * Source: https://blog.makerx.com.au/a-type-safe-event-emitter-in-node-js/
	 */
	export class TypedEventEmitter<TEvents extends Record<string, any>> {
	  private emitter = new EventEmitter()
	  emit<TEventName extends keyof TEvents & string>(
	    eventName: TEventName,
	    ...eventArg: TEvents[TEventName]
	  ) {
    this.emitter.emit(eventName, ...(eventArg as []))
	  }
	  on<TEventName extends keyof TEvents & string>(
	    eventName: TEventName,
	    handler: (...eventArg: TEvents[TEventName]) => void
	  ) {
	    this.emitter.on(eventName, handler as any)
	  }
	  off<TEventName extends keyof TEvents & string>(
	    eventName: TEventName,
    handler: (...eventArg: TEvents[TEventName]) => void
	  ) {
	    this.emitter.off(eventName, handler as any)
	  }
	}

