	   */
	  public getScrapeCallback(): ScrapeCallback<T> {
	    return JsonScraper.makeScrapeCallback<T>();

  }
	  public static makeScrapeCallback<T extends object = object>(): ScrapeCallback<T> {
	    return (response: Response, content: string): T[] => {
