	    };
	  }
	}

import { Scraper, ScrapeResult } from "./scraper.js";
	export async function scrapeAndCollect<T extends ScrapeResult>(scraper: Scraper<T>) {
	  const collected: T[] = [];
	  scraper.on("scraped", (url: string, results: T[]) => {
	    collected.push(...results);
	  });
	  await scraper.scrape();
	  return collected;
	}

import { Page, PageTraverseScraper } from './page-traverse-scraper.js';
	import { ScrapeCallback } from './scraper.js';
	import * as cheerio from 'cheerio';
	export interface WikiHistory {
	  dateTime: Date;
	  change: string;
	  user: string;
	  url: string;
	}
	export class WikiHistoryPage extends Page {
  public history: WikiHistory[] = [];
	  constructor(url: string, title: string) {
	    super(url, title);
	  }
	}
	export class WikiHistoryPageScraper extends PageTraverseScraper<WikiHistoryPage> {
	  constructor(baseUrl: string) {
	    super(baseUrl, (url: string, title: string) => new WikiHistoryPage(url, title));
	  }
	  /**
   * Scrapes a wiki history page for information on wiki changes
	   * 
	   * @param response The response from the page
	   * @param content The content of the request
	   * 
	   * @returns A list containing only the scraped page
	   */
	  public getScrapeCallback(): ScrapeCallback<WikiHistoryPage> {
	    const baseScrapeCallback = super.getScrapeCallback();
	    return async (response: Response, content: string): Promise<WikiHistoryPage[]> => {
      const pages = await baseScrapeCallback(response, content);
	      if (pages.length === 0)
	        return [];
	      // There is only one page per response
	      const page = pages[0];
	      const $ = cheerio.load(content);
	      const changeElements = $('table.changelist > tbody > .entry');
	      if (!changeElements)
	        return [page];
	      for (const changeElement of changeElements) {
        const pageLinkElement = $(changeElement).find('.address');
	        const pageLinkAnchorElement = pageLinkElement.find('a');
	        const user = $(changeElement).find('.user a').text();
	        const rawDateTime = pageLinkAnchorElement.attr('title') || ''; // Always in 4/30/2023 2:13:46 AM format
	        const dateTimeParts = rawDateTime.split(' ');
	        const dateParts = dateTimeParts[0].split('/');
	        const timeParts = dateTimeParts[1].split(':');
	        const dateTime = new Date(
	          Date.UTC(
	            parseInt(dateParts[2]), // Year
            parseInt(dateParts[0]) - 1, // Month
	            parseInt(dateParts[1]), // Day
	            parseInt(timeParts[0]) + (dateTimeParts[2] === 'PM' ? 12 : 0), // Hour
	            parseInt(timeParts[1]), // Minute
	            parseInt(timeParts[2]), // Second
	          ),
	        );
	        const url = pageLinkAnchorElement.attr('href') || '';
	        const change = $(pageLinkElement).text().replace($(pageLinkAnchorElement).text(), '').replace(/\s+/g, ' ');
	        page.history.push({
          dateTime,
	          user,
	          change,
	          url
	        });
	      }
	      return [ page ];
	    };
	  }
	}

import { Scraper, ScrapeCallback } from './scraper.js';
	export interface Scrapeable {
	  childUrls: Set<string>;
	}
	export class TraverseScraper<T extends Scrapeable> extends Scraper<T> {
	  protected readonly traversedUrls: Set<string> = new Set();
	  protected childPageFilter?: (url: string) => boolean;
	  public setChildPageFilter(filter: (url: string) => boolean): void {
	    this.childPageFilter = filter;
	  }
  /**
	   * Override scraping so we traverse all child URLs of the first scraped page
	   */
	  public async scrape(): Promise<void> {
	    const callback = this.getScrapeCallback();
	    await this.traverse(this.baseUrl, callback.bind(this));
	  }
	  protected getTraverseUrl(url: string): string | false {
	    if (!url.startsWith(this.baseUrl))
	      return false;
    if (url.endsWith('/'))
	      url = url.substring(0, url.length - 1);
	    if (url.includes('#'))
	      url = url.substring(0, url.indexOf('#'));
	    if (this.traversedUrls.has(url))
	      return false;
	    if (this.childPageFilter && !this.childPageFilter(url))
	      return false;
	    return url;
	  }
  public async traverse(url: string, callback?: ScrapeCallback<T>): Promise<void> {
	    if (!callback)
	      callback = this.getScrapeCallback();
	    const urlsToTraverse: string[] = [url];
	    while (urlsToTraverse.length > 0) {
	      let currentUrl = urlsToTraverse.shift()!;
	      let url = this.getTraverseUrl(currentUrl);
	      if (!url)
	        continue;
	      const currentResults = await this.visitOne(url, callback);
      this.traversedUrls.add(url);
	      for (const result of currentResults) {
	        for (const childUrl of result.childUrls) {
	          const traverseUrl = this.getTraverseUrl(childUrl);
	          if (traverseUrl && !urlsToTraverse.includes(traverseUrl))
	            urlsToTraverse.push(traverseUrl);
	        }
	      }
	    }
	  }
}

